INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5B74E395CA13E04DAAFC78C3D7C31FA8', X'6E2EEAC4B1C89E4FA0803F7B17142098', X'6E2EEAC4B1C89E4FA0803F7B17142098', 2, NULL, NULL, 2, NULL, 0, 'Add the sliced onion and Worcestershire sauce.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AAB79D757985F8418D4FFDE3EE253DFA', X'6E2EEAC4B1C89E4FA0803F7B17142098', X'6E2EEAC4B1C89E4FA0803F7B17142098', 3, NULL, NULL, 2, NULL, 0, 'Cover the pork with water.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'01A0AAEA6AA7A3429CBAE2CE576F5AA6', X'6E2EEAC4B1C89E4FA0803F7B17142098', X'6E2EEAC4B1C89E4FA0803F7B17142098', 4, NULL, NULL, 2, NULL, 0, 'Cook on low all day (at least 12 hours).');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EB482FB3BB686E4ABADD7C9613684EBA', X'6E2EEAC4B1C89E4FA0803F7B17142098', X'6E2EEAC4B1C89E4FA0803F7B17142098', 5, NULL, NULL, 2, NULL, 0, 'Remove roast, discarding cooking water. Save onions.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E54FBF308021F845A6F327778E0D3649', X'6E2EEAC4B1C89E4FA0803F7B17142098', X'6E2EEAC4B1C89E4FA0803F7B17142098', 6, NULL, NULL, 2, NULL, 0, 'Fork-shred pork, discarding any fat and bones.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AA5B7D1D15848846BA219105304CFF0C', X'6E2EEAC4B1C89E4FA0803F7B17142098', X'6E2EEAC4B1C89E4FA0803F7B17142098', 7, NULL, NULL, 2, NULL, 0, 'Return shredded meat and onions to the slow cooker.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8A938021200EEA42ABF7DFD0D534A8B6', X'6E2EEAC4B1C89E4FA0803F7B17142098', X'6E2EEAC4B1C89E4FA0803F7B17142098', 8, NULL, NULL, 2, NULL, 0, 'Dump a bottle of your favorite barbecue sauce over it, and cook an hour or so on low until heated thoroughly.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5A989C918CD0E64BA9CD628167F8560A', X'6E2EEAC4B1C89E4FA0803F7B17142098', X'6E2EEAC4B1C89E4FA0803F7B17142098', 9, NULL, NULL, 2, NULL, 0, 'Serve on heated buns.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BFC122343649974EA4C812E906716222', X'6E2EEAC4B1C89E4FA0803F7B17142098', X'6E2EEAC4B1C89E4FA0803F7B17142098', 10, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'255F2C0D9A31CA4B831B7B44E692789C', X'B5648648B30ACA47B90123D9445C6A9D', X'B5648648B30ACA47B90123D9445C6A9D', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'117CFF360A4A614A968C7A174B709969', X'B5648648B30ACA47B90123D9445C6A9D', X'B5648648B30ACA47B90123D9445C6A9D', 1, NULL, NULL, 2, NULL, 0, 'In a medium mixing bowl, combine rub ingredients into a paste. Coat pork shoulder with rub, cover with plastic wrap and refrigerate 1 hour.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CF7C4994DF6F064287620C83DA4B7C19', X'B5648648B30ACA47B90123D9445C6A9D', X'B5648648B30ACA47B90123D9445C6A9D', 2, NULL, NULL, 2, NULL, 0, 'Preheat smoker and prepare for an 8-hour smoke. Make sure wood chips are soaked and drained before using them. Add charcoal and wood chips as necessary to keep the temperature low, or between 200 and 225 F/95 to 110 C. Smoke pork shoulder for about 8 hours or until internal temperatures reach 185 F/85 C.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2DD773E230C69F468F5CCC613FAA0B9F', X'B5648648B30ACA47B90123D9445C6A9D', X'B5648648B30ACA47B90123D9445C6A9D', 3, NULL, NULL, 2, NULL, 0, 'Once the pork is cooked, remove from the smoker. Wrap tightly in foil and allow to sit for an hour. While pork is resting, prepare finishing sauce. Blend together everything except the ketchup.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'61CC2A6F209BF441B783118049EED846', X'B5648648B30ACA47B90123D9445C6A9D', X'B5648648B30ACA47B90123D9445C6A9D', 4, NULL, NULL, 2, NULL, 0, 'Pour into a saucepan and add the ketchup. Simmer over low heat for 15 minutes. Remove from heat and set aside.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DC07DCCBAC82A745B60410A22F661EF7', X'B5648648B30ACA47B90123D9445C6A9D', X'B5648648B30ACA47B90123D9445C6A9D', 5, NULL, NULL, 2, NULL, 0, 'Unwrap pork butt and shred. Combine pork with 1/3 of the table sauce. Keep warm and serve on white bread buns with remaining sauce on the side. Top with coleslaw.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FB43C7CA175D2D4C9764906E034F31F3', X'B5648648B30ACA47B90123D9445C6A9D', X'B5648648B30ACA47B90123D9445C6A9D', 6, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9D66309595E7394E933F53442B7F603F', X'16685DB46F4B214AB45DD3D99901CB48', X'16685DB46F4B214AB45DD3D99901CB48', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'88A21877AE916C498A204CF334DDB432', X'16685DB46F4B214AB45DD3D99901CB48', X'16685DB46F4B214AB45DD3D99901CB48', 1, NULL, NULL, 2, NULL, 0, 'Place the pork shoulder in the slow cooker. Add the water and sprinkle with salt and pepper. If desired, add chopped onions.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D8B3A42FFD29DA46AC7748A49345E924', X'16685DB46F4B214AB45DD3D99901CB48', X'16685DB46F4B214AB45DD3D99901CB48', 2, NULL, NULL, 2, NULL, 0, 'Cover and cook on HIGH for 1 hour. Turn to LOW and cook 7 to 9 hours longer, until very tender.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'158353F667D09E498E6B8D5030C1845A', X'16685DB46F4B214AB45DD3D99901CB48', X'16685DB46F4B214AB45DD3D99901CB48', 3, NULL, NULL, 2, NULL, 0, 'Remove roast and discard the fat and juices. Chop or shred the pork and then return the meat to the slow cooker.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4CF359936B6FF14D871B29B454EF990D', X'16685DB46F4B214AB45DD3D99901CB48', X'16685DB46F4B214AB45DD3D99901CB48', 4, NULL, NULL, 2, NULL, 0, 'Mix some barbecue sauce into the meat for flavorabout 1/2 to 1 cup of sauce. Cover and cook on LOW for about 30 minutes to 1 hour longer, until hot.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C66C540ED64A624BB0837E4C83881E62', X'16685DB46F4B214AB45DD3D99901CB48', X'16685DB46F4B214AB45DD3D99901CB48', 5, NULL, NULL, 2, NULL, 0, 'Serve as-is or on warm split sandwich buns with coleslaw and extra barbecue sauce on the side.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AE84850CBB913C41AA409FC86429F539', X'16685DB46F4B214AB45DD3D99901CB48', X'16685DB46F4B214AB45DD3D99901CB48', 6, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'898D9170423A244887C90FABB8BC746F', X'6E690B6AE99881479CEA4E24973E6B55', X'6E690B6AE99881479CEA4E24973E6B55', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8EA00F32011395448F71F18EC8F3E5EB', X'6E690B6AE99881479CEA4E24973E6B55', X'6E690B6AE99881479CEA4E24973E6B55', 1, NULL, NULL, 2, NULL, 0, 'In a saucepan, combine the vinegar, sugar, red pepper, 1 teaspoon of salt, and the 1/4 teaspoon of pepper. Bring to a boil; remove from heat.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ACB168B9E35D8C4B8C835CEDD43CC759', X'6E690B6AE99881479CEA4E24973E6B55', X'6E690B6AE99881479CEA4E24973E6B55', 2, NULL, NULL, 2, NULL, 0, 'Save about 2 cups of the vinegar mixture for serving, and use the rest for basting.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0490BE1B07DEFD4186C8F0B01A164684', X'6E690B6AE99881479CEA4E24973E6B55', X'6E690B6AE99881479CEA4E24973E6B55', 3, NULL, NULL, 2, NULL, 0, 'Sprinkle the pork roast with salt and pepper; place on a foil-lined baking or roasting pan.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F413FD53A2DEE045991106AAD52DE5F7', X'6E690B6AE99881479CEA4E24973E6B55', X'6E690B6AE99881479CEA4E24973E6B55', 4, NULL, NULL, 2, NULL, 0, 'Roast, uncovered, at 275 F for about 7 hours, or until the roast reaches 200 F on a meat thermometer or oven probe inserted in the center of the thickest part of the roast.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D7FAC6D3DC239D43B7292C4BC40E0DF4', X'6E690B6AE99881479CEA4E24973E6B55', X'6E690B6AE99881479CEA4E24973E6B55', 5, NULL, NULL, 2, NULL, 0, 'Baste about every hour with the sauce saved for basting. The roast should be very tender.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A87A4FF7D481C1479FEF29027661DB4F', X'6E690B6AE99881479CEA4E24973E6B55', X'6E690B6AE99881479CEA4E24973E6B55', 6, NULL, NULL, 2, NULL, 0, 'Remove the roast and shred with 2 forks, discarding large pieces of fat.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6A2EA0A65998E54D8FCCE2F175965E0F', X'6E690B6AE99881479CEA4E24973E6B55', X'6E690B6AE99881479CEA4E24973E6B55', 7, NULL, NULL, 2, NULL, 0, 'Warm the reserved sauce and pass at the table for people to spoon over their pork. (You can also serve barbecue sauce with this pork; it''s delicious as a topping with coleslaw and pickle slices.)');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'69720297848FD1478F2D708BF13AFF69', X'6E690B6AE99881479CEA4E24973E6B55', X'6E690B6AE99881479CEA4E24973E6B55', 8, NULL, NULL, 2, NULL, 0, 'Serve with slaw, pickles, toasted buns, and baked beans or your favorite sides.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E5552F00B4734241863030A5A7262EEA', X'4AF6D59AE251A0458753DAB4A4485AFE', X'4AF6D59AE251A0458753DAB4A4485AFE', 0, NULL, NULL, 2, NULL, 0, 'Lay a large sheet of heavy duty foil in a roasting pan (large enough to wrap the roast).');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'481E6D81E6D9164CA47AFDEF07553AD6', X'4AF6D59AE251A0458753DAB4A4485AFE', X'4AF6D59AE251A0458753DAB4A4485AFE', 1, NULL, NULL, 2, NULL, 0, 'Place pork roast on foil. Rub pork all over with garlic and seasonings. Arrange onion slices over top.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5748C68BCF6565479BDC3730F0764D60', X'4AF6D59AE251A0458753DAB4A4485AFE', X'4AF6D59AE251A0458753DAB4A4485AFE', 2, NULL, NULL, 2, NULL, 0, 'Wrap foil around the pork roast.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B98F92E419811140902A937A322EAA62', X'4AF6D59AE251A0458753DAB4A4485AFE', X'4AF6D59AE251A0458753DAB4A4485AFE', 3, NULL, NULL, 2, NULL, 0, 'Bake at 350 F for about 4 to 5 hours, to about 185 F. The meat should be falling apart.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0934C971D02ABB448879586B1B7B81E4', X'4AF6D59AE251A0458753DAB4A4485AFE', X'4AF6D59AE251A0458753DAB4A4485AFE', 4, NULL, NULL, 2, NULL, 0, 'Let the roast cool a bit, then begin to shred with forks or chop, discarding as much of the excess fat as possible.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4A457F03B1AFBF438B23D93C06D8E7ED', X'4AF6D59AE251A0458753DAB4A4485AFE', X'4AF6D59AE251A0458753DAB4A4485AFE', 5, NULL, NULL, 2, NULL, 0, 'Saut chopped onions in a little oil until tender.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'812EC6A8FC9F774391C858DDE3A60926', X'4AF6D59AE251A0458753DAB4A4485AFE', X'4AF6D59AE251A0458753DAB4A4485AFE', 6, NULL, NULL, 2, NULL, 0, 'Put shredded pork in a slow cooker or a large saucepan or Dutch oven.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2D6BF309857D494BA00F682D01C7E69D', X'4AF6D59AE251A0458753DAB4A4485AFE', X'4AF6D59AE251A0458753DAB4A4485AFE', 7, NULL, NULL, 2, NULL, 0, 'Blend in vinegar and barbecue sauce, along with the cooked chopped onion.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'82793C6B818F86448682F74A56B37A36', X'4AF6D59AE251A0458753DAB4A4485AFE', X'4AF6D59AE251A0458753DAB4A4485AFE', 8, NULL, NULL, 2, NULL, 0, 'Heat thoroughly on LOW setting or over low heat.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2A186E5E0CD828488222D0665A6D1639', X'4AF6D59AE251A0458753DAB4A4485AFE', X'4AF6D59AE251A0458753DAB4A4485AFE', 9, NULL, NULL, 2, NULL, 0, 'Serve hot on split toasted buns, coleslaw, and baked beans. Pulled pork is also excellent with potato salad or fries.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CE82D44ADA57F245B0C389BC37CD068D', X'71D788D704639245B45F3E2CB55A1E19', X'71D788D704639245B45F3E2CB55A1E19', 0, NULL, NULL, 2, NULL, 0, 'Arrange the sliced onions in the slow cooker. Top with the pork roast (netting removed), then sprinkle with salt, pepper and the chili powder.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B160308DEA94984B9C7529A81C6694B2', X'71D788D704639245B45F3E2CB55A1E19', X'71D788D704639245B45F3E2CB55A1E19', 1, NULL, NULL, 2, NULL, 0, 'Cover and cook for 8 to 10 hours, until tender enough to shred.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'415EF1DF5F79944C9DCA3EC908D10806', X'71D788D704639245B45F3E2CB55A1E19', X'71D788D704639245B45F3E2CB55A1E19', 2, NULL, NULL, 2, NULL, 0, 'Meanwhile, in a saucepan over medium heat, melt the butter. Add the chopped onion and saut it until the onion is translucent.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C63CAFBF80233446AB80D76C10BF19F7', X'71D788D704639245B45F3E2CB55A1E19', X'71D788D704639245B45F3E2CB55A1E19', 3, NULL, NULL, 2, NULL, 0, 'Add the garlic and saut for 2 minutes longer.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2AFB33739BBC1D40AE86ECD03E35D2B3', X'71D788D704639245B45F3E2CB55A1E19', X'71D788D704639245B45F3E2CB55A1E19', 4, NULL, NULL, 2, NULL, 0, 'Add the ketchup, lemon juice, brown sugar, molasses, Worcestershire sauce, mustard, 1/4 teaspoon black pepper, and 1/2 teaspoon of crushed red pepper. To keep splatters to a minimum, cover with a splatter screen or cover loosely with a lid. Bring to a simmer and continue cooking for about 10 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'22D96506A173C84B9225F191238B7678', X'71D788D704639245B45F3E2CB55A1E19', X'71D788D704639245B45F3E2CB55A1E19', 5, NULL, NULL, 2, NULL, 0, 'Remove the cooked pork shoulder and place it on a cutting board or in a large bowl. Shred it and discard excess fats and liquids. Return the shredded pork to the slow cooker with some of the sauce from the saucepan. Cover and cook on the low setting for about 30 minutes to 1 hour longer.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E2C1CCAAC933554996DC23D146E25717', X'71D788D704639245B45F3E2CB55A1E19', X'71D788D704639245B45F3E2CB55A1E19', 6, NULL, NULL, 2, NULL, 0, 'Serve the pork barbecue on split rolls with the remaining sauce on the side. Add coleslaw and dill pickle slices, along with your favorite sides.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CE0B1E8CA661C74ABE7C5C45E53C905E', X'96DFF5409989AA4682BF762A67EDEDC1', X'96DFF5409989AA4682BF762A67EDEDC1', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D93524B5D9D8F34CAD45E29595229673', X'96DFF5409989AA4682BF762A67EDEDC1', X'96DFF5409989AA4682BF762A67EDEDC1', 1, NULL, NULL, 2, NULL, 0, 'In a small pan, cook pork shoulder with just enough water to cover, for 15 to 20 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1413D26A6E2D0244A08805A7DF195454', X'96DFF5409989AA4682BF762A67EDEDC1', X'96DFF5409989AA4682BF762A67EDEDC1', 2, NULL, NULL, 2, NULL, 0, 'When cool enough to handle, cut meat and fat into large pieces. Reserve the cooking water and let it cool.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'395584C66BDCDB49AD7A3D28E6D5137B', X'96DFF5409989AA4682BF762A67EDEDC1', X'96DFF5409989AA4682BF762A67EDEDC1', 3, NULL, NULL, 2, NULL, 0, 'Meanwhile, in a small skillet, saut bacon until golden.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8AAA97E010DA26448A7E7D3C89FBCFF9', X'96DFF5409989AA4682BF762A67EDEDC1', X'96DFF5409989AA4682BF762A67EDEDC1', 4, NULL, NULL, 2, NULL, 0, 'Add onions and saut 5 minutes more, remove from heat and set aside.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'27C741EED026534BB72734A4A0BAAAE3', X'96DFF5409989AA4682BF762A67EDEDC1', X'96DFF5409989AA4682BF762A67EDEDC1', 5, NULL, NULL, 2, NULL, 0, 'Using a medium grinding blade, push the cooled pork pieces (fat and all) and potatoes down the chute of your grinder using a wooden pestle. Don''t ever put your fingers in a grinder, running or not running!');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EECF01FD0D7C5849817DE6BF798B97E9', X'96DFF5409989AA4682BF762A67EDEDC1', X'96DFF5409989AA4682BF762A67EDEDC1', 6, NULL, NULL, 2, NULL, 0, 'Add cooked bacon and onions, salt, pepper, marjoram, and peppermint leaves to the water the meat was cooked in and mix well.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F7A0197D18AFB24AA2374884FF7A410B', X'96DFF5409989AA4682BF762A67EDEDC1', X'96DFF5409989AA4682BF762A67EDEDC1', 7, NULL, NULL, 2, NULL, 0, 'Then combine with meat-potato mixture until just moistened.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F84E09FA73A81243A9DE5B6D10D19182', X'96DFF5409989AA4682BF762A67EDEDC1', X'96DFF5409989AA4682BF762A67EDEDC1', 8, NULL, NULL, 2, NULL, 0, 'Fry a small patty of meat and taste to make sure the seasonings are just right.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FF051DC99EC4BD44B6B4F14816225F4D', X'96DFF5409989AA4682BF762A67EDEDC1', X'96DFF5409989AA4682BF762A67EDEDC1', 9, NULL, NULL, 2, NULL, 0, 'Using the procedure for stuffing Polish sausage, stuff the meat-potato mixture into the casings. Store refrigerated and covered up to two days until ready to cook.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'58631BCD35A62E4E9CF39755D5E0D1A9', X'96DFF5409989AA4682BF762A67EDEDC1', X'96DFF5409989AA4682BF762A67EDEDC1', 10, NULL, NULL, 2, NULL, 0, 'Before cooking, prick sausage along the length of the link to allow air bubbles to escape. Otherwise, they will explode in the cooking water.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AA190EC1E5E16D40BA09967FABB30B33', X'96DFF5409989AA4682BF762A67EDEDC1', X'96DFF5409989AA4682BF762A67EDEDC1', 11, NULL, NULL, 2, NULL, 0, 'Place sausage in a large pot and cover with water. Bring to a boil over high heat. Reduce heat to medium-low and simmer uncovered for 45 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F1A71728B2F28A40BDC3EBBAD541711C', X'96DFF5409989AA4682BF762A67EDEDC1', X'96DFF5409989AA4682BF762A67EDEDC1', 12, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'711972D706D8FA44917ACADEAFF8B873', X'4CEFEF71E9E84F42B9E6CAEF61ECD9E7', X'4CEFEF71E9E84F42B9E6CAEF61ECD9E7', 0, NULL, NULL, 2, NULL, 0, 'Remove the netting from the pork roast and cut in half. Sprinkle all over with salt and pepper.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'92AD4C9FB350DC45B6DDEA0A3B1B8E0C', X'4CEFEF71E9E84F42B9E6CAEF61ECD9E7', X'4CEFEF71E9E84F42B9E6CAEF61ECD9E7', 1, NULL, NULL, 2, NULL, 0, 'In a large skillet, heat the oil over medium heat.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'41761CDEF0E9914B9E94E3E8086CB7FE', X'4CEFEF71E9E84F42B9E6CAEF61ECD9E7', X'4CEFEF71E9E84F42B9E6CAEF61ECD9E7', 2, NULL, NULL, 2, NULL, 0, 'Brown the pork on all sides in the oil; transfer the pork to the slow cooker, keeping the oil in the skillet.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0387D31C783EE247AFE0602573903D13', X'4CEFEF71E9E84F42B9E6CAEF61ECD9E7', X'4CEFEF71E9E84F42B9E6CAEF61ECD9E7', 3, NULL, NULL, 2, NULL, 0, 'Brown the onions in the same oil in the skillet, and then transfer to the slow cooker.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9CC403C0E43F544C86261DCF30827F55', X'4CEFEF71E9E84F42B9E6CAEF61ECD9E7', X'4CEFEF71E9E84F42B9E6CAEF61ECD9E7', 4, NULL, NULL, 2, NULL, 0, 'Combine the barbecue sauce, chipotle peppers with adobo, and Worcestershire sauce in a bowl or measuring cup; pour over the pork and onions.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F7703BBE070F58459411149A4E7D7906', X'4CEFEF71E9E84F42B9E6CAEF61ECD9E7', X'4CEFEF71E9E84F42B9E6CAEF61ECD9E7', 5, NULL, NULL, 2, NULL, 0, 'Cover and cook on high for 4 1/2 to 6 hours, or until the pork is easily shredded with two forks.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1009821E25773F469E43572D5037B459', X'4CEFEF71E9E84F42B9E6CAEF61ECD9E7', X'4CEFEF71E9E84F42B9E6CAEF61ECD9E7', 6, NULL, NULL, 2, NULL, 0, 'Remove the pork to a large bowl. Using two forks, shred the pork.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'412D13247B54DD49BF9442632579B9B1', X'4CEFEF71E9E84F42B9E6CAEF61ECD9E7', X'4CEFEF71E9E84F42B9E6CAEF61ECD9E7', 7, NULL, NULL, 2, NULL, 0, 'If the liquid in the crockpot is very thin, transfer it to a saucepan and boil for 5 to 7 minutes to reduce and thicken somewhat. Pour the liquid back into the slow cooker; add the shredded pork and continue cooking on low for about 20 to 30 minutes longer.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2F2F7843BAA9B3478E864811FD6F8840', X'4CEFEF71E9E84F42B9E6CAEF61ECD9E7', X'4CEFEF71E9E84F42B9E6CAEF61ECD9E7', 8, NULL, NULL, 2, NULL, 0, 'Serve the pulled pork on split toasted rolls with coleslaw and baked beans, along with macaroni and cheese, potato salad, or your favorite sides for barbecue.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0791C2DE25A4694A94EC235DD35C002F', X'4CEFEF71E9E84F42B9E6CAEF61ECD9E7', X'4CEFEF71E9E84F42B9E6CAEF61ECD9E7', 9, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A8679B01E344154997E8DAC571F8AEEC', X'B85D625D467E0947B5C9739956950A6A', X'B85D625D467E0947B5C9739956950A6A', 0, NULL, NULL, 2, NULL, 0, 'Heat the oven to 350 F.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6A23DCAFEEC1674A838280BAAF6F1D46', X'B85D625D467E0947B5C9739956950A6A', X'B85D625D467E0947B5C9739956950A6A', 1, NULL, NULL, 2, NULL, 0, 'Lay a large sheet of heavy duty foil in a roasting pan. Place the pork roast on foil. Rub pork all over with garlic and seasonings. Arrange the medium onion slices over the top. Wrap the foil around the pork roast.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'608126353DF4ED41BF618ECD9C439E5C', X'B85D625D467E0947B5C9739956950A6A', X'B85D625D467E0947B5C9739956950A6A', 2, NULL, NULL, 2, NULL, 0, 'Bake the pork in the preheated oven for about 4 to 5 hours, or until the pork is very tender and falling apart. When the meat is cool enough to handle, remove the bones and begin to shred the meat with forks or chop, discarding as much of the excess fat as possible.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3FE2ECF7C20EA94DA54E1D649D77FE66', X'B85D625D467E0947B5C9739956950A6A', X'B85D625D467E0947B5C9739956950A6A', 3, NULL, NULL, 2, NULL, 0, 'Saut the chopped onions in oil until tender. Put the shredded pork in a slow cooker. Blend in the 1 cup barbecue sauce and vinegar, along with the sauted onion. Heat the pork on LOW setting for about 2 hours, or until hot and flavorful. Or, simmer, covered, over low heat for about 30 minutes to 1 hour.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'59F491BD8632F2428C0B494AA3F0A23E', X'B85D625D467E0947B5C9739956950A6A', X'B85D625D467E0947B5C9739956950A6A', 4, NULL, NULL, 2, NULL, 0, 'While the pork is in the slow cooker, make the buns.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E461748655989C4FADBBA7AC4999D6DC', X'B85D625D467E0947B5C9739956950A6A', X'B85D625D467E0947B5C9739956950A6A', 5, NULL, NULL, 2, NULL, 0, 'In a bowl or large cup, whisk together the milk and yeast. Let the mixture stand at room temperature until the yeast has dissolved. Place the milk and yeast mixture, melted butter and beaten egg, flour, sugar, and 1 teaspoon of salt in the bread machine in the order suggested by your bread machine manufacturer. Run on dough cycle. Remove finished dough to a lightly floured surface. Roll half of the dough to about 1/4-inch thickness or slightly more. Cut out with a 1 1/2-inch cutter. Or make your sandwiches slider-size using a 2-inch cutter.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E0A8318D306F14428CB84DA785848705', X'B85D625D467E0947B5C9739956950A6A', X'B85D625D467E0947B5C9739956950A6A', 6, NULL, NULL, 2, NULL, 0, 'Place the dough rounds on a parchment paper-lined baking sheet. Cover with a kitchen towel and let them rise for about 30 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5E7F1A931DD05B498EE66888FA50A2AE', X'B85D625D467E0947B5C9739956950A6A', X'B85D625D467E0947B5C9739956950A6A', 7, NULL, NULL, 2, NULL, 0, 'Heat the oven to 350 F.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3A120EFFD524144B9CF2BDBB7193C167', X'B85D625D467E0947B5C9739956950A6A', X'B85D625D467E0947B5C9739956950A6A', 8, NULL, NULL, 2, NULL, 0, 'Bake for 15 to 18 minutes, or until browned. Brush tops with a little butter while they''re hot. Let the buns cool completely on a rack.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D2D40412EB5A44409A3C2E26678E08A3', X'B85D625D467E0947B5C9739956950A6A', X'B85D625D467E0947B5C9739956950A6A', 9, NULL, NULL, 2, NULL, 0, 'Split the cooled buns.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F65687C35D421B43B844944DFCFBBCE2', X'B85D625D467E0947B5C9739956950A6A', X'B85D625D467E0947B5C9739956950A6A', 10, NULL, NULL, 2, NULL, 0, 'Fill each small split bun with a little of the shredded pork, then add a small spoonful of slaw. Or serve slaw on the side with a small spoon. Replace the top of the bun and secure with a toothpick, if desired.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E7FB6291FC06F44992B5CACA7679333A', X'B85D625D467E0947B5C9739956950A6A', X'B85D625D467E0947B5C9739956950A6A', 11, NULL, NULL, 2, NULL, 0, 'Pass the mini pulled pork sandwiches on a tray with a small bowl of barbecue sauce and more slaw, if desired.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EE9C16A748B7F948A7678A84C193E0D5', X'F9F18B835892E94CB23EDC15D693030D', X'F9F18B835892E94CB23EDC15D693030D', 0, NULL, NULL, 2, NULL, 0, 'Place the pork shoulder roast in slow cooker; sprinkle with salt and pepper.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9B5F63E8A27A974E9F21A8098FE82C66', X'F9F18B835892E94CB23EDC15D693030D', X'F9F18B835892E94CB23EDC15D693030D', 1, NULL, NULL, 2, NULL, 0, 'In a bowl, combine orange juice concentrate, brown sugar, nutmeg, and allspice; pour over the pork roast. Cover and cook on HIGH for 1 hour. Reduce heat to LOW and cook for 8 hours.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'064714937AB96949B86795D9F8A26911', X'F9F18B835892E94CB23EDC15D693030D', X'F9F18B835892E94CB23EDC15D693030D', 2, NULL, NULL, 2, NULL, 0, 'Before serving, skim fat from juices and pour the cooking juices into a small saucepan. Bring to a boil and cook, stirring, until slightly reduced and the flavors are concentrated.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6CB34F82E9085B4BAEBA0CF2F69F2D0A', X'F9F18B835892E94CB23EDC15D693030D', X'F9F18B835892E94CB23EDC15D693030D', 3, NULL, NULL, 2, NULL, 0, 'Whisk in the flour and water mixture and continue cooking until thickened. Serve the thickened juices with the pork roast.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'076995A5D5C896459B81AF96342552AC', X'07F671A56E09364294B15FEF053186ED', X'07F671A56E09364294B15FEF053186ED', 0, NULL, NULL, 2, NULL, 0, 'Brown roast in a small amount of fat; place in a large roasting or baking pan.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2D77C990CEFECE4BA76577F24B16A187', X'07F671A56E09364294B15FEF053186ED', X'07F671A56E09364294B15FEF053186ED', 1, NULL, NULL, 2, NULL, 0, 'Mix the next 9 ingredients in a saucepan and bring to a boil. Pour over roast and cover with foil.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AF0A470C4BC88545A21F4D6BC225DDD5', X'07F671A56E09364294B15FEF053186ED', X'07F671A56E09364294B15FEF053186ED', 2, NULL, NULL, 2, NULL, 0, 'Bake the roast in a preheated 325 F oven, about 40 minutes per pound, or until done and very tender, basting occasionally with drippings.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D9FBA1BC72522944A7D24979F1856F32', X'07F671A56E09364294B15FEF053186ED', X'07F671A56E09364294B15FEF053186ED', 3, NULL, NULL, 2, NULL, 0, 'Transfer pork roast to a cutting board.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E673FEED98AC4445AEC1F12C920D1D8D', X'07F671A56E09364294B15FEF053186ED', X'07F671A56E09364294B15FEF053186ED', 4, NULL, NULL, 2, NULL, 0, 'Remove meat from the bone and chop or shred the pork into small pieces.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FAD1764D57335A48979D172FF4D42639', X'07F671A56E09364294B15FEF053186ED', X'07F671A56E09364294B15FEF053186ED', 5, NULL, NULL, 2, NULL, 0, 'Season pork to taste with additional vinegar and hot sauce.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9D03127B0C8AFA41ADBD140C24C0573F', X'07F671A56E09364294B15FEF053186ED', X'07F671A56E09364294B15FEF053186ED', 6, NULL, NULL, 2, NULL, 0, 'Serve the pulled pork hot with sandwich buns, coleslaw, potato salad, and baked beans.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0D414D6419696541A723429E0A3666BA', X'63D6693D69C7684AB9F578EB044892EE', X'63D6693D69C7684AB9F578EB044892EE', 0, NULL, NULL, 2, NULL, 0, 'Pre-heat oven to 300 F/150 C.?');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D57A89B0A8117F4FB456A04E5B2D5774', X'63D6693D69C7684AB9F578EB044892EE', X'63D6693D69C7684AB9F578EB044892EE', 1, NULL, NULL, 2, NULL, 0, 'In a heavy, cast-iron dutch oven or brazier, heat the oil over high heat, then add the pork and sear it thoroughly, using a pair of tongs to turn it. When a nice brown crust has developed on all sides of the meat, remove it from the pan and set it aside. To enhance the browning of the meat, pat off excess moisture with clean paper towels before searing it.?');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F668A40BB6D29B489344980282BFC69D', X'63D6693D69C7684AB9F578EB044892EE', X'63D6693D69C7684AB9F578EB044892EE', 2, NULL, NULL, 2, NULL, 0, 'Add the carrots, celery, leeks, sliced fennel and garlic to the pot and saut for 5 minutes or so, or until the leeks and fennel are slightly soft.?');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9470847F9437034AAA043D3AC4176C2C', X'63D6693D69C7684AB9F578EB044892EE', X'63D6693D69C7684AB9F578EB044892EE', 3, NULL, NULL, 2, NULL, 0, 'Now return the meat to the pot and add the tomatoes, juice, stock, bay leaves and peppercorns. Heat on the stovetop until the liquid comes to a boil, then cover with a tight-fitting lid and transfer the whole thing to the oven.?');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4CF01DA17F00A845B7D6EE38F3584176', X'63D6693D69C7684AB9F578EB044892EE', X'63D6693D69C7684AB9F578EB044892EE', 4, NULL, NULL, 2, NULL, 0, 'Cook 3 to 4 hours or until the pork is tender.?');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EB2D56F0EE9A574F9AC04582413691A8', X'63D6693D69C7684AB9F578EB044892EE', X'63D6693D69C7684AB9F578EB044892EE', 5, NULL, NULL, 2, NULL, 0, 'Remove pot from the oven and leave the meat in the braising liquid while you do the next step to make a simple pork jus.?');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7AAAF2AD2AE9044B8F56DB3ABCD70D40', X'63D6693D69C7684AB9F578EB044892EE', X'63D6693D69C7684AB9F578EB044892EE', 6, NULL, NULL, 2, NULL, 0, 'Ladle out around two cups of the braising liquid and pour it through a mesh strainer. Skim off any fat from the top and bring the remaining liquid to a boil in a saucepan. Reduce for about five minutes, and season to taste with Kosher salt and freshly ground white pepper.?');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'489922B9E346F541A2FDEB8986FF5EBD', X'63D6693D69C7684AB9F578EB044892EE', X'63D6693D69C7684AB9F578EB044892EE', 7, NULL, NULL, 2, NULL, 0, 'Remove the pork from the liquid, slice across the grain, arrange slices on warm plates with plenty of pork jus and serve right away.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'18465D3C8123E94892C0359250ACC5E1', X'98FFC6F012051141A92012E90BB95F74', X'98FFC6F012051141A92012E90BB95F74', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4BE13B458976014285F7C71CCD0C84D6', X'98FFC6F012051141A92012E90BB95F74', X'98FFC6F012051141A92012E90BB95F74', 1, NULL, NULL, 2, NULL, 0, 'In a small mixing bowl, combine all rub ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EEA216234F0448408DD791C7C5EC27D6', X'98FFC6F012051141A92012E90BB95F74', X'98FFC6F012051141A92012E90BB95F74', 2, NULL, NULL, 2, NULL, 0, 'Coat pork shoulder with seasonings and cover with plastic wrap.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'46714521EE5DFD4D98FD185B71A27E86', X'98FFC6F012051141A92012E90BB95F74', X'98FFC6F012051141A92012E90BB95F74', 3, NULL, NULL, 2, NULL, 0, 'Refrigerate one to three hours.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CB2C82407EAE584587F53905F006C5ED', X'98FFC6F012051141A92012E90BB95F74', X'98FFC6F012051141A92012E90BB95F74', 4, NULL, NULL, 2, NULL, 0, 'Preheat smoker and prepare for an eight-hour smoke.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ADA545274ABDDA4799D1EE6A30EB5E11', X'98FFC6F012051141A92012E90BB95F74', X'98FFC6F012051141A92012E90BB95F74', 5, NULL, NULL, 2, NULL, 0, 'Make sure wood chips are soaked and drained before using them.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B755403F69D1B24BAC0345CE60749CDE', X'98FFC6F012051141A92012E90BB95F74', X'98FFC6F012051141A92012E90BB95F74', 6, NULL, NULL, 2, NULL, 0, 'Add charcoal and wood chips as necessary to keep the temperature low, around 225 F/110 C.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5249F6E142B14942A1EBBC49ABB73B88', X'98FFC6F012051141A92012E90BB95F74', X'98FFC6F012051141A92012E90BB95F74', 7, NULL, NULL, 2, NULL, 0, 'Smoke pork shoulder for about eight hours or until internal temperatures reach 185 F/85 C.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'84A17CC9129014448D1649173B9FE4B2', X'98FFC6F012051141A92012E90BB95F74', X'98FFC6F012051141A92012E90BB95F74', 8, NULL, NULL, 2, NULL, 0, 'Once the pork is cooked, remove it from the smoker.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E906C21519EA5B4BAF36F2E9A6D90BDD', X'98FFC6F012051141A92012E90BB95F74', X'98FFC6F012051141A92012E90BB95F74', 9, NULL, NULL, 2, NULL, 0, 'Wrap tightly in foil and allow to sit for an hour.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F7AD7152F30F9840A5085FBCD232938E', X'98FFC6F012051141A92012E90BB95F74', X'98FFC6F012051141A92012E90BB95F74', 10, NULL, NULL, 2, NULL, 0, 'Afterward, unwrap the pork butt and shred.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D4559E3126B06C498916DA623957AC55', X'98FFC6F012051141A92012E90BB95F74', X'98FFC6F012051141A92012E90BB95F74', 11, NULL, NULL, 2, NULL, 0, 'Combine pork with 1/3 of your favorite table sauce.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B9C8BB4214A8AC449683124A6E10F338', X'98FFC6F012051141A92012E90BB95F74', X'98FFC6F012051141A92012E90BB95F74', 12, NULL, NULL, 2, NULL, 0, 'Keep warm and serve on white bread buns with remaining sauce on the side.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D6C68C50D46DEE46B8DC3A41D2C147A7', X'98FFC6F012051141A92012E90BB95F74', X'98FFC6F012051141A92012E90BB95F74', 13, NULL, NULL, 2, NULL, 0, 'Top with coleslaw.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D04204463649DC4592FDBDCF4A3DBA71', X'98FFC6F012051141A92012E90BB95F74', X'98FFC6F012051141A92012E90BB95F74', 14, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'372AF73D0560D04797A770A3B8089916', X'4C9CE4D57C0C4245A5B726712E6E09A4', X'4C9CE4D57C0C4245A5B726712E6E09A4', 0, NULL, NULL, 2, NULL, 0, 'Wash the beef, pork and pork fat. Drain and place in a large pot. Add bay leaf, allspice, black pepper grains and pour in sufficient cold water to cover the meat.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'03082D4340D0AE46B2261E44EB9D0EC0', X'4C9CE4D57C0C4245A5B726712E6E09A4', X'4C9CE4D57C0C4245A5B726712E6E09A4', 1, NULL, NULL, 2, NULL, 0, 'Simmer until the meat is tender. Remove the pan from the heat. Place the rolls on the stock to soak. Fry the peeled and thinly sliced onions in a little oil in a frying pan until tender.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'49B2C6B61129F1439252622122847939', X'4C9CE4D57C0C4245A5B726712E6E09A4', X'4C9CE4D57C0C4245A5B726712E6E09A4', 2, NULL, NULL, 2, NULL, 0, 'Once the meat has cooled, remove it from the stock and then, together with the roll and fried onions, grind this mixture twice in a meat grinder. Season to taste with salt, pepper, nutmeg, and crushed garlic. Mix these ingredients thoroughly. The pt mixture must be moist. If it is too dry, add some of the stock from the meat cooking.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CCFC6442BD87EF49960167BDEB604368', X'4C9CE4D57C0C4245A5B726712E6E09A4', X'4C9CE4D57C0C4245A5B726712E6E09A4', 3, NULL, NULL, 2, NULL, 0, 'Grease a small mold or baking pan with oil and sprinkle with bread crumbs. Transfer the pt mixture to the pan, filling it to 3/4 of its height. Bake for approximately 40 minutes in an oven heated to 350 F/180 C. Allow the baked pt to cool before unmolding or slicing and serving. If you like, serve this with Polish cranberry-currant sauce.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0DD22F7474CF2942BBB949BDC986CEB3', X'4DF1FC6B35C3F240984658EEA867CAB0', X'4DF1FC6B35C3F240984658EEA867CAB0', 0, NULL, NULL, 2, NULL, 0, 'Arrange onion slices in the bottom of a large slow cooker.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F44D8B288B5AE24EACFE0516E7B816A7', X'4DF1FC6B35C3F240984658EEA867CAB0', X'4DF1FC6B35C3F240984658EEA867CAB0', 1, NULL, NULL, 2, NULL, 0, 'Rinse the roast and pat dry; leave it in its net; place on the onions.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'74FBB95F608ADD4A8A8D2CD1D1304E93', X'4DF1FC6B35C3F240984658EEA867CAB0', X'4DF1FC6B35C3F240984658EEA867CAB0', 2, NULL, NULL, 2, NULL, 0, 'Combine remaining ingredients in a cup; pour over the roast.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3081744BD68A894880830E6865B4F9E1', X'4DF1FC6B35C3F240984658EEA867CAB0', X'4DF1FC6B35C3F240984658EEA867CAB0', 3, NULL, NULL, 2, NULL, 0, 'Cover and cook on high for 2 hours.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7483C9B54746524EB72F4855E8D70E05', X'4DF1FC6B35C3F240984658EEA867CAB0', X'4DF1FC6B35C3F240984658EEA867CAB0', 4, NULL, NULL, 2, NULL, 0, 'Turn to low and cook for 6 to 8 hours longer.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D1B755E2B09E664687165CB7BDEE2858', X'649832D3C1E4524E90199A33AC329427', X'649832D3C1E4524E90199A33AC329427', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5E5805EEAA805842855748C9AFAD5923', X'649832D3C1E4524E90199A33AC329427', X'649832D3C1E4524E90199A33AC329427', 1, NULL, NULL, 2, NULL, 0, 'Pat the meat dry with paper towels. Trim off any large pieces of excess fat.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B8A60E2EF5BE7D46AE0C132EDB26C14A', X'649832D3C1E4524E90199A33AC329427', X'649832D3C1E4524E90199A33AC329427', 2, NULL, NULL, 2, NULL, 0, 'Mix the rub ingredients together, and thoroughly coat all sides of the pork, and into any folds in the meat');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'176B27FC9E63414F97DE4F20FDA770BF', X'649832D3C1E4524E90199A33AC329427', X'649832D3C1E4524E90199A33AC329427', 3, NULL, NULL, 2, NULL, 0, 'Preheat oven to 215 F.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B82DD64A34698E4C9E9250CB91F68910', X'649832D3C1E4524E90199A33AC329427', X'649832D3C1E4524E90199A33AC329427', 4, NULL, NULL, 2, NULL, 0, 'Place the pork, fattier side up, in a large Dutch oven.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4A84B3293F9D9C4AB4353BE99DD9FFB9', X'649832D3C1E4524E90199A33AC329427', X'649832D3C1E4524E90199A33AC329427', 5, NULL, NULL, 2, NULL, 0, 'Pour 1/3 cup water into a small, ovenproof ramekin, and add the liquid smoke.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9B77B038C1BB3349A69EB3CAA67BA8DE', X'649832D3C1E4524E90199A33AC329427', X'649832D3C1E4524E90199A33AC329427', 6, NULL, NULL, 2, NULL, 0, 'Place this ramekin in the Dutch oven next to the meat. As the pork roasts, this will introduce moisture to the cooking environment, as well as add a subtle smokiness to the meat.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D59380D7535B51409F6ABF0BE4417A6C', X'649832D3C1E4524E90199A33AC329427', X'649832D3C1E4524E90199A33AC329427', 7, NULL, NULL, 2, NULL, 0, 'Cover tightly with the lid, and place in the center of the preheated oven.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'295D6FAFFFFF9947AE7C83C98CAED4FE', X'649832D3C1E4524E90199A33AC329427', X'649832D3C1E4524E90199A33AC329427', 8, NULL, NULL, 2, NULL, 0, 'Roast pork for 12 hours, or until fork tender. If you''re using a thermometer, the internal temperature will be around 200 degrees F. Turn off the oven and allow the pork to rest for 1 hour before removing.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'396B4B6142B931478B431DCFCAD42FA7', X'649832D3C1E4524E90199A33AC329427', X'649832D3C1E4524E90199A33AC329427', 9, NULL, NULL, 2, NULL, 0, 'Place the pork on a cutting board and use forks to pull it apart into small pieces.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5625764005B9344B9D3590C170CEE404', X'649832D3C1E4524E90199A33AC329427', X'649832D3C1E4524E90199A33AC329427', 10, NULL, NULL, 2, NULL, 0, 'Taste for salt and spice level, and adjust the seasoning.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E532BA7B90814D4DB7C8B6E919BB1E36', X'649832D3C1E4524E90199A33AC329427', X'649832D3C1E4524E90199A33AC329427', 11, NULL, NULL, 2, NULL, 0, 'Serve warm or room temperature on soft rolls splashed with some of your favorite barbecue sauce.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'97A8BB5F68EE4649B1FC0D0FDCF96A56', X'649832D3C1E4524E90199A33AC329427', X'649832D3C1E4524E90199A33AC329427', 12, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'38C6362465BB4044A663849F5C60765E', X'5EF0B4F29B0B30418B15C45BD93AAA05', X'5EF0B4F29B0B30418B15C45BD93AAA05', 0, NULL, NULL, 2, NULL, 0, 'With a sharp knife, make several shallow long cuts along the roast. Rub with salt and liquid smoke. Wrap with banana leaves until completely covered. Tie securely with twine. Cover with aluminum foil and place it in the refrigerator overnight.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2F275E2133679B468EB77B91A1213882', X'5EF0B4F29B0B30418B15C45BD93AAA05', X'5EF0B4F29B0B30418B15C45BD93AAA05', 1, NULL, NULL, 2, NULL, 0, 'The next day, remove foil and place it in a shallow pan with water. Place in smoker at about 220 F for 6 to 8 hours.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2AE016820AC51A45A63399850BA86009', X'5EF0B4F29B0B30418B15C45BD93AAA05', X'5EF0B4F29B0B30418B15C45BD93AAA05', 2, NULL, NULL, 2, NULL, 0, 'When done, remove leaves and shred pork. Traditionally this is served with poi and sweet potatoes, but it''s great on a sandwich too.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EB6ED28782912C4B96EA0E3722649DAA', X'EA5C6BDE05B7714297319A3DAA0DBB6B', X'EA5C6BDE05B7714297319A3DAA0DBB6B', 0, NULL, NULL, 2, NULL, 0, 'Trim any visible fat from the meat and pat it dry with paper towels. Don''t rinse the meat; that step is not necessary and can spread bacteria around your kitchen. Set the meat aside.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BFB5E4D0E1DA76479EF1F4D2D1A131AD', X'EA5C6BDE05B7714297319A3DAA0DBB6B', X'EA5C6BDE05B7714297319A3DAA0DBB6B', 1, NULL, NULL, 2, NULL, 0, 'In a small mixing bowl combine the caraway seed, salt, and pepper; rub this mixture over roast.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8CEA7A45FE44574A86445A5DC278EA84', X'EA5C6BDE05B7714297319A3DAA0DBB6B', X'EA5C6BDE05B7714297319A3DAA0DBB6B', 2, NULL, NULL, 2, NULL, 0, 'In a 4- or 6-quart pressure cooker, heat 1 tablespoon oil over medium heat or use the brown function.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FEDEF13EC1ACA34388B14EA59C0C7A9A', X'EA5C6BDE05B7714297319A3DAA0DBB6B', X'EA5C6BDE05B7714297319A3DAA0DBB6B', 3, NULL, NULL, 2, NULL, 0, 'Cook the meat until brown on all sides, adding more oil, if necessary. Remove the meat from the pressure cooker and set aside. Drain off the fat.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'711893F846C146428B232A55E2C481F6', X'EA5C6BDE05B7714297319A3DAA0DBB6B', X'EA5C6BDE05B7714297319A3DAA0DBB6B', 4, NULL, NULL, 2, NULL, 0, 'Place the trivet or an oven-proof rack in the pressure cooker. Return the meat to the pressure cooker and add the onion, water or broth, and the apple cider.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F752E8225B2AA44E8ECEE77CD7FD71A5', X'EA5C6BDE05B7714297319A3DAA0DBB6B', X'EA5C6BDE05B7714297319A3DAA0DBB6B', 5, NULL, NULL, 2, NULL, 0, 'Lock the lid in place and bring the cooker up to high pressure; cook for 45 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4613D946E912A9488F410D55025355E5', X'EA5C6BDE05B7714297319A3DAA0DBB6B', X'EA5C6BDE05B7714297319A3DAA0DBB6B', 6, NULL, NULL, 2, NULL, 0, 'Allow the pressure to come down naturally, then carefully remove the lid. Transfer the meat and onion to a serving platter; keep warm by covering with foil.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CC95142C11076344B7894C10DFE843D4', X'EA5C6BDE05B7714297319A3DAA0DBB6B', X'EA5C6BDE05B7714297319A3DAA0DBB6B', 7, NULL, NULL, 2, NULL, 0, 'Add the apples to the pressure cooker and bring to boiling. Cover loosely (do not lock lid) and cook over medium heat about 5 minutes or until apples are crisp-tender. With a slotted spoon, remove the apples to the serving platter with the pork roast and onions. Serve immediately.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8F2DC8487547054B856C8FBE237643DC', X'88967251FC12FC428595A10CEC086AF8', X'88967251FC12FC428595A10CEC086AF8', 0, NULL, NULL, 2, NULL, 0, 'In a large, heavy pot, heat two tablespoons of the lard or oil. Brown the meat, turning to brown on all sides.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D074847F2F3ED84AB60053727B8BFA2E', X'88967251FC12FC428595A10CEC086AF8', X'88967251FC12FC428595A10CEC086AF8', 1, NULL, NULL, 2, NULL, 0, 'Take the pot off the heat and add the orange juice. Pour in enough broth to cover the meat. Add the garlic, the bay leaf, and the chunks of onion. Place the pan back on the stove and bring the liquid to a boil.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CDEFA41F5F906F4BAAD309A153586C9E', X'88967251FC12FC428595A10CEC086AF8', X'88967251FC12FC428595A10CEC086AF8', 2, NULL, NULL, 2, NULL, 0, 'Turn the heat down and let the meat simmer, covered, for about 2 and 1/2 hours. At the end of the cooking time, take off the lid and turn up the heat to help evaporate the excess liquid.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4B83894502AD464BA2CBE12B4F385466', X'88967251FC12FC428595A10CEC086AF8', X'88967251FC12FC428595A10CEC086AF8', 3, NULL, NULL, 2, NULL, 0, 'When the liquid is gone and just the meat and fat are left, remove the bay leaf and any remaining pieces of garlic or onion. Use tongs to break up the meat into smaller chunks (golf ball-sized or smaller) and allow them to fry, adding a little more lard or oil when necessary. Sprinkle salt on the meat as you fry it to season your carnitas and help keep spattering fat to a minimum.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'94498B684658AC4F9B1C24D7084DD757', X'88967251FC12FC428595A10CEC086AF8', X'88967251FC12FC428595A10CEC086AF8', 4, NULL, NULL, 2, NULL, 0, 'Your delicious little meats are ready when the edges of the chunks of meat become browned and crispy. You can serve them as-is (in larger chunks), or you can remove the pieces from the pot and chop them up into smaller morselsor even shred themto use as a filling for tacos or burritos.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3E3EF3351D9E6E4084D6F2DE43E1F74D', X'88967251FC12FC428595A10CEC086AF8', X'88967251FC12FC428595A10CEC086AF8', 5, NULL, NULL, 2, NULL, 0, 'Store any leftovers well covered in the refrigerator; reheat slowly in a pan on the stove (they will probably not need any more oil added) or in a microwave oven.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FCE829039354814AB5E7ACF46CE6FA0E', X'FB360A78CE87BD41915DAB24484E139F', X'FB360A78CE87BD41915DAB24484E139F', 0, NULL, NULL, 2, NULL, 0, 'Trim pork roast of excess fat.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9C691AF666E8E545A137AB8A1A34DA15', X'FB360A78CE87BD41915DAB24484E139F', X'FB360A78CE87BD41915DAB24484E139F', 1, NULL, NULL, 2, NULL, 0, 'Heat oil in large pot or skillet.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'23069BABC24CF24B9B988FD80C43DFFC', X'FB360A78CE87BD41915DAB24484E139F', X'FB360A78CE87BD41915DAB24484E139F', 2, NULL, NULL, 2, NULL, 0, 'Season pork with salt and pepper.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5934398719B28040B33BFCFF82574A83', X'FB360A78CE87BD41915DAB24484E139F', X'FB360A78CE87BD41915DAB24484E139F', 3, NULL, NULL, 2, NULL, 0, 'When the pan is nice and hot, place pork into skillet and sear all sides. This will take about 5 to 6 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'38803638FE74B240BB3314541A90FF96', X'FB360A78CE87BD41915DAB24484E139F', X'FB360A78CE87BD41915DAB24484E139F', 4, NULL, NULL, 2, NULL, 0, 'Set slow cooker for high heat.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5CD4885967B03A458D2F9BD2E93F7BDF', X'FB360A78CE87BD41915DAB24484E139F', X'FB360A78CE87BD41915DAB24484E139F', 5, NULL, NULL, 2, NULL, 0, 'Remove pork from skillet and place into slow cooker.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BF274A8D44E8A34C92CE25A3D997296B', X'FB360A78CE87BD41915DAB24484E139F', X'FB360A78CE87BD41915DAB24484E139F', 6, NULL, NULL, 2, NULL, 0, 'Pour vinegar and water (or broth) over roast and turn to make sure the vinegar reaches all sides.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E90D74AF1BEBC5418771259A68586124', X'FB360A78CE87BD41915DAB24484E139F', X'FB360A78CE87BD41915DAB24484E139F', 7, NULL, NULL, 2, NULL, 0, 'Cook on high for 1 hour, reduce temperature and cook on low for 8 to 9 hours, or until roast is tender enough to pull apart with your fingers.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0FFCC80A52F4A14988E6863A725F5BBA', X'FB360A78CE87BD41915DAB24484E139F', X'FB360A78CE87BD41915DAB24484E139F', 8, NULL, NULL, 2, NULL, 0, 'Once cooked, remove roast and discard liquid.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7A5E5DDDE02BA54DAD5C34320BE17317', X'FB360A78CE87BD41915DAB24484E139F', X'FB360A78CE87BD41915DAB24484E139F', 9, NULL, NULL, 2, NULL, 0, 'Leave slow cooker on.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0E0AA991D994C946861F9C00830D737D', X'FB360A78CE87BD41915DAB24484E139F', X'FB360A78CE87BD41915DAB24484E139F', 10, NULL, NULL, 2, NULL, 0, 'Pour in barbecue sauce.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4169D09F230FD84990E129CC0785576F', X'FB360A78CE87BD41915DAB24484E139F', X'FB360A78CE87BD41915DAB24484E139F', 11, NULL, NULL, 2, NULL, 0, 'Shred pork roast into small pieces and return it to the slow cooker. There should be enough barbecue sauce to coat the meat but not so much as to make it runny.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'19D1E6943B0D4D4BB72AE4B0F1C0EEA2', X'70AA242B9A8DEC4684FC0AC62305226D', X'70AA242B9A8DEC4684FC0AC62305226D', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients. Preheat the oven to 450 F.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'24FD27ADA2EF564988C9EDAAC67F031E', X'70AA242B9A8DEC4684FC0AC62305226D', X'70AA242B9A8DEC4684FC0AC62305226D', 1, NULL, NULL, 2, NULL, 0, 'Remove the netting from the pork and trim off some of the fat if needed from the pork.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4BAC325EFDC14745B56187C3610B0B9E', X'70AA242B9A8DEC4684FC0AC62305226D', X'70AA242B9A8DEC4684FC0AC62305226D', 2, NULL, NULL, 2, NULL, 0, 'Combine the salt, pepper, garlic powder, onion powder, cumin, and dried thyme and mix well. Rub all over the pork.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5650DD741C1E424EAFEEF23C830C968D', X'70AA242B9A8DEC4684FC0AC62305226D', X'70AA242B9A8DEC4684FC0AC62305226D', 3, NULL, NULL, 2, NULL, 0, 'Heat a heavy skillet over high heat and sear the roast for a few minutes. Repeat, searing the pork butt on all sides. If using a cast iron or heavy oven-safe skillet, leave the pork in the skillet. Otherwise, transfer the pork to a roasting or baking pan.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3EA450A03307E448B0836736849273FE', X'70AA242B9A8DEC4684FC0AC62305226D', X'70AA242B9A8DEC4684FC0AC62305226D', 4, NULL, NULL, 2, NULL, 0, 'Cover with foil. Put the roast in the oven, reduce the temperature to 325 F, and roast for 3 hours.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'966A55E90104CB4D8C3A52552E96ACF0', X'70AA242B9A8DEC4684FC0AC62305226D', X'70AA242B9A8DEC4684FC0AC62305226D', 5, NULL, NULL, 2, NULL, 0, 'Remove the foil and continue roasting for another hour, or until the roast is pull-apart tender.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5E26CC3A15B4604F859BA382F732C110', X'70AA242B9A8DEC4684FC0AC62305226D', X'70AA242B9A8DEC4684FC0AC62305226D', 6, NULL, NULL, 2, NULL, 0, 'Let cool for a few minutes before shredding using two forks. Toss with barbecue sauce or serve as-is. Enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'81EB472EA2097643B82D0DD8593E16E1', X'A161C817D7875B47AF0EA564F74C073A', X'A161C817D7875B47AF0EA564F74C073A', 0, NULL, NULL, 2, NULL, 0, 'Remove excess fat from the pork. No need to remove all of the fat  just the really big pieces. Cut the pork into 3 or 4 pieces and sprinkle all over with salt and pepper.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ED308DB6ADD61D40BA7797FCB95AF694', X'A161C817D7875B47AF0EA564F74C073A', X'A161C817D7875B47AF0EA564F74C073A', 1, NULL, NULL, 2, NULL, 0, 'Place all the ingredients in the crock pot and cook on low for about 7 to 8 hours, or until pork is very tender and can be pulled apart easily with a fork. Alternatively, you can cook on high for 4 to 5 hours.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'19F355DB2CC103429F72491D74EA7270', X'A161C817D7875B47AF0EA564F74C073A', X'A161C817D7875B47AF0EA564F74C073A', 2, NULL, NULL, 2, NULL, 0, 'Line a rimmed baking sheet with foil. Preheat the broiler.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8597727D9D97DC44A0B1B8E45C9C7A24', X'A161C817D7875B47AF0EA564F74C073A', X'A161C817D7875B47AF0EA564F74C073A', 3, NULL, NULL, 2, NULL, 0, 'Shred the pork, and place in on the baking sheet. Pour the sauce from the crock pot over the pork and mix gently.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2456F0294756D046A9A4C7895BAFAD3D', X'A161C817D7875B47AF0EA564F74C073A', X'A161C817D7875B47AF0EA564F74C073A', 4, NULL, NULL, 2, NULL, 0, 'Broil meat, turning once, until brown and caramelized around the edges, or desired amount (about 5 minutes). Be sure to watch the oven and pull the tray out quickly after caramelizing to prevent it from drying out.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FB6C7B318BDDEE40998B9C6B1903B1E0', X'A161C817D7875B47AF0EA564F74C073A', X'A161C817D7875B47AF0EA564F74C073A', 5, NULL, NULL, 2, NULL, 0, 'Serve immediately with warm with rice, beans, and tortillas. Garnish plate with a squeeze of lime and chopped cilantro if you have on hand.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DEACC8D74D8198459C5CABE632770C13', X'95EF7DC4DB51734EA251AF3B711C1CC9', X'95EF7DC4DB51734EA251AF3B711C1CC9', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3196619D3396BF4BAC5C88BFC1D07209', X'95EF7DC4DB51734EA251AF3B711C1CC9', X'95EF7DC4DB51734EA251AF3B711C1CC9', 1, NULL, NULL, 2, NULL, 0, 'In a medium mixing bowl, mix rub ingredients into a paste. Coat pork shoulder with rub, cover with plastic wrap and refrigerate 1 hours. Preheat smoker and prepare for an 8 hour smoke.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FC3EF1F37BFC6A49921F2FB71946B843', X'95EF7DC4DB51734EA251AF3B711C1CC9', X'95EF7DC4DB51734EA251AF3B711C1CC9', 2, NULL, NULL, 2, NULL, 0, 'Make sure wood chips are soaked and drained before using. Add charcoal and wood chips as necessary to keep the temperature low, around 225 F/110 C. Smoke pork shoulder for about 8 hours or until internal temperatures reach 185 F/85 C.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DB8AEA24FEF8F044A1CDD648C4DBE7EC', X'95EF7DC4DB51734EA251AF3B711C1CC9', X'95EF7DC4DB51734EA251AF3B711C1CC9', 3, NULL, NULL, 2, NULL, 0, 'Once the pork is cooked remove from the smoker. Wrap tightly in foil and allow to sit for an hour.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B654AD1E8D9A864A9619AB7C47EFB9B9', X'95EF7DC4DB51734EA251AF3B711C1CC9', X'95EF7DC4DB51734EA251AF3B711C1CC9', 4, NULL, NULL, 2, NULL, 0, 'Afterward, unwrap pork butt and shred.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8D27E49A5190C94E8CE10DF938D667DF', X'95EF7DC4DB51734EA251AF3B711C1CC9', X'95EF7DC4DB51734EA251AF3B711C1CC9', 5, NULL, NULL, 2, NULL, 0, 'Combine pork with 1/3 of your favorite table sauce. Keep warm and serve on white bread buns with remaining sauce on the side.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E484C92B97977841B3649633A48EB7C7', X'95EF7DC4DB51734EA251AF3B711C1CC9', X'95EF7DC4DB51734EA251AF3B711C1CC9', 6, NULL, NULL, 2, NULL, 0, 'Top with cole slaw and favorite finishing sauce.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'831DB1773953A849B6E21371D6084858', X'95EF7DC4DB51734EA251AF3B711C1CC9', X'95EF7DC4DB51734EA251AF3B711C1CC9', 7, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3DE1EF8991B48041993ACEEEF040B6E0', X'8B7DB06860AB154BAADF01D4DC97C2C1', X'8B7DB06860AB154BAADF01D4DC97C2C1', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'584C1A091785E54BB452B6885C509837', X'8B7DB06860AB154BAADF01D4DC97C2C1', X'8B7DB06860AB154BAADF01D4DC97C2C1', 1, NULL, NULL, 2, NULL, 0, 'In a large pot brown pork roast in 2 tablespoon oil.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1977D9257738914BAA5630E3235F90A4', X'8B7DB06860AB154BAADF01D4DC97C2C1', X'8B7DB06860AB154BAADF01D4DC97C2C1', 2, NULL, NULL, 2, NULL, 0, 'Once browned, add garlic and onions. Cook for 10 minutes on med-low heat until green onions are limp.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'956E7BDFE139D642A2F67574BCCF6E2C', X'8B7DB06860AB154BAADF01D4DC97C2C1', X'8B7DB06860AB154BAADF01D4DC97C2C1', 3, NULL, NULL, 2, NULL, 0, 'Add tomatoes, tomato sauce, tomato paste, tomato juice, Italian seasoning, and salt and pepper. Simmer without the lid for at least 6 hours stirring occasionally to prevent scorching.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'28ED4D2E34905048AF7352332B871320', X'8B7DB06860AB154BAADF01D4DC97C2C1', X'8B7DB06860AB154BAADF01D4DC97C2C1', 4, NULL, NULL, 2, NULL, 0, 'After 6 hours, remove pork roast and let cool.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8BCC0748B90C4240A16F5F5E29E96C0F', X'8B7DB06860AB154BAADF01D4DC97C2C1', X'8B7DB06860AB154BAADF01D4DC97C2C1', 5, NULL, NULL, 2, NULL, 0, 'While the pork roast is cooling combine the ground sausage, beef, minced celery, and bread crumbs. Shape the mixture into meatballs.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A62E21B92F8B91418ED25343BC8ACB78', X'8B7DB06860AB154BAADF01D4DC97C2C1', X'8B7DB06860AB154BAADF01D4DC97C2C1', 6, NULL, NULL, 2, NULL, 0, 'Drop them into the sauce. Simmer for one hour.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5E99E3BE61605746BD29BD75AAA41CAE', X'8B7DB06860AB154BAADF01D4DC97C2C1', X'8B7DB06860AB154BAADF01D4DC97C2C1', 7, NULL, NULL, 2, NULL, 0, 'Tear or shred the pork roast, discarding excess fat. Add the meat to the sauce.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5540EFE679AC7C42A72DFD1FFF4465B7', X'8B7DB06860AB154BAADF01D4DC97C2C1', X'8B7DB06860AB154BAADF01D4DC97C2C1', 8, NULL, NULL, 2, NULL, 0, 'Cook 1 medium package of spaghetti according to package directions. Drain and rinse.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'18B902AA6AF3164B81D2218164036451', X'8B7DB06860AB154BAADF01D4DC97C2C1', X'8B7DB06860AB154BAADF01D4DC97C2C1', 9, NULL, NULL, 2, NULL, 0, 'Serve sauce over spaghetti.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2DA6A9C20DD3FE40953A474819F3D22F', X'A859D28CEDF0A9489FB9020523B67215', X'A859D28CEDF0A9489FB9020523B67215', 0, NULL, NULL, 2, NULL, 0, 'Place the chopped onion in a large crock pot. Season the pork roast with salt and pepper on all sides, and place it on top of the onions.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6803DB7BEEBD7044BC8EA15C5294C8EA', X'A859D28CEDF0A9489FB9020523B67215', X'A859D28CEDF0A9489FB9020523B67215', 1, NULL, NULL, 2, NULL, 0, 'Press the garlic slices into the pork roast in various places, making slits with a knife if necessary.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'261BFDCA779E104B80853E26409B8DCA', X'A859D28CEDF0A9489FB9020523B67215', X'A859D28CEDF0A9489FB9020523B67215', 2, NULL, NULL, 2, NULL, 0, 'Stir the cumin, oregano, green chilies, ham base, and aji panca (if using) into the water, and pour over the roast.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C4CC24CECAD5E448AC307A4FAD50FA8E', X'A859D28CEDF0A9489FB9020523B67215', X'A859D28CEDF0A9489FB9020523B67215', 3, NULL, NULL, 2, NULL, 0, 'Layer the lime and orange slices over the roast. Cover and turn crock pot to high.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0FB83E64EE60B545ADFF72104070CF20', X'A859D28CEDF0A9489FB9020523B67215', X'A859D28CEDF0A9489FB9020523B67215', 4, NULL, NULL, 2, NULL, 0, 'Cook for 2 hours on high then reduce temperature to low and cook for 6 more hours.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'44335DED22DCFE4B89E5305FDFE77680', X'A859D28CEDF0A9489FB9020523B67215', X'A859D28CEDF0A9489FB9020523B67215', 5, NULL, NULL, 2, NULL, 0, 'Remove meat and reserve. Strain liquid through a colander into a saucepan. Discard contents of the colander.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C3E7F54867D13943885867BB716ABC3E', X'A859D28CEDF0A9489FB9020523B67215', X'A859D28CEDF0A9489FB9020523B67215', 6, NULL, NULL, 2, NULL, 0, 'Simmer cooking liquid on the stove until it''s reduced to about 1 cup.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9B8EF77F21E64F4B9C5CC38F58398864', X'A859D28CEDF0A9489FB9020523B67215', X'A859D28CEDF0A9489FB9020523B67215', 7, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400 F. Shred meat, removing excess fat. Stir reduced sauce into meat.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EBD1B05C60FC904888D08CA52C792EBD', X'A859D28CEDF0A9489FB9020523B67215', X'A859D28CEDF0A9489FB9020523B67215', 8, NULL, NULL, 2, NULL, 0, 'Spread meat onto a baking sheet, and bake for 10 to 15 minutes, until slightly browned and heated through.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'866DF09F564E3E4E947E891D295FCA55', X'A859D28CEDF0A9489FB9020523B67215', X'A859D28CEDF0A9489FB9020523B67215', 9, NULL, NULL, 2, NULL, 0, 'Serve with lime wedges, and garnish with cilantro, if desired.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AA2559B255551D478897650F4B88067A', X'1A7CC0049523F94F83218F08904C316E', X'1A7CC0049523F94F83218F08904C316E', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CB8B1A14D345994A81DE85DE7CE33667', X'1A7CC0049523F94F83218F08904C316E', X'1A7CC0049523F94F83218F08904C316E', 1, NULL, NULL, 2, NULL, 0, 'Cut trimmed roast in half, if necessary, and place in the crock pot. Sprinkle with salt and pepper.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9062F6AEF88EF14F9B6F42467CE287C6', X'1A7CC0049523F94F83218F08904C316E', X'1A7CC0049523F94F83218F08904C316E', 2, NULL, NULL, 2, NULL, 0, 'Combine all ingredients except flour and water; pour over roast.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B6ED49FB60312049B990AC9E1F750F0A', X'1A7CC0049523F94F83218F08904C316E', X'1A7CC0049523F94F83218F08904C316E', 3, NULL, NULL, 2, NULL, 0, 'Cover and cook on low for 8 to 10 hours.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5CAD588BA87DA64C96097F74F519E1B0', X'1A7CC0049523F94F83218F08904C316E', X'1A7CC0049523F94F83218F08904C316E', 4, NULL, NULL, 2, NULL, 0, 'Remove roast from the liquids.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3B1D2BC9DC05EC43A258A71076A83405', X'1A7CC0049523F94F83218F08904C316E', X'1A7CC0049523F94F83218F08904C316E', 5, NULL, NULL, 2, NULL, 0, 'Place a sieve over a large measuring cup and pour the liquids through it.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2E07243B6196DD4FBCB21930492B54E3', X'1A7CC0049523F94F83218F08904C316E', X'1A7CC0049523F94F83218F08904C316E', 6, NULL, NULL, 2, NULL, 0, 'Return the pineapple solids to the slow cooker along with the pork.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1C08F6BF63A6B44E8C91A917B2F10CCD', X'1A7CC0049523F94F83218F08904C316E', X'1A7CC0049523F94F83218F08904C316E', 7, NULL, NULL, 2, NULL, 0, 'Add water to liquids in the measuring cup to make 1 3/4 cups. Pour into saucepan.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6C35C1990B37E0439B7DEE56D1BC820D', X'1A7CC0049523F94F83218F08904C316E', X'1A7CC0049523F94F83218F08904C316E', 8, NULL, NULL, 2, NULL, 0, 'Blend flour and cold water together to form a smooth paste. Stir into hot reserved liquid. Cook and stir until thickened. Pour over roast; serve with rice, if desired.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D94A8914FD5C5A4A9C81ABED56F54BAC', X'1A7CC0049523F94F83218F08904C316E', X'1A7CC0049523F94F83218F08904C316E', 9, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'456C69806593A84F9CD08201CD40E0DA', X'BC4731BB4123394EAF71C5F78C1A72F0', X'BC4731BB4123394EAF71C5F78C1A72F0', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AD8E036557E864459987C1CD19EEF832', X'BC4731BB4123394EAF71C5F78C1A72F0', X'BC4731BB4123394EAF71C5F78C1A72F0', 1, NULL, NULL, 2, NULL, 0, 'Fold a piece of unbleached muslin, about 12 inches long by 8 inches wide, lengthwise and tightly stitch a seam across one of the short ends and continue along the open side. Keep the stitching about a 1/8-inch from the edge of the material. The short side of the seam can be curved in a semicircle to give the finished product a rounded end.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C48923D95D641C4C977F73257470093B', X'BC4731BB4123394EAF71C5F78C1A72F0', X'BC4731BB4123394EAF71C5F78C1A72F0', 2, NULL, NULL, 2, NULL, 0, 'Turn the casing inside out so that the stitching is on the inside. Set it aside until you are ready to stuff it. As an alternative, you can use large collagen casings.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B00818F8A2EA3A46BF18C70868959DE1', X'BC4731BB4123394EAF71C5F78C1A72F0', X'BC4731BB4123394EAF71C5F78C1A72F0', 3, NULL, NULL, 2, NULL, 0, 'Put the cubes of liver, pork, and fat through the fine blade of a grinder separately.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2D977E854100A14CB451DB39BE3440BA', X'BC4731BB4123394EAF71C5F78C1A72F0', X'BC4731BB4123394EAF71C5F78C1A72F0', 4, NULL, NULL, 2, NULL, 0, 'Then mix and grind together.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D3D6C8FF82307B44BAFE55D8EBB97D83', X'BC4731BB4123394EAF71C5F78C1A72F0', X'BC4731BB4123394EAF71C5F78C1A72F0', 5, NULL, NULL, 2, NULL, 0, 'Sprinkle the onion, powdered milk, pepper, salt, paprika, sugar, marjoram, coriander, mace, allspice, and cardamom over the ground meat and mix thoroughly with your hands.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8187446D20708445AC5D8C75617AE926', X'BC4731BB4123394EAF71C5F78C1A72F0', X'BC4731BB4123394EAF71C5F78C1A72F0', 6, NULL, NULL, 2, NULL, 0, 'Put the mixture through the fine blade of the grinder twice more. Chill the mixture for 30 minutes between grindings.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'044DD9B76B57FF429193B85D554DDC0F', X'BC4731BB4123394EAF71C5F78C1A72F0', X'BC4731BB4123394EAF71C5F78C1A72F0', 7, NULL, NULL, 2, NULL, 0, 'Pack the mixture into the muslin casing. It helps to fold the open end down over itself to get things started. This makes it easier to reach the bottom. Pack the meat as firmly as possible.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2C87032E7DB4E9469F18EF518333E5C9', X'BC4731BB4123394EAF71C5F78C1A72F0', X'BC4731BB4123394EAF71C5F78C1A72F0', 8, NULL, NULL, 2, NULL, 0, 'Stitch the open end closed or firmly secure it with a wire twist tie.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'71CDAB43F709D34A8CC69D86D69FF383', X'BC4731BB4123394EAF71C5F78C1A72F0', X'BC4731BB4123394EAF71C5F78C1A72F0', 9, NULL, NULL, 2, NULL, 0, 'In a large kettle, bring enough water to a boil to cover the liverwurst by 2 or 3 inches. Put the sausage in the boiling water and place a weight on it to keep it submerged. Two or three large dinner plates work just fine.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C7CD8E837A3C4646B4B1A1D7B391385D', X'BC4731BB4123394EAF71C5F78C1A72F0', X'BC4731BB4123394EAF71C5F78C1A72F0', 10, NULL, NULL, 2, NULL, 0, 'When the water returns to a boil, reduce the heat so that the water barely simmers. Cook for 3 hours.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D8AE8F5FF956CC4085F53DC83CB9BFD6', X'BC4731BB4123394EAF71C5F78C1A72F0', X'BC4731BB4123394EAF71C5F78C1A72F0', 11, NULL, NULL, 2, NULL, 0, 'Drain the hot water and replace it with an equal quantity of ice water.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BD0283FF286C35458EB2023ACA41BA5C', X'BC4731BB4123394EAF71C5F78C1A72F0', X'BC4731BB4123394EAF71C5F78C1A72F0', 12, NULL, NULL, 2, NULL, 0, 'When the liverwurst has cooled, refrigerate it overnight.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FEE6254839A1EF45BF3421DB176CCA16', X'BC4731BB4123394EAF71C5F78C1A72F0', X'BC4731BB4123394EAF71C5F78C1A72F0', 13, NULL, NULL, 2, NULL, 0, 'Remove the muslin casing and enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'268ACB40057A4F4B9C4F97E58831133B', X'9033963449317A4D9194B14FC571A981', X'9033963449317A4D9194B14FC571A981', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F12AE8B007CF184FAAACC3D16CB38DA4', X'9033963449317A4D9194B14FC571A981', X'9033963449317A4D9194B14FC571A981', 1, NULL, NULL, 2, NULL, 0, 'Cut the pork into chunks. Fairly big pieces are traditional, but if you prefer, cut the pork into bite-sized pieces.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BA36EBC6A7FF924DAD59E0726FF88D80', X'9033963449317A4D9194B14FC571A981', X'9033963449317A4D9194B14FC571A981', 2, NULL, NULL, 2, NULL, 0, 'Put the pork in a large pot and add enough cool water to cover it by about 2 inches (approximately 5 to 6 cups). Bring to a boil, skimming off any foam that forms in the pot.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B8CD1D3D8574334DB45B3FEE25833C5A', X'9033963449317A4D9194B14FC571A981', X'9033963449317A4D9194B14FC571A981', 3, NULL, NULL, 2, NULL, 0, 'Remove the stem and seeds from the chiles, and peel the garlic. Add the chiles, garlic, and salt to the pork.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'685E644DB419EE4E84871D848D3EF4EE', X'9033963449317A4D9194B14FC571A981', X'9033963449317A4D9194B14FC571A981', 4, NULL, NULL, 2, NULL, 0, 'Reduce the heat to maintain a steady simmer, cover, and cook until the pork is fork tender, about 90 minutes. Alternatively, put the covered, ovenproof pot in a 350 F oven for the same amount of time.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C7D8553848C4B84FA512C8CFBA5181D6', X'9033963449317A4D9194B14FC571A981', X'9033963449317A4D9194B14FC571A981', 5, NULL, NULL, 2, NULL, 0, 'After the pork has cooked, add the hominy and the Mexican oregano. Continue cooking at a simmer until the flavors blend and the pork is very tender, for another hour. Add additional water, if necessary, to keep the moisture at a good level, return the mixture to a boil and reduce back down to a simmer when needed. Add salt to taste.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F2E1C54823819042997A243BBC358E7C', X'9033963449317A4D9194B14FC571A981', X'9033963449317A4D9194B14FC571A981', 6, NULL, NULL, 2, NULL, 0, 'Serve the pozole in deep bowls.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C8BD7E11AD17F044BAE910701F067C3E', X'9033963449317A4D9194B14FC571A981', X'9033963449317A4D9194B14FC571A981', 7, NULL, NULL, 2, NULL, 0, 'Garnish with chopped cilantro, scallion, radish, and green cabbage. If you like, squeeze fresh lime juice on top. Enjoy.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'37E30FDF0E4FCE4C9E455A4A82A464AE', X'F4F24B521F727E4A99042298FE3E1DAE', X'F4F24B521F727E4A99042298FE3E1DAE', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D82D50AE73709D4CAC64206547B20BAE', X'F4F24B521F727E4A99042298FE3E1DAE', X'F4F24B521F727E4A99042298FE3E1DAE', 1, NULL, NULL, 2, NULL, 0, 'Trim visible fat from pork.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'72BE9E698CD15A4AA4CB5BEBC8F01C04', X'F4F24B521F727E4A99042298FE3E1DAE', X'F4F24B521F727E4A99042298FE3E1DAE', 2, NULL, NULL, 2, NULL, 0, 'Toss the pork cubes with flour to coat well.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B7FE176FD9A6844AB0554FBD7676CE43', X'F4F24B521F727E4A99042298FE3E1DAE', X'F4F24B521F727E4A99042298FE3E1DAE', 3, NULL, NULL, 2, NULL, 0, 'Combine salt, thyme, crushed coriander seed, and pepper; reserve.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7AF5CA7A3355A644AC7A32D525D8D585', X'F4F24B521F727E4A99042298FE3E1DAE', X'F4F24B521F727E4A99042298FE3E1DAE', 4, NULL, NULL, 2, NULL, 0, 'Drain liquid from beans into a 2-cup measuring cup; add boiling water to make 1 1/2 cups. If you''d like, you can use chicken broth instead of water.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'782A7C92AF81C14C84CA4CB5A52A535C', X'F4F24B521F727E4A99042298FE3E1DAE', X'F4F24B521F727E4A99042298FE3E1DAE', 5, NULL, NULL, 2, NULL, 0, 'Layer vegetables and pork pieces in the slow cooker in the following order, sprinkling each layer with the seasoning mixture: half of each of the potatoes, onions, pork, beans, and carrots.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'677DD58536E60F4D9ED16F740CF20EBD', X'F4F24B521F727E4A99042298FE3E1DAE', X'F4F24B521F727E4A99042298FE3E1DAE', 6, NULL, NULL, 2, NULL, 0, 'Repeat with remaining vegetables, pork and seasoning mixture for a second layer.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ED9347ACD75BBC4C8CD01BEA4335562A', X'F4F24B521F727E4A99042298FE3E1DAE', X'F4F24B521F727E4A99042298FE3E1DAE', 7, NULL, NULL, 2, NULL, 0, 'Pour liquid over; dot with butter.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'27A996975C0C3B4098E274A4E470D7FB', X'F4F24B521F727E4A99042298FE3E1DAE', X'F4F24B521F727E4A99042298FE3E1DAE', 8, NULL, NULL, 2, NULL, 0, 'Cover and cook on low for 8 hours or high for 4 hours or until meat and vegetables are tender.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D3AA5F7ED512C64495EF76C1F52244C7', X'A84FB5C082B2D64F9F1BE74F3DD43F7A', X'A84FB5C082B2D64F9F1BE74F3DD43F7A', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'62477304881EBE48BD0713E1C3AF2705', X'A84FB5C082B2D64F9F1BE74F3DD43F7A', X'A84FB5C082B2D64F9F1BE74F3DD43F7A', 1, NULL, NULL, 2, NULL, 0, 'Combine the cabbage, onion, garlic, brown sugar, vinegar, and thyme in 4 to 5-quart slow cooker.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CAF6E77ED81310408831A6A88D49F8B8', X'A84FB5C082B2D64F9F1BE74F3DD43F7A', X'A84FB5C082B2D64F9F1BE74F3DD43F7A', 2, NULL, NULL, 2, NULL, 0, 'Sprinkle the pork roast with salt and pepper and brown in olive oil in a heavy skillet, about 5 to 6 minutes total, turning until browned on all sides. (You don''t have to brown the pork if you don''t want to; in that case, just sprinkle it with the salt and pepper and put it in the crock pot on top of the vegetables.) If you do brown the pork, add about 1/4 cup water to the skillet to remove the drippings, scraping the bottom of the skillet. Add this liquid to the crock pot.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'15D9EA4CDE521A46A108CA589E85E049', X'A84FB5C082B2D64F9F1BE74F3DD43F7A', X'A84FB5C082B2D64F9F1BE74F3DD43F7A', 3, NULL, NULL, 2, NULL, 0, 'Place the pork in the slow cooker on top of the vegetables; cover, and cook on Low for 7 to 8 hours or until the pork registers at least 145 F.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D62821E40BECE641BB9701893475C629', X'A84FB5C082B2D64F9F1BE74F3DD43F7A', X'A84FB5C082B2D64F9F1BE74F3DD43F7A', 4, NULL, NULL, 2, NULL, 0, 'Remove the pork from the slow cooker; cover and let stand for 10 minutes before slicing to serve with the cabbage mixture.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E8813444D1D2F048A085A346BDCEF04C', X'A84FB5C082B2D64F9F1BE74F3DD43F7A', X'A84FB5C082B2D64F9F1BE74F3DD43F7A', 5, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'878E846AFA936A46A524829C01AD7BC1', X'BB061106DACFD14C9044AF025FCCAFEE', X'BB061106DACFD14C9044AF025FCCAFEE', 0, NULL, NULL, 2, NULL, 0, 'Place the pork roast in a crockpot sprayed with nonstick cooking spray. Sprinkle with salt, pepper, then add minced garlic and water.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2E0943F360FE464FABEB49CADD850B64', X'BB061106DACFD14C9044AF025FCCAFEE', X'BB061106DACFD14C9044AF025FCCAFEE', 1, NULL, NULL, 2, NULL, 0, 'Cover and cook on LOW for 9 to 11 hours, or until pork roast is very tender. Lift pork out of the slow cooker with a slotted spoon.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'87FCBE656FC1434690DCEF33625FD167', X'BB061106DACFD14C9044AF025FCCAFEE', X'BB061106DACFD14C9044AF025FCCAFEE', 2, NULL, NULL, 2, NULL, 0, 'Shred and serve with warmed flour tortillas, with dishes of shredded cheese and your choice of topping ingredients at the table.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F2F9A360A01FFF45B4A4FDE2FE269D5A', X'0F9EC977FD2DE342B803FF5E8C5382DB', X'0F9EC977FD2DE342B803FF5E8C5382DB', 0, NULL, NULL, 2, NULL, 0, 'Place reserved smoked pork butt cooking liquid into a Dutch oven or large pot. Add all remaining ingredients except potatoes and meat. Add additional water or broth, if necessary, so ingredients are covered by 2 inches. Bring to a boil and simmer, 30 minutes, partially covered.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BC06342C87E34E4E9BB8BBD235EA94DB', X'0F9EC977FD2DE342B803FF5E8C5382DB', X'0F9EC977FD2DE342B803FF5E8C5382DB', 1, NULL, NULL, 2, NULL, 0, 'Add potatoes and return to a boil. Cook, partially covered, until tender, 20 to 30 minutes. Add meat and heat through. If you have leftover cabbage and potatoes from the smoked pork butt dinner, you can add them to the soup along with the meat. Portion into warm bowls and serve with crusty bread.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0252AEAD1791D0418F2932053DDFD44C', X'7B9A465D3746CF4CA5CC62ADAE9FA085', X'7B9A465D3746CF4CA5CC62ADAE9FA085', 0, NULL, NULL, 2, NULL, 0, 'Place smoked pork butt in a Dutch oven or stockpot. Add water to cover and add brown sugar. Cover pot and bring to a boil.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4FB5A974D21DDF4885AB049E951B879E', X'7B9A465D3746CF4CA5CC62ADAE9FA085', X'7B9A465D3746CF4CA5CC62ADAE9FA085', 1, NULL, NULL, 2, NULL, 0, 'Reduce heat and simmer, partially covered, for 2 hours. (A pork butt requires about 40 minutes per pound.)');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D43B020BD202E6469C0B601AFFA8F3D6', X'7B9A465D3746CF4CA5CC62ADAE9FA085', X'7B9A465D3746CF4CA5CC62ADAE9FA085', 2, NULL, NULL, 2, NULL, 0, 'Halfway through the cooking process (after 1 hour), add the cabbage and peppercorns. Bring back to the boil, reduce the heat, and continue to simmer.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CA2CECB567F6C5418DB0C5100634EA13', X'7B9A465D3746CF4CA5CC62ADAE9FA085', X'7B9A465D3746CF4CA5CC62ADAE9FA085', 3, NULL, NULL, 2, NULL, 0, 'About 30 minutes before the cooking time is up, add the whole potatoes. Return to the boil, reduce the heat, and continue to simmer, partially covered, until the potatoes and meat are tender.');
