INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'581B6D546C5D7346AD3048E4E37DE180', X'AF105A2AB951FD46A3C6F7F01B86675B', X'AF105A2AB951FD46A3C6F7F01B86675B', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6AE45A6E385DDE46A20ECAED0A338E0B', X'AF105A2AB951FD46A3C6F7F01B86675B', X'AF105A2AB951FD46A3C6F7F01B86675B', 1, NULL, NULL, 2, NULL, 0, 'Place a skillet, saut pan, or saucier over medium-low heat and add the butter. Cook, constantly stirring, until the butter has melted and begins to get foamy. This might take 1 to 2 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3EE0AA8C056CA64AB65B96DE71A6CD07', X'AF105A2AB951FD46A3C6F7F01B86675B', X'AF105A2AB951FD46A3C6F7F01B86675B', 2, NULL, NULL, 2, NULL, 0, 'Sprinkle the flour over the butter and stir or whisk until smooth and well blended, approximately 2 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A518C25487B55743A9879F1188E74C86', X'AF105A2AB951FD46A3C6F7F01B86675B', X'AF105A2AB951FD46A3C6F7F01B86675B', 3, NULL, NULL, 2, NULL, 0, 'Continue cooking the flour and butter, while stirring or whisking, for 2 to 3 minutes. This step is essential and requires care because the roux could easily burn; keep an eye on the mixture for the whole time.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'76167FCA0727D34ABF0A9DAC5A395BD1', X'AF105A2AB951FD46A3C6F7F01B86675B', X'AF105A2AB951FD46A3C6F7F01B86675B', 4, NULL, NULL, 2, NULL, 0, 'Gradually whisk in the stock, stirring until well blended. Cook, constantly stirring, until the gravy begins to bubble and thicken. If your gravy seems too thin, mix in a bowl a few tablespoons of flour with the same amount of cold water until a smooth paste has formed. Slowly stir the water-flour paste into the boiling gravy until it is thickened as desired. On the contrary, if the gravy seems too thick, thin it with more stock, broth, or water. Cook, stirring, for 8 to 10 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3E31BADEE0427A409F578C18FB67EC9D', X'AF105A2AB951FD46A3C6F7F01B86675B', X'AF105A2AB951FD46A3C6F7F01B86675B', 5, NULL, NULL, 2, NULL, 0, 'If using, add browning sauce, a few drops at a time, until you have a nice rich color. Don''t pour directly from the bottle, because your gravy could end up darker in color than you''d like. Taste the gravy and add the optional kosher salt and freshly ground black pepper, to taste. Cook for an extra 2 to 3 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'179D8A454C899C41B6453CF7FC97EE20', X'AF105A2AB951FD46A3C6F7F01B86675B', X'AF105A2AB951FD46A3C6F7F01B86675B', 6, NULL, NULL, 2, NULL, 0, 'Pour the gravy through a fine-mesh sieve into a serving bowl with a ladle or into a gravy boat.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6B9A1CA4FFE7BA45AFA107CADBA8388A', X'AF105A2AB951FD46A3C6F7F01B86675B', X'AF105A2AB951FD46A3C6F7F01B86675B', 7, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3D0B10F88EEB974CA6510F162CA37BCF', X'7881E2B17549A942A1CAF29A8B159B3D', X'7881E2B17549A942A1CAF29A8B159B3D', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients. Preheat the oven to 350 F.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'601D400AFAEEC54D8BC218B4DA5C682B', X'7881E2B17549A942A1CAF29A8B159B3D', X'7881E2B17549A942A1CAF29A8B159B3D', 1, NULL, NULL, 2, NULL, 0, 'Line up the wings on a baking pan fitted with a rack. Sprinkle the wings on all sides with salt and pepper.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F0F1BA1DE502454486DB4A51804F0D58', X'7881E2B17549A942A1CAF29A8B159B3D', X'7881E2B17549A942A1CAF29A8B159B3D', 2, NULL, NULL, 2, NULL, 0, 'Pour in a cup of water and cover the pan. Roast until the meat is tender when you poke it with a fork, but not falling apart, about 1 1/2 hours.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C88189FC6F266745B06ADBCDBC208AA0', X'7881E2B17549A942A1CAF29A8B159B3D', X'7881E2B17549A942A1CAF29A8B159B3D', 3, NULL, NULL, 2, NULL, 0, 'Uncover the wings, increase the oven temperature to 450 F, and roast them until the skin becomes firm and crisp, about 15 more minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'92659E8736CCEA4D87DD03E7979E6665', X'7881E2B17549A942A1CAF29A8B159B3D', X'7881E2B17549A942A1CAF29A8B159B3D', 4, NULL, NULL, 2, NULL, 0, 'Meanwhile make the sauce: Melt a stick of butter in a pan on medium heat. Add hot sauce. Transfer the sauce to a large bowl.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'651FE4B71A98EF45981A43D32F1F5D8B', X'7881E2B17549A942A1CAF29A8B159B3D', X'7881E2B17549A942A1CAF29A8B159B3D', 5, NULL, NULL, 2, NULL, 0, 'Take the wings from the oven and toss them with the sauce in the bowl.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D0901FE680C591479EF148B8DA14C920', X'7881E2B17549A942A1CAF29A8B159B3D', X'7881E2B17549A942A1CAF29A8B159B3D', 6, NULL, NULL, 2, NULL, 0, 'Serve wings with blue cheese or ranch dressing, carrot and celery sticks. Enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A5B84AE3C735AC4D8CDC68D4F8DA2CD8', X'BA1463AC29359E43A4832525A1B6FAAE', X'BA1463AC29359E43A4832525A1B6FAAE', 0, NULL, NULL, 2, NULL, 0, 'Add one tablespoon of the olive oil to a large skillet or cast iron pan. Do not use a non-stick skillet for this. Add the ground turkey and brown it for 5 to 7 minutes, breaking it up with a spatula or wooden spoon as you cook. When it is ready, move the cooked turkey to a plate and set aside while you saute the vegetables. Note that this dish works equally well with ground chicken.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'92054A38D705A8498402497DD3BFA43B', X'BA1463AC29359E43A4832525A1B6FAAE', X'BA1463AC29359E43A4832525A1B6FAAE', 1, NULL, NULL, 2, NULL, 0, 'Add the remaining tablespoon of olive oil to the pan along with the diced onion. Saute on medium high heat, stirring often, for about one minute. Add the broccoli florets (these should be cut small) and the diced red bell pepper. Continue sauteing and stirring for another couple of minutes until the vegetables begin to soften.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9C295C795E434E46B5888F1FCBF4436B', X'BA1463AC29359E43A4832525A1B6FAAE', X'BA1463AC29359E43A4832525A1B6FAAE', 2, NULL, NULL, 2, NULL, 0, 'Add the finely minced garlic and continue sauteing for an additional minute. Then return the browned ground turkey to the pan.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'65678DBEB0723344BF1865DB73BB4E12', X'BA1463AC29359E43A4832525A1B6FAAE', X'BA1463AC29359E43A4832525A1B6FAAE', 3, NULL, NULL, 2, NULL, 0, 'Add in the soy sauce and continue cooking and stirring for another couple of minutes, until the turkey has heated through and all the vegetables are combined. Serve over cooked white rice.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5E3F770D65413A4DAB762136A2363160', X'BA1463AC29359E43A4832525A1B6FAAE', X'BA1463AC29359E43A4832525A1B6FAAE', 4, NULL, NULL, 2, NULL, 0, 'Optional ingredients include julienned carrots and a splash of hot sauce or chili paste if you like your food spicy.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'136B3D7C65F91F4FBBC287EAE3808CDA', X'BA195B540921AA479A928ECC2984C1FD', X'BA195B540921AA479A928ECC2984C1FD', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5ACBF8A0B756EA4BA18D5D87234B7DB1', X'BA195B540921AA479A928ECC2984C1FD', X'BA195B540921AA479A928ECC2984C1FD', 1, NULL, NULL, 2, NULL, 0, 'Mix the brine ingredients together in a container that''s large enough to fully submerge the turkey breast. A large stock pot works well.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4814CEDFA8803F4FA472E51DC41C78B0', X'BA195B540921AA479A928ECC2984C1FD', X'BA195B540921AA479A928ECC2984C1FD', 2, NULL, NULL, 2, NULL, 0, 'Add the turkey breast to the water and weigh it down with a heavy plate, if necessary, then cover with a lid. Place it in the refrigerator and allow it to brine for at least 18 hours or up to 24 hours.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'20F9270968013E4E8F19992AB39853E9', X'BA195B540921AA479A928ECC2984C1FD', X'BA195B540921AA479A928ECC2984C1FD', 3, NULL, NULL, 2, NULL, 0, 'Remove the turkey breast from the refrigerator and pat it completely dry with paper towels.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'20A6E3642EFDB348A3E207ACC46D82B7', X'BA195B540921AA479A928ECC2984C1FD', X'BA195B540921AA479A928ECC2984C1FD', 4, NULL, NULL, 2, NULL, 0, 'Mix together the softened butter, garlic powder, poultry seasoning, ground sage, and salt and pepper in a bowl. Combine until its evenly mixed.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'16DF8D3DD5B53241ABCD4E6BA8841AF4', X'BA195B540921AA479A928ECC2984C1FD', X'BA195B540921AA479A928ECC2984C1FD', 5, NULL, NULL, 2, NULL, 0, 'Place the turkey breast into the bin of the air fryer, breast side down.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F4BCB39CCD1F4A408213974675C1BB76', X'BA195B540921AA479A928ECC2984C1FD', X'BA195B540921AA479A928ECC2984C1FD', 6, NULL, NULL, 2, NULL, 0, 'Cook for 45 minutes at 375 F.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'14ADDF6A7446CC40B2483886A312DAAA', X'BA195B540921AA479A928ECC2984C1FD', X'BA195B540921AA479A928ECC2984C1FD', 7, NULL, NULL, 2, NULL, 0, 'Flip the turkey breast and cook for an additional 30 minutes at 375 F, or until the top of the breast is golden brown and the internal temperature reaches 165 F.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DFF710E9E43F48459232C93DDBBF3C20', X'BA195B540921AA479A928ECC2984C1FD', X'BA195B540921AA479A928ECC2984C1FD', 8, NULL, NULL, 2, NULL, 0, 'Remove from the air fryer and allow it to rest for ten minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'47A1EBD060485445BBBA0B5D455F5517', X'BA195B540921AA479A928ECC2984C1FD', X'BA195B540921AA479A928ECC2984C1FD', 9, NULL, NULL, 2, NULL, 0, 'Remove the breasts from the bone and then slice and serve!');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'81A1175C8DA3FB4EAE3822F56850BE5B', X'A9C2BC771D985F49889BBCF13E1E9C8D', X'A9C2BC771D985F49889BBCF13E1E9C8D', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'26674EDCA70A1545966FDBA98F61A926', X'A9C2BC771D985F49889BBCF13E1E9C8D', X'A9C2BC771D985F49889BBCF13E1E9C8D', 1, NULL, NULL, 2, NULL, 0, 'Pat the turkey breast with paper towels to dry; do not remove the netting.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'960B3DF046173D4B94942FE0AD05D349', X'A9C2BC771D985F49889BBCF13E1E9C8D', X'A9C2BC771D985F49889BBCF13E1E9C8D', 2, NULL, NULL, 2, NULL, 0, 'Peel the garlic; mince or put it through a garlic press. Transfer the garlic to a small bowl and add the paprika, minced thyme, minced rosemary, pepper, and kosher salt. Mix thoroughly. Rub the turkey breast all over with the garlic and spice mixture.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'75159A154A203B4294D46A3E09B67B27', X'A9C2BC771D985F49889BBCF13E1E9C8D', X'A9C2BC771D985F49889BBCF13E1E9C8D', 3, NULL, NULL, 2, NULL, 0, 'Select the high saut setting on the Instant Pot and add the olive oil or ghee. Place the turkey breast in the Instant Pot and cook for about 8 to 10 minutes, turning to brown all sides. Alternatively, sear the turkey breast in a large skillet over medium heat.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1344C88C5F0F684286DC69001DE919CB', X'A9C2BC771D985F49889BBCF13E1E9C8D', X'A9C2BC771D985F49889BBCF13E1E9C8D', 4, NULL, NULL, 2, NULL, 0, 'Remove the turkey breast to a plate and place the rack in the Instant Pot. Add the chicken stock to the pot along with a few sprigs of herbs, if desired. Return the turkey breast to the Instant Pot.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'90B26A3B6FEB3F47831CDA9E3AF81096', X'A9C2BC771D985F49889BBCF13E1E9C8D', X'A9C2BC771D985F49889BBCF13E1E9C8D', 5, NULL, NULL, 2, NULL, 0, 'Lock the lid in place and ensure the vent is set to `sealing.` Choose the manual setting; adjust the pressure to high and set the time for 35 minutes. When the time is up, let the pressure release naturally for 10 minutes. Check the temperature of the turkey with an instant-read thermometer inserted into the center. If the turkey is not at least 165 F, lock the lid in place and continue to cook it for a few more minutes. Remove the turkey breast to a platter; tent with foil and keep warm.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9A30D4D5B77CB240B25E691087CBEC49', X'A9C2BC771D985F49889BBCF13E1E9C8D', X'A9C2BC771D985F49889BBCF13E1E9C8D', 6, NULL, NULL, 2, NULL, 0, 'Strain the drippings into a bowl or gravy separator and skim off excess fat. Measure 2 cups of strained drippings and set aside.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DE3DD086282EF048A25D33BE34A4CF23', X'A9C2BC771D985F49889BBCF13E1E9C8D', X'A9C2BC771D985F49889BBCF13E1E9C8D', 7, NULL, NULL, 2, NULL, 0, 'Select the normal/medium saut setting on the Instant Pot. Melt the 4 tablespoons of butter in the pot, then add the flour. Cook the butter and flour mixture, constantly stirring, for 2 minutes. Gradually add the reserved 2 cups of drippings and continue cooking, frequently stirring, until the gravy is thickened. Taste and add salt and pepper as needed.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E02130290A2CA04DB41E526A20852EE0', X'A9C2BC771D985F49889BBCF13E1E9C8D', X'A9C2BC771D985F49889BBCF13E1E9C8D', 8, NULL, NULL, 2, NULL, 0, 'Carefully remove the netting from the turkey breast and slice thinly.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4C8A18ACB0685846B44DCFC37A493CE5', X'A9C2BC771D985F49889BBCF13E1E9C8D', X'A9C2BC771D985F49889BBCF13E1E9C8D', 9, NULL, NULL, 2, NULL, 0, 'Slice the turkey breast and serve it with the gravy, cranberry sauce, mashed potatoes, or any of your favorite side dishes.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7616B0F97CD1CE40B6D76184025B4E99', X'A9C2BC771D985F49889BBCF13E1E9C8D', X'A9C2BC771D985F49889BBCF13E1E9C8D', 10, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0032BA6B8B9A5C4593E0C339F6049B46', X'0DC4F540F2549B40A8535F3BC7CAB577', X'0DC4F540F2549B40A8535F3BC7CAB577', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1B200199367C104D94CC680A69DCC82B', X'0DC4F540F2549B40A8535F3BC7CAB577', X'0DC4F540F2549B40A8535F3BC7CAB577', 1, NULL, NULL, 2, NULL, 0, 'In a large cast iron pot, cook bacon until all fat has been released.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8953FDA7CFAA8B468081BBD44F3BE37C', X'0DC4F540F2549B40A8535F3BC7CAB577', X'0DC4F540F2549B40A8535F3BC7CAB577', 2, NULL, NULL, 2, NULL, 0, 'Remove cooked bacon with slotted spoon and set to the side.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AFDDC1DE375F4B40A9FBF6C0EA724929', X'0DC4F540F2549B40A8535F3BC7CAB577', X'0DC4F540F2549B40A8535F3BC7CAB577', 3, NULL, NULL, 2, NULL, 0, 'Combine the fat with flour.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AE03ADA22F61E94782FC0589F64FCC28', X'0DC4F540F2549B40A8535F3BC7CAB577', X'0DC4F540F2549B40A8535F3BC7CAB577', 4, NULL, NULL, 2, NULL, 0, 'Stir the mixture slowly and constantly for 20 to 25 minutes over low heat until it reaches a deep yellow color.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A4AD4F493FFE224B845FAB415BAD23B9', X'0DC4F540F2549B40A8535F3BC7CAB577', X'0DC4F540F2549B40A8535F3BC7CAB577', 5, NULL, NULL, 2, NULL, 0, 'Add your chopped onions, bell pepper, garlic and celery to the roux and cook until soft, about 5 minutes.Add the sausage and the Cajun seasoning and cook, stirring often, for 5 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0036CFA269D07E40B3E7241E3E581351', X'0DC4F540F2549B40A8535F3BC7CAB577', X'0DC4F540F2549B40A8535F3BC7CAB577', 6, NULL, NULL, 2, NULL, 0, 'Add the broth and bring to a boil. Reduce the heat to medium-low and simmer, uncovered, for 45 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3C2E5A1857CB4344A41676D218F0F07B', X'0DC4F540F2549B40A8535F3BC7CAB577', X'0DC4F540F2549B40A8535F3BC7CAB577', 7, NULL, NULL, 2, NULL, 0, 'Add the turkey meat, shrimp, okra, and extra stuffing, if using, and cook for 15 to 20 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C32BF6C53340A143AB12BDE916ABF621', X'0DC4F540F2549B40A8535F3BC7CAB577', X'0DC4F540F2549B40A8535F3BC7CAB577', 8, NULL, NULL, 2, NULL, 0, 'Serve with steamed rice and top with green onions.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D685E882E53F2047B4C312492F2AEA38', X'348E6E5517F30C47BC91AF5630743956', X'348E6E5517F30C47BC91AF5630743956', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients. Place a rack in a large roasting pan and preheat the oven to 350 F.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9A2338A183576A41B1B4A353C8697A72', X'348E6E5517F30C47BC91AF5630743956', X'348E6E5517F30C47BC91AF5630743956', 1, NULL, NULL, 2, NULL, 0, 'Remove the thawed turkey from its packaging and pat dry with paper towels. Remove the innards and discard them or use them to make homemade stock. Place the turkey on the rack in the roasting pan and sprinkle lightly with salt and pepper. Rub the inside of the cavity with salt and add the carrot, celery, onion, and sprigs of herbs. Tuck the wings under the bird and tie the drumsticks together with kitchen twine.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6A77BA516FCB6945BD743A9B17D8B3E8', X'348E6E5517F30C47BC91AF5630743956', X'348E6E5517F30C47BC91AF5630743956', 2, NULL, NULL, 2, NULL, 0, 'Melt 6 tablespoons of butter in a saucepan over medium heat. Add the fresh chopped herbs and bring to a simmer.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'977D15CE1F9AD74DB3909C2880177553', X'348E6E5517F30C47BC91AF5630743956', X'348E6E5517F30C47BC91AF5630743956', 3, NULL, NULL, 2, NULL, 0, 'Brush the turkey with the butter and herb mixture. Place in the oven and roast for 1 hour and 45 minutes. Place a foil tent over the turkey and continue to roast for about 1 to 1 1/2 hours longer, basting every 20 to 30 minutes. The turkey should register about 156 F to 160 F in the breast meat and about 170 in the thigh (make sure the thermometer is in the thickest part of the meat and is not touching bone).');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7C5372982F52CD418FEB86C315C2FD1F', X'348E6E5517F30C47BC91AF5630743956', X'348E6E5517F30C47BC91AF5630743956', 4, NULL, NULL, 2, NULL, 0, 'Remove the turkey to a platter and let it rest for 20 to 30 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7B2425ECCC80AD4EBE941228EB9DB7A8', X'348E6E5517F30C47BC91AF5630743956', X'348E6E5517F30C47BC91AF5630743956', 5, NULL, NULL, 2, NULL, 0, 'While the turkey rests, prepare the gravy. Pour the pan drippings into a gravy separator and let stand for 2 minutes, until the fat rises to the top. Pour the broth into a cup or bowl, leaving the fat in the separator. Set the defatted broth aside and measure 1/2 cup of fat. If you don''t have 1/2 cup of fat, add butter to make 1/2 cup. Pour the fat into a saucepan and place it over medium-low heat.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5AC7B58C24794F41A195B447D5FB8B3B', X'348E6E5517F30C47BC91AF5630743956', X'348E6E5517F30C47BC91AF5630743956', 6, NULL, NULL, 2, NULL, 0, 'Add the flour to the hot fat and cook the roux, whisking constantly, for 2 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3CF90274EBEF19429B35F0CE6784833A', X'348E6E5517F30C47BC91AF5630743956', X'348E6E5517F30C47BC91AF5630743956', 7, NULL, NULL, 2, NULL, 0, 'Whisk in the reserved defatted broth along with 2 cups of chicken stock. Continue to cook, constantly whisking, until the gravy is thickened. Add more stock to thin the gravy, as desired. Taste the gravy and add salt and pepper, as needed.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7F26E9CEA9DF704DA79BEADACD75E4C9', X'348E6E5517F30C47BC91AF5630743956', X'348E6E5517F30C47BC91AF5630743956', 8, NULL, NULL, 2, NULL, 0, 'Strain the gravy, if desired, and pour it into a gravy boat or serving bowl. Serve the gravy with the turkey.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8DBF1341BB65D442A9732A1DF9A0EEF1', X'3CE97BF65A84314DB2BF263E1D97080D', X'3CE97BF65A84314DB2BF263E1D97080D', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3B5CB0DE8C053549983C93C3C48B1443', X'3CE97BF65A84314DB2BF263E1D97080D', X'3CE97BF65A84314DB2BF263E1D97080D', 1, NULL, NULL, 2, NULL, 0, 'Add 1 tablespoon of olive oil to the Instant Pot and choose the saut setting. Add the chopped onion, bell pepper, and shredded carrots and cook, frequently stirring, for about 4 minutes or until the vegetables are softened.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A5499C5D1717F44E9AE29303F8C96295', X'3CE97BF65A84314DB2BF263E1D97080D', X'3CE97BF65A84314DB2BF263E1D97080D', 2, NULL, NULL, 2, NULL, 0, 'Remove the vegetables to a plate and set aside. Add the remaining 1 tablespoon of olive oil to the pot along with the ground turkey. Cook, stirring, until the turkey is no longer pink. Add the chopped jalapeo peppers and garlic and cook for 1 minute longer.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'84F43CF262CE6F45BB931D7251859D46', X'3CE97BF65A84314DB2BF263E1D97080D', X'3CE97BF65A84314DB2BF263E1D97080D', 3, NULL, NULL, 2, NULL, 0, 'Add the onion, bell pepper, and carrot back to the pot along with the Worcestershire sauce, chili powder, cumin, and pepper. Stir to blend.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C8B90D16C2D9D943B5426DD74D0CC79A', X'3CE97BF65A84314DB2BF263E1D97080D', X'3CE97BF65A84314DB2BF263E1D97080D', 4, NULL, NULL, 2, NULL, 0, 'Add the tomatoes, undrained beans, and chicken stock to the pot. Stir to blend.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F5E1707CC3385943B7D35216D617C206', X'3CE97BF65A84314DB2BF263E1D97080D', X'3CE97BF65A84314DB2BF263E1D97080D', 5, NULL, NULL, 2, NULL, 0, 'Lock the lid in place and make sure the knob is in the `sealing` position. Choose the manual setting, high pressure, and set the timer to 20 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1D0E170002A7AD4B80CDB39B2C9E4D1C', X'3CE97BF65A84314DB2BF263E1D97080D', X'3CE97BF65A84314DB2BF263E1D97080D', 6, NULL, NULL, 2, NULL, 0, 'When the time is up, let the pressure release naturally for 15 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'85DD6A97FE11A74EA0A423EA3803AFAD', X'3CE97BF65A84314DB2BF263E1D97080D', X'3CE97BF65A84314DB2BF263E1D97080D', 7, NULL, NULL, 2, NULL, 0, 'Enjoy the big bowl of chili with freshly baked cornbread and your favorite toppings. Croutons, shredded cheese, sour cream, cilantro leaves, and chilies are excellent garnish options.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5F119BD0F2738B459582ACF0DCE5530E', X'98A5386698BEB74C89266BFCD3471409', X'98A5386698BEB74C89266BFCD3471409', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2238AF6B139B5A41A91303DD7BBD8A4D', X'98A5386698BEB74C89266BFCD3471409', X'98A5386698BEB74C89266BFCD3471409', 1, NULL, NULL, 2, NULL, 0, 'Preheat a large, heavy-bottomed pot over medium heat. Add the oil, followed by the onion. Saut for 2 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6394E460D820AE408699225D3A3659F2', X'98A5386698BEB74C89266BFCD3471409', X'98A5386698BEB74C89266BFCD3471409', 2, NULL, NULL, 2, NULL, 0, 'Add the bell pepper, jalapeo and garlic. Saut for 2 more minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0F0C46A4558E47458AB112C030C38EB0', X'98A5386698BEB74C89266BFCD3471409', X'98A5386698BEB74C89266BFCD3471409', 3, NULL, NULL, 2, NULL, 0, 'Add the chili powder, cumin, and oregano and cook, stirring, for 1 minute.');
