INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8AA93AF4DE61654EAC822A2C13DE2F26', X'8EE09092D8B80B4E9D74BA3FC2FF9CD6', X'8EE09092D8B80B4E9D74BA3FC2FF9CD6', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7561BA8F9143BE4BAB0ED18C8841BD8F', X'8EE09092D8B80B4E9D74BA3FC2FF9CD6', X'8EE09092D8B80B4E9D74BA3FC2FF9CD6', 1, NULL, NULL, 2, NULL, 0, 'In a large bowl, whisk together the flour, baking powder, and salt.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7BA46121CEC72740B4ECAD0C96B7260E', X'8EE09092D8B80B4E9D74BA3FC2FF9CD6', X'8EE09092D8B80B4E9D74BA3FC2FF9CD6', 2, NULL, NULL, 2, NULL, 0, 'Add the butter to the flour mixture and combine using two forks or a pastry blender until the butter is the size of small peas. You can also use a food processor to combine the ingredients as well.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DA6E5F6A0AB5BB47857DBD4DCED77DA3', X'8EE09092D8B80B4E9D74BA3FC2FF9CD6', X'8EE09092D8B80B4E9D74BA3FC2FF9CD6', 3, NULL, NULL, 2, NULL, 0, 'Pour the buttermilk over the flour and butter mixture and stir with a spatula to combine until it becomes a soft dough. Be careful not to overwork the dough. The less it is mixed up, the more tender the dumplings will be.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'53E16004964EF1489524612EAD6C6A9E', X'8EE09092D8B80B4E9D74BA3FC2FF9CD6', X'8EE09092D8B80B4E9D74BA3FC2FF9CD6', 4, NULL, NULL, 2, NULL, 0, 'After you have created the dough for the dumplings, heat the broth in a large dutch oven or pot until boiling.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E8A0046C02E9B745AD6BD63DFB611555', X'8EE09092D8B80B4E9D74BA3FC2FF9CD6', X'8EE09092D8B80B4E9D74BA3FC2FF9CD6', 5, NULL, NULL, 2, NULL, 0, 'While the broth is heating, dust a clean kitchen surface with a generous amount of flour and roll out the dough into a 1/4-inch thick sheet.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1D2A87E269DF2F42B7EB2D8267C32E9D', X'8EE09092D8B80B4E9D74BA3FC2FF9CD6', X'8EE09092D8B80B4E9D74BA3FC2FF9CD6', 6, NULL, NULL, 2, NULL, 0, 'Slice the dough into small squares using a pizza cutter to create the dumplings.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0388C17D1BD5AC40B44FDF860BB49F12', X'8EE09092D8B80B4E9D74BA3FC2FF9CD6', X'8EE09092D8B80B4E9D74BA3FC2FF9CD6', 7, NULL, NULL, 2, NULL, 0, 'Add the dumplings a few at a time into the boiling broth, making sure not to dust off too much of the extra flour as it will thicken the broth.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3FE5781F20823949B0BA578877FA748B', X'8EE09092D8B80B4E9D74BA3FC2FF9CD6', X'8EE09092D8B80B4E9D74BA3FC2FF9CD6', 8, NULL, NULL, 2, NULL, 0, 'Continue adding dumplings until there are no more left. Cook for 8 to 10 minutes or until the dumplings are tender and fluffy and the broth has thickened.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5F88F91077F86A4F8CC1A7FCCA25C5ED', X'8EE09092D8B80B4E9D74BA3FC2FF9CD6', X'8EE09092D8B80B4E9D74BA3FC2FF9CD6', 9, NULL, NULL, 2, NULL, 0, 'Add the cooked turkey to the broth and heat until it is warmed through.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3E6E9C00AEA66B4D9D9BCEFA9A004B36', X'8EE09092D8B80B4E9D74BA3FC2FF9CD6', X'8EE09092D8B80B4E9D74BA3FC2FF9CD6', 10, NULL, NULL, 2, NULL, 0, 'Add salt and pepper to taste.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'142FAAB3D02A9E46A29110FACC460080', X'8EE09092D8B80B4E9D74BA3FC2FF9CD6', X'8EE09092D8B80B4E9D74BA3FC2FF9CD6', 11, NULL, NULL, 2, NULL, 0, 'Serve with the parsley if using and enjoy.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F1AC8613410CBF4A9F7476FE7CCBD3A7', X'C75B567DFCF9C34EA4DA3AF95184C43D', X'C75B567DFCF9C34EA4DA3AF95184C43D', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
