INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F232D249B8F54042B56C6F23805CAFD8', X'C75B567DFCF9C34EA4DA3AF95184C43D', X'C75B567DFCF9C34EA4DA3AF95184C43D', 2, NULL, NULL, 2, NULL, 0, 'Chop the leftover stuffing and add it to the mixing bowl.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C68A60DD77E3134587FF5DEBC393F7E2', X'C75B567DFCF9C34EA4DA3AF95184C43D', X'C75B567DFCF9C34EA4DA3AF95184C43D', 3, NULL, NULL, 2, NULL, 0, 'Dice the brie and add it to the turkey and stuffing. Mix to combine the three ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C25987DA9A15BB418601BBF932EFF235', X'C75B567DFCF9C34EA4DA3AF95184C43D', X'C75B567DFCF9C34EA4DA3AF95184C43D', 4, NULL, NULL, 2, NULL, 0, 'Heat the oil to 375 F, using a candy thermometer to monitor the temperature.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1526954A5C230743AFF5861668BF5D15', X'C75B567DFCF9C34EA4DA3AF95184C43D', X'C75B567DFCF9C34EA4DA3AF95184C43D', 5, NULL, NULL, 2, NULL, 0, 'Beat the egg in a small bowl, and then beat in the water to create an egg wash.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6B71DD78AD048746900B58209BD606CC', X'C75B567DFCF9C34EA4DA3AF95184C43D', X'C75B567DFCF9C34EA4DA3AF95184C43D', 6, NULL, NULL, 2, NULL, 0, 'Place an egg roll wrapper on a plate or other flat surface so that one corner is facing you.');
