INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E4A0862B323EA843B08CDBA811808208', X'C75B567DFCF9C34EA4DA3AF95184C43D', X'C75B567DFCF9C34EA4DA3AF95184C43D', 9, NULL, NULL, 2, NULL, 0, 'Fry 3 to 4 egg rolls in the hot oil until they are golden brown, about 2 to 3 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6C009E1AE6AC1F4FBD5E9BC8DB480175', X'C75B567DFCF9C34EA4DA3AF95184C43D', X'C75B567DFCF9C34EA4DA3AF95184C43D', 10, NULL, NULL, 2, NULL, 0, 'Remove from the oil and place on a paper towel-lined plate to absorb the extra grease. Repeat until all of the egg rolls are cooked. Keep them warm in a 200 F oven while you prepare the sauce.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3E75C22D3479A04196D0B249662AADDC', X'C75B567DFCF9C34EA4DA3AF95184C43D', X'C75B567DFCF9C34EA4DA3AF95184C43D', 11, NULL, NULL, 2, NULL, 0, 'Place the cranberry sauce ingredients in a small saucepan and heat until the ingredients are heated through, stirring to combine.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DAE87C26EDF8F74490EC0CAF728AE1E7', X'C75B567DFCF9C34EA4DA3AF95184C43D', X'C75B567DFCF9C34EA4DA3AF95184C43D', 12, NULL, NULL, 2, NULL, 0, 'Serve with the egg rolls immediately.');
