INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D50E2AD529A25D4DB445CD26DF5F24A5', X'41BDC4AE925A4348B63CF2A5950634AB', X'41BDC4AE925A4348B63CF2A5950634AB', 3, NULL, NULL, 2, NULL, 0, 'Place the tortilla on a large oven-safe plate, then smear the center with sour cream.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5C445ACB8980C7429FCD61A19BC56B28', X'41BDC4AE925A4348B63CF2A5950634AB', X'41BDC4AE925A4348B63CF2A5950634AB', 4, NULL, NULL, 2, NULL, 0, 'Separately, heat up the roast turkey, mashed potatoes, and leftover stuffing. You can microwave for faster results).');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'19CC549A98225944BE36AAC4091F06AE', X'41BDC4AE925A4348B63CF2A5950634AB', X'41BDC4AE925A4348B63CF2A5950634AB', 5, NULL, NULL, 2, NULL, 0, 'Drop the turkey mixture onto the center of the warmed tortilla, covering the sour cream.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C711C8B7A0E08F4B896C1855D0B70859', X'41BDC4AE925A4348B63CF2A5950634AB', X'41BDC4AE925A4348B63CF2A5950634AB', 6, NULL, NULL, 2, NULL, 0, 'Roll the burrito by folding the end of the burrito over the filling and then folding in the sides while rolling the tortilla.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'53CDD5EE8782ED4487F6A0AD77B741EA', X'41BDC4AE925A4348B63CF2A5950634AB', X'41BDC4AE925A4348B63CF2A5950634AB', 7, NULL, NULL, 2, NULL, 0, 'Heat the gravy in a small pot on medium-low heat until it is hot.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'69F613CC61DA6943A532018FD42C35FB', X'41BDC4AE925A4348B63CF2A5950634AB', X'41BDC4AE925A4348B63CF2A5950634AB', 8, NULL, NULL, 2, NULL, 0, 'Pour the gravy over the top of the burrito while it is on the oven proof plate. Cover as much, or as little, of the burrito that you wish.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'01E6D0803639DA4A9B7EFADBEF26DCA9', X'41BDC4AE925A4348B63CF2A5950634AB', X'41BDC4AE925A4348B63CF2A5950634AB', 9, NULL, NULL, 2, NULL, 0, 'Sprinkle the top of the burrito with the shredded cheese.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D08347C862BF4A4A83E42C91CE8267F3', X'41BDC4AE925A4348B63CF2A5950634AB', X'41BDC4AE925A4348B63CF2A5950634AB', 10, NULL, NULL, 2, NULL, 0, 'Place the burrito under the broiler for about 1 minute, or until the cheese is melted.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DC9C2BE10B28444897A42A9323C900A5', X'41BDC4AE925A4348B63CF2A5950634AB', X'41BDC4AE925A4348B63CF2A5950634AB', 11, NULL, NULL, 2, NULL, 0, 'Sprinkle with chopped parsley, if using.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'96FC9380B8A0354D8887683B174C892F', X'41BDC4AE925A4348B63CF2A5950634AB', X'41BDC4AE925A4348B63CF2A5950634AB', 12, NULL, NULL, 2, NULL, 0, 'Serve with leftover cranberry sauce on the side.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'84026750D2272346A9F3F27AA5F6D8FD', X'996AC48428102E4D8108B72751034C00', X'996AC48428102E4D8108B72751034C00', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C5D45E71F29DE54F963A7B89824E157C', X'996AC48428102E4D8108B72751034C00', X'996AC48428102E4D8108B72751034C00', 1, NULL, NULL, 2, NULL, 0, 'Shred or chop the roasted turkey meat into bite-sized pieces.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'26CFFE10206F5B479C49A6A4FB751AC3', X'996AC48428102E4D8108B72751034C00', X'996AC48428102E4D8108B72751034C00', 2, NULL, NULL, 2, NULL, 0, 'Whisk together the mayonnaise, sour cream, garlic powder, salt, and pepper in a bowl.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4335CCCF823B6E46AE46BFBBD7447A62', X'996AC48428102E4D8108B72751034C00', X'996AC48428102E4D8108B72751034C00', 3, NULL, NULL, 2, NULL, 0, 'Add the shredded turkey, sharp cheddar cheese and scallions. Stir to completely combine and coat the chicken. Cover and place in the refrigerator while preparing the cranberry mustard.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A8CB95F8C7E37C4C8E66977953520B91', X'996AC48428102E4D8108B72751034C00', X'996AC48428102E4D8108B72751034C00', 4, NULL, NULL, 2, NULL, 0, 'In a small saucepan, combine the frozen cranberries and water. Heat on medium low for about 10 to 15 minutes or until the cranberries have burst and are softened.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C43740BC8F3F204B871035538964460A', X'996AC48428102E4D8108B72751034C00', X'996AC48428102E4D8108B72751034C00', 5, NULL, NULL, 2, NULL, 0, 'Add the mustard and maple syrup to the cranberries and stir to combine. Cook for another 2 to 3 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F8D24FF163227C40AE0D8A4CA45C6EA8', X'996AC48428102E4D8108B72751034C00', X'996AC48428102E4D8108B72751034C00', 6, NULL, NULL, 2, NULL, 0, 'Remove the cranberry mixture from the heat. Use an immersion blender to blend the ingredients until completely smooth, then add salt to taste. Cool the mustard in the refrigerator.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F126093235D34C42A720C6403E43A9C7', X'996AC48428102E4D8108B72751034C00', X'996AC48428102E4D8108B72751034C00', 7, NULL, NULL, 2, NULL, 0, 'Place about a 1/3 cup of the turkey mixture in each butter lettuce leaf and drizzle with the cranberry mustard.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'61F032C03CBEF347A0F2BE6B493D9AB3', X'996AC48428102E4D8108B72751034C00', X'996AC48428102E4D8108B72751034C00', 8, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A15FC3984C76904F9311A5DE629EECB1', X'9E7B2331B52CCB4E82805BB5698AFC36', X'9E7B2331B52CCB4E82805BB5698AFC36', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients and preheat the oven to 350 F/180 C/Gas 4.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'49831D1389928D4C9FEE66CFB0BB503D', X'9E7B2331B52CCB4E82805BB5698AFC36', X'9E7B2331B52CCB4E82805BB5698AFC36', 1, NULL, NULL, 2, NULL, 0, 'Grease a 2 1/2- to 3-quart casserole dish.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DDAFDA98BF346C478F82300C7345BA63', X'9E7B2331B52CCB4E82805BB5698AFC36', X'9E7B2331B52CCB4E82805BB5698AFC36', 2, NULL, NULL, 2, NULL, 0, 'Cook the spaghetti in boiling salted water following the package directions. Drain well.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8CEBF2CDF334E64C8D74AB6514E43A65', X'9E7B2331B52CCB4E82805BB5698AFC36', X'9E7B2331B52CCB4E82805BB5698AFC36', 3, NULL, NULL, 2, NULL, 0, 'In a large bowl, combine the cooked and drained spaghetti with 1 cup of cheddar cheese and 2 tablespoons of the Parmesan cheese.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'87161D0D271E93489E5F3278BA2CC14A', X'9E7B2331B52CCB4E82805BB5698AFC36', X'9E7B2331B52CCB4E82805BB5698AFC36', 4, NULL, NULL, 2, NULL, 0, 'Add the diced turkey and pimientos.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'46EF336C3B85FE4ABC67D6287BD9F451', X'9E7B2331B52CCB4E82805BB5698AFC36', X'9E7B2331B52CCB4E82805BB5698AFC36', 5, NULL, NULL, 2, NULL, 0, 'In a skillet or saute pan, melt butter and saute the mushrooms and onions until they are tender and most of the liquid has evaporated; add to the turkey mixture along with remaining ingredients. Taste and add salt and pepper, as needed. Gently stir to combine ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'67E69FB78057F2439850274D0292FCC7', X'9E7B2331B52CCB4E82805BB5698AFC36', X'9E7B2331B52CCB4E82805BB5698AFC36', 6, NULL, NULL, 2, NULL, 0, 'Transfer the mixture to the prepared casserole and sprinkle with remaining cheddar and Parmesan cheeses.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BE4F12FEBB75FD43AD4209D6BFF04CF5', X'9E7B2331B52CCB4E82805BB5698AFC36', X'9E7B2331B52CCB4E82805BB5698AFC36', 7, NULL, NULL, 2, NULL, 0, 'Cover the baking dish with a lid or foil and bake for about 45 minutes, or until hot and bubbly.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'998310FBECB97D488E6A7F547A0DBF02', X'9E7B2331B52CCB4E82805BB5698AFC36', X'9E7B2331B52CCB4E82805BB5698AFC36', 8, NULL, NULL, 2, NULL, 0, 'If desired, place the casserole under the broiler (high heat) for about 4 to 6 minutes, or until the top is lightly browned.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AFE35D2AFAA3884885C0CC8175EBFEB4', X'9E7B2331B52CCB4E82805BB5698AFC36', X'9E7B2331B52CCB4E82805BB5698AFC36', 9, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EE1CDE33715D1141AC741D5205DF9F92', X'E395CA9AF96D2F478F77D1FE309DC0B6', X'E395CA9AF96D2F478F77D1FE309DC0B6', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EE4EC4FDC7EB4C40B363CE40E4A1EC88', X'E395CA9AF96D2F478F77D1FE309DC0B6', X'E395CA9AF96D2F478F77D1FE309DC0B6', 1, NULL, NULL, 2, NULL, 0, 'Place 2 1/4 cups of the bread stuffing in a bowl. Place 1 1/2 cups of the cornbread stuffing in another bowl.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B1807AF27D38B94E8816501F50863A81', X'E395CA9AF96D2F478F77D1FE309DC0B6', X'E395CA9AF96D2F478F77D1FE309DC0B6', 2, NULL, NULL, 2, NULL, 0, 'Place the remaining 1/2 cup of bread stuffing in a third bowl and add the remaining 1/2 cup of the cornbread stuffing. Add the whole berry cranberry sauce and pecans and toss gently to combine. You should now have 3 separate bowls of stuffings.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9806E03EB7148545B58E48835E34285F', X'E395CA9AF96D2F478F77D1FE309DC0B6', X'E395CA9AF96D2F478F77D1FE309DC0B6', 3, NULL, NULL, 2, NULL, 0, 'In a food processor fitted with the metal blade, combine the butter, garlic, sage, and thyme until the herbs are finely chopped.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4BF17A8B59AB734B8D7DF16873B7AD2B', X'E395CA9AF96D2F478F77D1FE309DC0B6', X'E395CA9AF96D2F478F77D1FE309DC0B6', 4, NULL, NULL, 2, NULL, 0, 'Gently run your hand under the turkey skin to make sort of a pocket, but do not separate the skin completely from the meat. Distribute the herb butter mixture evenly under the skin.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DD1E0665EBA6224AAA50C434EC13DD10', X'E395CA9AF96D2F478F77D1FE309DC0B6', X'E395CA9AF96D2F478F77D1FE309DC0B6', 5, NULL, NULL, 2, NULL, 0, 'Rub the skin with the browning sauce, then the olive oil. Sprinkle generously with kosher salt and freshly ground pepper.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BC0381B380A67346ABAB7E6FA5060FE1', X'E395CA9AF96D2F478F77D1FE309DC0B6', X'E395CA9AF96D2F478F77D1FE309DC0B6', 6, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 300 F. Flip the deboned turkey over so it is open and skin-side down. Sprinkle with salt and pepper. Spread bread stuffing evenly over the turkey cavity.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6F84E7F2D9430D4FB401EB8FF35F11F1', X'E395CA9AF96D2F478F77D1FE309DC0B6', X'E395CA9AF96D2F478F77D1FE309DC0B6', 7, NULL, NULL, 2, NULL, 0, 'Place the duck on top of the bread stuffing, skin-side down. Spread the cranberry nut stuffing on top of the open duck cavity.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'73FB6187AEC32046B41F7F3486DB2705', X'E395CA9AF96D2F478F77D1FE309DC0B6', X'E395CA9AF96D2F478F77D1FE309DC0B6', 8, NULL, NULL, 2, NULL, 0, 'Top with the chicken, skin-side down. Spread cornbread stuffing on top of the open chicken cavity. Skewer the back of the chicken closed.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'228629B8A721CC4C82C5EFB4C85A7B0C', X'E395CA9AF96D2F478F77D1FE309DC0B6', X'E395CA9AF96D2F478F77D1FE309DC0B6', 9, NULL, NULL, 2, NULL, 0, 'Bring up the sides of the duck to cover the chicken. Skewer the back of the duck closed.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'344BB3FF3558DA44840B81E6DD4E8270', X'E395CA9AF96D2F478F77D1FE309DC0B6', X'E395CA9AF96D2F478F77D1FE309DC0B6', 10, NULL, NULL, 2, NULL, 0, 'Repeat the process with the turkey.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3F47D8D346349E4B840178FCE562AC49', X'E395CA9AF96D2F478F77D1FE309DC0B6', X'E395CA9AF96D2F478F77D1FE309DC0B6', 11, NULL, NULL, 2, NULL, 0, 'Carefully turn the turducken over so it is seam-side down and breast-side up. If possible, remove all skewers except the last one holding the turkey together.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5DD741B2707B194FA9A2C1F973FBABEE', X'E395CA9AF96D2F478F77D1FE309DC0B6', X'E395CA9AF96D2F478F77D1FE309DC0B6', 12, NULL, NULL, 2, NULL, 0, 'Place the turducken in a heavy roaster. Roast 3 to 4 hours, or until a meat thermometer inserted in the very center of the chicken stuffing reaches 165 F. Baste once per hour with pan juices. If turducken begins to get too brown, tent loosely with heavy-duty aluminum foil that has been coated with vegetable spray.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'15E9E793A652AE4B82939A235926EE33', X'E395CA9AF96D2F478F77D1FE309DC0B6', X'E395CA9AF96D2F478F77D1FE309DC0B6', 13, NULL, NULL, 2, NULL, 0, 'Let the turducken rest 30 minutes before carving. Slice the turducken across the breast to show off each layer.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BFF82489F0F3F64795AEEBED30BE4730', X'44E14640EEB69B43A3EA9874819188B9', X'44E14640EEB69B43A3EA9874819188B9', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'868364558735524D9006A51BDDBAE2AA', X'44E14640EEB69B43A3EA9874819188B9', X'44E14640EEB69B43A3EA9874819188B9', 1, NULL, NULL, 2, NULL, 0, 'Put the bacon in a large skillet and place the skillet over medium heat. Cook until the bacon has rendered its fat. With a slotted spoon, remove the bacon pieces to paper towels to drain. You should have about 1/4 to 1/3 cup of drippings left in the pan. Refrigerate the crumbled bacon to use as a garnish or save it for another use.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'21AF93429057254481BE42CB02485E34', X'44E14640EEB69B43A3EA9874819188B9', X'44E14640EEB69B43A3EA9874819188B9', 2, NULL, NULL, 2, NULL, 0, 'Remove 2 tablespoons of oil from the 3/4 cup and set it aside. Add the remaining oilabout 2/3 cupto the bacon drippings. Add the flour to the bacon drippings and oil mixture and cook over medium heat, constantly stirring, for about 25 to 30 minutes, or until the roux is the color of milk chocolate. Remove the roux from the heat and set it aside.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1859A136EF7EAC4AA1FA5EC54C3F9702', X'44E14640EEB69B43A3EA9874819188B9', X'44E14640EEB69B43A3EA9874819188B9', 3, NULL, NULL, 2, NULL, 0, 'Heat the remaining 2 tablespoons of oil in a large Dutch oven over medium heat. Add the smoked sausage and cook for about 5 minutes, stirring frequently.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2CB60BB0EBC22C4BAFE57F2DE94CC723', X'44E14640EEB69B43A3EA9874819188B9', X'44E14640EEB69B43A3EA9874819188B9', 4, NULL, NULL, 2, NULL, 0, 'Add the onions, celery, green and red bell pepper, and the white and light green parts of the green onions to the sausage; continue to cook for about 4 minutes, or until the vegetables are soft. Add the garlic and cook for 1 minute longer.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0DF0E17AEDA5834A86F9696E9F746DFD', X'44E14640EEB69B43A3EA9874819188B9', X'44E14640EEB69B43A3EA9874819188B9', 5, NULL, NULL, 2, NULL, 0, 'To the vegetables and sausage, add the reserved roux; stir to combine.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'67B59E981BD8364C9F3030AA8E2ECE01', X'44E14640EEB69B43A3EA9874819188B9', X'44E14640EEB69B43A3EA9874819188B9', 6, NULL, NULL, 2, NULL, 0, 'Gradually add 2 cups the chicken stock to the pot a little at a time, stirring constantly. Add the chopped turkey, Worcestershire sauce, bay leaves, salt, Cajun seasoning, thyme, and black pepper. Bring to a boil and then reduce the heat to low and simmer for 1 1/2 hours, stirring occasionally. If too thick, add more stock in small amounts.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3FC7A3226F428B4BAED11EF493B4F5AF', X'44E14640EEB69B43A3EA9874819188B9', X'44E14640EEB69B43A3EA9874819188B9', 7, NULL, NULL, 2, NULL, 0, 'Stir the parsley and chopped green onions (dark green parts) into the gumbo and serve it over hot steamed rice.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5DD98530171C2743AF996D9DCDAF48DA', X'44E14640EEB69B43A3EA9874819188B9', X'44E14640EEB69B43A3EA9874819188B9', 8, NULL, NULL, 2, NULL, 0, 'Garnish the turkey gumbo with extra green onions, parsley, and the crumbled cooked bacon, if desired.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AFB6CF76BF071142A7A02858538CCC75', X'ABE1EA16EFCFF1499DACD78EFF0CC7E0', X'ABE1EA16EFCFF1499DACD78EFF0CC7E0', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7443AA84F324824D842BE0A67ED3CC4E', X'ABE1EA16EFCFF1499DACD78EFF0CC7E0', X'ABE1EA16EFCFF1499DACD78EFF0CC7E0', 1, NULL, NULL, 2, NULL, 0, 'In a large bowl, combine the celery, chopped red onion, diced turkey, and parsley, if using.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'12CC0012ECDA274C88CFD43F9BD90C2B', X'ABE1EA16EFCFF1499DACD78EFF0CC7E0', X'ABE1EA16EFCFF1499DACD78EFF0CC7E0', 2, NULL, NULL, 2, NULL, 0, 'Toss to blend ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'65AFDE8E5D40A74292EFE672B986557B', X'ABE1EA16EFCFF1499DACD78EFF0CC7E0', X'ABE1EA16EFCFF1499DACD78EFF0CC7E0', 3, NULL, NULL, 2, NULL, 0, 'In a small bowl, combine the lemon juice, 1/2 cup mayonnaise, and freshly ground black pepper.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'05484D1CEA61B148B1943668FF4BDCD3', X'ABE1EA16EFCFF1499DACD78EFF0CC7E0', X'ABE1EA16EFCFF1499DACD78EFF0CC7E0', 4, NULL, NULL, 2, NULL, 0, 'Stir into the turkey mixture until blended, adding more mayonnaise if needed. Add salt, to taste.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4DC40F7DF864274DB6DE866C869CDB56', X'ABE1EA16EFCFF1499DACD78EFF0CC7E0', X'ABE1EA16EFCFF1499DACD78EFF0CC7E0', 5, NULL, NULL, 2, NULL, 0, 'Line slices of bread or rolls with lettuce leaves, if desired, and fill with the turkey mixture.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'619EBC7BFFE99C42B522943C9F40B948', X'ABE1EA16EFCFF1499DACD78EFF0CC7E0', X'ABE1EA16EFCFF1499DACD78EFF0CC7E0', 6, NULL, NULL, 2, NULL, 0, 'Serve sandwiches with potato chips or fries and sliced dill pickles for a delicious lunch or sandwich supper. Alternatively, serve the turkey salad on lettuce leaves or spring greens with sliced tomatoes or avocado wedges.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B8AC1EE35465384D961796ABB010E1A6', X'7A2C38A7FC936B42A09A778367412DDF', X'7A2C38A7FC936B42A09A778367412DDF', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8D892946F55FB44EBA29DA21AF1837D3', X'7A2C38A7FC936B42A09A778367412DDF', X'7A2C38A7FC936B42A09A778367412DDF', 1, NULL, NULL, 2, NULL, 0, 'Place the turkey breast in a large cooking bag; place the bag with the turkey in a large roasting pan or another large container (just in case the bag leaks).');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D7C56800F1CAC440B5C6B4236FF22AE4', X'7A2C38A7FC936B42A09A778367412DDF', X'7A2C38A7FC936B42A09A778367412DDF', 2, NULL, NULL, 2, NULL, 0, 'Add the brine ingredients to the bag and then close the bag and twist until brine is covering turkey. Use a twist-tie to secure the bag. Alternately, you can use a large, nonreactive covered container. Just make sure the turkey is completely covered with brine (you might have to increase the amount of brine for a container). Place the turkey breast in the refrigerator for up to 24 hours.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A24CEF9802A8644CA7C17B9F48DE36FC', X'7A2C38A7FC936B42A09A778367412DDF', X'7A2C38A7FC936B42A09A778367412DDF', 3, NULL, NULL, 2, NULL, 0, 'Heat oven to 400 F/200 C/Gas 6.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8513B639A47E794EB25F59E2DF77A38C', X'7A2C38A7FC936B42A09A778367412DDF', X'7A2C38A7FC936B42A09A778367412DDF', 4, NULL, NULL, 2, NULL, 0, 'Remove turkey from brine and rinse it thoroughly with cold water; pat dry with paper towels.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ADE3CF133A44C840AE02BCF3DCA08704', X'7A2C38A7FC936B42A09A778367412DDF', X'7A2C38A7FC936B42A09A778367412DDF', 5, NULL, NULL, 2, NULL, 0, 'Place the turkey breast on a rack, breast side up, in a large roasting pan. Discard brine.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'22DDA632C77B424D923095F72618683C', X'7A2C38A7FC936B42A09A778367412DDF', X'7A2C38A7FC936B42A09A778367412DDF', 6, NULL, NULL, 2, NULL, 0, 'Rub the turkey with vegetable oil, butter or extra-virgin olive oil and sprinkle with salt and pepper or a seasoned salt blend.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'98BDE48CB2FD724A8F83B804D0D8E183', X'7A2C38A7FC936B42A09A778367412DDF', X'7A2C38A7FC936B42A09A778367412DDF', 7, NULL, NULL, 2, NULL, 0, 'If desired, fill the cavity of the turkey breast with chunks of onion, fruit and fresh herbs.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1AE4F1F20752AD4EAAC291D669A9E72C', X'7A2C38A7FC936B42A09A778367412DDF', X'7A2C38A7FC936B42A09A778367412DDF', 8, NULL, NULL, 2, NULL, 0, 'Place a meat thermometer in the thickest part of the breast, not touching bone.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'87998B5430DF724F91E92D6BB6518D66', X'7A2C38A7FC936B42A09A778367412DDF', X'7A2C38A7FC936B42A09A778367412DDF', 9, NULL, NULL, 2, NULL, 0, 'Roast at 400 F/200 C/Gas 6 for 20 minutes. Reduce heat to 325 F/165 C/Gas 3 and continue roasting until the turkey breast registers at least 165 F/74 C, about 2 to 3 hours.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A0229153F517A14496B3368B5F35A840', X'7A2C38A7FC936B42A09A778367412DDF', X'7A2C38A7FC936B42A09A778367412DDF', 10, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6948C56F950F764D8974DCE9E1E50C60', X'F48E5314FC8EEB40A642A0876499BCB6', X'F48E5314FC8EEB40A642A0876499BCB6', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients and preheat oven to 425 F.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EA3D0CE519D5394880451C1F1E3DD774', X'F48E5314FC8EEB40A642A0876499BCB6', X'F48E5314FC8EEB40A642A0876499BCB6', 1, NULL, NULL, 2, NULL, 0, 'Place the turkey breast on a rack in a roasting pan.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9B6C951783496848BE9BEEA8C6A75D4F', X'F48E5314FC8EEB40A642A0876499BCB6', X'F48E5314FC8EEB40A642A0876499BCB6', 2, NULL, NULL, 2, NULL, 0, 'Brush turkey with the melted butter, then season generously with Kosher salt and black pepper.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AECA95C30C6A264A840E9DFC461B3436', X'F48E5314FC8EEB40A642A0876499BCB6', X'F48E5314FC8EEB40A642A0876499BCB6', 3, NULL, NULL, 2, NULL, 0, 'Roast for 45 minutes to 1 hour, basting twice with stockonce after about 15 minutes and again about 15 minutes later (see note below)');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C70CAF649843C54F8645D059AB5E0C99', X'F48E5314FC8EEB40A642A0876499BCB6', X'F48E5314FC8EEB40A642A0876499BCB6', 4, NULL, NULL, 2, NULL, 0, 'When thermometer reads 155 F, remove the turkey from the oven.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D52773A446DDC54FB9402637915B86F1', X'F48E5314FC8EEB40A642A0876499BCB6', X'F48E5314FC8EEB40A642A0876499BCB6', 5, NULL, NULL, 2, NULL, 0, 'Cover loosely with foil and let it rest for about 20 minutes (or when the thermometer reads 120 F) before carving and serving.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F9339BDC84144744AAC58081F5CEB47C', X'F48E5314FC8EEB40A642A0876499BCB6', X'F48E5314FC8EEB40A642A0876499BCB6', 6, NULL, NULL, 2, NULL, 0, 'Serve and enjoy');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1643643B0223464483A6871F55A3283B', X'6A84813C91A7C146BBDC164523BBB74B', X'6A84813C91A7C146BBDC164523BBB74B', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BDBE353C50E15B49A71B2C7BCB9BA4DA', X'6A84813C91A7C146BBDC164523BBB74B', X'6A84813C91A7C146BBDC164523BBB74B', 1, NULL, NULL, 2, NULL, 0, 'Combine the liquid smoke, soy sauce, Tabasco, Worcestershire, mesquite, brown sugar, onion powder, garlic powder, and kosher salt in a large zip-top bag.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'45A505A1B3C7A8458F56E24B33C08191', X'6A84813C91A7C146BBDC164523BBB74B', X'6A84813C91A7C146BBDC164523BBB74B', 2, NULL, NULL, 2, NULL, 0, 'Add thinly-sliced turkey strips to the marinade, seal, and squish to coat all the meat. Re-open the bag, squeeze out all the air, re-seal, and refrigerate 12 to 24 hours.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DF3A9C888C38A44BAE576DB282150AAF', X'6A84813C91A7C146BBDC164523BBB74B', X'6A84813C91A7C146BBDC164523BBB74B', 3, NULL, NULL, 2, NULL, 0, 'Remove the turkey strips from marinade and gently pat off excess moisture with a paper towel.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'11764F212EB3644AAD76B48FF4A3248B', X'6A84813C91A7C146BBDC164523BBB74B', X'6A84813C91A7C146BBDC164523BBB74B', 4, NULL, NULL, 2, NULL, 0, 'Dehydrate until the turkey jerky is leathery and chewy, but not crisp enough to snap when bent.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BB0D4F44B41C194F98736D102EE86213', X'6A84813C91A7C146BBDC164523BBB74B', X'6A84813C91A7C146BBDC164523BBB74B', 5, NULL, NULL, 2, NULL, 0, 'Place the marinated strips in a single layer, with space in between, on dehydrator racks.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FFBA64A6902B8D40B437A86D2C0891DE', X'6A84813C91A7C146BBDC164523BBB74B', X'6A84813C91A7C146BBDC164523BBB74B', 6, NULL, NULL, 2, NULL, 0, 'Store the jerky in sealed bags in the refrigerator.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'502E5EB934170B4CB2BE21159B5A00BB', X'192A399E0A038343926CAB0DD0DFFC71', X'192A399E0A038343926CAB0DD0DFFC71', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4025A1025E437841B7D79E5CA58ACDDE', X'192A399E0A038343926CAB0DD0DFFC71', X'192A399E0A038343926CAB0DD0DFFC71', 1, NULL, NULL, 2, NULL, 0, 'Check to make sure that the turkey will fit the grill with 1 inch of clearance on all sides.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7838D95DD503F44CB5D6A77B2D839446', X'192A399E0A038343926CAB0DD0DFFC71', X'192A399E0A038343926CAB0DD0DFFC71', 2, NULL, NULL, 2, NULL, 0, 'Combine the seasoning ingredients in a small bowl.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E1EC9A9AA8A18D49911BCE73AC931143', X'192A399E0A038343926CAB0DD0DFFC71', X'192A399E0A038343926CAB0DD0DFFC71', 3, NULL, NULL, 2, NULL, 0, 'Slather the inside and outside of turkey with the mixture, making sure to get seasonings under the skin. If this proves to be difficult, melt the mixture and use a marinade injector to deposit seasonings into the bird.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'76A17970F76A004B89AA51128AE1734C', X'192A399E0A038343926CAB0DD0DFFC71', X'192A399E0A038343926CAB0DD0DFFC71', 4, NULL, NULL, 2, NULL, 0, 'Thread the turkey onto the spit through the neck skin and out just below the tail.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'916E74B79FC90E42BC545F74E91F275B', X'192A399E0A038343926CAB0DD0DFFC71', X'192A399E0A038343926CAB0DD0DFFC71', 5, NULL, NULL, 2, NULL, 0, 'Thread onion onto the spit so that it rests inside the turkey. Fasten tightly with spit forks.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7D635CED6D699A46B2165A2FB7B7158F', X'192A399E0A038343926CAB0DD0DFFC71', X'192A399E0A038343926CAB0DD0DFFC71', 6, NULL, NULL, 2, NULL, 0, 'Tie wings and legs with string tightly to the body of the turkey. Hold spit on each side of the turkey and roll it in your hands to check the balance. Readjust, if necessary, so that it will turn evenly.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9BE9F1F311170445960F48F9286662A2', X'192A399E0A038343926CAB0DD0DFFC71', X'192A399E0A038343926CAB0DD0DFFC71', 7, NULL, NULL, 2, NULL, 0, 'Remove cooking racks from grill and preheat. Place drip pan under where the turkey will turn. If using charcoal, place hot coals around the edge with few coals directly under the turkey.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ABAD3CA13444614A811C2E0D398C0FC5', X'192A399E0A038343926CAB0DD0DFFC71', X'192A399E0A038343926CAB0DD0DFFC71', 8, NULL, NULL, 2, NULL, 0, 'Grill over a medium heat until the skin starts to blister and brown. Reduce heat and continue grilling until the turkey reaches an internal temperature (measure in the center of the breast) of 185 F. Plan on a cooking time of about 3 to 3 1/2 hours for a 12-pound turkey.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6D030BB05F0F324BB6492794024498EF', X'192A399E0A038343926CAB0DD0DFFC71', X'192A399E0A038343926CAB0DD0DFFC71', 9, NULL, NULL, 2, NULL, 0, 'Carve and enjoy.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'86384AAB6AC533429614618FA087CAD4', X'611703226120FC47822D5169C7232C1B', X'611703226120FC47822D5169C7232C1B', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'879AD521CD4DAC4588D33969B98DD382', X'611703226120FC47822D5169C7232C1B', X'611703226120FC47822D5169C7232C1B', 1, NULL, NULL, 2, NULL, 0, 'Spray the crockpot with nonstick cooking spray.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'81326EAD8ABD4A4198BCF5921FF2D9E5', X'611703226120FC47822D5169C7232C1B', X'611703226120FC47822D5169C7232C1B', 2, NULL, NULL, 2, NULL, 0, 'Place the chunks of celery and carrots in the slow cooker. The vegetables will act as a rack.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C536835A180E8A42ABFA6C5B97B1FB02', X'611703226120FC47822D5169C7232C1B', X'611703226120FC47822D5169C7232C1B', 3, NULL, NULL, 2, NULL, 0, 'Sprinkle salt and pepper over the turkey breast and arrange in a large slow cooker. Pour melted butter over the turkey.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9C8B001DE784F848835AB7112D886833', X'611703226120FC47822D5169C7232C1B', X'611703226120FC47822D5169C7232C1B', 4, NULL, NULL, 2, NULL, 0, 'Cover and cook on HIGH for about 5 to 6 hours, or until turkey is done and juices run clear when pierced with a knife. A boneless turkey breast might take less time, so check for doneness earlier.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'28F9309C29188649B0F73926844DFB31', X'611703226120FC47822D5169C7232C1B', X'611703226120FC47822D5169C7232C1B', 5, NULL, NULL, 2, NULL, 0, 'To be sure the turkey is fully cooked, check the temperature with a reliable food thermometer. The minimum safe temperature for turkey and other poultry is 165 F.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E71EFC84CEE7CB4381792140781FEBD1', X'611703226120FC47822D5169C7232C1B', X'611703226120FC47822D5169C7232C1B', 6, NULL, NULL, 2, NULL, 0, 'Pour the juices from the slow cooker into a saucepan. Bring to boil slowly.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D117A0E8BC7D084BBE93A4F97693FDD7', X'611703226120FC47822D5169C7232C1B', X'611703226120FC47822D5169C7232C1B', 7, NULL, NULL, 2, NULL, 0, 'Combine the cornstarch and water together and then add to the saucepan.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'51AE2F974BDDAF41B781B6CEED5B0718', X'611703226120FC47822D5169C7232C1B', X'611703226120FC47822D5169C7232C1B', 8, NULL, NULL, 2, NULL, 0, 'Add some turkey or chicken stock, about 1/2 to 1 cup, depending on the amount of liquid left in the crockpot. Whisk over medium-low heat until smooth and thickened.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'33B3D392F800D044A160B33AE536A29C', X'611703226120FC47822D5169C7232C1B', X'611703226120FC47822D5169C7232C1B', 9, NULL, NULL, 2, NULL, 0, 'Serve the sliced turkey breast on a plate topped with the turkey gravy.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1A2044403E6F15498C248D4169DB43EC', X'E06BC85A4485094F9495880FA15E2DB2', X'E06BC85A4485094F9495880FA15E2DB2', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients and preheat the oven to 400 F.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'753018FAA467FD41ADCEE8A980C4158A', X'E06BC85A4485094F9495880FA15E2DB2', X'E06BC85A4485094F9495880FA15E2DB2', 1, NULL, NULL, 2, NULL, 0, 'Bake the bacon for 10 minutes. You can also brown in a pan.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'980296B2C715644BBFD8EE6AAC9C13E5', X'E06BC85A4485094F9495880FA15E2DB2', X'E06BC85A4485094F9495880FA15E2DB2', 2, NULL, NULL, 2, NULL, 0, 'Whisk together the mayonnaise, lemon juice, and garlic powder in a small bowl.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DEAC262AACCCCB4C93022AE507929570', X'E06BC85A4485094F9495880FA15E2DB2', X'E06BC85A4485094F9495880FA15E2DB2', 3, NULL, NULL, 2, NULL, 0, 'Toast the bread so that it is still slightly soft, but also a nice light brown color. Place it on a cooling rack if you can, so that it doesn''t get soggy while you prepare the sandwich.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'80157A9DCBC6D74B8F257B19DCD1517A', X'E06BC85A4485094F9495880FA15E2DB2', X'E06BC85A4485094F9495880FA15E2DB2', 4, NULL, NULL, 2, NULL, 0, 'Place one slice of toast on a cutting board. Add two slices of turkey, top with a small handful of fresh spinach, a slice of tomato, and 2 slices of bacon.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'612D51A9C4B0BC4E952D14E166E32DB9', X'E06BC85A4485094F9495880FA15E2DB2', X'E06BC85A4485094F9495880FA15E2DB2', 5, NULL, NULL, 2, NULL, 0, 'Layer another slice of toast on top. Place three more slices of turkey, 4 slices of fresh avocado, and two more pieces of bacon.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C02140B1B7204742B99A6780F9A06742', X'E06BC85A4485094F9495880FA15E2DB2', X'E06BC85A4485094F9495880FA15E2DB2', 6, NULL, NULL, 2, NULL, 0, 'Spread some of the aioli onto the last slice of toast. Place the toast aioli side down on top of the sandwich.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BA8B287914224344AD1A6B984C3805CA', X'E06BC85A4485094F9495880FA15E2DB2', X'E06BC85A4485094F9495880FA15E2DB2', 7, NULL, NULL, 2, NULL, 0, 'Cut into four triangles, secure each piece with a toothpick.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'900ABE42D327EB43826D7BDA6B53C837', X'E06BC85A4485094F9495880FA15E2DB2', X'E06BC85A4485094F9495880FA15E2DB2', 8, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6495FE910E03BD46997F2367EAD28EBF', X'58162E226274CD45A4E5C81CD79F0722', X'58162E226274CD45A4E5C81CD79F0722', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'42B3EF41FFA1EC48B6F9D0085DABABD7', X'58162E226274CD45A4E5C81CD79F0722', X'58162E226274CD45A4E5C81CD79F0722', 1, NULL, NULL, 2, NULL, 0, 'Heat the oven to 450 F.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'16E0B91D55A52348B4B96116971248DB', X'58162E226274CD45A4E5C81CD79F0722', X'58162E226274CD45A4E5C81CD79F0722', 2, NULL, NULL, 2, NULL, 0, 'Line a large baking pan with foil and spray with nonstick spray (jelly roll pan, baking pan, or roasting pan).');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C9B14E0ED60D6F4886E8F84F53C0428D', X'58162E226274CD45A4E5C81CD79F0722', X'58162E226274CD45A4E5C81CD79F0722', 3, NULL, NULL, 2, NULL, 0, 'Sprinkle the turkey cutlets all over with the salt and pepper.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F538971AB13CCA409EB7E9043D9ECCF1', X'58162E226274CD45A4E5C81CD79F0722', X'58162E226274CD45A4E5C81CD79F0722', 4, NULL, NULL, 2, NULL, 0, 'Set up three breading trays or shallow bowls. Put flour in a breading tray or bowl. In another breading tray or bowl, combine the crumbs and Parmesan cheese.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6C592756E321C5448346BD5D0970C76C', X'58162E226274CD45A4E5C81CD79F0722', X'58162E226274CD45A4E5C81CD79F0722', 5, NULL, NULL, 2, NULL, 0, 'Beat the eggs, egg whites, water, and Dijon together and pour into another tray or bowl.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0BE6C60B0824E342A90DFA1A04A1A90A', X'58162E226274CD45A4E5C81CD79F0722', X'58162E226274CD45A4E5C81CD79F0722', 6, NULL, NULL, 2, NULL, 0, 'One at a time, dip cutlets in the flour, then dip in the egg mixture, then dredge in the crumb mixture until thoroughly coated.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D4C011393216C6419AF00AC2654448AA', X'58162E226274CD45A4E5C81CD79F0722', X'58162E226274CD45A4E5C81CD79F0722', 7, NULL, NULL, 2, NULL, 0, 'Arrange the cutlets on the prepared baking pan. Spray the cutlets lightly with olive oil or canola oil spray or drizzle with melted butter.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'863155D26E81954096CEB746FC64E23B', X'58162E226274CD45A4E5C81CD79F0722', X'58162E226274CD45A4E5C81CD79F0722', 8, NULL, NULL, 2, NULL, 0, 'Bake cutlets for 10 to 15 minutes, or longer if your turkey cutlets are thick. To be sure, use an instant-read food thermometer. The temperature should be at least 165 F on a food thermometer inserted into the thickest part of a cutlet.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'011574DB95C77F48A851FD4E31EE38D4', X'FBEAB8971E426A4E9C0443702D7B1688', X'FBEAB8971E426A4E9C0443702D7B1688', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients and preheat oven to 350 F.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'909A53DC4FD55E4BBF140037F0761DD2', X'FBEAB8971E426A4E9C0443702D7B1688', X'FBEAB8971E426A4E9C0443702D7B1688', 1, NULL, NULL, 2, NULL, 0, 'Lightly grease a 2-quart baking dish or spray with nonstick cooking spray.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B5F4630CDF7C554B90EB5ABD1F422DB8', X'FBEAB8971E426A4E9C0443702D7B1688', X'FBEAB8971E426A4E9C0443702D7B1688', 2, NULL, NULL, 2, NULL, 0, 'In a skillet, melt the butter and saute the celery, onion, and apple until softened.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AFAF49973379CA498B31AF6A216B4761', X'FBEAB8971E426A4E9C0443702D7B1688', X'FBEAB8971E426A4E9C0443702D7B1688', 3, NULL, NULL, 2, NULL, 0, 'Pour the sauteed onion and apple mixture over the bread cubes in a large bowl and toss to blend.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7F17C993AEBE2E46B38C0C1CB6E96966', X'FBEAB8971E426A4E9C0443702D7B1688', X'FBEAB8971E426A4E9C0443702D7B1688', 4, NULL, NULL, 2, NULL, 0, 'Toss with the dried cranberries, if using, poultry seasoning, salt, and a dash of pepper.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3CFBB381C9145940B1D7D9C129F0C59A', X'FBEAB8971E426A4E9C0443702D7B1688', X'FBEAB8971E426A4E9C0443702D7B1688', 5, NULL, NULL, 2, NULL, 0, 'Add 3/4 cup chicken broth and stir until moistened, adding more broth if needed. Taste and adjust seasonings before you add the egg. Stir in the beaten egg until well blended.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ACBD200DA58A3F49B018ADECB0032EB8', X'FBEAB8971E426A4E9C0443702D7B1688', X'FBEAB8971E426A4E9C0443702D7B1688', 6, NULL, NULL, 2, NULL, 0, 'Gently pat the stuffing mixture into the prepared baking dish.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'73F297AA375D7240BB23DB69FA467E28', X'FBEAB8971E426A4E9C0443702D7B1688', X'FBEAB8971E426A4E9C0443702D7B1688', 7, NULL, NULL, 2, NULL, 0, 'In a large skillet over medium heat, heat olive oil or spray the pan with nonstick cooking spray. Sprinkle turkey cutlets with seasoned salt and pepper and saute for about 2 minutes on each side.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0EA75962194B9B4D87BB1A91544C1870', X'FBEAB8971E426A4E9C0443702D7B1688', X'FBEAB8971E426A4E9C0443702D7B1688', 8, NULL, NULL, 2, NULL, 0, 'Add the gravy and bring to a boil. Simmer for 3 to 4 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7509374395990541B6641D2B28CBAEF5', X'FBEAB8971E426A4E9C0443702D7B1688', X'FBEAB8971E426A4E9C0443702D7B1688', 9, NULL, NULL, 2, NULL, 0, 'Arrange the turkey over the stuffing then pour the gravy over all.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B92F0BCC305EF9428421FC528D8E5372', X'FBEAB8971E426A4E9C0443702D7B1688', X'FBEAB8971E426A4E9C0443702D7B1688', 10, NULL, NULL, 2, NULL, 0, 'Bake for 25 to 30 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AD982BFDFC9B5548945054963EB3EFA2', X'FBEAB8971E426A4E9C0443702D7B1688', X'FBEAB8971E426A4E9C0443702D7B1688', 11, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9E7C8AAB9F7B74439EC32E26962A6125', X'23A743C0AD000242ACD0A4AD559F937F', X'23A743C0AD000242ACD0A4AD559F937F', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'649BED6964CB5B419117B8CC39767327', X'23A743C0AD000242ACD0A4AD559F937F', X'23A743C0AD000242ACD0A4AD559F937F', 1, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400 F. Spray two baking sheets with nonstick cooking spray and set aside.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6BADBC69F9B7E04BB07647099AE4F30E', X'23A743C0AD000242ACD0A4AD559F937F', X'23A743C0AD000242ACD0A4AD559F937F', 2, NULL, NULL, 2, NULL, 0, 'In a large bowl, combine the turkey, stuffing, and cheese and mix lightly with your hands.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'14F66A99B678CF4AB3CAF8BB7D5D5212', X'23A743C0AD000242ACD0A4AD559F937F', X'23A743C0AD000242ACD0A4AD559F937F', 3, NULL, NULL, 2, NULL, 0, 'Open the tubes of pizza dough and spread the dough out onto a lightly-floured surface. Cut each square of dough in half to make equal rectangles.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FAD35408AEE35F498A3B8BD2C6CC43F7', X'23A743C0AD000242ACD0A4AD559F937F', X'23A743C0AD000242ACD0A4AD559F937F', 4, NULL, NULL, 2, NULL, 0, 'Mound 1 1/2 cups of the turkey mixture on one half of each of the rectangles, leaving a 1-inch border around the edges.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6905815A8C50464984C1A0138EC370FE', X'23A743C0AD000242ACD0A4AD559F937F', X'23A743C0AD000242ACD0A4AD559F937F', 5, NULL, NULL, 2, NULL, 0, 'Fold half of the pizza dough over the stuffing to enclose. Press and fold the edges to seal.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A2B3A87143258147AB46C7C75A8D86A8', X'23A743C0AD000242ACD0A4AD559F937F', X'23A743C0AD000242ACD0A4AD559F937F', 6, NULL, NULL, 2, NULL, 0, 'Cut vent holes or slits in the top of each calzone. Brush each with some of the beaten egg.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7EF6E6650EDD674AA5B27E2BDC563C21', X'23A743C0AD000242ACD0A4AD559F937F', X'23A743C0AD000242ACD0A4AD559F937F', 7, NULL, NULL, 2, NULL, 0, 'Carefully place each calzone on the prepared baking sheets, two per sheet.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'797D536093FEF24993A9E8CCC272EE0B', X'23A743C0AD000242ACD0A4AD559F937F', X'23A743C0AD000242ACD0A4AD559F937F', 8, NULL, NULL, 2, NULL, 0, 'Bake for 20 to 25 minutes or until the calzones are nicely browned and the filling is very hot.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'59011CF65F28B64192E5470288CE120A', X'23A743C0AD000242ACD0A4AD559F937F', X'23A743C0AD000242ACD0A4AD559F937F', 9, NULL, NULL, 2, NULL, 0, 'Let stand on a wire rack for 5 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'417C8BCCB6CCB84FA1DD6B625DAB8086', X'23A743C0AD000242ACD0A4AD559F937F', X'23A743C0AD000242ACD0A4AD559F937F', 10, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0562C929E803C6449B2DEE567302B56B', X'8765FE88BDB0DF40B764DC742B5373F9', X'8765FE88BDB0DF40B764DC742B5373F9', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'94D49FEACC90DB4E9C045BF7141D5FA3', X'8765FE88BDB0DF40B764DC742B5373F9', X'8765FE88BDB0DF40B764DC742B5373F9', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 F.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CC2539C726AD0F4B826F1ECCF0B6EAC0', X'8765FE88BDB0DF40B764DC742B5373F9', X'8765FE88BDB0DF40B764DC742B5373F9', 2, NULL, NULL, 2, NULL, 0, 'Pat the turkey dry with paper towels. Never rinse the turkey before cooking it as the bacteria on the surface of the meat will aerosolize and spread around your kitchen. A good pat dry is a reliable procedure and enough for the seasonings to adhere to the skin.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A49F9051049DEC419030B8D0ABB6E1E9', X'8765FE88BDB0DF40B764DC742B5373F9', X'8765FE88BDB0DF40B764DC742B5373F9', 3, NULL, NULL, 2, NULL, 0, 'In a small bowl, combine the softened butter, salt, pepper, garlic, thyme, and sage and mix well.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'582A45B58E416942B3F3E703C9E4F5A2', X'8765FE88BDB0DF40B764DC742B5373F9', X'8765FE88BDB0DF40B764DC742B5373F9', 4, NULL, NULL, 2, NULL, 0, 'Loosen the skin from the meat and rub the butter mixture into the meat. Smooth the skin back over and rub the remaining butter mixture on the skin.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EFF8353A01B463439C17ECB950078ACC', X'8765FE88BDB0DF40B764DC742B5373F9', X'8765FE88BDB0DF40B764DC742B5373F9', 5, NULL, NULL, 2, NULL, 0, 'Place the thighs in a roasting pan and pour the broth around the turkey.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'403B7A83231D72429032896781F3BF54', X'8765FE88BDB0DF40B764DC742B5373F9', X'8765FE88BDB0DF40B764DC742B5373F9', 6, NULL, NULL, 2, NULL, 0, 'Roast the turkey thighs for 60 to 70 minutes or until a meat thermometer registers 170 F when inserted in the meat away from the bone.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2C9278047A858A489D2D9ACA8C66B3D5', X'8765FE88BDB0DF40B764DC742B5373F9', X'8765FE88BDB0DF40B764DC742B5373F9', 7, NULL, NULL, 2, NULL, 0, 'Remove the pan from the oven, cover tightly with foil or the pan lid, and let rest for 10 minutes before serving.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'59B895CB226C944EA0E2549707469B81', X'8765FE88BDB0DF40B764DC742B5373F9', X'8765FE88BDB0DF40B764DC742B5373F9', 8, NULL, NULL, 2, NULL, 0, 'Enjoy.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'42E5DB756BAB044695BC354077D40266', X'512C8931EA0D1F4E934D1F9823F17AF0', X'512C8931EA0D1F4E934D1F9823F17AF0', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B5E2F969C1DF57439D713B495DE34C3A', X'512C8931EA0D1F4E934D1F9823F17AF0', X'512C8931EA0D1F4E934D1F9823F17AF0', 1, NULL, NULL, 2, NULL, 0, 'Combine apple juice, brown sugar, and salt in a large saucepan. Bring to a boil and continue heating until the sugar and salt have dissolved.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3D85A000EAB20549A78AF651F1B25C67', X'512C8931EA0D1F4E934D1F9823F17AF0', X'512C8931EA0D1F4E934D1F9823F17AF0', 2, NULL, NULL, 2, NULL, 0, 'Skim off any foam that forms on the top and let cool.?');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AAC21CD905EDF645959D2840BF5DE6E3', X'512C8931EA0D1F4E934D1F9823F17AF0', X'512C8931EA0D1F4E934D1F9823F17AF0', 3, NULL, NULL, 2, NULL, 0, 'In a large (five-gallon or larger) stockpot or similar container, combine the apple juice mixture with three quarts of water, oranges, ginger, cloves, bay leaves, and garlic.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9DED9742F989814BA1217D0C19057C34', X'512C8931EA0D1F4E934D1F9823F17AF0', X'512C8931EA0D1F4E934D1F9823F17AF0', 4, NULL, NULL, 2, NULL, 0, 'Remove any fatty deposit you might find on the turkey and everything from the body cavity.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'20189BF1683BCE43997A2AB194AB6E39', X'512C8931EA0D1F4E934D1F9823F17AF0', X'512C8931EA0D1F4E934D1F9823F17AF0', 5, NULL, NULL, 2, NULL, 0, 'Place turkey in brine mixture and refrigerate for 24 hours. Make sure that the turkey remains completely submerged.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8A289F2991D9A140854C4DF0D30BAC5E', X'512C8931EA0D1F4E934D1F9823F17AF0', X'512C8931EA0D1F4E934D1F9823F17AF0', 6, NULL, NULL, 2, NULL, 0, 'Place hickory chips in water and prepare grill for indirect grilling on medium heat.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'45402625FBA4D149822599C7103DC3B7', X'512C8931EA0D1F4E934D1F9823F17AF0', X'512C8931EA0D1F4E934D1F9823F17AF0', 7, NULL, NULL, 2, NULL, 0, 'Remove turkey from brine and pat dry with paper towels.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A7B3B0C81692364AAAFF22FF429301D8', X'512C8931EA0D1F4E934D1F9823F17AF0', X'512C8931EA0D1F4E934D1F9823F17AF0', 8, NULL, NULL, 2, NULL, 0, 'Tie legs together with string and lightly brush turkey with vegetable oil.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'59739DA0A7518249B15861067D55CAF0', X'512C8931EA0D1F4E934D1F9823F17AF0', X'512C8931EA0D1F4E934D1F9823F17AF0', 9, NULL, NULL, 2, NULL, 0, 'Preheat your grill to about 325 F/165 C. Place turkey on a roasting rack inside a foil pan. Place on the grill away from direct heat.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1F21992EA227594487A18CCF14FCB0FB', X'512C8931EA0D1F4E934D1F9823F17AF0', X'512C8931EA0D1F4E934D1F9823F17AF0', 10, NULL, NULL, 2, NULL, 0, 'After 30 to 40 minutes, you will need to wrap the wings in foil to keep them from burning.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9040267623845F4796F0500E87786190', X'512C8931EA0D1F4E934D1F9823F17AF0', X'512C8931EA0D1F4E934D1F9823F17AF0', 11, NULL, NULL, 2, NULL, 0, 'Brush with vegetable oil periodically. If the breasts start to get too brown, cover with foil. The smoked turkey is done when the internal temperature reaches about 175 F/80 C in the thigh or about 165 F/75 C in the breast. You should expect it to take about 12 to 14 minutes per pound.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'78F903C700F107429B8A754C10D6155D', X'512C8931EA0D1F4E934D1F9823F17AF0', X'512C8931EA0D1F4E934D1F9823F17AF0', 12, NULL, NULL, 2, NULL, 0, 'When done, remove from grill and let rest for about 15 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'828CA988A66E5746BB9618A013AE096E', X'512C8931EA0D1F4E934D1F9823F17AF0', X'512C8931EA0D1F4E934D1F9823F17AF0', 13, NULL, NULL, 2, NULL, 0, 'Carve and serve!');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A048332888EE214C9837FBF1E4AB34ED', X'7FA1AF018B5B634384756F317A36ACC5', X'7FA1AF018B5B634384756F317A36ACC5', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2AF16F36E917FC4CBEC1CC07BFA5EFF3', X'7FA1AF018B5B634384756F317A36ACC5', X'7FA1AF018B5B634384756F317A36ACC5', 1, NULL, NULL, 2, NULL, 0, 'In a large skillet, melt the butter and add the curry powder, stirring for 2 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'35D4006A6CC1C64C9004375BEE2DCB03', X'7FA1AF018B5B634384756F317A36ACC5', X'7FA1AF018B5B634384756F317A36ACC5', 2, NULL, NULL, 2, NULL, 0, 'Add onions and stir frequently, cooking the onions until soft and translucid.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C18C3334DD373F4C9420F5C0CA2E91F6', X'7FA1AF018B5B634384756F317A36ACC5', X'7FA1AF018B5B634384756F317A36ACC5', 3, NULL, NULL, 2, NULL, 0, 'Add in flour and ginger. Stir vigorously to avoid large clumps.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'15EB489DE6722544835165FE3DFFC0C3', X'7FA1AF018B5B634384756F317A36ACC5', X'7FA1AF018B5B634384756F317A36ACC5', 4, NULL, NULL, 2, NULL, 0, 'Add chicken broth and pineapple. Mix well until there are no visible clumps of flour. Bring to a boil, then lower the heat and simmer, uncovered, for 5 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D87333332612B74DA5B567B3512C564E', X'7FA1AF018B5B634384756F317A36ACC5', X'7FA1AF018B5B634384756F317A36ACC5', 5, NULL, NULL, 2, NULL, 0, 'Stir in turkey and coat all the pieces well.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F0708C5F3DD8AD4B9D9332454545AF47', X'7FA1AF018B5B634384756F317A36ACC5', X'7FA1AF018B5B634384756F317A36ACC5', 6, NULL, NULL, 2, NULL, 0, 'Cover the mixture and simmer for 10 more minutes, or until heated through.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DA67339F59F52047A070E1928F7F4108', X'7FA1AF018B5B634384756F317A36ACC5', X'7FA1AF018B5B634384756F317A36ACC5', 7, NULL, NULL, 2, NULL, 0, 'Add in lemon juice and stir.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9CB9ADEFBAAA694A94486AEA19F7F572', X'7FA1AF018B5B634384756F317A36ACC5', X'7FA1AF018B5B634384756F317A36ACC5', 8, NULL, NULL, 2, NULL, 0, 'Garnish with fresh herbs if using. Serve over hot cooked rice and enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6C77ACA27A636D44AA946E1EEE8D95A4', X'E85AF6326CF1B345974A27EECC69A5BF', X'E85AF6326CF1B345974A27EECC69A5BF', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B19DDFEB14948B489B7B424D480D79BB', X'E85AF6326CF1B345974A27EECC69A5BF', X'E85AF6326CF1B345974A27EECC69A5BF', 1, NULL, NULL, 2, NULL, 0, 'Pat the turkey dry and place in a roasting pan. Bend the wings and tuck them under the turkey for a neat appearance.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'49B8E0265EB7D24ABB6B5AFDD78B8DBC', X'E85AF6326CF1B345974A27EECC69A5BF', X'E85AF6326CF1B345974A27EECC69A5BF', 2, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 F. Rub the turkey with the butter and sprinkle with salt and pepper.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B5EDD7FC34FA614A8655A3FEA610DF07', X'E85AF6326CF1B345974A27EECC69A5BF', X'E85AF6326CF1B345974A27EECC69A5BF', 3, NULL, NULL, 2, NULL, 0, 'Put the orange segments and thyme sprigs in the turkey cavity. Set the bird aside.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9C6F1556F5845C4C8BB1C2D9EA5F46FB', X'E85AF6326CF1B345974A27EECC69A5BF', X'E85AF6326CF1B345974A27EECC69A5BF', 4, NULL, NULL, 2, NULL, 0, 'In a small saucepan, cook the vinegar over medium-high heat until it is reduced to about half.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'39F65EBF0B5EAF4890EA317E8D214930', X'E85AF6326CF1B345974A27EECC69A5BF', X'E85AF6326CF1B345974A27EECC69A5BF', 5, NULL, NULL, 2, NULL, 0, 'Stir in the honey, orange juice, dried thyme, and butter and simmer for 5 minutes. Remove from heat.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3CE18EFED1922C4FA236336EC58D7944', X'E85AF6326CF1B345974A27EECC69A5BF', X'E85AF6326CF1B345974A27EECC69A5BF', 6, NULL, NULL, 2, NULL, 0, 'Brush the turkey with some of the glaze and place in the oven. Roast according to cooking charts for unstuffed or stuffed turkey (depending on what you''re doing), brushing with the glaze every 15 minutes for the first hour or until the glaze is used up.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'99B577315957474BB05FE2B1EAB824DD', X'E85AF6326CF1B345974A27EECC69A5BF', X'E85AF6326CF1B345974A27EECC69A5BF', 7, NULL, NULL, 2, NULL, 0, 'Continue roasting, basting with the juices in the pan every hour, until a meat thermometer inserted into the turkey thigh (make sure you are not touching the bone) registers 165 F.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5A345553851DA248B365A34755FFFA23', X'E85AF6326CF1B345974A27EECC69A5BF', X'E85AF6326CF1B345974A27EECC69A5BF', 8, NULL, NULL, 2, NULL, 0, 'Remove the turkey from the oven, cover it with foil, and let stand while you make finish cooking the rest of your Thanksgiving feast.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1D3D46DBA5E0D44F8E69C9C1FAC6A7EE', X'E85AF6326CF1B345974A27EECC69A5BF', X'E85AF6326CF1B345974A27EECC69A5BF', 9, NULL, NULL, 2, NULL, 0, 'Carve and serve!');
