INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'814EA875C5CAA347B10DD1E7829C6918', X'D3F7295080574944A270E6559CCDC53E', X'D3F7295080574944A270E6559CCDC53E', 1, NULL, NULL, 2, NULL, 0, 'Pat the turkey legs dry with paper towels and place them in a large roasting pan. Avoid rinsing poultry in the sink; that will just spread bacteria around your kitchen.');
