INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'937BC410222F68488FE60B596A7FE1FD', X'D3F7295080574944A270E6559CCDC53E', X'D3F7295080574944A270E6559CCDC53E', 5, NULL, NULL, 2, NULL, 0, 'Roast the turkey legs for 1 hour and 30 minutes to 1 hour and 40 minutes or until a meat thermometer registers 170 F. Remove the roasting pan from the oven, cover it with foil, and let stand for 10 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'797058E27BAEED4B9E7BBAFD92E61A8E', X'D3F7295080574944A270E6559CCDC53E', X'D3F7295080574944A270E6559CCDC53E', 6, NULL, NULL, 2, NULL, 0, 'You can serve whole legs or slice the meat off the bones and serve it that way.');
