INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BBDD2574B0FC0F48878F358AE7A42A08', X'24C21F2E044B9E43879D4E57E14FF530', X'24C21F2E044B9E43879D4E57E14FF530', 2, NULL, NULL, 2, NULL, 0, 'Combine the flour, salt, black pepper, and the paprika; dredge cutlets to coat both sides.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F4D65C9AD061154498B48ACE7959A6BB', X'24C21F2E044B9E43879D4E57E14FF530', X'24C21F2E044B9E43879D4E57E14FF530', 3, NULL, NULL, 2, NULL, 0, 'In a large over medium-low to medium heat, saut the turkey cutlets, a few at a time, in the 1 tablespoon of oil and 1 tablespoon butter. Cook until browned and cooked through, or about 2 to 3 minutes on each side.');
