INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A4D3180CA4747B4D992F074E238F16B5', X'24C21F2E044B9E43879D4E57E14FF530', X'24C21F2E044B9E43879D4E57E14FF530', 6, NULL, NULL, 2, NULL, 0, 'Add the broth, wine, rosemary, parsley, and a few shakes of garlic herb seasoning, if using. Increase heat to simmer the mixture briskly until reduced by about 1/3 to 1/2. Decrease the heat to medium-low.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F63E8BE7A1AA2C4D8E449A96A9216657', X'24C21F2E044B9E43879D4E57E14FF530', X'24C21F2E044B9E43879D4E57E14FF530', 7, NULL, NULL, 2, NULL, 0, 'Stir the flour and water mixture into the broth mixture. Cook, stirring, until thickened.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0D15DEDE66A1A840A2F16ADB9E32F46B', X'24C21F2E044B9E43879D4E57E14FF530', X'24C21F2E044B9E43879D4E57E14FF530', 8, NULL, NULL, 2, NULL, 0, 'Add about 1/2 cup of the cream. Taste and add more cream if the sauce seems a little too strong. It depends on how much the broth cooked down.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A031E5FD9BF726418568BBF77A3DFA17', X'24C21F2E044B9E43879D4E57E14FF530', X'24C21F2E044B9E43879D4E57E14FF530', 9, NULL, NULL, 2, NULL, 0, 'Serve the sauce along with the turkey cutlets with pasta or rice and a salad or vegetable.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'974D63A400ECDD4AB338F10B0CB92EEE', X'26AB9EA8AD03974D99E253E05485B22C', X'26AB9EA8AD03974D99E253E05485B22C', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
