INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E21A8F9E9815FC48AB6BB0A5EDA7071B', X'26AB9EA8AD03974D99E253E05485B22C', X'26AB9EA8AD03974D99E253E05485B22C', 2, NULL, NULL, 2, NULL, 0, 'Prepare the stuffing mix following the package directions; pack it on or around the turkey, patting down, but not packing it tight.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4A5E4016366A7D45A1109905252F8C34', X'26AB9EA8AD03974D99E253E05485B22C', X'26AB9EA8AD03974D99E253E05485B22C', 3, NULL, NULL, 2, NULL, 0, 'Drizzle butter over the stuffing.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AEB456955B705C459E3B9E3998D1E381', X'26AB9EA8AD03974D99E253E05485B22C', X'26AB9EA8AD03974D99E253E05485B22C', 4, NULL, NULL, 2, NULL, 0, 'Cover and cook on HIGH for 1 hour. Reduce the setting to LOW and cook for about 4 to 6 hours longer, or until turkey is very tender. Or, continue cooking on HIGH for about 3 to 3 1/2 hours.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'663BFE9B4A059843AB1C93901358AD21', X'26AB9EA8AD03974D99E253E05485B22C', X'26AB9EA8AD03974D99E253E05485B22C', 5, NULL, NULL, 2, NULL, 0, 'If the dressing gets too soggy, remove it and spread in a shallow greased pan. Bake in a 400 F oven for about 30 minutes, or until brown and crusty.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0002D57217EE614B8EE4C9D3C87ACDE8', X'26AB9EA8AD03974D99E253E05485B22C', X'26AB9EA8AD03974D99E253E05485B22C', 6, NULL, NULL, 2, NULL, 0, 'Serve with gravy, if desired.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6E11DF9C8EF60545B4CD5AC948DE3902', X'D99675237E2D3B4E84DC50AEF4EFD960', X'D99675237E2D3B4E84DC50AEF4EFD960', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8837A9360A1C0E4C86A60D8E969E5AFA', X'D99675237E2D3B4E84DC50AEF4EFD960', X'D99675237E2D3B4E84DC50AEF4EFD960', 1, NULL, NULL, 2, NULL, 0, 'In a large stockpot or Dutch oven, heat one tablespoon of the olive oil over medium heat. Add the sausage and saut for 6 to 8 minutes, breaking it up with a spatula into small chunks until it is browned.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F7177271813C004781CC5C85DDAC235E', X'D99675237E2D3B4E84DC50AEF4EFD960', X'D99675237E2D3B4E84DC50AEF4EFD960', 2, NULL, NULL, 2, NULL, 0, 'Drain the sausage in a colander and set aside.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1C58142725A8904EB2F0915A0D91449C', X'D99675237E2D3B4E84DC50AEF4EFD960', X'D99675237E2D3B4E84DC50AEF4EFD960', 3, NULL, NULL, 2, NULL, 0, 'In the same large stockpot or Dutch oven, heat the remaining tablespoon of olive oil over medium heat. Add the onions and saut for about 4 minutes until golden. Add the broccoli rabe and cook, stirring occasionally, for about 5 minutes until the broccoli rabe starts to wilt slightly and begins to become tender, then add the chicken broth.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'361FF228EB92334BBE65335099C26B7C', X'D99675237E2D3B4E84DC50AEF4EFD960', X'D99675237E2D3B4E84DC50AEF4EFD960', 4, NULL, NULL, 2, NULL, 0, 'Add the salt and pepper, turn the heat to high and bring to a simmer.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4207BA0A40551344ACAF2B6355C56460', X'D99675237E2D3B4E84DC50AEF4EFD960', X'D99675237E2D3B4E84DC50AEF4EFD960', 5, NULL, NULL, 2, NULL, 0, 'Keeping the mixture at a summer, add the millet, cooking the soup uncovered for about 20 to 25 minutes until the grains are fairly tender.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'053F29FBE3B9E24682053743659CA5F0', X'D99675237E2D3B4E84DC50AEF4EFD960', X'D99675237E2D3B4E84DC50AEF4EFD960', 6, NULL, NULL, 2, NULL, 0, 'Add the crumbled cooked sausage and an additional one cup or more water or additional broth, as needed, if the soup is too thick. Simmer for another 10 minutes to blend the flavors and taste and adjust the amount of salt and pepper as needed.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2A3A7D47FA2CC44E8B23A0A0B18C6E45', X'D99675237E2D3B4E84DC50AEF4EFD960', X'D99675237E2D3B4E84DC50AEF4EFD960', 7, NULL, NULL, 2, NULL, 0, 'Serve hot.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D31001F48087FD4CB59F085ADBACA9A5', X'4099FEE511D2D448B031647BA8439C99', X'4099FEE511D2D448B031647BA8439C99', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients. Preheat the oven to 350 F/180 C/Gas 4.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B0168D591C87894DBF8113C64324F28D', X'4099FEE511D2D448B031647BA8439C99', X'4099FEE511D2D448B031647BA8439C99', 1, NULL, NULL, 2, NULL, 0, 'Lightly grease a 9x13x2-inch baking pan.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D8264AC6DA51D743925B33A869AAC107', X'4099FEE511D2D448B031647BA8439C99', X'4099FEE511D2D448B031647BA8439C99', 2, NULL, NULL, 2, NULL, 0, 'Combine the shredded turkey with sour cream, 2 cups of the shredded cheddar cheese, and salt. Stir to blend thoroughly.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A6AEC01D51108E4EB3FE7F5FA0989F04', X'4099FEE511D2D448B031647BA8439C99', X'4099FEE511D2D448B031647BA8439C99', 3, NULL, NULL, 2, NULL, 0, 'Heat the oil and dip tortillas in until soft and drain. Or, follow package directions for heating tortillas in the microwave oven or conventional oven.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E6BAFBCB1B36064E9E55238C3288238B', X'4099FEE511D2D448B031647BA8439C99', X'4099FEE511D2D448B031647BA8439C99', 4, NULL, NULL, 2, NULL, 0, 'Stuff the warm tortillas with turkey mixture and roll up. Arrange the filled rolls seam side down, side-by-side in the prepared baking pan.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'230D548E7EF8EB4DADE3E2DDE513BE0E', X'4099FEE511D2D448B031647BA8439C99', X'4099FEE511D2D448B031647BA8439C99', 5, NULL, NULL, 2, NULL, 0, 'Gather the sauce ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A6465314EC04AA4E962D3AAAC6749BCC', X'4099FEE511D2D448B031647BA8439C99', X'4099FEE511D2D448B031647BA8439C99', 6, NULL, NULL, 2, NULL, 0, 'In a skillet, saute the chile peppers with garlic in the olive oil for 2 to 3 minutes. Add the stewed tomatoes, onions, salt, oregano, and water. Simmer uncovered until the sauce is reduced and thickened, or about 1/2 hour.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F65C87B5E1A8E347BB2AE14A74E4E06E', X'4099FEE511D2D448B031647BA8439C99', X'4099FEE511D2D448B031647BA8439C99', 7, NULL, NULL, 2, NULL, 0, 'Pour the chile sauce over enchiladas and bake them for about 30 minutes, or until hot and bubbly.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'26C265B8EE7DE14EB8FED5A23AC3AB10', X'4099FEE511D2D448B031647BA8439C99', X'4099FEE511D2D448B031647BA8439C99', 8, NULL, NULL, 2, NULL, 0, 'Sprinkle with the remaining 1/2 cup of shredded cheese and return them to the oven for a few minutes, or just until the cheese has melted.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EA1445FF6CA3C644B8543E74CB16040A', X'4099FEE511D2D448B031647BA8439C99', X'4099FEE511D2D448B031647BA8439C99', 9, NULL, NULL, 2, NULL, 0, 'Let the casserole stand for about 5 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'13B2347A8D52264FB28C25019BF9A21A', X'4099FEE511D2D448B031647BA8439C99', X'4099FEE511D2D448B031647BA8439C99', 10, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'134850B71752834EAD9BC569B8E8A006', X'83397CE12BD5824CBB689ED3F5AF013B', X'83397CE12BD5824CBB689ED3F5AF013B', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'48B9A63928FC774EB0BB8C3EB9C30E8C', X'83397CE12BD5824CBB689ED3F5AF013B', X'83397CE12BD5824CBB689ED3F5AF013B', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 325 F.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DF74D9A21B78B941974E7CA655EC36B3', X'83397CE12BD5824CBB689ED3F5AF013B', X'83397CE12BD5824CBB689ED3F5AF013B', 2, NULL, NULL, 2, NULL, 0, 'Grease a 13x9-inch glass baking dish with unsalted butter and set aside.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7FB2C5789235CB469881D9A9DC1E96E9', X'83397CE12BD5824CBB689ED3F5AF013B', X'83397CE12BD5824CBB689ED3F5AF013B', 3, NULL, NULL, 2, NULL, 0, 'Cut the turkey tenderloin into thirds crosswise to form 6 pieces. Then, cut a lengthwise slit into each tenderloin half to within 1/2-inch of opposite side.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7D704C0862419045A479E064EE7C40AA', X'83397CE12BD5824CBB689ED3F5AF013B', X'83397CE12BD5824CBB689ED3F5AF013B', 4, NULL, NULL, 2, NULL, 0, 'Open the tenderloins and place smooth side down on the work surface. Pound the tenderloins slightly with a rolling pin or the smooth side of a meat mallet until they are about 1/2-inch thick.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7DCF675B25894B4CB26D126A7CA35CAA', X'83397CE12BD5824CBB689ED3F5AF013B', X'83397CE12BD5824CBB689ED3F5AF013B', 5, NULL, NULL, 2, NULL, 0, 'In a small bowl, combine the melted butter and mustard; brush over the cut sides of the tenderloins.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EC1FB8908F016448AA36C022F9B1A1B9', X'83397CE12BD5824CBB689ED3F5AF013B', X'83397CE12BD5824CBB689ED3F5AF013B', 6, NULL, NULL, 2, NULL, 0, 'Place a piece of cheese in the center of open tenderloin on top of the mustard mixture.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7DBF62C8AEEB1440ADDD1CE05B43D93C', X'83397CE12BD5824CBB689ED3F5AF013B', X'83397CE12BD5824CBB689ED3F5AF013B', 7, NULL, NULL, 2, NULL, 0, 'Close the tenderloin around the cheese, forming a bundle and securing with toothpicks.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'04C64E2908C816438C7183D0D2D53B92', X'83397CE12BD5824CBB689ED3F5AF013B', X'83397CE12BD5824CBB689ED3F5AF013B', 8, NULL, NULL, 2, NULL, 0, 'Combine the bread crumbs, flour, Parmesan cheese, thyme, and salt in a shallow dish.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8D7205F7A5543341A2794D13027B8F3F', X'83397CE12BD5824CBB689ED3F5AF013B', X'83397CE12BD5824CBB689ED3F5AF013B', 9, NULL, NULL, 2, NULL, 0, 'In another shallow dish, combine the cream and beaten egg.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'956876D93AB60B4CB81D96D624C1E03A', X'83397CE12BD5824CBB689ED3F5AF013B', X'83397CE12BD5824CBB689ED3F5AF013B', 10, NULL, NULL, 2, NULL, 0, 'Dip the filled tenderloins in the egg mixture, then roll in the crumb mixture to coat.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CFDE26A17E017C4AAE656715356A0A89', X'83397CE12BD5824CBB689ED3F5AF013B', X'83397CE12BD5824CBB689ED3F5AF013B', 11, NULL, NULL, 2, NULL, 0, 'Place the filled and coated turkey tenderloins in the prepared baking dish and bake for 40 to 45 minutes until turkey is thoroughly cooked and registers 165 F on a meat thermometer. Remove the toothpicks.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1CEAF24CABF9454FB153E9266B890972', X'83397CE12BD5824CBB689ED3F5AF013B', X'83397CE12BD5824CBB689ED3F5AF013B', 12, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'431A439955A61E46922B9DF83830301B', X'CA59CF1802483243907DA180A5F2C23D', X'CA59CF1802483243907DA180A5F2C23D', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A616FAEA527C304AAD94B29B92703328', X'CA59CF1802483243907DA180A5F2C23D', X'CA59CF1802483243907DA180A5F2C23D', 1, NULL, NULL, 2, NULL, 0, 'Place the turkey, water, onion, celery, bell pepper, garlic, 2 teaspoons of the salt, 1 teaspoon of the black pepper, and the cayenne in a medium saucepan. Bring to a boil, reduce the heat, and gently boil, uncovered, for 45 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'402C7FD9BFB9FA4BAA1E9492143A278E', X'CA59CF1802483243907DA180A5F2C23D', X'CA59CF1802483243907DA180A5F2C23D', 2, NULL, NULL, 2, NULL, 0, 'Drain, reserving the liquid. Place the meat and cooked vegetables, fresh parsley, and green onions in a food processor and pulse about 20 times until well mixed but not pureed. Transfer to a large bowl.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8EC9028C4AF2DB4EBE88C25E29A3FADD', X'CA59CF1802483243907DA180A5F2C23D', X'CA59CF1802483243907DA180A5F2C23D', 3, NULL, NULL, 2, NULL, 0, 'Add rice to the mixture and gently stir to combine, adding the reserved broth 1 tablespoon at a time, until the mixture can be formed into balls and will hold its shape. Taste, and add more seasoning if necessary.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'97CD79342259AC4D80C79B567C907496', X'CA59CF1802483243907DA180A5F2C23D', X'CA59CF1802483243907DA180A5F2C23D', 4, NULL, NULL, 2, NULL, 0, 'Using about 2 tablespoons turkey mixture for each one, shape into balls. These may also be formed into mini balls the size of large marbles to have as hors d''oeuvres. Use about 2 teaspoons turkey mixture for each mini ball. The frying time will be 1 to 2 minutes for these smaller balls.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4C041FE7DFA25446ADE5E91CD62D271A', X'CA59CF1802483243907DA180A5F2C23D', X'CA59CF1802483243907DA180A5F2C23D', 5, NULL, NULL, 2, NULL, 0, 'Place flour, eggs, and bread crumbs mixed with the remaining salt and pepper in three separate dishes. Roll the balls in the flour, then the beaten egg, and then in the breadcrumbs.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1C591B4CC8FA1443A4A4B337CC135978', X'CA59CF1802483243907DA180A5F2C23D', X'CA59CF1802483243907DA180A5F2C23D', 6, NULL, NULL, 2, NULL, 0, 'Heat the oil to 375 F in a large saucepan. When the oil is hot add the balls, 5 or 6 at a time, adjusting the heat up or down as necessary to maintain a temperature of about 375 F.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2676381F27C9DB449182B4FC4DABB232', X'CA59CF1802483243907DA180A5F2C23D', X'CA59CF1802483243907DA180A5F2C23D', 7, NULL, NULL, 2, NULL, 0, 'Transfer the balls to paper towels as you remove them from the oil; they will take 2 to 3 minutes to fry.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E13355C223EC7C4590A7C6C378BF7317', X'CA59CF1802483243907DA180A5F2C23D', X'CA59CF1802483243907DA180A5F2C23D', 8, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4B75B026C4F9384B9325297E448AAC0F', X'3A4153AB0DDF0C49B43D0A052ED160AC', X'3A4153AB0DDF0C49B43D0A052ED160AC', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CD0D0921430C364FBCDC12429827EBB6', X'3A4153AB0DDF0C49B43D0A052ED160AC', X'3A4153AB0DDF0C49B43D0A052ED160AC', 1, NULL, NULL, 2, NULL, 0, 'Mash the garlic cloves and salt into a paste using a piln (mortar and pestle).');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'41574072DB95234CBF2CC3C305861B6D', X'3A4153AB0DDF0C49B43D0A052ED160AC', X'3A4153AB0DDF0C49B43D0A052ED160AC', 2, NULL, NULL, 2, NULL, 0, 'Add the peppercorns and adobo and crush the ingredients into the paste.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'96A87E8CF7613E47BBCA0CF66A283357', X'3A4153AB0DDF0C49B43D0A052ED160AC', X'3A4153AB0DDF0C49B43D0A052ED160AC', 3, NULL, NULL, 2, NULL, 0, 'Stir in the olive oil and apple cider vinegar.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EAB979D868EFAF458D4987246A38328A', X'3A4153AB0DDF0C49B43D0A052ED160AC', X'3A4153AB0DDF0C49B43D0A052ED160AC', 4, NULL, NULL, 2, NULL, 0, 'Rub the mixture under the turkeys skin inside the cavities, then rub the rest of the mixture on top of the skin.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C01FCFA3A4BC81499246460575E6D985', X'3A4153AB0DDF0C49B43D0A052ED160AC', X'3A4153AB0DDF0C49B43D0A052ED160AC', 5, NULL, NULL, 2, NULL, 0, 'Roast the turkey according to the manufacturers recommended time and temperature. A 14 to ?16-pound turkey will take about 2 to 2 1/2 hours to cook.');
