INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'65B9B34414BD994AB228349C012FA38D', X'2993CF2B0C118A4496FFD344358FC912', X'2993CF2B0C118A4496FFD344358FC912', 5, NULL, NULL, 2, NULL, 0, 'Top with turkey breast, skin side up. Drizzle the lemon juice over the turkey.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'516FA525B2BD7D4080C071C8B144B5C3', X'2993CF2B0C118A4496FFD344358FC912', X'2993CF2B0C118A4496FFD344358FC912', 6, NULL, NULL, 2, NULL, 0, 'Cover and cook on high for 1 hour. Continue cooking on low for 6 to 7 hours or on high for about 3 hours longer. According to the USDA char(36)elines, poultry must reach at least 165 F on a food thermometer inserted into the thickest part of the meat. Check with a reliable instant-read food thermometer.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'69E517A75E346D449525DA6DE698D646', X'2993CF2B0C118A4496FFD344358FC912', X'2993CF2B0C118A4496FFD344358FC912', 7, NULL, NULL, 2, NULL, 0, 'Transfer the turkey breast to a serving platter and keep warm.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8E10C3685B6BC949B3F5DA6D3AA86AA2', X'2993CF2B0C118A4496FFD344358FC912', X'2993CF2B0C118A4496FFD344358FC912', 8, NULL, NULL, 2, NULL, 0, 'If desired, brown the skin under the broiler for about 4 to 5 minutes. Let turkey breast stand for 10 minutes before slicing.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4E11710A66D6C641A3930D95002362DE', X'2993CF2B0C118A4496FFD344358FC912', X'2993CF2B0C118A4496FFD344358FC912', 9, NULL, NULL, 2, NULL, 0, 'Turn the slow cooker to HIGH. Whisk broth and wine into the juices in the slow cooker.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'469983B90321DE42BEEC2CC86F26BF43', X'2993CF2B0C118A4496FFD344358FC912', X'2993CF2B0C118A4496FFD344358FC912', 10, NULL, NULL, 2, NULL, 0, 'In a small bowl or cup, combine the butter and flour until the flour is well incorporated and the mixture is smooth.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1880DBB439343C409A897AF1EA5C5238', X'2993CF2B0C118A4496FFD344358FC912', X'2993CF2B0C118A4496FFD344358FC912', 11, NULL, NULL, 2, NULL, 0, 'Whisk the flour and butter combination into the slow cooker liquids and cook, uncovered, stirring occasionally until thickened and bubbly, about 15 minutes. Cook and stir for another minute.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F9EB271947BEE74A82663B7891E2CC5C', X'2993CF2B0C118A4496FFD344358FC912', X'2993CF2B0C118A4496FFD344358FC912', 12, NULL, NULL, 2, NULL, 0, 'Alternatively, you can cook the broth, wine, and juices in a saucepan on the stovetop more quickly. Add the butter and flour mixture and cook over medium heat, stirring, until thickened, about 2 to 3 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'204BA2F49A85EE4EB8F3A84B72BD8C03', X'2993CF2B0C118A4496FFD344358FC912', X'2993CF2B0C118A4496FFD344358FC912', 13, NULL, NULL, 2, NULL, 0, 'Pour the sauce into a serving dish and spoon over slices of the turkey breast.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'01FAD92588179444A62501C265EBB2E1', X'B4508DA578A0E14989EF94F8CBB2AE3E', X'B4508DA578A0E14989EF94F8CBB2AE3E', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D3207FB07A74464BACD94B4F47A511B3', X'B4508DA578A0E14989EF94F8CBB2AE3E', X'B4508DA578A0E14989EF94F8CBB2AE3E', 1, NULL, NULL, 2, NULL, 0, 'Put turkey cutlets between sheets of plastic wrap and gently pound to about 1/2-inch thickness.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CAD5C22456D23A429C6FD147C24DFCAF', X'B4508DA578A0E14989EF94F8CBB2AE3E', X'B4508DA578A0E14989EF94F8CBB2AE3E', 2, NULL, NULL, 2, NULL, 0, 'In a large skillet over medium heat, melt 2 tablespoons of butter with 1 tablespoon of olive oil.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EA3A805BFF1A0A4B8CA36DF1A63A9185', X'B4508DA578A0E14989EF94F8CBB2AE3E', X'B4508DA578A0E14989EF94F8CBB2AE3E', 3, NULL, NULL, 2, NULL, 0, 'Put 3 tablespoons of the flour in a wide shallow bowl or plate. Dredge the turkey cutlets in the flour, then saute 2 to 3 cutlets for about 3 to 4 minutes on each side, until browned and cooked through. Repeat with the remaining cutlets.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AF9F407EB3952B4099F950ABB94FF076', X'B4508DA578A0E14989EF94F8CBB2AE3E', X'B4508DA578A0E14989EF94F8CBB2AE3E', 4, NULL, NULL, 2, NULL, 0, 'Remove browned cutlets to a plate and keep warm.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6F4178341367484E82FAB30A2A5CD657', X'B4508DA578A0E14989EF94F8CBB2AE3E', X'B4508DA578A0E14989EF94F8CBB2AE3E', 5, NULL, NULL, 2, NULL, 0, 'Add the chicken broth, lemon juice, and wine to the skillet and bring to a boil.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C61BDA743E992547962AF3FB44E23177', X'B4508DA578A0E14989EF94F8CBB2AE3E', X'B4508DA578A0E14989EF94F8CBB2AE3E', 6, NULL, NULL, 2, NULL, 0, 'In a small bowl or cup, combine the remaining 1 tablespoon of flour with the remaining 1 tablespoon of soft butter; stir until the mixture is smooth.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9C0B2DE978D22E4F86770DBE45113DEE', X'B4508DA578A0E14989EF94F8CBB2AE3E', X'B4508DA578A0E14989EF94F8CBB2AE3E', 7, NULL, NULL, 2, NULL, 0, 'Whisk the butter and flour mixture into the broth and lemon juice mixture and simmer for 2 minutes. Add the capers and parsley.');
