INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E37361D3FDF87645885F902BEFBFD8A6', X'EE1A0C24A2DAD6408EF1B49F014AF6FC', X'EE1A0C24A2DAD6408EF1B49F014AF6FC', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
