INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0971AE3A2A51A64EA965E4075DF5B790', X'EE1A0C24A2DAD6408EF1B49F014AF6FC', X'EE1A0C24A2DAD6408EF1B49F014AF6FC', 2, NULL, NULL, 2, NULL, 0, 'In a bowl, combine the water, soup, mushrooms with liquid, and pimiento.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3DD43833A88D054BBBFD76FA4CE75EF4', X'EE1A0C24A2DAD6408EF1B49F014AF6FC', X'EE1A0C24A2DAD6408EF1B49F014AF6FC', 3, NULL, NULL, 2, NULL, 0, 'Stir in the turkey, cheese, onion, parsley, and nutmeg.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'864B55CBC438F349BB84B84819F18A5C', X'EE1A0C24A2DAD6408EF1B49F014AF6FC', X'EE1A0C24A2DAD6408EF1B49F014AF6FC', 4, NULL, NULL, 2, NULL, 0, 'Add broken up spaghetti. Stir to combine and pour into crockpot.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EC60C6EEAAA840489314CE0B8E77F7F3', X'EE1A0C24A2DAD6408EF1B49F014AF6FC', X'EE1A0C24A2DAD6408EF1B49F014AF6FC', 5, NULL, NULL, 2, NULL, 0, 'Cover and cook on low for 4 to 6 hours, until spaghetti is tender.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'03DE40E702EDC7449BCF0FEFE2825CB4', X'EE1A0C24A2DAD6408EF1B49F014AF6FC', X'EE1A0C24A2DAD6408EF1B49F014AF6FC', 6, NULL, NULL, 2, NULL, 0, 'Stir before serving.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C749297208D0E6429DAAB260E100764B', X'51A097B3CF37E1409730ED556D9B10D3', X'51A097B3CF37E1409730ED556D9B10D3', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'099FB8AA87C0E84F8D87221DBC6C27DD', X'51A097B3CF37E1409730ED556D9B10D3', X'51A097B3CF37E1409730ED556D9B10D3', 1, NULL, NULL, 2, NULL, 0, 'Place the turkey breast in the pot you are going to fry it in. Add enough water to cover the breast with about 3 inches to spare. Remove the breast and measure the water. This is how much oil you will need.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DAA57AF064E4584DACEE6993286C2D75', X'51A097B3CF37E1409730ED556D9B10D3', X'51A097B3CF37E1409730ED556D9B10D3', 2, NULL, NULL, 2, NULL, 0, 'Dump out water and dry the pan well with paper towels.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'44781CF49A798E408095402916D033BA', X'51A097B3CF37E1409730ED556D9B10D3', X'51A097B3CF37E1409730ED556D9B10D3', 3, NULL, NULL, 2, NULL, 0, 'Pat dry the trussed breast dry with paper towels and coat all over with Cajun turkey seasoning. Cover with plastic wrap and set aside for 15 to 20 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FC326A4333E87A4B9B3B1F26B5960042', X'51A097B3CF37E1409730ED556D9B10D3', X'51A097B3CF37E1409730ED556D9B10D3', 4, NULL, NULL, 2, NULL, 0, 'Meanwhile, heat oil to 350 F. The temperature is important, so use a thermometer to test. VERY SLOWLY, lower the seasoned breast into the hot oil. Use heat-resistant gloves to protect yourself from splashing.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'25D3E2661AAABD4EB52348A0BF58D1A6', X'51A097B3CF37E1409730ED556D9B10D3', X'51A097B3CF37E1409730ED556D9B10D3', 5, NULL, NULL, 2, NULL, 0, 'Fry breast to 3 minutes per pound plus 5 extra minutes. Therefore a 3-pound breast will take 14 minutes ((3 X 3) + 5 = 14). The internal temperature of the thickest part of turkey breast should be at least 170 to 175 F.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8DF201D633760440B30539EF702469CA', X'51A097B3CF37E1409730ED556D9B10D3', X'51A097B3CF37E1409730ED556D9B10D3', 6, NULL, NULL, 2, NULL, 0, 'Carefully remove from oil and place onto a paper towel lined cutting board. Allow breast to drain for 5 to 7 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6B181357458BD24EA3FA5E437B9094F5', X'51A097B3CF37E1409730ED556D9B10D3', X'51A097B3CF37E1409730ED556D9B10D3', 7, NULL, NULL, 2, NULL, 0, 'Carve into 1/2-inch slices and serve with your favorite sides.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E77D1E83CCB1234790E3E6AAD00C2460', X'51A097B3CF37E1409730ED556D9B10D3', X'51A097B3CF37E1409730ED556D9B10D3', 8, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'57364DAA7292174D8A30106C2BA83B7B', X'784D35BC7CC43B418736302AA2E3D187', X'784D35BC7CC43B418736302AA2E3D187', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients and preheat the oven to 425 F.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BFA5AE15EA05FE448F45541323F7D016', X'784D35BC7CC43B418736302AA2E3D187', X'784D35BC7CC43B418736302AA2E3D187', 1, NULL, NULL, 2, NULL, 0, 'Line the unbaked crust with parchment paper or foil and fill it with dried beans or pie weights.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EDD337183098374C9923FAED365BDD02', X'784D35BC7CC43B418736302AA2E3D187', X'784D35BC7CC43B418736302AA2E3D187', 2, NULL, NULL, 2, NULL, 0, 'Bake the lined crust for about 15 minutes, or until the edges are browned. Remove the crust to a cooling rack and carefully remove the pie weights and paper or foil. Reduce the oven temperature to 375 F.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B5ECEE46A6EA78439DDF7B1C55DFC239', X'784D35BC7CC43B418736302AA2E3D187', X'784D35BC7CC43B418736302AA2E3D187', 3, NULL, NULL, 2, NULL, 0, 'Meanwhile, in a large skillet over medium-low heat, melt the butter. Add a few tablespoons of water along with the broccoli and onion and cook, stirring, until tender. Drain well.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'57B494210F460A4A91BA6721D3F6831D', X'784D35BC7CC43B418736302AA2E3D187', X'784D35BC7CC43B418736302AA2E3D187', 4, NULL, NULL, 2, NULL, 0, 'Sprinkle broccoli and onion, turkey, and cheese into the partially-baked pie shell.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6CB7D32411358F4FA14BCA26328924E2', X'784D35BC7CC43B418736302AA2E3D187', X'784D35BC7CC43B418736302AA2E3D187', 5, NULL, NULL, 2, NULL, 0, 'In a medium bowl, whisk the eggs until slightly beaten. Whisk in the half-and-half, curry powder, and salt.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4ED46150A9A13844BC2EA608EF0FC7CF', X'784D35BC7CC43B418736302AA2E3D187', X'784D35BC7CC43B418736302AA2E3D187', 6, NULL, NULL, 2, NULL, 0, 'Pour the egg mixture over turkey and broccoli mixture.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F4EB8C7C78EAB04982C673ABE6B6EBC1', X'784D35BC7CC43B418736302AA2E3D187', X'784D35BC7CC43B418736302AA2E3D187', 7, NULL, NULL, 2, NULL, 0, 'Place the quiche on a baking sheet and carefully move it to the oven.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9D7327E23ECF0244AB89AAA6E424A53C', X'784D35BC7CC43B418736302AA2E3D187', X'784D35BC7CC43B418736302AA2E3D187', 8, NULL, NULL, 2, NULL, 0, 'Bake the quiche at 375 F for about 35 minutes, or until a knife inserted in center comes out clean.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5F9B72F953E9D042A8BB94A83F485B32', X'784D35BC7CC43B418736302AA2E3D187', X'784D35BC7CC43B418736302AA2E3D187', 9, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8BD8FDEC726ACE4FB6F051420F80F7FC', X'AA016B9B0278D2499848BD6BEBC0B646', X'AA016B9B0278D2499848BD6BEBC0B646', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'120D59F64C4A32469F82E08084A0749E', X'AA016B9B0278D2499848BD6BEBC0B646', X'AA016B9B0278D2499848BD6BEBC0B646', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 325 F. Place a rack in a large open roasting pan.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'122FBFDAA5425340B42470F448903939', X'AA016B9B0278D2499848BD6BEBC0B646', X'AA016B9B0278D2499848BD6BEBC0B646', 2, NULL, NULL, 2, NULL, 0, 'Clean the turkey inside and out. Sprinkle the cavity with salt and pepper and place on the rack, breast side up.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D3CBE5EC2DDF9D47BB6719CFE1053658', X'AA016B9B0278D2499848BD6BEBC0B646', X'AA016B9B0278D2499848BD6BEBC0B646', 3, NULL, NULL, 2, NULL, 0, 'Cut the orange, apple, and onion into large chunks and stuff into the turkey cavity. Tie the legs together with twine. Sprinkle the turkey all over with salt and pepper.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'68C066A739FBD44F80BA5BC60BFEBC9C', X'AA016B9B0278D2499848BD6BEBC0B646', X'AA016B9B0278D2499848BD6BEBC0B646', 4, NULL, NULL, 2, NULL, 0, 'In a small bowl, combine the butter and dried herbs.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3CE485394A195A45A649CBBD58B229D6', X'AA016B9B0278D2499848BD6BEBC0B646', X'AA016B9B0278D2499848BD6BEBC0B646', 5, NULL, NULL, 2, NULL, 0, 'Using your hand, separate the skin from the breasts and push most of the butter mixture evenly under the skin on each side. Rub the turkey all over with any remaining butter mixture.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1076E0208972234786DADF30F74F3647', X'AA016B9B0278D2499848BD6BEBC0B646', X'AA016B9B0278D2499848BD6BEBC0B646', 6, NULL, NULL, 2, NULL, 0, 'In a small saucepan, combine the maple syrup, brown sugar, and cider. Simmer over low heat until sugar is dissolved. Set aside.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9AEFB7127F4AC14BA4942E83EAB3E828', X'AA016B9B0278D2499848BD6BEBC0B646', X'AA016B9B0278D2499848BD6BEBC0B646', 7, NULL, NULL, 2, NULL, 0, 'Wrap the wing tips and drumstick ends with small pieces of foil. Loosely tent the turkey with a large piece of foil.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9FFFC8A01299BA40B462AB48EB4664C5', X'AA016B9B0278D2499848BD6BEBC0B646', X'AA016B9B0278D2499848BD6BEBC0B646', 8, NULL, NULL, 2, NULL, 0, 'Roast for 2 1/2 hours, then remove and discard the foil tent and baste with the maple mixture. Continue roasting, basting frequently, for about 1 hour longer, or until a meat thermometer registers about 180 F when inserted in the thickest part of the turkey thigh or 170 F in the breast. The minimum safe temperature for poultry is 165 F.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'92549EE58AEC3947B502E65CB9104D77', X'AA016B9B0278D2499848BD6BEBC0B646', X'AA016B9B0278D2499848BD6BEBC0B646', 9, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0BD9D00A0B00794B91BA6B9A37E4656B', X'75E97C5DD2F1DD47B30BC0A805379B8D', X'75E97C5DD2F1DD47B30BC0A805379B8D', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients. Preheat oven to 375 F.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FA2A844179A1E649A0B0E55A6278B1B5', X'75E97C5DD2F1DD47B30BC0A805379B8D', X'75E97C5DD2F1DD47B30BC0A805379B8D', 1, NULL, NULL, 2, NULL, 0, 'Grease a jelly-roll pan or a 9-by-13-by-2-inch baking pan.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DD15758A4784F7459DC7C3EAA005F75E', X'75E97C5DD2F1DD47B30BC0A805379B8D', X'75E97C5DD2F1DD47B30BC0A805379B8D', 2, NULL, NULL, 2, NULL, 0, 'Using the flat side of a meat mallet or rolling pin, gently pound cutlets between sheets of plastic wrap to thin slightly.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'79184FD52071C84FADB2A626952156A1', X'75E97C5DD2F1DD47B30BC0A805379B8D', X'75E97C5DD2F1DD47B30BC0A805379B8D', 3, NULL, NULL, 2, NULL, 0, 'Lightly sprinkle both sides of the cutlets with salt and pepper.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D6FF059B3D31EA4580B6914DCF454622', X'75E97C5DD2F1DD47B30BC0A805379B8D', X'75E97C5DD2F1DD47B30BC0A805379B8D', 4, NULL, NULL, 2, NULL, 0, 'Put the flour in a wide, shallow bowl and set aside.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3AA6FCC1D581DB49B8146B6CFC556DCE', X'75E97C5DD2F1DD47B30BC0A805379B8D', X'75E97C5DD2F1DD47B30BC0A805379B8D', 5, NULL, NULL, 2, NULL, 0, 'In a food processor or chopper, pulse the breadcrumbs, Parmesan cheese, parsley, and basil together until the crumbs are fine. Pour the breadcrumb mixture into a pie plate or shallow bowl.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'56A5D55F7BB8FE43AC4B63502788B936', X'75E97C5DD2F1DD47B30BC0A805379B8D', X'75E97C5DD2F1DD47B30BC0A805379B8D', 6, NULL, NULL, 2, NULL, 0, 'In another bowl, whisk together the mustard and eggs.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F93B9E02CB621D449CAC48DDA8F06948', X'75E97C5DD2F1DD47B30BC0A805379B8D', X'75E97C5DD2F1DD47B30BC0A805379B8D', 7, NULL, NULL, 2, NULL, 0, 'Dip cutlets in the flour to coat, followed by the egg mixture. Then, coat them with the breadcrumb mixture.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DD052933849EC24BB3B31C49723F4AF7', X'75E97C5DD2F1DD47B30BC0A805379B8D', X'75E97C5DD2F1DD47B30BC0A805379B8D', 8, NULL, NULL, 2, NULL, 0, 'Place the breaded cutlets in the prepared baking dish.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'363A4C355B2DC54983FE479D65DF4AEC', X'75E97C5DD2F1DD47B30BC0A805379B8D', X'75E97C5DD2F1DD47B30BC0A805379B8D', 9, NULL, NULL, 2, NULL, 0, 'Bake the cutlets for 20 to 25 minutes, depending on thickness, or until turkey is no longer pink inside. The minimum safe temperature for poultry is 165 F. If in doubt, check them with an instant-read thermometer.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6043A970706103409218F1AA327E1098', X'E5C6929F5BE7714CB75A25C71694DF64', X'E5C6929F5BE7714CB75A25C71694DF64', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CFD76217E4037848BDFF5C558CB31B3B', X'E5C6929F5BE7714CB75A25C71694DF64', X'E5C6929F5BE7714CB75A25C71694DF64', 1, NULL, NULL, 2, NULL, 0, 'With a sharp knife, poke 1/2-inch-deep holes scattered evenly over the surface of the leg of lamb. Insert the garlic clove sticks into the holes.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C305237943F4B54283562B8DE857C14D', X'E5C6929F5BE7714CB75A25C71694DF64', X'E5C6929F5BE7714CB75A25C71694DF64', 2, NULL, NULL, 2, NULL, 0, 'Mix the olive oil, sea salt, rosemary, thyme, sage, and black pepper together for the marinade.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'609E2EB9E422D5429AC375213474888F', X'E5C6929F5BE7714CB75A25C71694DF64', X'E5C6929F5BE7714CB75A25C71694DF64', 3, NULL, NULL, 2, NULL, 0, 'Rub the surface of the meat with the herb paste and place the leg of lamb into a large Dutch oven. Carefully pour the white wine into the side of the pan, avoiding pouring the wine over the meat itself, to keep the herb marinade in place. Refrigerate the leg of lamb for 2 hours, turning once every 30 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E0267D7270418E44BB0E2CE6E3153D28', X'E5C6929F5BE7714CB75A25C71694DF64', X'E5C6929F5BE7714CB75A25C71694DF64', 4, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 275 F. Add 1 cup of water to the lamb and marinade. Place the Dutch oven into the preheated oven and cook the lamb, covered, for 3 hours, basting with the pan juices occasionally.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'72622F98223B204881B419682F30202B', X'E5C6929F5BE7714CB75A25C71694DF64', X'E5C6929F5BE7714CB75A25C71694DF64', 5, NULL, NULL, 2, NULL, 0, 'Sprinkle the leeks, celery root, carrots, turnips, onions, and garlic with salt and pepper. Uncover the pan and place the vegetables around the sides of the lamb.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F23DB4D6873F3D4185D213F16F88EB03', X'E5C6929F5BE7714CB75A25C71694DF64', X'E5C6929F5BE7714CB75A25C71694DF64', 6, NULL, NULL, 2, NULL, 0, 'Continue roasting the leg of lamb and vegetables for 4 hours, uncovered and basting occasionally, until the meat easily falls off the bone.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4A8C4E6A3771F94BA484620A17706D8E', X'E5C6929F5BE7714CB75A25C71694DF64', X'E5C6929F5BE7714CB75A25C71694DF64', 7, NULL, NULL, 2, NULL, 0, 'Remove the roast leg of lamb and vegetables to a serving platter and allow it to rest for 15 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3F4B21A1E347F047B6F0281D250EB17E', X'E5C6929F5BE7714CB75A25C71694DF64', X'E5C6929F5BE7714CB75A25C71694DF64', 8, NULL, NULL, 2, NULL, 0, 'While the roast lamb is resting, taste the pan juices and reduce them by simmering for a few minutes on the stovetop, if necessary.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F46796A3BD9E8747B982FA6082D7E6C6', X'E5C6929F5BE7714CB75A25C71694DF64', X'E5C6929F5BE7714CB75A25C71694DF64', 9, NULL, NULL, 2, NULL, 0, 'Serve the lamb leg and vegetables, sliced or shredded, with the warm pan juices.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EFF433301A387A44AF42976D97F7BF80', X'521C5DFEDF38FF488AF208692A40CA5B', X'521C5DFEDF38FF488AF208692A40CA5B', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AB78F740CCA2014E9F01DC295DF37080', X'521C5DFEDF38FF488AF208692A40CA5B', X'521C5DFEDF38FF488AF208692A40CA5B', 1, NULL, NULL, 2, NULL, 0, 'Choose the saut setting on the Instant Pot and add the lamb and onions. Cook, stirring, until the meat is no longer pink. Add the garlic, rosemary, and parsley and saut for about 1 minute longer.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A7CA1F76CF766044B8064A86A11CC469', X'521C5DFEDF38FF488AF208692A40CA5B', X'521C5DFEDF38FF488AF208692A40CA5B', 2, NULL, NULL, 2, NULL, 0, 'Cancel the saut and add the carrots and beef stock to the Instant Pot.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C79F7722CA7F8B4CA52C5D26AE59E2CB', X'521C5DFEDF38FF488AF208692A40CA5B', X'521C5DFEDF38FF488AF208692A40CA5B', 3, NULL, NULL, 2, NULL, 0, 'Put the potatoes in a steamer basket and place it on top of the lamb and carrot mixture.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4A679A27A5CFFB4BA94439E70867D789', X'521C5DFEDF38FF488AF208692A40CA5B', X'521C5DFEDF38FF488AF208692A40CA5B', 4, NULL, NULL, 2, NULL, 0, 'Lock the Instant Pot lid in place and turn the venting knob to `sealing.` Select the manual setting, high pressure. Set the time to 3 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BD9EABC3A255A24C85C1CE3C9CD682DD', X'521C5DFEDF38FF488AF208692A40CA5B', X'521C5DFEDF38FF488AF208692A40CA5B', 5, NULL, NULL, 2, NULL, 0, 'When the time is up, carefully turn the knob to `venting` for a quick release of pressure. The steam is very hot, so protect your hands and use a long wooden spoon handle or tongs to turn the knob.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'79AADE6C5AF85E46B1E7ACE6105E2D02', X'521C5DFEDF38FF488AF208692A40CA5B', X'521C5DFEDF38FF488AF208692A40CA5B', 6, NULL, NULL, 2, NULL, 0, 'Remove the potatoes to a large mixing bowl and set aside. Add the peas and gravy mix the Instant Pot. Set the pot on saut and cook until the filling is thickened.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EC0C816825E0884282CBD9EA559CF7AE', X'521C5DFEDF38FF488AF208692A40CA5B', X'521C5DFEDF38FF488AF208692A40CA5B', 7, NULL, NULL, 2, NULL, 0, 'Meanwhile, mash the potatoes with 2 tablespoons of butter and the milk. Season to taste with salt and pepper.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4FD537B4AEA9914E9E300D69A5DAD545', X'521C5DFEDF38FF488AF208692A40CA5B', X'521C5DFEDF38FF488AF208692A40CA5B', 8, NULL, NULL, 2, NULL, 0, 'Preheat the broiler. Pour the thickened filling into a 1 1/2-quart baking dish.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7BB74E16BDD9144BA4B67DF500C4A153', X'521C5DFEDF38FF488AF208692A40CA5B', X'521C5DFEDF38FF488AF208692A40CA5B', 9, NULL, NULL, 2, NULL, 0, 'Spoon the mashed potatoes evenly over the filling and gently spread them to cover. Alternatively, pipe the potatoes onto the filling with a pastry bag and large star tip. Melt the remaining 1 tablespoon of butter and brush it lightly over the potatoes. Sprinkle lightly with paprika.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'52855FA6631D03458093BA55FE2CC56E', X'521C5DFEDF38FF488AF208692A40CA5B', X'521C5DFEDF38FF488AF208692A40CA5B', 10, NULL, NULL, 2, NULL, 0, 'Place the shepherd''s pie under the preheated broiler and cook just until the potatoes are browned.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'917077E34D3F2E4E8A07632A2A9FF040', X'521C5DFEDF38FF488AF208692A40CA5B', X'521C5DFEDF38FF488AF208692A40CA5B', 11, NULL, NULL, 2, NULL, 0, 'Serve the shepherd''s pie with a salad or sliced fresh tomatoes on the side.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7D5672315FF38640A08E6F2DE009B1C4', X'521C5DFEDF38FF488AF208692A40CA5B', X'521C5DFEDF38FF488AF208692A40CA5B', 12, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C69878B16844CE4A806C7DE3F0384B74', X'49B5E6A68DA13F4DBD4762D6C68333A4', X'49B5E6A68DA13F4DBD4762D6C68333A4', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C8026DDF81AA3D4C8A958F86303505FD', X'49B5E6A68DA13F4DBD4762D6C68333A4', X'49B5E6A68DA13F4DBD4762D6C68333A4', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 300 F.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6CEB20B32590DF4688BC7AA55C609073', X'49B5E6A68DA13F4DBD4762D6C68333A4', X'49B5E6A68DA13F4DBD4762D6C68333A4', 2, NULL, NULL, 2, NULL, 0, 'Put 2 tablespoons of olive oil and the sliced onions in a heavy Dutch oven or stew pot over medium heat.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7ED78B37F83D4D4B93FC55BB061A2C62', X'49B5E6A68DA13F4DBD4762D6C68333A4', X'49B5E6A68DA13F4DBD4762D6C68333A4', 3, NULL, NULL, 2, NULL, 0, 'Cook the onions, stirring frequently, until lightly browned. Remove to a plate.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1775609F888FF243872C613F7E214CA2', X'49B5E6A68DA13F4DBD4762D6C68333A4', X'49B5E6A68DA13F4DBD4762D6C68333A4', 4, NULL, NULL, 2, NULL, 0, 'Add the remaining olive oil and the chops?in batches, if necessary?and cook, turning until nicely browned on both sides, about 3 to 4 minutes on each side.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4CB7706B9C2ECA43A078A89F1BFA44AE', X'49B5E6A68DA13F4DBD4762D6C68333A4', X'49B5E6A68DA13F4DBD4762D6C68333A4', 5, NULL, NULL, 2, NULL, 0, 'Add the garlic and cook for 1 more minute.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8C6195ACEBDED34195F98EE849F0EAA3', X'49B5E6A68DA13F4DBD4762D6C68333A4', X'49B5E6A68DA13F4DBD4762D6C68333A4', 6, NULL, NULL, 2, NULL, 0, 'Add the onions back to the pot with the lamb and garlic.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5D973FC63A26614EA53E07403125EFDE', X'49B5E6A68DA13F4DBD4762D6C68333A4', X'49B5E6A68DA13F4DBD4762D6C68333A4', 7, NULL, NULL, 2, NULL, 0, 'In a bowl or 2-cup measuring cup, combine the dry red wine with the mustard, salt, pepper, and rosemary. Blend well with spoon or whisk.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FBD03C0DE763AE47929383D68A15034A', X'49B5E6A68DA13F4DBD4762D6C68333A4', X'49B5E6A68DA13F4DBD4762D6C68333A4', 8, NULL, NULL, 2, NULL, 0, 'Pour over the lamb mixture.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2C2ED50FFBF94F439A4C363CC883C2BF', X'49B5E6A68DA13F4DBD4762D6C68333A4', X'49B5E6A68DA13F4DBD4762D6C68333A4', 9, NULL, NULL, 2, NULL, 0, 'If the pot isn''t oven-safe, move everything to a large baking pan or casserole. Cover the pot or pan tightly with a lid or foil.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1B0DD46E025F9A4CA03732599E5CB31D', X'49B5E6A68DA13F4DBD4762D6C68333A4', X'49B5E6A68DA13F4DBD4762D6C68333A4', 10, NULL, NULL, 2, NULL, 0, 'Braise in the oven for 2 to 3 hours, or until the lamb chops are very tender.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D375840B1F7AC642B9909BBD22926E90', X'49B5E6A68DA13F4DBD4762D6C68333A4', X'49B5E6A68DA13F4DBD4762D6C68333A4', 11, NULL, NULL, 2, NULL, 0, 'Serve the lamb chops with the cooking juices, mashed potatoes, and cabbage, or another side vegetable, along with warm biscuits or a loaf of hearty bread.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5583BF0068A45943849A5A98C735324C', X'D1A270D1BA16F342AE6B3BAE7C273935', X'D1A270D1BA16F342AE6B3BAE7C273935', 0, NULL, NULL, 2, NULL, 0, 'Cut the meat into approximately 2-inch cubes.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3C04FDE0BCBCF54F8EB56275ECCDADE5', X'D1A270D1BA16F342AE6B3BAE7C273935', X'D1A270D1BA16F342AE6B3BAE7C273935', 1, NULL, NULL, 2, NULL, 0, 'Mix the vinegar with the garlic, cumin, 1/4 cup vegetable oil, and some salt and black pepper, and pour over the meat. Let meat marinate at room temperature for 30 minutes to an hour.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'63D2BC78A4917A4BA5357BBEFCCBBF40', X'D1A270D1BA16F342AE6B3BAE7C273935', X'D1A270D1BA16F342AE6B3BAE7C273935', 2, NULL, NULL, 2, NULL, 0, 'Pure the cilantro (stems removed) with some water in a blender or food processor until you get a smooth paste.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'60B6E16AA95D434FB9DAB058CA09E0E0', X'D1A270D1BA16F342AE6B3BAE7C273935', X'D1A270D1BA16F342AE6B3BAE7C273935', 3, NULL, NULL, 2, NULL, 0, 'Heat 3-4 tablespoons oil in a large pot or skillet on high heat. Working in 2 batches, brown the meat on all sides. Remove meat to a plate.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5ECD26430060E148ACDE38B82476C07E', X'D1A270D1BA16F342AE6B3BAE7C273935', X'D1A270D1BA16F342AE6B3BAE7C273935', 4, NULL, NULL, 2, NULL, 0, 'Lower the heat and add the onion and aji pepper to the same skillet with the leftover marinade. Cook until soft and fragrant, about 5 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0D22BDA4B9342D43BB93E6F0ABEEE1D1', X'D1A270D1BA16F342AE6B3BAE7C273935', X'D1A270D1BA16F342AE6B3BAE7C273935', 5, NULL, NULL, 2, NULL, 0, 'Add the meat back to the skillet, along with the beer, the cilantro, and 1 cup of the chicken stock. Cover and cook over low heat until the meat is tender, about an hour.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'29E6A91EF5A21043ACB8CFC9B111FB91', X'D1A270D1BA16F342AE6B3BAE7C273935', X'D1A270D1BA16F342AE6B3BAE7C273935', 6, NULL, NULL, 2, NULL, 0, 'Peel and quarter the potatoes, and add them to the stew. Continue to cook over low heat until potatoes are tender, about 30 minutes more, adding more chicken broth if necessary.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'210B1F9203ED1F489EFA59F518FCC2DF', X'D1A270D1BA16F342AE6B3BAE7C273935', X'D1A270D1BA16F342AE6B3BAE7C273935', 7, NULL, NULL, 2, NULL, 0, 'Serve warm over rice.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A3024138B649A1499DD1A993856AA255', X'3F8378E7303E294794A375DF4DF24563', X'3F8378E7303E294794A375DF4DF24563', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BE33F8006417AE45AF357ECD1B3779C9', X'3F8378E7303E294794A375DF4DF24563', X'3F8378E7303E294794A375DF4DF24563', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 F.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F2307BC4DE241D41AB90AB2CAABB6428', X'3F8378E7303E294794A375DF4DF24563', X'3F8378E7303E294794A375DF4DF24563', 2, NULL, NULL, 2, NULL, 0, 'In a large bowl, combine the ground lamb, egg, garlic, ground cumin, ground coriander, smoked paprika, dried oregano, salt, and black pepper.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'581F0C647860254BBEE4168F157A83EA', X'3F8378E7303E294794A375DF4DF24563', X'3F8378E7303E294794A375DF4DF24563', 3, NULL, NULL, 2, NULL, 0, 'Place the mixture into an oiled 9 x 5-inch loaf pan and cook in the oven for approximately 30 minutes or until the top is a light golden brown.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'21A9404EA3F62B489B88430BCBD65C37', X'3F8378E7303E294794A375DF4DF24563', X'3F8378E7303E294794A375DF4DF24563', 4, NULL, NULL, 2, NULL, 0, 'You can slice the loaf immediately if you like but, for best results, cool completely, wrap in aluminum foil, and refrigerate until firm.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8901AB1BD11E204AB61FB6E3A6E5C626', X'3F8378E7303E294794A375DF4DF24563', X'3F8378E7303E294794A375DF4DF24563', 5, NULL, NULL, 2, NULL, 0, 'To reheat, add a little olive oil to a large skillet, slice the loaf very thinly and crisp up the slices in the hot pan for a few minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'60959651E6D9D74FB09A69F956153C0D', X'3F8378E7303E294794A375DF4DF24563', X'3F8378E7303E294794A375DF4DF24563', 6, NULL, NULL, 2, NULL, 0, 'Assemble the sandwiches with pita, warmed and toasted through. Spread on some tzatziki or tahini sauce, add lettuce, tomato, cucumber, onion, and top with more sauce.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C388A0170845A546AB04CD76EE081054', X'3F8378E7303E294794A375DF4DF24563', X'3F8378E7303E294794A375DF4DF24563', 7, NULL, NULL, 2, NULL, 0, 'Serve and enjoy.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D6223969698B5D4B8F0C654F23A61467', X'0EE436B82DE8A84CA653A433040450F6', X'0EE436B82DE8A84CA653A433040450F6', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DC0B73DB8CCAF2438045D480059CF3EC', X'0EE436B82DE8A84CA653A433040450F6', X'0EE436B82DE8A84CA653A433040450F6', 1, NULL, NULL, 2, NULL, 0, 'Begin by forming your lamb patties.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ECDE6719F7E93B4FB4E6A7B8CCF0C14C', X'0EE436B82DE8A84CA653A433040450F6', X'0EE436B82DE8A84CA653A433040450F6', 2, NULL, NULL, 2, NULL, 0, 'You can either cook it on a cast-iron skillet or you can cook it on the grill. Either way, get the heat source blazing hot.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4D9DB47CD6C963428E860CC8225E6E9D', X'0EE436B82DE8A84CA653A433040450F6', X'0EE436B82DE8A84CA653A433040450F6', 3, NULL, NULL, 2, NULL, 0, 'Add your burger sphere, let it cook for a few minutes until the bottom becomes crispy and cooked about halfway up the meat.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C49B8C3C156C3F468AC9479F53AA89CC', X'0EE436B82DE8A84CA653A433040450F6', X'0EE436B82DE8A84CA653A433040450F6', 4, NULL, NULL, 2, NULL, 0, 'At this point, flip the burger gently, and then brown the other side for a minute or two.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DB89729D7BBC5749B2EB7575AE78637A', X'0EE436B82DE8A84CA653A433040450F6', X'0EE436B82DE8A84CA653A433040450F6', 5, NULL, NULL, 2, NULL, 0, 'Next, you''ll want to take a spatula and smush the burger. The grilled bits will help the burger stay together and will taste amazing. Continue cooking until the meat is cooked through and set aside to let it rest.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6DF2024FA66F4C449A2A4E608154A9FD', X'0EE436B82DE8A84CA653A433040450F6', X'0EE436B82DE8A84CA653A433040450F6', 6, NULL, NULL, 2, NULL, 0, 'Then, toast your bun. Do this either directly on the grill, in a toaster, or using a broiler.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7E0FB82A04934E4FA67838FC76661794', X'0EE436B82DE8A84CA653A433040450F6', X'0EE436B82DE8A84CA653A433040450F6', 7, NULL, NULL, 2, NULL, 0, 'Once the bun is crispy and warm, smear half the tzatziki onto both halves. On the bottom half, add the lamb burger, then the roasted tomatoes, then the red onion, and the mint. Serve immediately.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F51CF7AE323E834A833A0EC5581AE271', X'08CE973DEB461941885F2071C077DF54', X'08CE973DEB461941885F2071C077DF54', 0, NULL, NULL, 2, NULL, 0, 'Use a meat mallet to gently flatten the lamb chops slightly.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'38DAD68BC7680046B638A23AB85B2186', X'08CE973DEB461941885F2071C077DF54', X'08CE973DEB461941885F2071C077DF54', 1, NULL, NULL, 2, NULL, 0, 'Put the garlic, ginger, onion, poppy seeds, cinnamon, cloves, green chilies, coriander, turmeric, and salt into a clean, dry blender/food processor. Grind into a smooth paste. Keep this paste aside for later use.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3F63D8F6EDBA0A42A0BE30EF64A1FD77', X'08CE973DEB461941885F2071C077DF54', X'08CE973DEB461941885F2071C077DF54', 2, NULL, NULL, 2, NULL, 0, 'Take a large skillet or heavy-bottomed pan and add 3 tbsps of oil to it. Heat on medium. Add the lamb chops to it and fry until browned on each side. This should take 5 to 7 minutes on each side.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1288FBA840B98946BA06CAE6B88FFCC8', X'08CE973DEB461941885F2071C077DF54', X'08CE973DEB461941885F2071C077DF54', 3, NULL, NULL, 2, NULL, 0, 'While you are frying the lamb chops, set up another heavy-bottomed pan on medium heat. Add the remaining cooking oil to it. When the oil is hot, add the previously prepared green masala to it. Fry well for at least 10 minutes, stirring frequently to prevent the masala from burning or sticking.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'17A5341B6094A54F99D3D2497C1D0B2F', X'08CE973DEB461941885F2071C077DF54', X'08CE973DEB461941885F2071C077DF54', 4, NULL, NULL, 2, NULL, 0, 'When the masala has been fried well, add the browned lamb chops to it. Stir well to coat the chops well with the masala.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F85F1E9DD343F54B9C1F3A0806F2FC97', X'08CE973DEB461941885F2071C077DF54', X'08CE973DEB461941885F2071C077DF54', 5, NULL, NULL, 2, NULL, 0, 'Season with salt if required and add 1/2 a cup of hot water. Bring the whole mixture to a boil and then simmer the heat. Cover the pan and cook until the meat is tender and most of the liquid has dried up. When this happens, turn off heat.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'898E52A8093F664AB5A8719ED52C8B44', X'08CE973DEB461941885F2071C077DF54', X'08CE973DEB461941885F2071C077DF54', 6, NULL, NULL, 2, NULL, 0, 'Serve Chops Hara Masala with small potatoes. Add a green, leafy salad for a wholesome, delicious meal!');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'53ED70B60B888748B0388B79C6C8DE07', X'6AD4A3312E1A004194472510B820B15F', X'6AD4A3312E1A004194472510B820B15F', 0, NULL, NULL, 2, NULL, 0, 'Toss the lamb cubes with a few tablespoons of flour; shake off excess.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1C14942C47C77242AE15B5290C8F9C8A', X'6AD4A3312E1A004194472510B820B15F', X'6AD4A3312E1A004194472510B820B15F', 1, NULL, NULL, 2, NULL, 0, 'Melt the butter with the vegetable oil in a large Dutch oven or stockpot over medium heat. Add the floured lamb cubes and cook until browned on all sides, stirring and turning frequently.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E0CA41FBAE6A834794B510829D4197FA', X'6AD4A3312E1A004194472510B820B15F', X'6AD4A3312E1A004194472510B820B15F', 2, NULL, NULL, 2, NULL, 0, 'Remove the meat and add the mushrooms to the drippings. Cook until the mushrooms are golden brown and tender, stirring frequently. Add the garlic and cook for 1 minute longer.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7A47B4791A603D4E9ADEA7E779E3C646', X'6AD4A3312E1A004194472510B820B15F', X'6AD4A3312E1A004194472510B820B15F', 3, NULL, NULL, 2, NULL, 0, 'Add the lamb cubes back to the pot along with the water, broth, 1 teaspoon of salt, thyme, and pepper; cover and simmer for 1 hour. Skim off any fat on the surface.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'41A6144446700640B1F9F3AC2A51CC3B', X'6AD4A3312E1A004194472510B820B15F', X'6AD4A3312E1A004194472510B820B15F', 4, NULL, NULL, 2, NULL, 0, 'Add the potatoes and onion; cover and simmer until vegetables are tender, about 15 to 20 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F4AD3F324D75CC4EB7EDC7DF16FC7C7B', X'6AD4A3312E1A004194472510B820B15F', X'6AD4A3312E1A004194472510B820B15F', 5, NULL, NULL, 2, NULL, 0, 'Add peas, if using, and simmer for 5 more minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'90BD1BD00F3F104CA2B9A359C45E4A65', X'6AD4A3312E1A004194472510B820B15F', X'6AD4A3312E1A004194472510B820B15F', 6, NULL, NULL, 2, NULL, 0, 'Combine the milk and 1/3 cup of flour in a cup or small bowl; whisk until smooth. Add the mixture to the simmering stew and cook for about 1 to 2 minutes longer, or until thickened.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'91B505991844FF40829437402393AD41', X'6AD4A3312E1A004194472510B820B15F', X'6AD4A3312E1A004194472510B820B15F', 7, NULL, NULL, 2, NULL, 0, 'Taste and adjust seasonings with more salt and pepper, as needed.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FB5735A772BE1F4DB4E2DE530944D3F0', X'F792674C908D1A46A45431D562D4BD32', X'F792674C908D1A46A45431D562D4BD32', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E6302F3EF5E34A4C9BAEA57858BF280A', X'F792674C908D1A46A45431D562D4BD32', X'F792674C908D1A46A45431D562D4BD32', 1, NULL, NULL, 2, NULL, 0, 'In a large bowl, combine the ground lamb, minced onion and garlic, 1 teaspoon each of the ground cumin and sumac, salt, ground black pepper, red pepper flakes, and ice water.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F782D3E86E253C4680DC85A2B35C5156', X'F792674C908D1A46A45431D562D4BD32', X'F792674C908D1A46A45431D562D4BD32', 2, NULL, NULL, 2, NULL, 0, 'Knead the mixture by hand until it turns tacky and starts sticking to the side of the bowl. Place in the refrigerator for about 30 minutes to chill.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A1F108100BB77343B77F4DA5D26CA1BB', X'F792674C908D1A46A45431D562D4BD32', X'F792674C908D1A46A45431D562D4BD32', 3, NULL, NULL, 2, NULL, 0, 'Wet your hands and place a quarter of the lamb mixture onto each skewer. One easy way is to use a 1-ounce scoop to form balls, thread them onto the skewer, then mash them together.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9C26A154CE6432488808EE491FE163B1', X'F792674C908D1A46A45431D562D4BD32', X'F792674C908D1A46A45431D562D4BD32', 4, NULL, NULL, 2, NULL, 0, 'In a small bowl, combine the remaining 1/2 teaspoon each of ground cumin and sumac.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C0CF8517789ACE4C93C52A452DB2AA39', X'F792674C908D1A46A45431D562D4BD32', X'F792674C908D1A46A45431D562D4BD32', 5, NULL, NULL, 2, NULL, 0, 'Grill the kebabs for approximately 12 minutes, until well charred on both sides. Sprinkle the spice mix over the kebabs while they are cooking.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6780E83E1E3BBD429F6BA054DCA0D583', X'F792674C908D1A46A45431D562D4BD32', X'F792674C908D1A46A45431D562D4BD32', 6, NULL, NULL, 2, NULL, 0, 'Serve the kebabs on warm pita or naan bread with sliced red onions, parsley, tomatoes, and diced cucumbers. Enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DB1875974785C14192B1AC47E6E6E4C5', X'AFE2BCE0AF0CEC41BD7B64CBDD684253', X'AFE2BCE0AF0CEC41BD7B64CBDD684253', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9E6FAEDE183CA44B901047CA6F263F6D', X'AFE2BCE0AF0CEC41BD7B64CBDD684253', X'AFE2BCE0AF0CEC41BD7B64CBDD684253', 1, NULL, NULL, 2, NULL, 0, 'Heat oven to 375 F.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0D94603C3BCB99408F1FC5B022B803F1', X'AFE2BCE0AF0CEC41BD7B64CBDD684253', X'AFE2BCE0AF0CEC41BD7B64CBDD684253', 2, NULL, NULL, 2, NULL, 0, 'Using a pastry brush, coat the interior surfaces of both halves of a seasoned lamb cake mold with solid vegetable shortening, making sure you get into all the crevices.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C4EA5658E862A742A0733B06F9A121AB', X'AFE2BCE0AF0CEC41BD7B64CBDD684253', X'AFE2BCE0AF0CEC41BD7B64CBDD684253', 3, NULL, NULL, 2, NULL, 0, 'Place halves on a baking sheet, open-side up, and heat for 20 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FE2B2881EE98734C94FD0A7294381692', X'AFE2BCE0AF0CEC41BD7B64CBDD684253', X'AFE2BCE0AF0CEC41BD7B64CBDD684253', 4, NULL, NULL, 2, NULL, 0, 'Remove from the oven and let cool completely. Pour off any excess shortening. Reduce heat to 350 F.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1AA9A2B1A368A84185213E8981364C02', X'AFE2BCE0AF0CEC41BD7B64CBDD684253', X'AFE2BCE0AF0CEC41BD7B64CBDD684253', 5, NULL, NULL, 2, NULL, 0, 'Prepare pound cake mix according to box instructions.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'18AFBE284A139C4B9E8352C73DA0A0E1', X'AFE2BCE0AF0CEC41BD7B64CBDD684253', X'AFE2BCE0AF0CEC41BD7B64CBDD684253', 6, NULL, NULL, 2, NULL, 0, 'Place the front-half of the mold on a baking sheet and fill with batter to the lip, 1/4-inch from the top.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3CD7B625C9F00F4ABF18E2BCE1231A61', X'AFE2BCE0AF0CEC41BD7B64CBDD684253', X'AFE2BCE0AF0CEC41BD7B64CBDD684253', 7, NULL, NULL, 2, NULL, 0, 'Submerge a toothpick in the batter in each ear to reinforce them.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1336AC0622880740949F5A7AF026E29F', X'AFE2BCE0AF0CEC41BD7B64CBDD684253', X'AFE2BCE0AF0CEC41BD7B64CBDD684253', 8, NULL, NULL, 2, NULL, 0, 'Place the back-half of the mold on top of the front.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3197883D672FEA459EBC00D2983D80E4', X'AFE2BCE0AF0CEC41BD7B64CBDD684253', X'AFE2BCE0AF0CEC41BD7B64CBDD684253', 9, NULL, NULL, 2, NULL, 0, 'Place in oven and bake about 1 hour or until a skewer inserted into the vent holes of the lamb come out clean.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3803D4786EAD9445B1353FFEA02EE405', X'AFE2BCE0AF0CEC41BD7B64CBDD684253', X'AFE2BCE0AF0CEC41BD7B64CBDD684253', 10, NULL, NULL, 2, NULL, 0, 'Remove from oven and place on cooling rack for 15 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F9C53A1E66590247BD4DA47503C65E13', X'AFE2BCE0AF0CEC41BD7B64CBDD684253', X'AFE2BCE0AF0CEC41BD7B64CBDD684253', 11, NULL, NULL, 2, NULL, 0, 'Carefully remove the back end of the mold, inserting a knife between the mold edges if they are stuck together. Allow cooling for an additional 5 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4D76D2BB6DCAAD46B033C0D42C6FC903', X'AFE2BCE0AF0CEC41BD7B64CBDD684253', X'AFE2BCE0AF0CEC41BD7B64CBDD684253', 12, NULL, NULL, 2, NULL, 0, 'Place another rack on the lamb cake and carefully flip it over and remove the front half of the mold. Let lamb cake cool completely lying on its back.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4B4A5446417C4443909509BE43A9638D', X'AFE2BCE0AF0CEC41BD7B64CBDD684253', X'AFE2BCE0AF0CEC41BD7B64CBDD684253', 13, NULL, NULL, 2, NULL, 0, 'Place a dab of cream cheese frosting on an oblong platter and set the lamb on top to secure it.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FD256A8B974BC444B29B5F99F58CA274', X'AFE2BCE0AF0CEC41BD7B64CBDD684253', X'AFE2BCE0AF0CEC41BD7B64CBDD684253', 14, NULL, NULL, 2, NULL, 0, 'Frost all sides of lamb, swirling it to represent lamb''s wool.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'48A6EC3E7E63554280F838B80813EA2F', X'AFE2BCE0AF0CEC41BD7B64CBDD684253', X'AFE2BCE0AF0CEC41BD7B64CBDD684253', 15, NULL, NULL, 2, NULL, 0, 'Wrap a narrow red ribbon around the lamb''s neck. Use raisins for eyes and nose, cutting them in half if necessary to make them more proportional.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'363479F0D1D6604F95720B402803073D', X'AFE2BCE0AF0CEC41BD7B64CBDD684253', X'AFE2BCE0AF0CEC41BD7B64CBDD684253', 16, NULL, NULL, 2, NULL, 0, 'Place coconut and green food coloring in a zip-top back and shake it, to color the coconut. Place green `grass` around the lamb.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7EC9F3668D46934B90208FE5A9831DA6', X'AFE2BCE0AF0CEC41BD7B64CBDD684253', X'AFE2BCE0AF0CEC41BD7B64CBDD684253', 17, NULL, NULL, 2, NULL, 0, 'Serve for Easter and enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7D1040F45522794F84DCEBD32FB0C03E', X'FA653DD74FE4484F88DB7950E196599C', X'FA653DD74FE4484F88DB7950E196599C', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0F2E182B9DF8C64CA8CCFD3FB603B1B5', X'FA653DD74FE4484F88DB7950E196599C', X'FA653DD74FE4484F88DB7950E196599C', 1, NULL, NULL, 2, NULL, 0, 'Preheat pan or grill to 375 F.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'54415948FB4E84419F6ECC8A35BD7CC9', X'FA653DD74FE4484F88DB7950E196599C', X'FA653DD74FE4484F88DB7950E196599C', 2, NULL, NULL, 2, NULL, 0, 'In a bowl, combine lamb, parsley, salt, pepper, and paprika and mix well. (Roll up the sleeves, take off your rings, and do this by hand. The results are better this way.)');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B7D7E444C6F2594F810304E8E6FF29A2', X'FA653DD74FE4484F88DB7950E196599C', X'FA653DD74FE4484F88DB7950E196599C', 3, NULL, NULL, 2, NULL, 0, 'Form meat and spice mixture into 4 patties -- 1/2 to 3/4 inches thick.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'30D69B8D805054498456220B5B6FC3A8', X'FA653DD74FE4484F88DB7950E196599C', X'FA653DD74FE4484F88DB7950E196599C', 4, NULL, NULL, 2, NULL, 0, 'Put in heated pan or grill and cook for about 4 to 5 minutes each side. The flavor of lamb is best at a medium-well, so cook until you see a faint pink color in the center. You can certainly cook longer, should you choose.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3FC2B8018175D74BAFA03DBA562C1A29', X'FA653DD74FE4484F88DB7950E196599C', X'FA653DD74FE4484F88DB7950E196599C', 5, NULL, NULL, 2, NULL, 0, 'Place desired cheese on top and allow it to melt for about 30 seconds to one minute.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'66B0C007771A0447870AB41CF8967CA6', X'FA653DD74FE4484F88DB7950E196599C', X'FA653DD74FE4484F88DB7950E196599C', 6, NULL, NULL, 2, NULL, 0, 'Splash the pan with some cool water to create steam and put a stainless steel mixing bowl over the pan for about 30 seconds. The result is a perfectly melted cheese that hugs the burger.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'885BA13CACE5B54FB7FA93D9689DEA96', X'FA653DD74FE4484F88DB7950E196599C', X'FA653DD74FE4484F88DB7950E196599C', 7, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C8A5B48BE71D5944BEE021ABEC99A883', X'1E0605FC840ACA4B8A0F44AE711860D5', X'1E0605FC840ACA4B8A0F44AE711860D5', 0, NULL, NULL, 2, NULL, 0, 'Over medium heat, brown the lamb in 2 tablespoons of olive oil, using a fork to separate the meat out into small pieces. (Note: because ground lamb is softer than ground beef, take care not to mash it when breaking it up.)');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DD22B03A34BBE54E9AE7761899C7C9CB', X'1E0605FC840ACA4B8A0F44AE711860D5', X'1E0605FC840ACA4B8A0F44AE711860D5', 1, NULL, NULL, 2, NULL, 0, 'When meat is browned just to the point where it doesn''t look red, transfer to a colander and drain.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3CFF67EC4DE87342B1CF2A2EAAF0DA80', X'1E0605FC840ACA4B8A0F44AE711860D5', X'1E0605FC840ACA4B8A0F44AE711860D5', 2, NULL, NULL, 2, NULL, 0, 'Over medium heat, saut chopped onion in 2 tablespoons of olive oil.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CAE5445B03BB28459C953293B336CDF3', X'1E0605FC840ACA4B8A0F44AE711860D5', X'1E0605FC840ACA4B8A0F44AE711860D5', 3, NULL, NULL, 2, NULL, 0, 'When the onion turns translucent, stir in drained lamb, tomatoes, 1/2 cup of water, celery, bay leaf, 1 rounded teaspoon of sea salt, and 1/4 teaspoon of pepper.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FE84C5D4DB2176449BDD1E51FFD71B17', X'1E0605FC840ACA4B8A0F44AE711860D5', X'1E0605FC840ACA4B8A0F44AE711860D5', 4, NULL, NULL, 2, NULL, 0, 'When it reaches a boil, cover and reduce heat to a simmer, cooking for 20 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'89FCAB554B8203459FB521AE8CC83270', X'1E0605FC840ACA4B8A0F44AE711860D5', X'1E0605FC840ACA4B8A0F44AE711860D5', 5, NULL, NULL, 2, NULL, 0, 'Taste and add more salt and pepper to your preference.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'07F7BD73FE87144295B4E3E029258537', X'1E0605FC840ACA4B8A0F44AE711860D5', X'1E0605FC840ACA4B8A0F44AE711860D5', 6, NULL, NULL, 2, NULL, 0, 'In a saucepan, bring chicken broth or stock to a boil.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FBB70D60E216804CB58E0FF079EB76DA', X'1E0605FC840ACA4B8A0F44AE711860D5', X'1E0605FC840ACA4B8A0F44AE711860D5', 7, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 390F (200C).');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5B69A33B3755AD48830794A41871D1DF', X'1E0605FC840ACA4B8A0F44AE711860D5', X'1E0605FC840ACA4B8A0F44AE711860D5', 8, NULL, NULL, 2, NULL, 0, 'Remove bay leaf, and transfer the meat mixture to a greased baking or oven-safe casserole dish.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3CA956D0B3CACA4299F4197CD7BBA767', X'1E0605FC840ACA4B8A0F44AE711860D5', X'1E0605FC840ACA4B8A0F44AE711860D5', 9, NULL, NULL, 2, NULL, 0, 'Add boiling stock or broth and stir in the pasta.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C4011191F00C3D4EBE8C83222ED84642', X'1E0605FC840ACA4B8A0F44AE711860D5', X'1E0605FC840ACA4B8A0F44AE711860D5', 10, NULL, NULL, 2, NULL, 0, 'Cook uncovered in the oven at 390 F / 200 C for about 40 minutes, until the pasta is done and almost the liquid has been absorbed. Stir once or twice during cooking.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2D0A1C6ECA8E164692FE62C1F3F37A14', X'1E0605FC840ACA4B8A0F44AE711860D5', X'1E0605FC840ACA4B8A0F44AE711860D5', 11, NULL, NULL, 2, NULL, 0, 'Remove pot from the oven and cover with a cotton towel for 20 minutes (to absorb excess moisture).');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3F10350D74940545AA9DC995A0E0BEBC', X'1E0605FC840ACA4B8A0F44AE711860D5', X'1E0605FC840ACA4B8A0F44AE711860D5', 12, NULL, NULL, 2, NULL, 0, 'Stir in the fresh parsley just before serving, and add an optional topping of grated cheese.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F43B21DF7D62454C8C5199443388C0CC', X'D9B30990E7049D4FBBA0E76EBA80AE02', X'D9B30990E7049D4FBBA0E76EBA80AE02', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1EAAD169B39E8049B64D28378C3B7F9E', X'D9B30990E7049D4FBBA0E76EBA80AE02', X'D9B30990E7049D4FBBA0E76EBA80AE02', 1, NULL, NULL, 2, NULL, 0, 'Trim the fat from the lamb chops then cut the meat into 1-inch pieces. Place the meat and bones in a stock pot or Dutch oven; barely cover the meat with about 1 1/4 cups water. Cover the pan and simmer for 45 minutes (do not boil).');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1D1E8D9092ED404DA70AF3142BF3ABBD', X'D9B30990E7049D4FBBA0E76EBA80AE02', X'D9B30990E7049D4FBBA0E76EBA80AE02', 2, NULL, NULL, 2, NULL, 0, 'Add the potato, carrots, onion, celery, green pepper, and optional rutabaga with salt and pepper to the meat in the Dutch oven.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'39F04370A4D0CE4AAE93994E12FD3592', X'D9B30990E7049D4FBBA0E76EBA80AE02', X'D9B30990E7049D4FBBA0E76EBA80AE02', 3, NULL, NULL, 2, NULL, 0, 'Cover and simmer for 20 to 30 minutes, until meat and vegetables are tender.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'05ED88C502ACDA48983660FD5D7B68A7', X'D9B30990E7049D4FBBA0E76EBA80AE02', X'D9B30990E7049D4FBBA0E76EBA80AE02', 4, NULL, NULL, 2, NULL, 0, 'Using tongs, remove the bones to a bowl and discard.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9E49AE9446BED74FBA1BAEC8251C9D4B', X'D9B30990E7049D4FBBA0E76EBA80AE02', X'D9B30990E7049D4FBBA0E76EBA80AE02', 5, NULL, NULL, 2, NULL, 0, 'Remove the Dutch oven from the heat.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B4DB70FEBF2E1749B77E4C7863408C52', X'D9B30990E7049D4FBBA0E76EBA80AE02', X'D9B30990E7049D4FBBA0E76EBA80AE02', 6, NULL, NULL, 2, NULL, 0, 'Mix the flour and cold water. Stir the mixture into the stew. Return to heat. Cook, stirring until the stew is thickened.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DF4DFEBE9407E544BBC2514641C37AB5', X'D9B30990E7049D4FBBA0E76EBA80AE02', X'D9B30990E7049D4FBBA0E76EBA80AE02', 7, NULL, NULL, 2, NULL, 0, 'Add the chopped parsley and taste and adjust the seasonings.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'46DB30BFBABE1A41ADE408B5F8D7C534', X'D9B30990E7049D4FBBA0E76EBA80AE02', X'D9B30990E7049D4FBBA0E76EBA80AE02', 8, NULL, NULL, 2, NULL, 0, 'Serve the stew hot.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'84826B99E47AC2469DD481B9EE4D6926', X'735DD3F431C1C24AA4BEA8B5F1339672', X'735DD3F431C1C24AA4BEA8B5F1339672', 0, NULL, NULL, 2, NULL, 0, 'Thoroughly mix all ingredients together.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EB1EE1CC100BDC48899ADF0DDBBA1A55', X'735DD3F431C1C24AA4BEA8B5F1339672', X'735DD3F431C1C24AA4BEA8B5F1339672', 1, NULL, NULL, 2, NULL, 0, 'Shape into 4 oblong patties about 5 inches long and 3/4 inch thick and place on a small baking sheet. Cover with plastic and refrigerate for at least 1/2 hour.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CB368E15DC4FD042A6F324CADA8893A3', X'735DD3F431C1C24AA4BEA8B5F1339672', X'735DD3F431C1C24AA4BEA8B5F1339672', 2, NULL, NULL, 2, NULL, 0, 'Get the grill hot, oil it, and grill kebabs for about 4 minutes per side.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9D7C5F4333D4EA4EA4707E1F4A6B4DFC', X'735DD3F431C1C24AA4BEA8B5F1339672', X'735DD3F431C1C24AA4BEA8B5F1339672', 3, NULL, NULL, 2, NULL, 0, 'I serve these with plain yogurt for dipping and tabbouleh on the side. You can also make these a day in advance. If you make them in advance, simply wrap in tin foil and warm in a 200dg Fahrenheit oven for 25 minutes until warmed through');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'842F1F2FD922034EA21353441412C1D0', X'57A5BC19465F8248B951412C8FE2BCAD', X'57A5BC19465F8248B951412C8FE2BCAD', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'47CD18DD6A587140A0019A80E78EC65F', X'57A5BC19465F8248B951412C8FE2BCAD', X'57A5BC19465F8248B951412C8FE2BCAD', 1, NULL, NULL, 2, NULL, 0, 'Pre-heat oven to 220 C/ 425 F Gas 7.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'101B5F3EFE2D994AB5EE9EED14D8AF20', X'57A5BC19465F8248B951412C8FE2BCAD', X'57A5BC19465F8248B951412C8FE2BCAD', 2, NULL, NULL, 2, NULL, 0, 'Place the flour, butter , lard and salt into a food processor. Mix on a pulse setting until the mixture resembles breadcrumbs, add the water, slowly, through the funnel until the dough comes together in a ball. Wrap in clingfilm or plastic wrap and rest the pastry for 30 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C4A82E7DE9455044B8B28EBECDB1B1E6', X'57A5BC19465F8248B951412C8FE2BCAD', X'57A5BC19465F8248B951412C8FE2BCAD', 3, NULL, NULL, 2, NULL, 0, 'Meanwhile, melt the butter in a saucepan, add the leeks and cook on a low heat for 5 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0BB94017A8B96D49B70B41853F9F4C1A', X'57A5BC19465F8248B951412C8FE2BCAD', X'57A5BC19465F8248B951412C8FE2BCAD', 4, NULL, NULL, 2, NULL, 0, 'Add the potato chunks to the pan and cook for another 5 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'46DD42E329690748964566D5362BAEA4', X'57A5BC19465F8248B951412C8FE2BCAD', X'57A5BC19465F8248B951412C8FE2BCAD', 5, NULL, NULL, 2, NULL, 0, 'Add the lamb pieces and brown all over, cook for two minutes, cover with a lid and cook over a low heat for 6 minutes. Season the contents of the pan with salt and pepper and leave to one side to cool.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'77A4E10CF91C724889084D61CB9EEDC5', X'57A5BC19465F8248B951412C8FE2BCAD', X'57A5BC19465F8248B951412C8FE2BCAD', 6, NULL, NULL, 2, NULL, 0, 'Divide the pastry into 2 and roll each piece into rounds approx 22cm. Brush the rounds with beaten egg.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CD734C3EDAA4E143A5BDCB7A97A3A6CB', X'57A5BC19465F8248B951412C8FE2BCAD', X'57A5BC19465F8248B951412C8FE2BCAD', 7, NULL, NULL, 2, NULL, 0, 'Divide the meat mixture between each pastry circle and place to one side of the circle. Brush the edges with a little more beaten egg.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'92FB00BD163E90478BB4FF54FECB8474', X'57A5BC19465F8248B951412C8FE2BCAD', X'57A5BC19465F8248B951412C8FE2BCAD', 8, NULL, NULL, 2, NULL, 0, 'Fold the circle in half over the filling so the two edges meet. Crimp the two edges together to create a tight seal. Brush each pasty all over with the remaining beaten egg.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A1F10FFACC0BF14EAB67DC91C880ED24', X'57A5BC19465F8248B951412C8FE2BCAD', X'57A5BC19465F8248B951412C8FE2BCAD', 9, NULL, NULL, 2, NULL, 0, 'Place the pasties on a greased baking sheet and bake for 15 minutes, then reduce the heat to 180 C and cook for a further 30 until the Oggie is golden brown.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'423BB291945CE148895388D2ACC17967', X'57A5BC19465F8248B951412C8FE2BCAD', X'57A5BC19465F8248B951412C8FE2BCAD', 10, NULL, NULL, 2, NULL, 0, 'Serve hot or cold. Delicious with pickled onions, tomatoes and chutney, or if hot, with a little warm gravy and peas.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'55777C1DD9D0764BA2DF721B68448E70', X'29FCE52BFC84974E919EAF459CAC509A', X'29FCE52BFC84974E919EAF459CAC509A', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B478864AD980A74DA87BFA4F54CE3BBA', X'29FCE52BFC84974E919EAF459CAC509A', X'29FCE52BFC84974E919EAF459CAC509A', 1, NULL, NULL, 2, NULL, 0, 'Cut the lamb into thin strips. Combine with the marinade ingredients. Marinate the lamb for 25 minutes, then drain and reserve the marinade.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'68CD72927B2E35418118EB4D5591E81D', X'29FCE52BFC84974E919EAF459CAC509A', X'29FCE52BFC84974E919EAF459CAC509A', 2, NULL, NULL, 2, NULL, 0, 'While the lamb is marinating, prepare the sauce and other ingredients. In a small bowl, combine the sauce ingredients. Set aside.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'297E888C1F9E784984BD6CD71D7E8814', X'29FCE52BFC84974E919EAF459CAC509A', X'29FCE52BFC84974E919EAF459CAC509A', 3, NULL, NULL, 2, NULL, 0, 'Heat the wok over medium-high to high heat. Add 2 tablespoons vegetable oil. When the oil is hot, add the garlic. Stir-fry until aromatic, about 30 seconds.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4D1EE1242EC89F4C8D8BA82E5024A089', X'29FCE52BFC84974E919EAF459CAC509A', X'29FCE52BFC84974E919EAF459CAC509A', 4, NULL, NULL, 2, NULL, 0, 'Add the lamb. Stir-fry very briefly, until the lamb changes color, 1 to 2 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5CEDE1BC454DF140B7BECA5361748B0F', X'29FCE52BFC84974E919EAF459CAC509A', X'29FCE52BFC84974E919EAF459CAC509A', 5, NULL, NULL, 2, NULL, 0, 'Add the reserved marinade and bring to a boil.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'504302D6BF8CD941918DEE743D1EC18C', X'29FCE52BFC84974E919EAF459CAC509A', X'29FCE52BFC84974E919EAF459CAC509A', 6, NULL, NULL, 2, NULL, 0, 'Stir in the scallions. Taste and season with salt and pepper if desired.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F58C12C2ACF4AA4E8CC184A74DB88400', X'29FCE52BFC84974E919EAF459CAC509A', X'29FCE52BFC84974E919EAF459CAC509A', 7, NULL, NULL, 2, NULL, 0, 'Remove from the heat, stir in the sesame oil, and serve.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B919EE6005583B47AB414073EFC9A3C1', X'E32F2B54583599479E4FF0B3CDFBF4D0', X'E32F2B54583599479E4FF0B3CDFBF4D0', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EC664D2C2C587B478E4DCC8F30AA0DE0', X'E32F2B54583599479E4FF0B3CDFBF4D0', X'E32F2B54583599479E4FF0B3CDFBF4D0', 1, NULL, NULL, 2, NULL, 0, 'Place the raisins and prunes into a small bowl and pour over enough water to cover. Add lemon juice and let soak for at least 1 hour. Drain. Roughly chop the prunes.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7D32092C1911514EB63DD1FD4E8E37F7', X'E32F2B54583599479E4FF0B3CDFBF4D0', X'E32F2B54583599479E4FF0B3CDFBF4D0', 2, NULL, NULL, 2, NULL, 0, 'Meanwhile, heat the butter in a large pan, add the onion, and cook for 5 minutes. Add cubed lamb, ground lamb, and crushed garlic cloves. Fry for 5 minutes, stirring constantly until browned.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'04F07D7C9DF3374D9E0709105A9F722C', X'E32F2B54583599479E4FF0B3CDFBF4D0', X'E32F2B54583599479E4FF0B3CDFBF4D0', 3, NULL, NULL, 2, NULL, 0, 'Pour 2/3 cup/150 ml of stock into the pan. Bring to a boil, then lower the heat, cover, and simmer for 1 hour, or until the lamb is tender.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7AFC193E721A9140BDDC8B3C5498C048', X'E32F2B54583599479E4FF0B3CDFBF4D0', X'E32F2B54583599479E4FF0B3CDFBF4D0', 4, NULL, NULL, 2, NULL, 0, 'Add the remaining stock and bring to a boil. Add rinsed long-grain white rice and a large pinch of saffron. Stir, then cover, and simmer for 15 minutes, or until the rice is tender.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'81800DA3B70AAD41B3A37A466B654CCD', X'E32F2B54583599479E4FF0B3CDFBF4D0', X'E32F2B54583599479E4FF0B3CDFBF4D0', 5, NULL, NULL, 2, NULL, 0, 'Add the drained raisins, drained chopped prunes, and salt and pepper to taste. Heat through for a few minutes, then turn out onto a warmed serving dish and garnish with sprigs of flat-leaf parsley.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5121072AD1510A408594A777B7A915F6', X'F024FA7DBA0AF14E826F18DCD04DE052', X'F024FA7DBA0AF14E826F18DCD04DE052', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'625717203B68F4448442E1314C1B9089', X'F024FA7DBA0AF14E826F18DCD04DE052', X'F024FA7DBA0AF14E826F18DCD04DE052', 1, NULL, NULL, 2, NULL, 0, 'Heat the oven to 350 F. Cut the white and sweet potatoes into 1-inch-thick slices.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'68E5740DDF94AB42B45A9559B4D9F56F', X'F024FA7DBA0AF14E826F18DCD04DE052', X'F024FA7DBA0AF14E826F18DCD04DE052', 2, NULL, NULL, 2, NULL, 0, 'Cut the onions in half and then place all of the vegetables in a roasting pan in one even layer.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4521D68DBA05F643ABC93C4BD281BF8F', X'F024FA7DBA0AF14E826F18DCD04DE052', X'F024FA7DBA0AF14E826F18DCD04DE052', 3, NULL, NULL, 2, NULL, 0, 'Drizzle the vegetables with 2 tablespoons of the olive oil.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3EF32B6B9708784CA6B4FF151073F168', X'F024FA7DBA0AF14E826F18DCD04DE052', X'F024FA7DBA0AF14E826F18DCD04DE052', 4, NULL, NULL, 2, NULL, 0, 'Sprinkle with 1 teaspoon salt and toss to combine.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'29E2D456F6E91B488A40341FF2FA9CC3', X'F024FA7DBA0AF14E826F18DCD04DE052', X'F024FA7DBA0AF14E826F18DCD04DE052', 5, NULL, NULL, 2, NULL, 0, 'Place the lamb on top of the vegetables and rub the lamb with the remaining 2 tablespoons olive oil.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E2BACEC7FFDB144A8465696EA4B4AD8E', X'F024FA7DBA0AF14E826F18DCD04DE052', X'F024FA7DBA0AF14E826F18DCD04DE052', 6, NULL, NULL, 2, NULL, 0, 'Sprinkle the lamb with the rest of the sea salt.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6E9A9CBF89F7A44C8E07A11DD975CD61', X'F024FA7DBA0AF14E826F18DCD04DE052', X'F024FA7DBA0AF14E826F18DCD04DE052', 7, NULL, NULL, 2, NULL, 0, 'Use the point of a sharp knife to make small incisions all over the lamb.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'967B4882FD00124489BE6BF7ABD1477D', X'F024FA7DBA0AF14E826F18DCD04DE052', X'F024FA7DBA0AF14E826F18DCD04DE052', 8, NULL, NULL, 2, NULL, 0, 'Place the garlic slivers and rosemary springs in the slits.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E1E0E221D32C7943AD381C3EE440EA0B', X'F024FA7DBA0AF14E826F18DCD04DE052', X'F024FA7DBA0AF14E826F18DCD04DE052', 9, NULL, NULL, 2, NULL, 0, 'Place the roasting pan on the middle oven rack and roast for 1 1/2 hours, or until a meat thermometer placed in the thickest part of the lamb reads 140 F for rare, 160 F for medium, and 170 F for well done.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6B81412B84A78046AB8A33090EDFC500', X'F024FA7DBA0AF14E826F18DCD04DE052', X'F024FA7DBA0AF14E826F18DCD04DE052', 10, NULL, NULL, 2, NULL, 0, 'Remove from the oven and transfer the lamb to a plate to rest.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C7E9D4373AC90741A33E50698CBF0394', X'F024FA7DBA0AF14E826F18DCD04DE052', X'F024FA7DBA0AF14E826F18DCD04DE052', 11, NULL, NULL, 2, NULL, 0, 'Cover the lamb with aluminum foil and let it sit for 10 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0156647BBCE33B49938D5DBEB61EFBFD', X'F024FA7DBA0AF14E826F18DCD04DE052', X'F024FA7DBA0AF14E826F18DCD04DE052', 12, NULL, NULL, 2, NULL, 0, 'Serve the lamb with the roasted potatoes and onions, and mint sauce on the side.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'60509E9204BAA4449110D29373C84ABD', X'07241BC9F28F4149B8C0F7ED92BF4305', X'07241BC9F28F4149B8C0F7ED92BF4305', 0, NULL, NULL, 2, NULL, 0, 'Cut the lamb into paper thin rectangular slices. Slice the bean curd.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'262E418B10052C4EBD502FDE3E14FF17', X'07241BC9F28F4149B8C0F7ED92BF4305', X'07241BC9F28F4149B8C0F7ED92BF4305', 1, NULL, NULL, 2, NULL, 0, 'Wash, drain, and chop the vegetables. Prepare any side dishes that you are planning to serve.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'68849781B2249047A3818BEE4E37FA92', X'07241BC9F28F4149B8C0F7ED92BF4305', X'07241BC9F28F4149B8C0F7ED92BF4305', 2, NULL, NULL, 2, NULL, 0, 'Lay the lamb, chopped vegetables, and side dishes on separate platters on the table. Place the dipping sauces on the table in small individual bowls. Make sure each guest has a complete place setting, including a dipping fork (color-coded if possible) and a small bowl for placing the cooked food.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5F9DE8155484874793066EEAF1288568', X'07241BC9F28F4149B8C0F7ED92BF4305', X'07241BC9F28F4149B8C0F7ED92BF4305', 3, NULL, NULL, 2, NULL, 0, 'Bring the broth with the dark soy sauce to a boil, and add the ginger and green onion. Transfer enough broth so that the fondue pot is approximately 2/3 - 3/4 full. (How much broth you need will depend on the size of the fondue pot).');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9F06E10E57082F409708F325A2BF3574', X'07241BC9F28F4149B8C0F7ED92BF4305', X'07241BC9F28F4149B8C0F7ED92BF4305', 4, NULL, NULL, 2, NULL, 0, 'Place the fondue pot on the burner, and keep it simmering throughout the meal. Keep the remaining broth warming on the stovetop.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CEC29EDD4CD13B4B806141339DB96C5E', X'07241BC9F28F4149B8C0F7ED92BF4305', X'07241BC9F28F4149B8C0F7ED92BF4305', 5, NULL, NULL, 2, NULL, 0, 'To serve, invite guests to spear the food with a dipping fork and cook briefly in the broth until cooked, then dip the cooked food in the sauces as desired.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C6B09522F816D64388DB70585158D210', X'07241BC9F28F4149B8C0F7ED92BF4305', X'07241BC9F28F4149B8C0F7ED92BF4305', 6, NULL, NULL, 2, NULL, 0, 'Use a dipping basket to cook the vegetables in batches in the hot broth and ladle out into the soup bowls. Cook the noodles and serve at the end of the meal.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B75B30ED4DBF7C4E92C3FCB1A8728D58', X'50731F262AF85D4095CD39A1E6FD8E11', X'50731F262AF85D4095CD39A1E6FD8E11', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0C777820DCEF4C47971348E4691284C2', X'50731F262AF85D4095CD39A1E6FD8E11', X'50731F262AF85D4095CD39A1E6FD8E11', 1, NULL, NULL, 2, NULL, 0, 'Cut the lamb shoulder and fat into small pieces that will easily fit into your meat grinder, removing any sinew from the meat as you go.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'91F1E72B91B6B645BD3350872CAF9C00', X'50731F262AF85D4095CD39A1E6FD8E11', X'50731F262AF85D4095CD39A1E6FD8E11', 2, NULL, NULL, 2, NULL, 0, 'In a large bowl combine the meat and fat with the rosemary, salt, garlic, and pepper. Stir to mix. Cover and refrigerate 4 to 6 hours or overnight.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8948F48A351B474C88BD09801FE10FFB', X'50731F262AF85D4095CD39A1E6FD8E11', X'50731F262AF85D4095CD39A1E6FD8E11', 3, NULL, NULL, 2, NULL, 0, 'Before grinding, place the bowl from your stand mixer and meat grinder in the refrigerator for 2 hours.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DFE42EC8C9D8474793F40460B1ABDD8E', X'50731F262AF85D4095CD39A1E6FD8E11', X'50731F262AF85D4095CD39A1E6FD8E11', 4, NULL, NULL, 2, NULL, 0, 'Remove the meat mixture, bowl, meat grinder, and wine from the refrigerator. Using the finest grind on your grinder, grind the meat mixture into the chilled bowl.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CB1E957627D15E4CBABEBAE66BF30337', X'50731F262AF85D4095CD39A1E6FD8E11', X'50731F262AF85D4095CD39A1E6FD8E11', 5, NULL, NULL, 2, NULL, 0, 'Alternate pushing the pieces of fat and meat through the grinder to ensure that the fat doesn''t stick inside.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4D0B1C49D534424D917B965C4D225B0D', X'50731F262AF85D4095CD39A1E6FD8E11', X'50731F262AF85D4095CD39A1E6FD8E11', 6, NULL, NULL, 2, NULL, 0, 'Using the paddle attachment on your stand mixer, mix the ground meat on low speed, adding the cold red wine. The mixture will come together in about 2 minutes and be sticky.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DF88DB44A609FF4996FEFB02685434A5', X'50731F262AF85D4095CD39A1E6FD8E11', X'50731F262AF85D4095CD39A1E6FD8E11', 7, NULL, NULL, 2, NULL, 0, 'Take about 1 tablespoon and form a small patty. Fry it in a skillet. Taste and adjust the seasoning, adding more salt if desired. If you plan on stuffing the sausage into casings, refrigerate it for 2 hours.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BE059558355B1146BA8C000CD99F955D', X'50731F262AF85D4095CD39A1E6FD8E11', X'50731F262AF85D4095CD39A1E6FD8E11', 8, NULL, NULL, 2, NULL, 0, 'Otherwise, you can simply shape and wrap the sausages in plastic wrap for later use. If you''re stuffing the sausages, proceed to the next step.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EE1D91F3E30DCD4A980D9C16A4E82B92', X'50731F262AF85D4095CD39A1E6FD8E11', X'50731F262AF85D4095CD39A1E6FD8E11', 9, NULL, NULL, 2, NULL, 0, 'While the sausage meat is chilling, soak the casings in warm water for 1 hour.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F0FD14817CC6A742A693EC62C045ECFD', X'50731F262AF85D4095CD39A1E6FD8E11', X'50731F262AF85D4095CD39A1E6FD8E11', 10, NULL, NULL, 2, NULL, 0, 'Rinse the casings in cold water, then run water through them, by slipping one end of the casing over the tap and gently turn on the water to let it flow through the casing.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D9E2B92D0E2BEF40BD6F6210842E1AA4', X'50731F262AF85D4095CD39A1E6FD8E11', X'50731F262AF85D4095CD39A1E6FD8E11', 11, NULL, NULL, 2, NULL, 0, 'Place the casings in a fine-mesh sieve to drain. You want them to be moist when you fill them.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B4C13C4B3900E2478F740CE567398542', X'50731F262AF85D4095CD39A1E6FD8E11', X'50731F262AF85D4095CD39A1E6FD8E11', 12, NULL, NULL, 2, NULL, 0, 'Attach the sausage stuffer to the grinder, and push the damp sausage casing over the tube until about 4 inches is hanging from the end and tie a knot in this piece. (If it''s your first time at making sausage, ask a friend to help you with this step.)');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3D1E524B2A80764FBA272E92896B0BB8', X'50731F262AF85D4095CD39A1E6FD8E11', X'50731F262AF85D4095CD39A1E6FD8E11', 13, NULL, NULL, 2, NULL, 0, 'Add the chilled mixture to the grinder on low speed, and slowly stuff the sausage casings, trying to minimize the air pockets in the casings. As the sausage enters the casing, it should slowly slide off the tube.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F2CD47015923AA408332C3888D7BD5FF', X'50731F262AF85D4095CD39A1E6FD8E11', X'50731F262AF85D4095CD39A1E6FD8E11', 14, NULL, NULL, 2, NULL, 0, 'Once all the mixture is used up, ease any remaining casing off the tube.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'69724C9057F75F4DBACA34CA2DCE7040', X'50731F262AF85D4095CD39A1E6FD8E11', X'50731F262AF85D4095CD39A1E6FD8E11', 15, NULL, NULL, 2, NULL, 0, 'Roll the sausage on a damp surface to distribute the filling as evenly as possible, then form the sausage into links by twisting the casings at 6-inch intervals.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E9943A382E2C5841ABE1DD67FCF26FAE', X'50731F262AF85D4095CD39A1E6FD8E11', X'50731F262AF85D4095CD39A1E6FD8E11', 16, NULL, NULL, 2, NULL, 0, 'Twist each link in the opposite direction to prevent them from unwinding.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AF782B4D759077449AE3F737FDA6A280', X'50731F262AF85D4095CD39A1E6FD8E11', X'50731F262AF85D4095CD39A1E6FD8E11', 17, NULL, NULL, 2, NULL, 0, 'Cover the sausages, and refrigerate for up to 3 days or freeze them.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C1F2E0B1162ABD459260FE516EBC55B0', X'50731F262AF85D4095CD39A1E6FD8E11', X'50731F262AF85D4095CD39A1E6FD8E11', 18, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'26F6148E30D7194AA205C486450FFD13', X'0A7484E55DBAAF41A2FE006A811E9319', X'0A7484E55DBAAF41A2FE006A811E9319', 0, NULL, NULL, 2, NULL, 0, 'Heat the oven to 350 F. Grease a 2-quart casserole dish. Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'43DEFE489A2BFD4B9AA32A4A091215E4', X'0A7484E55DBAAF41A2FE006A811E9319', X'0A7484E55DBAAF41A2FE006A811E9319', 1, NULL, NULL, 2, NULL, 0, 'In a skillet, melt the butter and cook the onion until tender. Add the mushrooms and saute until lightly browned.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B13AC293A399754EB6E97A125924FA12', X'0A7484E55DBAAF41A2FE006A811E9319', X'0A7484E55DBAAF41A2FE006A811E9319', 2, NULL, NULL, 2, NULL, 0, 'In a large bowl, add the cooked and diced lamb, cooked rice, undrained tomatoes, salt, pepper, curry powder, and sauted onions and mushrooms with their cooking liquid. Mix gently to combine.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A005F1DC7D86F34D91391F2515161FC7', X'0A7484E55DBAAF41A2FE006A811E9319', X'0A7484E55DBAAF41A2FE006A811E9319', 3, NULL, NULL, 2, NULL, 0, 'Turn the mixture out into the greased casserole dish. Top with the grated cheese.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A6C94EA3B7001D418D6983B7710D6CF8', X'0A7484E55DBAAF41A2FE006A811E9319', X'0A7484E55DBAAF41A2FE006A811E9319', 4, NULL, NULL, 2, NULL, 0, 'Bake for 30 minutes, or until hot and bubbly. Serve hot.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7F50ABDE374A7D4BADB35E3F110B505D', X'D080FE9EC613CF4EB2FBE57F67B11E85', X'D080FE9EC613CF4EB2FBE57F67B11E85', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6E0CFCB7AD299B4CAAD99C65AB6EE564', X'D080FE9EC613CF4EB2FBE57F67B11E85', X'D080FE9EC613CF4EB2FBE57F67B11E85', 1, NULL, NULL, 2, NULL, 0, 'In a large bowl, mix together ground beef, ground pork, ground lamb, garlic, onions, salt, and sweet or hot paprika until thoroughly combined. Do not overmix because this will toughen the meat.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A04370A384CEC2499179677340764A7B', X'D080FE9EC613CF4EB2FBE57F67B11E85', X'D080FE9EC613CF4EB2FBE57F67B11E85', 2, NULL, NULL, 2, NULL, 0, 'Refrigerate meat mixture for several hours for the flavors to meld and for the mixture to firm.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5391B11F73F1D24EABC75C11B3910745', X'D080FE9EC613CF4EB2FBE57F67B11E85', X'D080FE9EC613CF4EB2FBE57F67B11E85', 3, NULL, NULL, 2, NULL, 0, 'Heat a grill, indoor grill, broiler or skillet. Using slightly dampened hands, divide meat mixture into 6 portions. Form into thin patties, 9 inches by 1/2 inch or about the size of a small dinner plate.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7CF2EE73A2651544AF69E0455226E00B', X'D080FE9EC613CF4EB2FBE57F67B11E85', X'D080FE9EC613CF4EB2FBE57F67B11E85', 4, NULL, NULL, 2, NULL, 0, 'Cook pljeskavice about 7 minutes per side.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3F51D204582E5B4CAE53E90C7325EB73', X'D080FE9EC613CF4EB2FBE57F67B11E85', X'D080FE9EC613CF4EB2FBE57F67B11E85', 5, NULL, NULL, 2, NULL, 0, 'Serve with green onions or chopped raw onion, tomatoes, ajvar, lepinje or pogacha bread and Serbian potato salad or coleslaw on the side. Some Serbs place the patty on a large bun like an American hamburger.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BA700F96488D8A458A389F096BE749BB', X'D080FE9EC613CF4EB2FBE57F67B11E85', X'D080FE9EC613CF4EB2FBE57F67B11E85', 6, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1C894C04A7D271429EE3DA75EA47CA5F', X'D5FDC0978120AD4792E9E12B0FC83BC8', X'D5FDC0978120AD4792E9E12B0FC83BC8', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AF891D2B039C7E429D7E5C5734D99593', X'D5FDC0978120AD4792E9E12B0FC83BC8', X'D5FDC0978120AD4792E9E12B0FC83BC8', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 325 F.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AD2EC69FC02F5242A64593A7EABF2D98', X'D5FDC0978120AD4792E9E12B0FC83BC8', X'D5FDC0978120AD4792E9E12B0FC83BC8', 2, NULL, NULL, 2, NULL, 0, 'Place a rack in a roasting pan.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'84377CE7FFBF094C9517A75E947005CC', X'D5FDC0978120AD4792E9E12B0FC83BC8', X'D5FDC0978120AD4792E9E12B0FC83BC8', 3, NULL, NULL, 2, NULL, 0, 'Pat the lamb dry with paper towels. If there is a tough outer layer of fat on your roast, remove it with a sharp knife. Trim excess fat but leave some so you''ll have rich drippings for gravy.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'34142292D10D9E4B9C989939773C3F3F', X'D5FDC0978120AD4792E9E12B0FC83BC8', X'D5FDC0978120AD4792E9E12B0FC83BC8', 4, NULL, NULL, 2, NULL, 0, 'Peel the garlic. Cut 2 cloves of garlic into thin slivers. Mince the third clove of garlic and set aside.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'03094157E3251B47AC32E429D648A043', X'D5FDC0978120AD4792E9E12B0FC83BC8', X'D5FDC0978120AD4792E9E12B0FC83BC8', 5, NULL, NULL, 2, NULL, 0, 'With a small, sharp knife, make several tiny, slitsabout 1/2-inch to 1-inch deepevenly over the surface of the roast. Insert the garlic slivers into the slits.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F7F8B941E0DAC84982D41B05CBF22944', X'D5FDC0978120AD4792E9E12B0FC83BC8', X'D5FDC0978120AD4792E9E12B0FC83BC8', 6, NULL, NULL, 2, NULL, 0, 'Rub the lamb all over with the lemon juice.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ED194A4619AD71439649B83C4A13C4C6', X'D5FDC0978120AD4792E9E12B0FC83BC8', X'D5FDC0978120AD4792E9E12B0FC83BC8', 7, NULL, NULL, 2, NULL, 0, 'Give the rosemary and thyme a rough chop (or crumble dried rosemary).');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E3E0264839F15F4FBB96EEDB472697DF', X'D5FDC0978120AD4792E9E12B0FC83BC8', X'D5FDC0978120AD4792E9E12B0FC83BC8', 8, NULL, NULL, 2, NULL, 0, 'In a small bowl, combine the minced garlic with the herbs, 1 teaspoon of kosher salt, and 1/4 teaspoon of freshly ground black pepper. Rub the garlic and herb mixture over the surface of the lamb.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1FB42DFE2DF440488C655D9E77FD6548', X'D5FDC0978120AD4792E9E12B0FC83BC8', X'D5FDC0978120AD4792E9E12B0FC83BC8', 9, NULL, NULL, 2, NULL, 0, 'Place the roast, fat side up, on the rack in the prepared roasting pan.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5CD96098CBB919419F71BA832A7CA88E', X'D5FDC0978120AD4792E9E12B0FC83BC8', X'D5FDC0978120AD4792E9E12B0FC83BC8', 10, NULL, NULL, 2, NULL, 0, 'Insert meat thermometer in the thickest part of the meat, not touching fat or bone. Roast in the preheated oven for about 25 to 30 minutes per pound, or until meat thermometer registers 145 F (medium rare) to 165 F (well done), depending on how well you like it done.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BB1A82007BA3AD4695EF62628FE97C33', X'D5FDC0978120AD4792E9E12B0FC83BC8', X'D5FDC0978120AD4792E9E12B0FC83BC8', 11, NULL, NULL, 2, NULL, 0, 'Remove the roast to a platter and let it rest for 15 minutes before carving.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C1089F061EA23F4B8EB80AB454B929E6', X'D5FDC0978120AD4792E9E12B0FC83BC8', X'D5FDC0978120AD4792E9E12B0FC83BC8', 12, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'844F68354D1FD9468EE3C677F338D0C8', X'773F4546F3DF4E49966438A815046566', X'773F4546F3DF4E49966438A815046566', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FCF473DD56BD8C4C8CC76F726AF46FBC', X'773F4546F3DF4E49966438A815046566', X'773F4546F3DF4E49966438A815046566', 1, NULL, NULL, 2, NULL, 0, 'In a large bowl, mix together beef, pork, lamb, garlic, 1/2 cup chopped onions, and salt until thoroughly combined.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8EBC3E2A4C7B484DA6A401943CB610BF', X'773F4546F3DF4E49966438A815046566', X'773F4546F3DF4E49966438A815046566', 2, NULL, NULL, 2, NULL, 0, 'To make sure your seasonings are just right, make a small patty and fry it to taste test. Add more garlic, salt, or onions to suit your palate.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C007C6B05F3E824790D19BBB5A2F3481', X'773F4546F3DF4E49966438A815046566', X'773F4546F3DF4E49966438A815046566', 3, NULL, NULL, 2, NULL, 0, 'Refrigerate the meat mixture, ideally overnight, ?before forming.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6F86D827AE26D646A7CD1171C71439EA', X'773F4546F3DF4E49966438A815046566', X'773F4546F3DF4E49966438A815046566', 4, NULL, NULL, 2, NULL, 0, 'To make the sausages by hand, roll the meat mixture into a long, 3/4-inch cylinder. Cut links at 4-inch intervals. If you prefer, you can use a sausage extruder without a casing attached.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E4C4BFEA5D0812489A7B63483B69CFBB', X'773F4546F3DF4E49966438A815046566', X'773F4546F3DF4E49966438A815046566', 5, NULL, NULL, 2, NULL, 0, 'Place the sausages on a plastic wrap-lined plate, cover with more plastic wrap and refrigerate for 1 hour to firm. They now can be cooked.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5EEB0F5B5F404749896D2837486B6066', X'773F4546F3DF4E49966438A815046566', X'773F4546F3DF4E49966438A815046566', 6, NULL, NULL, 2, NULL, 0, 'Broil cevapcici on a charcoal grill or a heated oven broiler rack coated with cooking spray 4 to 6 inches from the flame, 4 minutes per side or until no longer pink in the middle. Or they can be pan-fried in a large skillet coated with cooking spray over high heat for a total of about 8 minutes, turning frequently to brown all sides.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B93F82EAD2A14B4C89701BAB2FF3C816', X'773F4546F3DF4E49966438A815046566', X'773F4546F3DF4E49966438A815046566', 7, NULL, NULL, 2, NULL, 0, 'Serve with chopped raw onion, Serbian potato salad, and pogacha bread.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E0EBA008E78A5644BD9D3856A16310CC', X'756118FD9C24E14A89428841DF542799', X'756118FD9C24E14A89428841DF542799', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'659CC08AD186DE44930911FD8ECEED40', X'756118FD9C24E14A89428841DF542799', X'756118FD9C24E14A89428841DF542799', 1, NULL, NULL, 2, NULL, 0, 'Toast the cumin, fennel, coriander seeds in a dry skillet set over medium heat, constantly stirring, for about 2 minutes, until they become fragrant. Be very careful not to overcook, and worst still, burn the spices. If you leave them too long, this will happen, and you will need to throw them away and start again.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FDC991706E13C84799DF8F5821CF4D1B', X'756118FD9C24E14A89428841DF542799', X'756118FD9C24E14A89428841DF542799', 2, NULL, NULL, 2, NULL, 0, 'Once toasted, allow the seeds to cool and then process them in a spice grinder.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9662170C1EB55A47BA9B38046A11CAD0', X'756118FD9C24E14A89428841DF542799', X'756118FD9C24E14A89428841DF542799', 3, NULL, NULL, 2, NULL, 0, 'Add the toasted, ground seeds to the paprika, cayenne pepper, and cinnamon.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0C639B9461FA8344A221286EAB0789BC', X'756118FD9C24E14A89428841DF542799', X'756118FD9C24E14A89428841DF542799', 4, NULL, NULL, 2, NULL, 0, 'Place the ground lamb in a large bowl, drizzle it with the olive oil, and sprinkle the spice mixture, chopped herbs, crushed garlic, and salt over the meat. Mix the seasonings into the meat.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FCFBAC129525544D9F73765623B96199', X'756118FD9C24E14A89428841DF542799', X'756118FD9C24E14A89428841DF542799', 5, NULL, NULL, 2, NULL, 0, 'The seasoned meat can then be either formed into patties, the size and shape of your choice though avoid making them too big as they will be difficult to stuff into a sandwich. Or, fill bought sausage casings with the lamb sausage mixture. For this, you will need a sausage stuffer attachment often sold for stand mixers, or a stuffing machine.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FF8E800AA314BA4AB5B50F6AE39DA958', X'756118FD9C24E14A89428841DF542799', X'756118FD9C24E14A89428841DF542799', 6, NULL, NULL, 2, NULL, 0, 'Once made, chill before using.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'844680D60E8B184C84AFBE33F3E69493', X'756118FD9C24E14A89428841DF542799', X'756118FD9C24E14A89428841DF542799', 7, NULL, NULL, 2, NULL, 0, 'Cook the patties or sausages according to your recipe, or simply fry until cooked right through.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'15BFB662E4D4A443B43583682A84CA4E', X'756118FD9C24E14A89428841DF542799', X'756118FD9C24E14A89428841DF542799', 8, NULL, NULL, 2, NULL, 0, 'Serve stuffed into a bread bun, pita, or slice and serve in Middle Eastern Flatbreads and fresh salad.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F2D98BC41D19F54EAE70FBC6D3265740', X'534FF958D746F647B7E1E38FEE95F33C', X'534FF958D746F647B7E1E38FEE95F33C', 0, NULL, NULL, 2, NULL, 0, 'Combine olive oil, thyme, basil, rosemary, garlic, lemon zest, salt and pepper in a bowl. Dip lamb chops into the mixture to coat evenly. Cover chops and refrigerate for as little as 30 minutes or for up to 4 hours.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F075BB3964109847BF21103A02648B47', X'534FF958D746F647B7E1E38FEE95F33C', X'534FF958D746F647B7E1E38FEE95F33C', 1, NULL, NULL, 2, NULL, 0, 'Preheat grill for medium high heat.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3DB2E486B1FB1443A9C528F75CFC8BEC', X'534FF958D746F647B7E1E38FEE95F33C', X'534FF958D746F647B7E1E38FEE95F33C', 2, NULL, NULL, 2, NULL, 0, 'Place chops on grill and cook for about 5 to 7 minutes per side or until temperature reaches between 145 to 155 F.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'17A13B152C354A40BEAA5D59BA40541F', X'534FF958D746F647B7E1E38FEE95F33C', X'534FF958D746F647B7E1E38FEE95F33C', 3, NULL, NULL, 2, NULL, 0, 'Once cooked to desired doneness, remove chops from grill. Tent with foil and let meat rest for 5 minutes. Serve with grilled vegetables or your favorite side dishes.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A27921C267D7B64F9661BD4EB4DAF5D6', X'534FF958D746F647B7E1E38FEE95F33C', X'534FF958D746F647B7E1E38FEE95F33C', 4, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2F48065212312C46B8053F0B7ABD0F07', X'3A289BCA5BDD4F46A656316082F86E14', X'3A289BCA5BDD4F46A656316082F86E14', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 325 F.');
