INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'66192021726365459423DCEDEFADE5B7', X'3A289BCA5BDD4F46A656316082F86E14', X'3A289BCA5BDD4F46A656316082F86E14', 3, NULL, NULL, 2, NULL, 0, 'When the oil shimmers, add the lamb and brown well on all sides.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'677F5D866B47024A92B7BD076664D936', X'3A289BCA5BDD4F46A656316082F86E14', X'3A289BCA5BDD4F46A656316082F86E14', 4, NULL, NULL, 2, NULL, 0, 'Use tongs to hold up the rack so you can sear the ends and top edges of the rack.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'49A79BC929ECA747AB2C1EA111F87BE4', X'3A289BCA5BDD4F46A656316082F86E14', X'3A289BCA5BDD4F46A656316082F86E14', 5, NULL, NULL, 2, NULL, 0, 'Set the lamb aside on a rack placed in a baking sheet.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AAF0CFAD63B4DC4892354DCFEC177D40', X'3A289BCA5BDD4F46A656316082F86E14', X'3A289BCA5BDD4F46A656316082F86E14', 6, NULL, NULL, 2, NULL, 0, 'About 20 minutes before serving, place the rack in the oven and cook until lamb reaches 140 F for medium rare.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'18712F6BD30A4541910C1D2BD89B18AB', X'3A289BCA5BDD4F46A656316082F86E14', X'3A289BCA5BDD4F46A656316082F86E14', 7, NULL, NULL, 2, NULL, 0, 'Remove from oven and let rest for five minutes before slicing into double chops.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C06A7F974B52A14EB664056AC4BD26B4', X'DCE61CDA6DBC4F46B46484AA1362E85E', X'DCE61CDA6DBC4F46B46484AA1362E85E', 0, NULL, NULL, 2, NULL, 0, 'In the bowl with the lamb, add red onion, garlic, egg, bread crumbs, cumin, mayonnaise, fresh mint, and salt. Using your hands, mix thoroughly to distribute the ingredients evenly. With a teaspoon, form the mixture into 24 meatballs. Don''t level the meat off or pack the meatballs too tightly.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B476B103E2CA1D48BE4FCF1E7087F89C', X'DCE61CDA6DBC4F46B46484AA1362E85E', X'DCE61CDA6DBC4F46B46484AA1362E85E', 1, NULL, NULL, 2, NULL, 0, 'Preheat the broiler.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BC497D1BFCCB9747B25E298194B90722', X'DCE61CDA6DBC4F46B46484AA1362E85E', X'DCE61CDA6DBC4F46B46484AA1362E85E', 2, NULL, NULL, 2, NULL, 0, 'Lightly spread canola oil on a baking sheet using a paper towel. Place the meatballs two inches apart on the baking sheet, and position the baking sheet four inches from the heat source.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'94BA92D43B747A498FA471EB5F858DAB', X'DCE61CDA6DBC4F46B46484AA1362E85E', X'DCE61CDA6DBC4F46B46484AA1362E85E', 3, NULL, NULL, 2, NULL, 0, 'Broil for about five minutes, turning once halfway through.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EC1E30E45F026C47B5D4596B988BED3D', X'DCE61CDA6DBC4F46B46484AA1362E85E', X'DCE61CDA6DBC4F46B46484AA1362E85E', 4, NULL, NULL, 2, NULL, 0, 'Arrange the meatballs on a serving platter, and squeeze fresh lemon juice over the top. Sprinkle the cayenne pepper over the meatballs at the last minute. Serve at once with tzatziki.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E3239B651440BB4A93FA33AE04AD0581', X'16B478368AADEF459BABE23FD6B1CDF4', X'16B478368AADEF459BABE23FD6B1CDF4', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4D64A2CEF064144FAAC513A7F82EEB06', X'16B478368AADEF459BABE23FD6B1CDF4', X'16B478368AADEF459BABE23FD6B1CDF4', 1, NULL, NULL, 2, NULL, 0, 'Heat the vegetable oil in a large skillet over medium-high heat.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3C30E65E3E69894EA1EC12B5BB92A1A3', X'16B478368AADEF459BABE23FD6B1CDF4', X'16B478368AADEF459BABE23FD6B1CDF4', 2, NULL, NULL, 2, NULL, 0, 'Add the lamb cubes and cook, turning frequently to brown all sides.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9AB5CCB411C3454A8AEAD61E5EB0581E', X'16B478368AADEF459BABE23FD6B1CDF4', X'16B478368AADEF459BABE23FD6B1CDF4', 3, NULL, NULL, 2, NULL, 0, 'Meanwhile, prepare the vegetables. Cut the turnips into 1/2-inch cubes.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BDEBB40373B40440A3EFC997CBEF7DE0', X'16B478368AADEF459BABE23FD6B1CDF4', X'16B478368AADEF459BABE23FD6B1CDF4', 4, NULL, NULL, 2, NULL, 0, 'Peel the carrot and slice it into 1/2-inch thick rounds.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DFF4B4B744D3DD48A539B9D4D3B3D8FF', X'16B478368AADEF459BABE23FD6B1CDF4', X'16B478368AADEF459BABE23FD6B1CDF4', 5, NULL, NULL, 2, NULL, 0, 'Peel the onion and chop it coarsely.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5593D0F641525F43B4A66E2D442C35B0', X'16B478368AADEF459BABE23FD6B1CDF4', X'16B478368AADEF459BABE23FD6B1CDF4', 6, NULL, NULL, 2, NULL, 0, 'Quarter the potatoes (peel if desired.)');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8D716404E0C67241A2C956506D893433', X'16B478368AADEF459BABE23FD6B1CDF4', X'16B478368AADEF459BABE23FD6B1CDF4', 7, NULL, NULL, 2, NULL, 0, 'Add the lamb to the slow cooker along with the salt, pepper, turnip, carrots, onions, and potatoes. Add 2 cups of water or stock. If you use regular stock or broth with salt, don''t add until the end of the cooking time.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2B6DA3F91B140047946073B54C110B71', X'16B478368AADEF459BABE23FD6B1CDF4', X'16B478368AADEF459BABE23FD6B1CDF4', 8, NULL, NULL, 2, NULL, 0, 'Add 2 cups water and cook on low, covered, for 8 to 10 hours.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6F2AE5CC5586B64083ADF5A413FB24E9', X'16B478368AADEF459BABE23FD6B1CDF4', X'16B478368AADEF459BABE23FD6B1CDF4', 9, NULL, NULL, 2, NULL, 0, 'Uncover and turn on high. Blend flour with 1/4 cup water until it forms a paste; slowly add it to the stew, stirring constantly until slightly thickened.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'42528CA50A1C0B4FB2FC1FA3DE36C00C', X'16B478368AADEF459BABE23FD6B1CDF4', X'16B478368AADEF459BABE23FD6B1CDF4', 10, NULL, NULL, 2, NULL, 0, 'Stir in parsley, and serve.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BEACE8EFF4F8D84A8E07D290D1E64638', X'D9DB56EEFDE5ED4C97E9A00B294B18CF', X'D9DB56EEFDE5ED4C97E9A00B294B18CF', 0, NULL, NULL, 2, NULL, 0, 'Trim any visible fat from the lamb and cut it into bite-size pieces. Sprinkle with salt and pepper and then toss with the flour.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'93BB85896783F04A8182709CACEE11B2', X'D9DB56EEFDE5ED4C97E9A00B294B18CF', X'D9DB56EEFDE5ED4C97E9A00B294B18CF', 1, NULL, NULL, 2, NULL, 0, 'Heat 1 tablespoon of olive oilor vegetable oilin a Dutch oven or large saucepan over medium heat. Add the floured lamb pieces and the chopped onion. Cook the lamb until browned on all sides, stirring frequently.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'85BBC8AB278E2C43A1C9E5804F5D3DFA', X'D9DB56EEFDE5ED4C97E9A00B294B18CF', X'D9DB56EEFDE5ED4C97E9A00B294B18CF', 2, NULL, NULL, 2, NULL, 0, 'Add the onion and cook, stirring, for about 5 minutes longer, or until the onion is translucent.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FED7E603BD7D014C9C2F75DE861A2FA4', X'D9DB56EEFDE5ED4C97E9A00B294B18CF', X'D9DB56EEFDE5ED4C97E9A00B294B18CF', 3, NULL, NULL, 2, NULL, 0, 'Add the garlic and cook, stirring, for 1 minute longer.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F3226F752B17B84DAB46EF33D68CC63A', X'D9DB56EEFDE5ED4C97E9A00B294B18CF', X'D9DB56EEFDE5ED4C97E9A00B294B18CF', 4, NULL, NULL, 2, NULL, 0, 'Add remaining ingredients and bring to a boil.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'47345AED7D37604B8DCC14BEE2FB4F47', X'D9DB56EEFDE5ED4C97E9A00B294B18CF', X'D9DB56EEFDE5ED4C97E9A00B294B18CF', 5, NULL, NULL, 2, NULL, 0, 'Reduce heat, cover, and simmer for about 1 hour, until lamb is tender.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4A4942345598374E82ECE4DB87A4E9C4', X'D9DB56EEFDE5ED4C97E9A00B294B18CF', X'D9DB56EEFDE5ED4C97E9A00B294B18CF', 6, NULL, NULL, 2, NULL, 0, 'Serve over hot cooked rice, grits or polenta, or couscous, or serve with hot buttered noodles or potatoes.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8A000A3FCAB35A4F92CCDCEDB6D09494', X'332D39A459D0C240BAB0870FD45F7FEF', X'332D39A459D0C240BAB0870FD45F7FEF', 0, NULL, NULL, 2, NULL, 0, 'Mix together the breadcrumbs and milk in a large bowl and let sit for 20 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1A49B1F6065A96469DBCB48E5B86CEBA', X'332D39A459D0C240BAB0870FD45F7FEF', X'332D39A459D0C240BAB0870FD45F7FEF', 1, NULL, NULL, 2, NULL, 0, 'Add the rest of the lamb meatball ingredients and mix with your hands until just combined.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'344A773638F0A44288ABCB58E08C4B30', X'332D39A459D0C240BAB0870FD45F7FEF', X'332D39A459D0C240BAB0870FD45F7FEF', 2, NULL, NULL, 2, NULL, 0, 'To test the seasoning, cook a small piece of the lamb mixture to check for salt and pepper. With damp hands, form into 24 small meatballs and place them on a lightly oiled baking sheet.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6185FCDB33AF5348B9EAB76662D9C6E1', X'332D39A459D0C240BAB0870FD45F7FEF', X'332D39A459D0C240BAB0870FD45F7FEF', 3, NULL, NULL, 2, NULL, 0, 'Bake in a preheated 450 F oven for 15 minutes to brown.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2BB37109BE9493458B4C4B8952EB25FF', X'332D39A459D0C240BAB0870FD45F7FEF', X'332D39A459D0C240BAB0870FD45F7FEF', 4, NULL, NULL, 2, NULL, 0, 'Transfer into a large saucepan and add the sauce ingredients. Bring to a simmer and cook covered, on low, for 1 hour. Stir occasionally. Taste and adjust seasoning. Serve hot.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ECE4390FE0039B43BEFD48D8C4AF74C8', X'332D39A459D0C240BAB0870FD45F7FEF', X'332D39A459D0C240BAB0870FD45F7FEF', 5, NULL, NULL, 2, NULL, 0, 'These tasty lamb meatballs are delicious served over some lemon couscous.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F76CEA9B8B423C4B9DD6A94C0AB78C80', X'0103D148B25CA147A93C039B210CD756', X'0103D148B25CA147A93C039B210CD756', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7A48653F3AE476448D0685E3B49A5313', X'0103D148B25CA147A93C039B210CD756', X'0103D148B25CA147A93C039B210CD756', 1, NULL, NULL, 2, NULL, 0, 'In a bowl, mix the lamb and yogurt and keep aside. This will tenderize the lamb.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CAB32818523F3345943CD52FCBE06D4B', X'0103D148B25CA147A93C039B210CD756', X'0103D148B25CA147A93C039B210CD756', 2, NULL, NULL, 2, NULL, 0, 'Heat the oil in a deep pan and add the cinnamon, cardamom, cloves, bay leaves, and peppercorns. Fry till they turn slightly darker in color.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1947717E31F9FC4891B5FAFE671730C2', X'0103D148B25CA147A93C039B210CD756', X'0103D148B25CA147A93C039B210CD756', 3, NULL, NULL, 2, NULL, 0, 'Now add the onions and fry until they turn light golden.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1D299D85E31D9745BB3CB8FF8C0D101F', X'0103D148B25CA147A93C039B210CD756', X'0103D148B25CA147A93C039B210CD756', 4, NULL, NULL, 2, NULL, 0, 'Add the ginger and garlic paste and fry for a minute.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'40FB546CF31B07468D3D3A4DE3B99F98', X'0103D148B25CA147A93C039B210CD756', X'0103D148B25CA147A93C039B210CD756', 5, NULL, NULL, 2, NULL, 0, 'Add the coriander, cumin, turmeric, Kashmiri chilies, and garam masala and fry until the oil separates from the masala.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0812ED3B415281459167CE63F5B1F546', X'0103D148B25CA147A93C039B210CD756', X'0103D148B25CA147A93C039B210CD756', 6, NULL, NULL, 2, NULL, 0, 'Add the meat and yogurt mix to the masala and fry well.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'394CD82228A44948B1377914B61103F3', X'0103D148B25CA147A93C039B210CD756', X'0103D148B25CA147A93C039B210CD756', 7, NULL, NULL, 2, NULL, 0, 'Add the beef stock, water, and salt, to taste.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'157469CD5E7A07449081054E27582D04', X'0103D148B25CA147A93C039B210CD756', X'0103D148B25CA147A93C039B210CD756', 8, NULL, NULL, 2, NULL, 0, 'Cook till the gravy is reduced. Stir often. The gravy should be thick when done.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'187B00881FEE754BA62EF06E98FB96AD', X'0103D148B25CA147A93C039B210CD756', X'0103D148B25CA147A93C039B210CD756', 9, NULL, NULL, 2, NULL, 0, 'Whisk the cream until smooth.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ECC4DF9A480A95489D5817A0AB2193FE', X'0103D148B25CA147A93C039B210CD756', X'0103D148B25CA147A93C039B210CD756', 10, NULL, NULL, 2, NULL, 0, 'Stir it into the curry to mix well.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'875C5F3B405B9643B6460EA74A3B54EA', X'0103D148B25CA147A93C039B210CD756', X'0103D148B25CA147A93C039B210CD756', 11, NULL, NULL, 2, NULL, 0, 'Garnish with coriander leaves and serve with plain boiled rice or pulao and a vegetable side dish.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7E0728CFFBC44145BB52346A4528A2C4', X'0103D148B25CA147A93C039B210CD756', X'0103D148B25CA147A93C039B210CD756', 12, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'33DA708FDE5BBF48A03304CEFDCADED5', X'BC9207193D4A5C489AA764836CB74363', X'BC9207193D4A5C489AA764836CB74363', 0, NULL, NULL, 2, NULL, 0, 'Cutaway any excess fat from lamb leg. Rise and pat dry.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'47C0B8A4177C40409E4F7087EA194DEE', X'BC9207193D4A5C489AA764836CB74363', X'BC9207193D4A5C489AA764836CB74363', 1, NULL, NULL, 2, NULL, 0, 'Combine marinade ingredients in a food processor.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CF82E5BA16BED74FA07EA89219968842', X'BC9207193D4A5C489AA764836CB74363', X'BC9207193D4A5C489AA764836CB74363', 2, NULL, NULL, 2, NULL, 0, 'Remove 1/4 cup/60 mL and set aside.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BB0C2101FC24DE4E805D0FFF95A3A749', X'BC9207193D4A5C489AA764836CB74363', X'BC9207193D4A5C489AA764836CB74363', 3, NULL, NULL, 2, NULL, 0, 'Apply remaining marinade liberally onto lamb leg.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'17E464A5263F5241A826FCBDFFF1F17D', X'BC9207193D4A5C489AA764836CB74363', X'BC9207193D4A5C489AA764836CB74363', 4, NULL, NULL, 2, NULL, 0, 'Place into a deep dish, cover and refrigerate for 1 to 2 hours.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4F163112A9A72A4DBF8908E74AA9185C', X'BC9207193D4A5C489AA764836CB74363', X'BC9207193D4A5C489AA764836CB74363', 5, NULL, NULL, 2, NULL, 0, 'Meanwhile, prepare to baste. You can use some of this mixture to inject directly into the lamb before cooking.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'29E1BEEAAF55BB40A5B0E4905EDCF1CF', X'BC9207193D4A5C489AA764836CB74363', X'BC9207193D4A5C489AA764836CB74363', 6, NULL, NULL, 2, NULL, 0, 'Preheat grill for medium heat. This is best done with a drip pan underneath the lamb to prevent drippings from causing a flare-up.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7745A00460708A4BB2EB8E876AB092D4', X'BC9207193D4A5C489AA764836CB74363', X'BC9207193D4A5C489AA764836CB74363', 7, NULL, NULL, 2, NULL, 0, 'Putting a bone-in leg of lamb on a rotisserie skewer can be a little challenging. A long thin knife or large metal skewer can be used to make a hole by starting at the bone end and working it through the thickest part of the leg. It is important to try to find the center to balance the leg of lamb on the rotisserie rod. Secure with the rotisserie forks tightly. It might be necessary to tie the leg with kitchen twine (see rotisserie cooking for more information).');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'01E725368F8E4644B41462DF7DD74A97', X'BC9207193D4A5C489AA764836CB74363', X'BC9207193D4A5C489AA764836CB74363', 8, NULL, NULL, 2, NULL, 0, 'Place the leg of lamb on the grill, making sure it is properly centered and able to turn freely.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1F6CB9AC72D042409E0BF6F59BAB5C97', X'BC9207193D4A5C489AA764836CB74363', X'BC9207193D4A5C489AA764836CB74363', 9, NULL, NULL, 2, NULL, 0, 'Place a drip pan under and add several cups of water to the pan.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4B1A1899D1812B42AB68E39C8719C35D', X'BC9207193D4A5C489AA764836CB74363', X'BC9207193D4A5C489AA764836CB74363', 10, NULL, NULL, 2, NULL, 0, 'Keep the grill on medium to medium-high heat. Water in the pan should not boil. If the grill has a rotisserie burner, you must use that only. However, If the grill doesn''t have a rotisserie burner, but has more than two main burners, you must use the burners on either side for indirect grilling.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5419A2A5F43B464CB72785C4F40B619B', X'BC9207193D4A5C489AA764836CB74363', X'BC9207193D4A5C489AA764836CB74363', 11, NULL, NULL, 2, NULL, 0, 'Grill lamb leg for about 20 to 30 minutes per pound.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CE30B3EDFBB0E04689F62F9A3741A519', X'BC9207193D4A5C489AA764836CB74363', X'BC9207193D4A5C489AA764836CB74363', 12, NULL, NULL, 2, NULL, 0, 'Check the temperature in the thickest part of the meat, avoiding the bone. Once the lamb has reached an internal temperature above 145 F/65 C (or higher if desired), remove from heat and tent with aluminum foil for 10 to 12 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B1B85A5F1FBB4E47967B663B0436AF97', X'BC9207193D4A5C489AA764836CB74363', X'BC9207193D4A5C489AA764836CB74363', 13, NULL, NULL, 2, NULL, 0, 'Carve and serve.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'703B35C3D9E37C44B58E603BD9DD5F4F', X'B6374DDC2194E440B14CF9E974C2E5AC', X'B6374DDC2194E440B14CF9E974C2E5AC', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D5D45EA28FDAF147B33BA2EF884DAC78', X'B6374DDC2194E440B14CF9E974C2E5AC', X'B6374DDC2194E440B14CF9E974C2E5AC', 1, NULL, NULL, 2, NULL, 0, 'In a large bowl, mix all ingredients together with your hands, squeezing and kneading to combine thoroughly.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BB87B09CD9DDB146813EFB75489868C4', X'B6374DDC2194E440B14CF9E974C2E5AC', X'B6374DDC2194E440B14CF9E974C2E5AC', 2, NULL, NULL, 2, NULL, 0, 'Pinch off a small piece, fry it, and taste to adjust seasonings.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D40E9137941B6849A0D3A022CF35934B', X'B6374DDC2194E440B14CF9E974C2E5AC', X'B6374DDC2194E440B14CF9E974C2E5AC', 3, NULL, NULL, 2, NULL, 0, 'Form mixture into 4 sausage-shaped rolls about 1 inch in diameter and 4 to five inches long.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0729BE788E0A5948ACA6A3886E5796D6', X'B6374DDC2194E440B14CF9E974C2E5AC', X'B6374DDC2194E440B14CF9E974C2E5AC', 4, NULL, NULL, 2, NULL, 0, 'Arrange on a lightly oiled platter or large plate, cover with plastic, and refrigerate for 2 to 8 hours.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'96C142CEC0A5284FBB7106E73F16FF6F', X'B6374DDC2194E440B14CF9E974C2E5AC', X'B6374DDC2194E440B14CF9E974C2E5AC', 5, NULL, NULL, 2, NULL, 0, 'Build a hot fire, oil the grate, and cook, turning with a spatula, until browned and cooked through; 6 to 8 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5875ACB248BCC14997C1FD9FE9F17BFB', X'B6374DDC2194E440B14CF9E974C2E5AC', X'B6374DDC2194E440B14CF9E974C2E5AC', 6, NULL, NULL, 2, NULL, 0, 'Serve immediately with mustard and enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'41CF60221CAFB741AD89CF7863E7482D', X'97777C4FD7894942A780997D0C056803', X'97777C4FD7894942A780997D0C056803', 0, NULL, NULL, 2, NULL, 0, 'Trim the excess fat from the rack of lamb.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'49DBC027FDD98F4BA66E0F4F0BFA5599', X'97777C4FD7894942A780997D0C056803', X'97777C4FD7894942A780997D0C056803', 1, NULL, NULL, 2, NULL, 0, 'Place the rack of lamb into a glass baking dish or a resealable plastic bag.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DFBBC3536EA7494D9EA22E3EECE01625', X'97777C4FD7894942A780997D0C056803', X'97777C4FD7894942A780997D0C056803', 2, NULL, NULL, 2, NULL, 0, 'Combine remaining ingredients and slather them over meat portion of the rack.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3292B26426C5EB44BE6AD463F4B0EE0E', X'97777C4FD7894942A780997D0C056803', X'97777C4FD7894942A780997D0C056803', 3, NULL, NULL, 2, NULL, 0, 'Cover with plastic wrap or seal bag and allow the meat to marinate in the refrigerator for 8 to 12 hours.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8FFFE4B546CEA2468D0CB376671A2B9C', X'97777C4FD7894942A780997D0C056803', X'97777C4FD7894942A780997D0C056803', 4, NULL, NULL, 2, NULL, 0, 'Remove rack of lamb from the baking dish (reserving the marinade). Wrap the bones with aluminum foil and place onto the grill. Make loosely-packed balls of aluminum foil to place under the tips of the bones to ensure they are up off the grill and won''t get burned.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9994884B3DB64E46B7F06E335B81DD9D', X'97777C4FD7894942A780997D0C056803', X'97777C4FD7894942A780997D0C056803', 5, NULL, NULL, 2, NULL, 0, 'Sear the rack of lamb for 2 minutes on each side. Then turn the heat down to medium (or move the meat to a cooler part of the grill).');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CE30484831B2134CBA703BD4E4E0BBC5', X'97777C4FD7894942A780997D0C056803', X'97777C4FD7894942A780997D0C056803', 6, NULL, NULL, 2, NULL, 0, 'Cook the lamb for 30 to 40 minutes at medium heat, turning often and brushing with the reserved marinade throughout the cooking process.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'73CF9D5D38444A499CDEF90C4360D468', X'97777C4FD7894942A780997D0C056803', X'97777C4FD7894942A780997D0C056803', 7, NULL, NULL, 2, NULL, 0, 'Once the lamb has reached the desired doneness, remove it from the heat. Rare lamb will be at 120 F (50 C) and medium at about 150 F (65 C). The rack of lamb will go up a few degrees in temperature after you remove it from the grill.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A8D6A4A6BD5AA74A9386E2D2EE15EA2B', X'97777C4FD7894942A780997D0C056803', X'97777C4FD7894942A780997D0C056803', 8, NULL, NULL, 2, NULL, 0, 'Allow 5 to 10 minutes of resting time before carving.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CC3378ABABC97346A48D805607EC181D', X'97777C4FD7894942A780997D0C056803', X'97777C4FD7894942A780997D0C056803', 9, NULL, NULL, 2, NULL, 0, 'To carve, stand the rack up with the bones curving away from you. Using a sharp knife, slide the blade down between the bones gently. Cut the rack of lamb into chops by carving between each set of bones.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7D6B99FCF109D54C96D995BC9BEDD691', X'A9010267AFD9E448BB274CFCECFF6AFF', X'A9010267AFD9E448BB274CFCECFF6AFF', 0, NULL, NULL, 2, NULL, 0, 'Combine vinegar, lemon zest with the herbs and spices. Marinate racks of lamb in ?this mixture for about 12 hours.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CC6489A981D7BD4F83C02D61A6DAC91F', X'A9010267AFD9E448BB274CFCECFF6AFF', X'A9010267AFD9E448BB274CFCECFF6AFF', 1, NULL, NULL, 2, NULL, 0, 'Preheat grill and prepare for indirect grilling at about 300 F.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'325F1C8F274059499EFCD173D7FA28FC', X'A9010267AFD9E448BB274CFCECFF6AFF', X'A9010267AFD9E448BB274CFCECFF6AFF', 2, NULL, NULL, 2, NULL, 0, 'Combine Dijon mustard, red wine, Worcestershire sauce and soy sauce in a bowl.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D6D2C1CBD240594FBD833C773BDF3E44', X'A9010267AFD9E448BB274CFCECFF6AFF', X'A9010267AFD9E448BB274CFCECFF6AFF', 3, NULL, NULL, 2, NULL, 0, 'Remove lamb racks from marinade and place on grill over indirect heat. Grill for one hour or until done, basting with mustard sauce for the first 30 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AE7264C2468DE8438A22C7682B1F897D', X'A9010267AFD9E448BB274CFCECFF6AFF', X'A9010267AFD9E448BB274CFCECFF6AFF', 4, NULL, NULL, 2, NULL, 0, 'Remove from grill when done (an internal temperature of 145 F.) and let rest for 10 minutes. Carve and serve.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C8BCF9052104BF42817BEB665B2043EA', X'6E1F3063014D534097335A75CFF7239D', X'6E1F3063014D534097335A75CFF7239D', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'61F128BBCE1A5D4B835E1FA5D2EAA33E', X'6E1F3063014D534097335A75CFF7239D', X'6E1F3063014D534097335A75CFF7239D', 1, NULL, NULL, 2, NULL, 0, 'Prepare the marinade by grinding the garlic, mint, cumin, salt, and paprika with a mortar and pestle. Alternatively, you can use a food processor. Grind ingredients into a paste and then add the olive oil and lemon juice. Mix well.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D67722C9E980134AB93960CE66B9A353', X'6E1F3063014D534097335A75CFF7239D', X'6E1F3063014D534097335A75CFF7239D', 2, NULL, NULL, 2, NULL, 0, 'Make little incisions in the lamb and then coat it well with the marinade. Make sure you coat the inside and outside of the lamb thoroughly.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1338B219F08DEB40A346F8273EE4888E', X'6E1F3063014D534097335A75CFF7239D', X'6E1F3063014D534097335A75CFF7239D', 3, NULL, NULL, 2, NULL, 0, 'Place lamb in a bowl and cover. Place it in the fridge for at least 2 hours or even overnight.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6A0658DE7BC4E44B8CC6F884B479D919', X'6E1F3063014D534097335A75CFF7239D', X'6E1F3063014D534097335A75CFF7239D', 4, NULL, NULL, 2, NULL, 0, 'Remove the lamb from the fridge 1 hour prior to cooking. Preheat the oven to 350 F.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E6B721A381CF9E45AD61E17B3721AA7C', X'6E1F3063014D534097335A75CFF7239D', X'6E1F3063014D534097335A75CFF7239D', 5, NULL, NULL, 2, NULL, 0, 'Place the lamb in a baking dish and roast for 1 hour and 20 minutes for pink meat. Roast the lamb for an extra 20 minutes for medium to well-done meat.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E79D5DA9E18113479A7D6137A128A865', X'6E1F3063014D534097335A75CFF7239D', X'6E1F3063014D534097335A75CFF7239D', 6, NULL, NULL, 2, NULL, 0, 'Meanwhile, prepare the cucumber sauce. Mix together the yogurt, cucumber, garlic, lemon juice, mint, and cilantro. Add the salt and mix well. Set aside.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'844EF6CDCD60EA4DA4B8197D29CB2957', X'6E1F3063014D534097335A75CFF7239D', X'6E1F3063014D534097335A75CFF7239D', 7, NULL, NULL, 2, NULL, 0, 'Prepare the couscous about 20 minutes before serving the lamb. Bring the vegetable stock to a boil in a small pot. Remove the pot from the heat and add the couscous. Cover and let it sit for about 5 minutes. Transfer to a serving bowl.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FDC12C7AB4CB3D41BFC163BEC56294ED', X'6E1F3063014D534097335A75CFF7239D', X'6E1F3063014D534097335A75CFF7239D', 8, NULL, NULL, 2, NULL, 0, 'Add 1 tablespoon of olive oil to the couscous. Stir well with a fork. Add the curry powder and stir again. Next, add a pinch of salt.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BB6763ED9DE7EC4D8D27599C934D23D5', X'6E1F3063014D534097335A75CFF7239D', X'6E1F3063014D534097335A75CFF7239D', 9, NULL, NULL, 2, NULL, 0, 'Add the raisins and pine nuts to the couscous and stir with the fork.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5D4D59D7E394894D9677A822DCEB65F1', X'6E1F3063014D534097335A75CFF7239D', X'6E1F3063014D534097335A75CFF7239D', 10, NULL, NULL, 2, NULL, 0, 'Remove the lamb when ready and slice it. Spoon some of the liquid from the pan onto the lamb. Serve on a bed of couscous and spoon the yogurt sauce on top.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4AEF7C1CE7148C4385C0F4B7BDB9AC99', X'6A9E265869005742B59248F2D398F085', X'6A9E265869005742B59248F2D398F085', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B9BE7FCA9BC16744857ECC0D0CCC54B9', X'6A9E265869005742B59248F2D398F085', X'6A9E265869005742B59248F2D398F085', 1, NULL, NULL, 2, NULL, 0, 'To prepare yogurt sauce, combine all sauce ingredients in medium bowl. Make sure to squeeze grated cucumber, to release excess liquid before adding to mixture. Cover bowl with plastic wrap and place into refrigerator. Sauce can be made 1 to 6 hours in advance.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0A5DAD0EA260B14095B0F4A15D50BF2E', X'6A9E265869005742B59248F2D398F085', X'6A9E265869005742B59248F2D398F085', 2, NULL, NULL, 2, NULL, 0, 'Preheat grill for medium-high heat. To prepare burgers, combine ground lamb with lemon juice, zest, Worcestershire sauce, mint, onion powder, minced garlic, oregano, paprika salt and pepper. Form into 14 to 16 small patties, a little bigger than the buns. Place patties onto grill and cook for 4 to 5 minutes per side. Once cooked through, remove from heat, placed onto slider buns and top with yogurt sauce and red onion rings.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0557301756107E4F99FA54E6492B3E09', X'7C8C9A4F7510EF40A09B9786A58B95F2', X'7C8C9A4F7510EF40A09B9786A58B95F2', 0, NULL, NULL, 2, NULL, 0, 'Place lamb chops in a shallow baking dish. Combine remaining ingredients and pour over chops. Cover and refrigerate for 4 to 24 hours.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E8E65FAAF49E394BB8D694DA04607964', X'7C8C9A4F7510EF40A09B9786A58B95F2', X'7C8C9A4F7510EF40A09B9786A58B95F2', 1, NULL, NULL, 2, NULL, 0, 'Preheat grill for medium heat. Place chops on a well-oiled grill grate and cook for 4 to 5 minutes on each side.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'60CD531AFE968B41901CDD186282FB67', X'7C8C9A4F7510EF40A09B9786A58B95F2', X'7C8C9A4F7510EF40A09B9786A58B95F2', 2, NULL, NULL, 2, NULL, 0, 'Remove from heat and serve with your favorite chutney or yogurt sauce.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1522B0642326284F931111AEBC575DF1', X'73BCFE14F0733042A8DE0F656485E479', X'73BCFE14F0733042A8DE0F656485E479', 0, NULL, NULL, 2, NULL, 0, 'When you buy your leg of lamb, ask your butcher to clean off as much excess fat as possible. Then, have the leg separated into 3 pieces at the joints.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2BEDAD5DA77539419B54E1383EFEBB6A', X'73BCFE14F0733042A8DE0F656485E479', X'73BCFE14F0733042A8DE0F656485E479', 1, NULL, NULL, 2, NULL, 0, 'Preheat your oven to 285 F. Place the lamb in a shallow metal oven roasting tray. In a bowl, whisk together the olive oil, lemon juice, and spices. Pour the mixture over the lamb.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7E45C3D47D91D4429D1B92D3473AFF2D', X'73BCFE14F0733042A8DE0F656485E479', X'73BCFE14F0733042A8DE0F656485E479', 2, NULL, NULL, 2, NULL, 0, 'Wearing rubber gloves, rub the mixture all over the lamb, massaging it into the meat for several minutes. Add the bay leaves and rosemary sprigs. Place the tray in the oven uncovered.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'17B0580583636C429C18979DD4A1B1EF', X'73BCFE14F0733042A8DE0F656485E479', X'73BCFE14F0733042A8DE0F656485E479', 3, NULL, NULL, 2, NULL, 0, 'Let the lamb cook slowly at this low temperature for about 1 1/2 hours. You''ll see that the meat will release its fat and juices, then reabsorb them as it cooks. After the first 30 minutes, turn the pieces of lamb over. Repeat this two more times during the cooking process.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7C062E400C9A2747B830D5629086A3F8', X'73BCFE14F0733042A8DE0F656485E479', X'73BCFE14F0733042A8DE0F656485E479', 4, NULL, NULL, 2, NULL, 0, 'When 1 1/2 hours have passed and you have turned the meat three times, pour the 1/2 cup of hot water over the meat, then close the roasting pan completely with aluminum foil. Turn up the oven temperature to 365 F and leave the meat to roast for at least 1 hour more.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'34BFDCD766085940BD33B4F3AE6DCDE0', X'73BCFE14F0733042A8DE0F656485E479', X'73BCFE14F0733042A8DE0F656485E479', 5, NULL, NULL, 2, NULL, 0, 'After about 1 hour, remove the pan from the oven and let it rest for 5 minutes. Remove the foil. The meat should be very tender and fall cleanly off the bones. Using two forks, remove all the meat from the bones and discard them. Also, remove the bay leaves and rosemary.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E833731BCEA5664591BA565717978EC5', X'73BCFE14F0733042A8DE0F656485E479', X'73BCFE14F0733042A8DE0F656485E479', 6, NULL, NULL, 2, NULL, 0, 'Your kuzu tandir is ready to serve. Serve it with piping hot Turkish rice pilaf with orzo or with potato puree.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9BF214536718DF409223EBBAF7FE27BF', X'D103D41072EABF4E9231DAE60893E158', X'D103D41072EABF4E9231DAE60893E158', 0, NULL, NULL, 2, NULL, 0, 'Layer the sliced onions on the bottom of a tagine. In a bowl, mix the meat with the chopped onion and spices, and add the mixture to the tagine along with the water, argan oil and olives. Place the cilantro bouquet on top of the meat.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CA9E539CF145664BB9B397F828A850AD', X'D103D41072EABF4E9231DAE60893E158', X'D103D41072EABF4E9231DAE60893E158', 1, NULL, NULL, 2, NULL, 0, 'Cover the tagine, and place it on a diffuser over medium-low heat. It will take some time for the tagine to reach a simmer, but once it does, leave the tagine undisturbed for about three hours, using the lowest heat necessary to maintain the simmer.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6F89721C790CB94C9322411B770FBDF8', X'D103D41072EABF4E9231DAE60893E158', X'D103D41072EABF4E9231DAE60893E158', 2, NULL, NULL, 2, NULL, 0, 'There''s no need to open the tagine unless you smell something burning. In that case, the heat was likely too high and a little water will need to be added to prevent scorching.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B58B752EF89A274AA07E1DF2FC38D09E', X'D103D41072EABF4E9231DAE60893E158', X'D103D41072EABF4E9231DAE60893E158', 3, NULL, NULL, 2, NULL, 0, 'After the tagine has cooked for three hours, check on the meat. It should be very tender and easy to break apart with your fingers. If necessary, cook longer. When the meat is tender, reduce any excess liquid, and serve.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5DBAEA8501A31241ABFC07BD0A5FF033', X'F18FE999DC94D6498246F0353D15D447', X'F18FE999DC94D6498246F0353D15D447', 0, NULL, NULL, 2, NULL, 0, 'Cut excess fat from leg of lamb.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CB36AC2B8AC70C4C824F05754862A437', X'F18FE999DC94D6498246F0353D15D447', X'F18FE999DC94D6498246F0353D15D447', 1, NULL, NULL, 2, NULL, 0, 'Make six deep holes in lamb and insert one peeled clove of garlic into each hole. Set aside to allow it to reach room temperature.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A9C633855F6ECD49BE66D368897EC739', X'F18FE999DC94D6498246F0353D15D447', X'F18FE999DC94D6498246F0353D15D447', 2, NULL, NULL, 2, NULL, 0, 'Mix other ingredients in a large glass or plastic bowl.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'40ECD73D21634641B561992448726B72', X'F18FE999DC94D6498246F0353D15D447', X'F18FE999DC94D6498246F0353D15D447', 3, NULL, NULL, 2, NULL, 0, 'Place boneless lamb leg into a large glass dish. Pour half of the marinade over top, making sure lamb is well coated. Reserve the other half of marinade for basting.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1998F8D4DC4C604294CCD13573D49FF1', X'F18FE999DC94D6498246F0353D15D447', X'F18FE999DC94D6498246F0353D15D447', 4, NULL, NULL, 2, NULL, 0, 'Cover with plastic wrap and place in refrigerator for 6 to 12 hours, turning over every hour or so.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'90314A4506F2954A9F51DB9B606984B6', X'F18FE999DC94D6498246F0353D15D447', X'F18FE999DC94D6498246F0353D15D447', 5, NULL, NULL, 2, NULL, 0, 'Preheat grill.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C16C716B08067A428818A1814DE7A776', X'F18FE999DC94D6498246F0353D15D447', X'F18FE999DC94D6498246F0353D15D447', 6, NULL, NULL, 2, NULL, 0, 'Place lamb on rotisserie skewer and put it on the grill. Reduce temperature to low. Cook for 2 hours, brushing with reserved marinade every half hour.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D3332B2200825D40918609E68815A61A', X'F18FE999DC94D6498246F0353D15D447', X'F18FE999DC94D6498246F0353D15D447', 7, NULL, NULL, 2, NULL, 0, 'Once lamb reaches desired doneness (between 145 F to 160 F, depending on how rare or well done you like it), remove from heat, place onto a large cutting board and tent loosely with aluminum foil. Let meat rest for 5 to 7 minutes before slicing.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C5E2E54F7D247F47A5B84B7E44499CA0', X'F18FE999DC94D6498246F0353D15D447', X'F18FE999DC94D6498246F0353D15D447', 8, NULL, NULL, 2, NULL, 0, 'Cut into thin slices. Serve with your favorite sides.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7D73A0D6EB39CE418FCCC628D8997BA1', X'E2EE7EC1F89BA945954EEB85C97391EB', X'E2EE7EC1F89BA945954EEB85C97391EB', 0, NULL, NULL, 2, NULL, 0, 'Cut the meat and fat into bite-sized cubes and put it all in a saucepan. Add the salt and turn through with your hands.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BE01A5455B914543ACF0BA778BD47486', X'E2EE7EC1F89BA945954EEB85C97391EB', X'E2EE7EC1F89BA945954EEB85C97391EB', 1, NULL, NULL, 2, NULL, 0, 'Turn the heat on high until the bottom begins to sizzle. Cover the pan and reduce the heat to low. Leave the pan to simmer very gently for several hours. Turn the meat over occasionally with a wooden spoon.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6DE849CBEE06F543AEADDA71AD81DDBC', X'E2EE7EC1F89BA945954EEB85C97391EB', X'E2EE7EC1F89BA945954EEB85C97391EB', 2, NULL, NULL, 2, NULL, 0, 'Youll know the meat is ready when there is no liquid left except the melted fat and the meat is falling apart and has darkened in color.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'59A1945B1905404BB62302C96B9E30C8', X'E2EE7EC1F89BA945954EEB85C97391EB', X'E2EE7EC1F89BA945954EEB85C97391EB', 3, NULL, NULL, 2, NULL, 0, 'Serve the hot kavurma with a side dish of rice or bulgur pilaf and other traditional Turkish dishes.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AA0323D8644F2E418D8C023B9D541254', X'05273D115BE9EC479D603555B9424387', X'05273D115BE9EC479D603555B9424387', 0, NULL, NULL, 2, NULL, 0, 'Place the lamb chops in a shallow baking dish or resealable plastic bag.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'21EFF3C83EF6024FAC6936A9DDC7B820', X'05273D115BE9EC479D603555B9424387', X'05273D115BE9EC479D603555B9424387', 1, NULL, NULL, 2, NULL, 0, 'Combine olive oil, balsamic vinegar, garlic, rosemary, honey, mustard, oregano, salt, and pepper. Mix well and pour over lamb chops. Gently toss to coat.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E2BA46DC8C3A5746A39E81FE7450C79C', X'05273D115BE9EC479D603555B9424387', X'05273D115BE9EC479D603555B9424387', 2, NULL, NULL, 2, NULL, 0, 'Cover or seal bag and refrigerate for 1 to 2 hours.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'54633A9C9F12D94690C94F21486E545F', X'05273D115BE9EC479D603555B9424387', X'05273D115BE9EC479D603555B9424387', 3, NULL, NULL, 2, NULL, 0, 'Preheat the grill to medium-high heat.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'41BA7F89F20C67419E688C22B8FD527A', X'05273D115BE9EC479D603555B9424387', X'05273D115BE9EC479D603555B9424387', 4, NULL, NULL, 2, NULL, 0, 'Remove the chops and place onto grill grates.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'66113D0AB7AE3541B2D59BB7A5FF51CF', X'05273D115BE9EC479D603555B9424387', X'05273D115BE9EC479D603555B9424387', 5, NULL, NULL, 2, NULL, 0, 'Discard marinade.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9F64AE839721C845B3B23B2F2FFB08A6', X'05273D115BE9EC479D603555B9424387', X'05273D115BE9EC479D603555B9424387', 6, NULL, NULL, 2, NULL, 0, 'Grill, the lamb, chops 4 to 5 minutes per side on until cooked to desired doneness.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D0578182FC157648A5923538A02FB1AA', X'BC9150A425527E4C9AD3C07EE323287D', X'BC9150A425527E4C9AD3C07EE323287D', 0, NULL, NULL, 2, NULL, 0, 'Preheat grill for medium-high heat. (You can use a grill pan for indoor grilling as well.)');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'17D580D68D4AB1468F1082686E7516C7', X'BC9150A425527E4C9AD3C07EE323287D', X'BC9150A425527E4C9AD3C07EE323287D', 1, NULL, NULL, 2, NULL, 0, 'In a large bowl, combine ground lamb, chopped onion, bread crumbs, chopped mint, egg, garlic, cumin, black pepper, and Aleppo pepper (or pepper flakes).');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F9B3B25261496846B07EA84D9E2B3DA5', X'BC9150A425527E4C9AD3C07EE323287D', X'BC9150A425527E4C9AD3C07EE323287D', 2, NULL, NULL, 2, NULL, 0, 'Do not add salt at this point. Gently mix with hands until all ingredients are well combined. Form into 4 equal-sized patties and place onto a parchment-lined baking sheet.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BC86DA738189F84BB745981A425F9822', X'BC9150A425527E4C9AD3C07EE323287D', X'BC9150A425527E4C9AD3C07EE323287D', 3, NULL, NULL, 2, NULL, 0, 'Using your thumb, make indentations halfway through the center of each patty. This will help the burgers to cook more evenly and not bubble up in the center.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0F3FD586776A3945A0BF7A7BEA1230BF', X'BC9150A425527E4C9AD3C07EE323287D', X'BC9150A425527E4C9AD3C07EE323287D', 4, NULL, NULL, 2, NULL, 0, 'Using folded paper towels and a pair of large grilling tongs (if you are grilling outdoors), oil the grill grates well. Make at least three passes, making sure that the surface is nice and coated.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'68082B7064AA6843ABD58FA0E0A25223', X'BC9150A425527E4C9AD3C07EE323287D', X'BC9150A425527E4C9AD3C07EE323287D', 5, NULL, NULL, 2, NULL, 0, 'Place lamb burger patties onto grill and cook for 4-5 minutes per side, or until meat has reached the desired doneness. Use a large outdoor cooking spatula to flip these burgers. They will be a bit softer than beef burgers, not only because of the texture of ground lamb but because these patties also contain some wet ingredients. If you are grilling indoors, oil up the grilling pan, using the same method as you would outdoors, and use a regular-sized spatula to flip the burgers.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'25D687E318132344B24E7486252AF9AA', X'BC9150A425527E4C9AD3C07EE323287D', X'BC9150A425527E4C9AD3C07EE323287D', 6, NULL, NULL, 2, NULL, 0, 'Once cooked to your liking, remove from grill, place onto a plate and tent with aluminum foil. Brush burger buns with olive oil (vegetable oil work well too), and place oiled side down onto grill grates. Close the lid and let buns toast for 1 to 2 minutes. (If you are not grilling outdoors, toast your buns in a toaster oven for the same amount of time on the medium-low setting.) Remove. If using pita, place into a large piece of aluminum foil, seal edges and place onto the grill for 3 minutes, turn, and warm for another 3-4 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'07E75FE1FB04664499D2EF53B18CC5EB', X'BC9150A425527E4C9AD3C07EE323287D', X'BC9150A425527E4C9AD3C07EE323287D', 7, NULL, NULL, 2, NULL, 0, 'We recommend either a red pepper aioli or yogurt sauce for these burgers. For red pepper aioli, simply add the roasted red peppers to the mayonnaise in a food processor. Add the lemon juice, salt, and olive oil and blend to desired consistency.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7795F4C5CBBB4E4EA230E2E5F1A2BFD5', X'BC9150A425527E4C9AD3C07EE323287D', X'BC9150A425527E4C9AD3C07EE323287D', 8, NULL, NULL, 2, NULL, 0, 'For yogurt sauce, in a bowl add the cucumber, plain yogurt, minced garlic, cilantro, and salt, and stir together, until completely combined.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F956944B7B70194A870F74B006BDEC78', X'BC9150A425527E4C9AD3C07EE323287D', X'BC9150A425527E4C9AD3C07EE323287D', 9, NULL, NULL, 2, NULL, 0, 'Assemble burgers to your liking and add extra items such as red onion and lettuce.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'244A6725CDE25447B6AAF00C6C742346', X'8A78CAA0B8D9BD4FB5548A561D945462', X'8A78CAA0B8D9BD4FB5548A561D945462', 0, NULL, NULL, 2, NULL, 0, 'Sprinkle lamb chops with salt and pepper.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B1A801B578C9CB4998DD6E17C5034557', X'8A78CAA0B8D9BD4FB5548A561D945462', X'8A78CAA0B8D9BD4FB5548A561D945462', 1, NULL, NULL, 2, NULL, 0, 'In a large skillet, heat olive oil over medium-high heat.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'224587BBBC6DC04DAB9DDBC7677FD79C', X'8A78CAA0B8D9BD4FB5548A561D945462', X'8A78CAA0B8D9BD4FB5548A561D945462', 2, NULL, NULL, 2, NULL, 0, 'Lightly coat chops with flour; place in hot skillet. Sear until well browned, turning once. Remove to a plate and set aside. Reduce heat to medium.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F74B3B08D21390479765D0CC5A3261E2', X'8A78CAA0B8D9BD4FB5548A561D945462', X'8A78CAA0B8D9BD4FB5548A561D945462', 3, NULL, NULL, 2, NULL, 0, 'To the skillet add the mushrooms, green onions, garlic, and butter.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9C983F5EFE891E488A9898A3EFAEC6F3', X'8A78CAA0B8D9BD4FB5548A561D945462', X'8A78CAA0B8D9BD4FB5548A561D945462', 4, NULL, NULL, 2, NULL, 0, 'Cook the vegetables, stirring, for 4 minutes. Add beef broth and red wine and simmer until reduced by about 1/3.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FF2B3EEFBCA3DE458050DCA0F09C21E0', X'8A78CAA0B8D9BD4FB5548A561D945462', X'8A78CAA0B8D9BD4FB5548A561D945462', 5, NULL, NULL, 2, NULL, 0, 'Add lamb chops to the sauce, cover skillet, and cook for about 15 to 20 minutes, or until lamb chops are done as desired.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'691E829D08314947B6E74324680C74EF', X'2140A4E23DF0CA4F9F55940735E8225A', X'2140A4E23DF0CA4F9F55940735E8225A', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EACD79F24E667D408B1C8F8E5C288AC2', X'2140A4E23DF0CA4F9F55940735E8225A', X'2140A4E23DF0CA4F9F55940735E8225A', 1, NULL, NULL, 2, NULL, 0, 'In a heavy duty zipper lock food storage bag, combine the thyme, rosemary, garlic, onion, oil, vinegar, orange juice, and mustard, and mix well.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3D82AB6F33BF0E4695791D560AAFAF16', X'2140A4E23DF0CA4F9F55940735E8225A', X'2140A4E23DF0CA4F9F55940735E8225A', 2, NULL, NULL, 2, NULL, 0, 'Add the leg of lamb; seal the bag, and turn to coat. Massage the meat through the bag for a bit to help the marinade penetrate into the meat. Put the storage bag into a large roasting pan and marinate for 24 hours in the refrigerator.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7F5AD8923244FB4F919E7D0475AD3A5F', X'2140A4E23DF0CA4F9F55940735E8225A', X'2140A4E23DF0CA4F9F55940735E8225A', 3, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 F.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E8AB3FCBC1FA6D4B8DD92BEC03A23C91', X'2140A4E23DF0CA4F9F55940735E8225A', X'2140A4E23DF0CA4F9F55940735E8225A', 4, NULL, NULL, 2, NULL, 0, 'Remove the lamb from the marinade and place in shallow roasting pan. Sprinkle with the salt and pepper.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'360C263D28AC7F4BB0BD6C1E8A54E08C', X'2140A4E23DF0CA4F9F55940735E8225A', X'2140A4E23DF0CA4F9F55940735E8225A', 5, NULL, NULL, 2, NULL, 0, 'Roast the lamb, uncovered, for 50 to 70 minutes or until an instant-read meat thermometer registers 140 F for medium-rare or 150 F for medium. Make sure the lamb is cooked to a minimum of 140 F for food safety reasons.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'87C79B06E93DDF41A448A35EC730076B', X'2140A4E23DF0CA4F9F55940735E8225A', X'2140A4E23DF0CA4F9F55940735E8225A', 6, NULL, NULL, 2, NULL, 0, 'Baste the lamb with the remaining marinade several times during the time it''s in the oven, using all of the marinade.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EDD8B2AB300A3644B437CB892B173CC7', X'2140A4E23DF0CA4F9F55940735E8225A', X'2140A4E23DF0CA4F9F55940735E8225A', 7, NULL, NULL, 2, NULL, 0, 'Remove the lamb from the oven, cover with foil, and let stand for 15 minutes before serving.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2610E759BF3D584B9BBE8A42A1EF9BCE', X'2140A4E23DF0CA4F9F55940735E8225A', X'2140A4E23DF0CA4F9F55940735E8225A', 8, NULL, NULL, 2, NULL, 0, 'Carve the lamb into slices against the grain.');
INSERT INTO `VWR INTERNAL MeatRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'14B088867BFBFB4B93EFA6264054EB04', X'2140A4E23DF0CA4F9F55940735E8225A', X'2140A4E23DF0CA4F9F55940735E8225A', 9, NULL, NULL, 2, NULL, 0, 'Serve with the sauce that forms in the bottom of the pan.');
