INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'41850C297670A94CB7B5BF3142C22867', X'A3920171322DE74A84930BDFE9B1EAAF', X'A3920171322DE74A84930BDFE9B1EAAF', 0, NULL, NULL, 2, NULL, 0, 'Cut suet. Commercial producers cleanly cut suet in bowl cutters into 1-5 mm pieces. In home production partially frozen suet can be manually cut into 3-5 mm pieces. It can be ground through 1/8 (3 mm) plate, however, particle definition will be poor due to fat smearing.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3504BC912964604F88B4AA2DE608700D', X'A3920171322DE74A84930BDFE9B1EAAF', X'A3920171322DE74A84930BDFE9B1EAAF', 1, NULL, NULL, 2, NULL, 0, 'Chop the onions finely.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'409BB1EA8AB97A4AADCDA509E88C6A29', X'A3920171322DE74A84930BDFE9B1EAAF', X'A3920171322DE74A84930BDFE9B1EAAF', 2, NULL, NULL, 2, NULL, 0, 'Mix all ingredients with blood. Where dried blood is used, water is added to rehydrate the blood; this method is used when large volumes of pudding are required as it is not possible to transport such volumes of liquid blood. The mixture should exhibit a thick porridge-like consistency which can still be stirred but with some effort.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'284CA68B254FEB45B5BDDBA0A70D66F0', X'A3920171322DE74A84930BDFE9B1EAAF', X'A3920171322DE74A84930BDFE9B1EAAF', 3, NULL, NULL, 2, NULL, 0, 'Stuff the mixture into large sausage casings using a stuffer. The skins are made from cellulose which is an organic compound produced for industrial use. Synthetic casings are used for the production of Stornoway Black Pudding due to large volumes required for commercial manufacture; however, natural casings may also be used in small scale production and subject to availability of animal intestines (sheep, cow, pig).');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AE96B8806CA765478F56B303B70FC715', X'A3920171322DE74A84930BDFE9B1EAAF', X'A3920171322DE74A84930BDFE9B1EAAF', 4, NULL, NULL, 2, NULL, 0, 'Smaller amounts of sausage can be stuffed using a ladle by manually placing the sausage skin onto a large funnel.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C97B1ECD0ED07B4FBFBE30890B7E0229', X'A3920171322DE74A84930BDFE9B1EAAF', X'A3920171322DE74A84930BDFE9B1EAAF', 5, NULL, NULL, 2, NULL, 0, 'The ends are then either hand-tied with string or clipped by machine.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E4E56806DBA682458D6B5D581CB89A9B', X'A3920171322DE74A84930BDFE9B1EAAF', X'A3920171322DE74A84930BDFE9B1EAAF', 6, NULL, NULL, 2, NULL, 0, 'The puddings are then boiled or steamed for up to 2.5 hours at temperatures between 70-86° C (158-186° F).');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'27D0E3ECBBB7ED4EB4924E06507408B7', X'A3920171322DE74A84930BDFE9B1EAAF', X'A3920171322DE74A84930BDFE9B1EAAF', 7, NULL, NULL, 2, NULL, 0, 'Once cooked, each pudding is hung individually on a rack to cool gradually to an ambient temperature of 12° C (53° F), this can take between 2.5 - 4 hours. They are then placed in a chiller and stored at a temperature of 2° C (35° F).');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AFC7F7702918DE44BF81063920404563', X'7BF902B4020435489A615CE8FDFDF0AA', X'7BF902B4020435489A615CE8FDFDF0AA', 0, NULL, NULL, 2, NULL, 0, 'Dice back fat into 1/4 (6 mm) cubes.');
