INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7C7FEBB16B4C3C48996CDB58D244E72E', X'50BE1D10750F4143B7C8AEC75C5335D9', X'50BE1D10750F4143B7C8AEC75C5335D9', 0, NULL, NULL, 2, NULL, 0, 'Soak oatmeal in water. Drain.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2E32CF12503E874FADE1331F30B14C7C', X'50BE1D10750F4143B7C8AEC75C5335D9', X'50BE1D10750F4143B7C8AEC75C5335D9', 1, NULL, NULL, 2, NULL, 0, 'Cook barley in water for 30 minutes. Drain.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2D1A7F3CF8B69D498FCFEB7561DDEFA8', X'50BE1D10750F4143B7C8AEC75C5335D9', X'50BE1D10750F4143B7C8AEC75C5335D9', 2, NULL, NULL, 2, NULL, 0, 'Dice fat into 1/4 (6 mm) cubes.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9F1B78C1934B954291D7AFFFC7BD40EA', X'50BE1D10750F4143B7C8AEC75C5335D9', X'50BE1D10750F4143B7C8AEC75C5335D9', 3, NULL, NULL, 2, NULL, 0, 'Finely dice onions and fry in a little fat until glassy. Mix with oats and barley.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1A92577447BBF5468C59D05C40BBEC1A', X'50BE1D10750F4143B7C8AEC75C5335D9', X'50BE1D10750F4143B7C8AEC75C5335D9', 4, NULL, NULL, 2, NULL, 0, 'Mix blood with salt, add spices and cream. Pour over barley and oats. Mix all well together.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DFEE8DA87433D1429C3737E79E773AC1', X'50BE1D10750F4143B7C8AEC75C5335D9', X'50BE1D10750F4143B7C8AEC75C5335D9', 5, NULL, NULL, 2, NULL, 0, 'Stuff loosely into 32 - 36 mm hog casings.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5A020734738BF646877096EB351B18F5', X'50BE1D10750F4143B7C8AEC75C5335D9', X'50BE1D10750F4143B7C8AEC75C5335D9', 6, NULL, NULL, 2, NULL, 0, 'Cook in water at 80° C (176° F) for about 40 minutes. Store in refrigerator. The sausage can be eaten cold or fried.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'210B9C0157ECEA4CBD5A93B6937647C5', X'8C9654745F11544CB619AADC83C698E7', X'8C9654745F11544CB619AADC83C698E7', 0, NULL, NULL, 2, NULL, 0, 'Chop the fat into 1/8 (3 mm) cubes.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'64E84C5082B16B409629AA6440E4E8D0', X'8C9654745F11544CB619AADC83C698E7', X'8C9654745F11544CB619AADC83C698E7', 1, NULL, NULL, 2, NULL, 0, 'Pre-cook liver and kidneys in hot water. Drain and cool.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8EDB7C194DF5BC4C96C104298F45C27F', X'8C9654745F11544CB619AADC83C698E7', X'8C9654745F11544CB619AADC83C698E7', 2, NULL, NULL, 2, NULL, 0, 'Chop liver and kidneys into 1/8 (3 mm) cubes.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'98DE353CDF74AB48A3F03961D1EC4FE0', X'8C9654745F11544CB619AADC83C698E7', X'8C9654745F11544CB619AADC83C698E7', 3, NULL, NULL, 2, NULL, 0, 'Chop onions finely.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'105CDF297DDA694D84B882D298302BE6', X'8C9654745F11544CB619AADC83C698E7', X'8C9654745F11544CB619AADC83C698E7', 4, NULL, NULL, 2, NULL, 0, 'Mix meat, fat, milk, lamb stock, oats, flour, onions and spices together.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CA89472BFAC01745865B52A463245B9E', X'8C9654745F11544CB619AADC83C698E7', X'8C9654745F11544CB619AADC83C698E7', 5, NULL, NULL, 2, NULL, 0, 'Mix everything with blood.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B5FF88276C08234E8325E5DAE5933AD6', X'8C9654745F11544CB619AADC83C698E7', X'8C9654745F11544CB619AADC83C698E7', 6, NULL, NULL, 2, NULL, 0, 'Stuff loosely into sheep stomach.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D4967D4F341F464289D9901725C0BCA0', X'8C9654745F11544CB619AADC83C698E7', X'8C9654745F11544CB619AADC83C698E7', 7, NULL, NULL, 2, NULL, 0, 'Cook in water at 80° C (176° F) until the meat reaches 72° C (160° F) internal temperature.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F1ACE6538D8E394D9E5F16DACF9B4679', X'11288C7D3CD3F141A72D137FC8FA0366', X'11288C7D3CD3F141A72D137FC8FA0366', 0, NULL, NULL, 2, NULL, 0, 'Grind meat through 1/4 (6 mm) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'282B6A1C0B7D7F43AD0FD1817B813D39', X'11288C7D3CD3F141A72D137FC8FA0366', X'11288C7D3CD3F141A72D137FC8FA0366', 1, NULL, NULL, 2, NULL, 0, 'Dice partially frozen back fat into 1/2 (12 mm) cubes.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9441E0CE9261054C97FA5032B0C77BDF', X'11288C7D3CD3F141A72D137FC8FA0366', X'11288C7D3CD3F141A72D137FC8FA0366', 2, NULL, NULL, 2, NULL, 0, 'Mix ground meat with salt and spices.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A1C86B8274FB804CA7687F8CA6F48E4B', X'11288C7D3CD3F141A72D137FC8FA0366', X'11288C7D3CD3F141A72D137FC8FA0366', 3, NULL, NULL, 2, NULL, 0, 'Add fat cubes and mix again.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'343F78E3A2D6C74993040AB0612E29C5', X'11288C7D3CD3F141A72D137FC8FA0366', X'11288C7D3CD3F141A72D137FC8FA0366', 4, NULL, NULL, 2, NULL, 0, 'Stuff into beef (or pork) stomach.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1E59EC090A51B94FB8DE146118B3C7A6', X'11288C7D3CD3F141A72D137FC8FA0366', X'11288C7D3CD3F141A72D137FC8FA0366', 5, NULL, NULL, 2, NULL, 0, 'Cook in hot water at 80° C (176° F) until the sausage reaches 72° C (160° F) internal temperature what should take about 2 hours. Partially cool in cold water, finish cooling in the air.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1A2958BD3C1CCE48A681042E600FC0A6', X'11288C7D3CD3F141A72D137FC8FA0366', X'11288C7D3CD3F141A72D137FC8FA0366', 6, NULL, NULL, 2, NULL, 0, 'Refrigerate. Sausage can be eaten cold or hot.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B30621787C43974984D8E7DA9A25D726', X'7C75C6BCE20B5148B9EE427496BE4CFB', X'7C75C6BCE20B5148B9EE427496BE4CFB', 0, NULL, NULL, 2, NULL, 0, 'Rinse skins, lungs and all meats in cold running water.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E23A9EC9F2835845A3233F7D5D0FCAB3', X'7C75C6BCE20B5148B9EE427496BE4CFB', X'7C75C6BCE20B5148B9EE427496BE4CFB', 1, NULL, NULL, 2, NULL, 0, 'Boil all meats:skins at 85-95º C (185º -203º F) until soft. if lungs are used, remove harder connective tissues from lungs. poach jowls at 85º C (185º-203º F) until semi-soft. spread all meats on the table to cool down.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1268565F33407545AF2F62316C2345D5', X'7C75C6BCE20B5148B9EE427496BE4CFB', X'7C75C6BCE20B5148B9EE427496BE4CFB', 2, NULL, NULL, 2, NULL, 0, 'Bring meat stock to boil then insert buckwheat grouts or barley and cook for about 30 minutes, stirring oftencook buckwheat groats in 160 milliliters of meat stock until semi-soft cook barley groats in 200 milliliters of meat stock until semi-soft. cover the pot with a lid and leave cooked groats 60 minutes.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2F5319734B0C304E97F43412F15093BE', X'7C75C6BCE20B5148B9EE427496BE4CFB', X'7C75C6BCE20B5148B9EE427496BE4CFB', 3, NULL, NULL, 2, NULL, 0, 'Grind all meats and fats through 8 mm (3/8`) plate. Grind bacon, fat and skins with 3 mm (3/16`) plate. If jowls are used, they can be cut with a knife into 5 mm (1/4`) cubes (no grinding necessary).');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'09B89F499C368940B783EADED2336FE3', X'7C75C6BCE20B5148B9EE427496BE4CFB', X'7C75C6BCE20B5148B9EE427496BE4CFB', 4, NULL, NULL, 2, NULL, 0, 'Spread all meats and fats on the bottom of a mixing tub. Spread hot groats evenly on top of meats without disturbing them. Pour blood on top of groats and mix together. Add salt and spices and mix all well together.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'63CE6CCF0B1B50419B69E8D9CB471CE3', X'7C75C6BCE20B5148B9EE427496BE4CFB', X'7C75C6BCE20B5148B9EE427496BE4CFB', 5, NULL, NULL, 2, NULL, 0, 'Stuff warm sausage mass loosely into hog casings. Make 12` long links, form pairs.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'00D2FCAB8A70A84787307860028DB355', X'7C75C6BCE20B5148B9EE427496BE4CFB', X'7C75C6BCE20B5148B9EE427496BE4CFB', 6, NULL, NULL, 2, NULL, 0, 'Insert sausages into boiling water but poach at 80º C (176º F) for about 30 minutes to reach internal sausage temperature of 68º-72º C (154º-161º F).');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E1E4EF8362A74B409F9BEA8BA3F58CA6', X'7C75C6BCE20B5148B9EE427496BE4CFB', X'7C75C6BCE20B5148B9EE427496BE4CFB', 7, NULL, NULL, 2, NULL, 0, 'Remove from water and cool down to 6º C (42º F)');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E24B29FA30BEB24EAD24EEE79BEF750A', X'7C75C6BCE20B5148B9EE427496BE4CFB', X'7C75C6BCE20B5148B9EE427496BE4CFB', 8, NULL, NULL, 2, NULL, 0, 'Refrigerate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'672E08D772BB9540987F4A6879617E8B', X'57AF73EDE3CEDC4997AE59DEEE08CCEE', X'57AF73EDE3CEDC4997AE59DEEE08CCEE', 0, NULL, NULL, 2, NULL, 0, 'Poach meats (except liver) in small amount of water: skins at 203° F (95° C) until soft;');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B690275297EEC242A70DE4CB08AE5F00', X'57AF73EDE3CEDC4997AE59DEEE08CCEE', X'57AF73EDE3CEDC4997AE59DEEE08CCEE', 1, NULL, NULL, 2, NULL, 0, 'other meats at 176-185° F (80-85° C) until soft.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FF46DE2FAF893C4DA9FA653B687A0011', X'57AF73EDE3CEDC4997AE59DEEE08CCEE', X'57AF73EDE3CEDC4997AE59DEEE08CCEE', 2, NULL, NULL, 2, NULL, 0, 'Spread meats apart on a flat surface to cool. Save stock.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C81E7F1D172B4F4B9182DA7587311631', X'57AF73EDE3CEDC4997AE59DEEE08CCEE', X'57AF73EDE3CEDC4997AE59DEEE08CCEE', 3, NULL, NULL, 2, NULL, 0, 'Boil buckwheat or barley grouts in left over stock or water until semi-soft. Boil grouts for about ½ hr continuously stirring. After boiling leave for ½ to 1 hr covered.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B8F4448079C75E4CB2C5551FFAC3F799', X'57AF73EDE3CEDC4997AE59DEEE08CCEE', X'57AF73EDE3CEDC4997AE59DEEE08CCEE', 4, NULL, NULL, 2, NULL, 0, 'Grind pork through 10 mm plate. Grind boiled skins through 3 mm plate. Grind raw liver through 3 mm plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'00357169FDFFE44AABE5609052BD9303', X'57AF73EDE3CEDC4997AE59DEEE08CCEE', X'57AF73EDE3CEDC4997AE59DEEE08CCEE', 5, NULL, NULL, 2, NULL, 0, 'Add salt and spices to ground meats, mix barley or buckwheat grouts with blood and then mix everything well together.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'53A2DA098CD4A9418A2CF3BE19AD4114', X'57AF73EDE3CEDC4997AE59DEEE08CCEE', X'57AF73EDE3CEDC4997AE59DEEE08CCEE', 6, NULL, NULL, 2, NULL, 0, 'Stuff mixture loosely into pork middles, beef rounds or synthetic casings.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'37A9DE788C25664189E6CDC0EA3015B6', X'57AF73EDE3CEDC4997AE59DEEE08CCEE', X'57AF73EDE3CEDC4997AE59DEEE08CCEE', 7, NULL, NULL, 2, NULL, 0, 'Place sausages in boiling water and poach at 176-185° F (80-85° C) for about 60-90 min until internal meat temperature reaches 68-70° C (154-158° F).');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D89C50FB00F0CE48B5363EC49828FE0C', X'57AF73EDE3CEDC4997AE59DEEE08CCEE', X'57AF73EDE3CEDC4997AE59DEEE08CCEE', 8, NULL, NULL, 2, NULL, 0, 'Place sausages for 5 min in cold running water, then spread them on a flat surface to cool down below 6° C (43° F).');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'77EF9C467F457E4DBA09DF5720B8641C', X'57AF73EDE3CEDC4997AE59DEEE08CCEE', X'57AF73EDE3CEDC4997AE59DEEE08CCEE', 9, NULL, NULL, 2, NULL, 0, 'Store in refrigerator.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4DC1FF3F9393594A85E00B06397F8875', X'134400753E680447B6E0917C0D3DE8BA', X'134400753E680447B6E0917C0D3DE8BA', 0, NULL, NULL, 2, NULL, 0, 'Poach meats in small amount of water: heads, lungs and bloody meats at 80-85° C (176-185° F); skins at 95° C (203° F) until soft. After poaching remove gristle from lungs and meat from heads. Spread meats apart on a flat surface to cool. Save stock.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5F7D010BCF7B7D4B801EA3DA0F0E6281', X'134400753E680447B6E0917C0D3DE8BA', X'134400753E680447B6E0917C0D3DE8BA', 1, NULL, NULL, 2, NULL, 0, 'Grind all meats through 2 mm plate. Soak bread crumbs (or dry bread rolls) in 30% (in relation to the weight of the rolls) of left over poaching stock.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9C6B71524C126F488FAA35BA905BA1D3', X'134400753E680447B6E0917C0D3DE8BA', X'134400753E680447B6E0917C0D3DE8BA', 2, NULL, NULL, 2, NULL, 0, 'Add salt and spices to ground meats and then mix in bowl cutter together with blood and soaked rolls. Alternatively, you can use food processor or grind meats with a small plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'14CD8EFF37B99C4CB3EE9B1C2E93E563', X'134400753E680447B6E0917C0D3DE8BA', X'134400753E680447B6E0917C0D3DE8BA', 3, NULL, NULL, 2, NULL, 0, 'Stuffing materials: pork hog casings, butchers twine. Stuff casings loosely and form twisted links 8-12 cm (3-5) each. Make pairs and tie the ends with twine. Remove air pockets with a needle.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'53A31ADAD61FBA478168176C82EC2513', X'134400753E680447B6E0917C0D3DE8BA', X'134400753E680447B6E0917C0D3DE8BA', 4, NULL, NULL, 2, NULL, 0, 'Immerse sausages in boiling water and poach at 80-82° C (176-180° F) for 25-30 min until internal meat temperature reaches 68-70° C (154-158° F).');
