INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F4D73E761C5E1B40AD2B6A90EB7E76A8', X'BD3BD126B74F80429F0424FA4E9B1434', X'BD3BD126B74F80429F0424FA4E9B1434', 2, NULL, NULL, 2, NULL, 0, 'Mix meat, fat and all ingredients together. Hold for 24 hours in refrigerator.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'263D2F0926A766479CBFF59524C3A5DA', X'BD3BD126B74F80429F0424FA4E9B1434', X'BD3BD126B74F80429F0424FA4E9B1434', 3, NULL, NULL, 2, NULL, 0, 'Stuff into 32 mm pork casings.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AE088322C3B196419C92E85F3B842ECB', X'BD3BD126B74F80429F0424FA4E9B1434', X'BD3BD126B74F80429F0424FA4E9B1434', 4, NULL, NULL, 2, NULL, 0, 'Apply a thin cold smoke 18° C (64° F) for 10 days. Traditionally, the sausage was hung above the wood fired kitchen stove where it acquired different flavors and smoke from everyday cooking.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F1B115E1A9BFFD408A6406B12502481E', X'BD3BD126B74F80429F0424FA4E9B1434', X'BD3BD126B74F80429F0424FA4E9B1434', 5, NULL, NULL, 2, NULL, 0, 'Dry at 12-15° C (53-59° F), 75-85% humidity for 2 weeks.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2911BEE0522B604ABE83245FEEA32854', X'BD3BD126B74F80429F0424FA4E9B1434', X'BD3BD126B74F80429F0424FA4E9B1434', 6, NULL, NULL, 2, NULL, 0, 'Store in cool, not humid and well ventilated place. The sausages keep well for up to 6 months.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5E913CC9936BA547ACD64042BEF09728', X'B04F07897572364BB63F70186D1D7FB6', X'B04F07897572364BB63F70186D1D7FB6', 0, NULL, NULL, 2, NULL, 0, 'Peel onions and cook in water for 60 minutes. Drain, cut in half and let cool.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6550626506E3F842A5528442E33B8119', X'B04F07897572364BB63F70186D1D7FB6', X'B04F07897572364BB63F70186D1D7FB6', 1, NULL, NULL, 2, NULL, 0, 'Grind meat, fat and onions through 6 mm (1/4) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DD19A07F3C70C344A23A49E9E62375E0', X'B04F07897572364BB63F70186D1D7FB6', X'B04F07897572364BB63F70186D1D7FB6', 2, NULL, NULL, 2, NULL, 0, 'Mix all materials with salt, spices and blood together.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'04AC3D0F231E2443A293311EB698E992', X'B04F07897572364BB63F70186D1D7FB6', X'B04F07897572364BB63F70186D1D7FB6', 3, NULL, NULL, 2, NULL, 0, 'Stuff into 32-34 mm beef or pork casings. Make rings about 35-40 cm (13-16) long or form links about 10 cm (4) long.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3019A9DA267174429CB84F2BEA403665', X'B04F07897572364BB63F70186D1D7FB6', X'B04F07897572364BB63F70186D1D7FB6', 4, NULL, NULL, 2, NULL, 0, 'Cook in water at 80° C (176° F) for 30 minutes.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9F48166F3FE4D740A667D4D075649447', X'B04F07897572364BB63F70186D1D7FB6', X'B04F07897572364BB63F70186D1D7FB6', 5, NULL, NULL, 2, NULL, 0, 'Cool in air and refrigerate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'764EAC25C141E94D9BEDBF42B34DCC08', X'CBECAB72706E3C4BB9CC0AA0E78A1D5B', X'CBECAB72706E3C4BB9CC0AA0E78A1D5B', 0, NULL, NULL, 2, NULL, 0, 'Place lungs and skins in a boiling water and cook at 95-100° C (203-212° F) for 45 minutes. Cool and grind through 5 mm (1/4) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'555DDEE571AD5D40A35CCF05EDF6CDDC', X'CBECAB72706E3C4BB9CC0AA0E78A1D5B', X'CBECAB72706E3C4BB9CC0AA0E78A1D5B', 1, NULL, NULL, 2, NULL, 0, 'Place skins in a boiling water and cook at 95-100° C (203-212° F) until soft.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AB7089859064214894F0F6E0103867F2', X'CBECAB72706E3C4BB9CC0AA0E78A1D5B', X'CBECAB72706E3C4BB9CC0AA0E78A1D5B', 2, NULL, NULL, 2, NULL, 0, 'Grind skins when still warm through 5 mm (1/4) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'484D2B1303CD9A4A9DB77BFC473CE52A', X'CBECAB72706E3C4BB9CC0AA0E78A1D5B', X'CBECAB72706E3C4BB9CC0AA0E78A1D5B', 3, NULL, NULL, 2, NULL, 0, 'Grind back fat through 10 mm (3/8) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ABFDEBCF56250C4791574814765AB3AF', X'CBECAB72706E3C4BB9CC0AA0E78A1D5B', X'CBECAB72706E3C4BB9CC0AA0E78A1D5B', 4, NULL, NULL, 2, NULL, 0, 'Mix ground meat, fat, blood and spices together.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C51932D1843BE449BA992DD476C68D90', X'CBECAB72706E3C4BB9CC0AA0E78A1D5B', X'CBECAB72706E3C4BB9CC0AA0E78A1D5B', 5, NULL, NULL, 2, NULL, 0, 'Stuff into 34-38 mm pork casings linking sausages every 35-40 cm (14-16).');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'558596034DBC44458790B89CB63A653A', X'CBECAB72706E3C4BB9CC0AA0E78A1D5B', X'CBECAB72706E3C4BB9CC0AA0E78A1D5B', 6, NULL, NULL, 2, NULL, 0, 'Cook in water at 80° C (176° F) for 45-60 minutes.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C4EC6A1F911943449AB844D6BD25B12F', X'CBECAB72706E3C4BB9CC0AA0E78A1D5B', X'CBECAB72706E3C4BB9CC0AA0E78A1D5B', 7, NULL, NULL, 2, NULL, 0, 'Keep refrigerated. The sausage is ready to eat without cooking, however it is often fried.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D44E777B052F0147B24092804473D385', X'B9F8EB42ACE47B4FBC39EFF909BBAFD1', X'B9F8EB42ACE47B4FBC39EFF909BBAFD1', 0, NULL, NULL, 2, NULL, 0, 'Cut the fat into 25 mm (1) pieces and cook in hot water, at 90° C (194° F) for 20 minutes. Immerse in cold water to cool.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8A77345BE3CA4444826842355D10D176', X'B9F8EB42ACE47B4FBC39EFF909BBAFD1', X'B9F8EB42ACE47B4FBC39EFF909BBAFD1', 1, NULL, NULL, 2, NULL, 0, 'Grind the fat through 8 mm (3/8) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5B1174BF6E68404C8DDB807DF1242A02', X'B9F8EB42ACE47B4FBC39EFF909BBAFD1', X'B9F8EB42ACE47B4FBC39EFF909BBAFD1', 2, NULL, NULL, 2, NULL, 0, 'Peel onions then cook them in water at 90° C (194° F) for 5 minutes. Chop the onions finely then fry them on low heat in some lard. Stir the onions often and fry for 60 minutes, do not let them become brown.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FD840EE9A397924E8E3659B79D235043', X'B9F8EB42ACE47B4FBC39EFF909BBAFD1', X'B9F8EB42ACE47B4FBC39EFF909BBAFD1', 3, NULL, NULL, 2, NULL, 0, 'Precook head meat or skins in water at 90° C (194° F) until softer.');
