INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6892EA2E0F44E944946230C52AEE95B5', X'657F20E6C4309F4ABE5EA4C6C3AE6E3E', X'657F20E6C4309F4ABE5EA4C6C3AE6E3E', 7, NULL, NULL, 2, NULL, 0, 'Refrigerate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'813172919993C847B6A3F8B049F8C3E0', X'987F1761042F75478F188C4D4DED18E7', X'987F1761042F75478F188C4D4DED18E7', 0, NULL, NULL, 2, NULL, 0, 'Cut meat and fat into 1 (25 mm) pieces.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'49762E0170C97240A48FD2A552A49641', X'987F1761042F75478F188C4D4DED18E7', X'987F1761042F75478F188C4D4DED18E7', 1, NULL, NULL, 2, NULL, 0, 'Finely dice the onions.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3A6803548661E545ABA69EDB1EB90DF7', X'987F1761042F75478F188C4D4DED18E7', X'987F1761042F75478F188C4D4DED18E7', 2, NULL, NULL, 2, NULL, 0, 'Dissolve the chopped onions and all ingredients (except the blood and the flour) in red wine and season the meat. Place for up to 3 days in refrigerator stirring the mixture daily.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8043DC98BD53134F9DC18C9666D58539', X'987F1761042F75478F188C4D4DED18E7', X'987F1761042F75478F188C4D4DED18E7', 3, NULL, NULL, 2, NULL, 0, 'Mix the mixture with the blood and flour.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'86743D61AA5E394F9320BCB7F3EDAF51', X'987F1761042F75478F188C4D4DED18E7', X'987F1761042F75478F188C4D4DED18E7', 4, NULL, NULL, 2, NULL, 0, 'Stuff firmly into 36 mm hog casings and tie the ends together to make a loop about 1 (30 cm) long.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'25B5FBD089A5584B950899AA04BFCDBC', X'987F1761042F75478F188C4D4DED18E7', X'987F1761042F75478F188C4D4DED18E7', 5, NULL, NULL, 2, NULL, 0, 'Apply a thin cold smoke at 18º C (64º F) for 15 days using oak wood. Smoking need not to be continuous as long as the temperature inside of the smokehouse stays below 18° C.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'48A6A738A441514DAC6E8E2E1C1636F7', X'5156FCF4A3F0E34B9440FC75B967004E', X'5156FCF4A3F0E34B9440FC75B967004E', 0, NULL, NULL, 2, NULL, 0, 'Cook rice, but do not overcook.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'17EDD6A77E765649B5BBE9D3D68E37BF', X'5156FCF4A3F0E34B9440FC75B967004E', X'5156FCF4A3F0E34B9440FC75B967004E', 1, NULL, NULL, 2, NULL, 0, 'Grind fat or lard through 3 mm (1/8`) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1E0CE11519AD8E4C96F4095DA9F651E5', X'5156FCF4A3F0E34B9440FC75B967004E', X'5156FCF4A3F0E34B9440FC75B967004E', 2, NULL, NULL, 2, NULL, 0, 'Mix rice, blood, fat and spices together.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CFD8B1FA1A31AA4FB52ED455783BA4A8', X'5156FCF4A3F0E34B9440FC75B967004E', X'5156FCF4A3F0E34B9440FC75B967004E', 3, NULL, NULL, 2, NULL, 0, 'Stuff into 22-26 mm sheep casings forming long loops.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E4421CDE3A61CD41B3CC812CA132C785', X'5156FCF4A3F0E34B9440FC75B967004E', X'5156FCF4A3F0E34B9440FC75B967004E', 4, NULL, NULL, 2, NULL, 0, 'Cook delgadillas in hot tomato sauce for 15 minutes. Serve hot with bread and poached or fried eggs, noodles or potatoes.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'30D5B21D07171C41A84B4D5DEA81BFE3', X'6756D438CF7C324AABAD4DEEF56A171A', X'6756D438CF7C324AABAD4DEEF56A171A', 0, NULL, NULL, 2, NULL, 0, 'Grind pork through 1/4 (6 mm) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'25CC1043B46FBA4AB63232A17EB23490', X'6756D438CF7C324AABAD4DEEF56A171A', X'6756D438CF7C324AABAD4DEEF56A171A', 1, NULL, NULL, 2, NULL, 0, 'Chop green onions, rice paddy and cilantro leaves.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'721B324D1D56EC42B0B416C8ACEA57F0', X'6756D438CF7C324AABAD4DEEF56A171A', X'6756D438CF7C324AABAD4DEEF56A171A', 2, NULL, NULL, 2, NULL, 0, 'Mix everything together.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8079E93DA108BD4B8DCABB425C17706B', X'6756D438CF7C324AABAD4DEEF56A171A', X'6756D438CF7C324AABAD4DEEF56A171A', 3, NULL, NULL, 2, NULL, 0, 'Stuff into 36 mm hog casings, tie off into links 6 (15 cm) long.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'21981A59471CD842997394C7E4FC97CF', X'6756D438CF7C324AABAD4DEEF56A171A', X'6756D438CF7C324AABAD4DEEF56A171A', 4, NULL, NULL, 2, NULL, 0, 'Cook in water at 80 C (176 F) for 35 minutes.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F3F66FF975E6FA4E8F44ED1368CBE3C5', X'6756D438CF7C324AABAD4DEEF56A171A', X'6756D438CF7C324AABAD4DEEF56A171A', 5, NULL, NULL, 2, NULL, 0, 'Place in cold water for 10 minutes. Remove, dry briefly and refrigerate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'96C1C108BF883E488C8C54B16BF6EB0E', X'166055E3D0E75E409916CD92E8E6CA69', X'166055E3D0E75E409916CD92E8E6CA69', 0, NULL, NULL, 2, NULL, 0, 'Add salt to blood and stir it.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'76B85322CBE69143B742C3A851720870', X'166055E3D0E75E409916CD92E8E6CA69', X'166055E3D0E75E409916CD92E8E6CA69', 1, NULL, NULL, 2, NULL, 0, 'Mix blood with cream, then add bread crumbs and other ingredients. Mix all together well.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6432984B6694DE439E5F62C421373BBA', X'166055E3D0E75E409916CD92E8E6CA69', X'166055E3D0E75E409916CD92E8E6CA69', 2, NULL, NULL, 2, NULL, 0, 'Stuff into largest hog casings (38 - 42 mm), pork stomachs or beef bungs.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'05FD248EFB1131499AE1392F8B4CF832', X'166055E3D0E75E409916CD92E8E6CA69', X'166055E3D0E75E409916CD92E8E6CA69', 3, NULL, NULL, 2, NULL, 0, 'Place sausages into boiling water and poach at 80º - 85º C (176º - 185º F) for about 60 - 90 minutes to reach internal sausage temperature of 68º - 70º C (154º - 158º F). Stomach will require longer cooking time. When sausage casings are not available bake in oven like a meat loaf. Put into greased glass pan and bake at 300º - 350º F (149º - 177º C) for about 1 hour.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E9EAFFF8879C1E48A78D2F2BAC26DEE0', X'166055E3D0E75E409916CD92E8E6CA69', X'166055E3D0E75E409916CD92E8E6CA69', 4, NULL, NULL, 2, NULL, 0, 'Cool and keep in refrigerator.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3223C23DC8250249A3440BA32DBF4FF6', X'166055E3D0E75E409916CD92E8E6CA69', X'166055E3D0E75E409916CD92E8E6CA69', 5, NULL, NULL, 2, NULL, 0, 'Drisheen is sliced and either fried or grilled, often with bacon, eggs and other sausages.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'975D771B014C84499617F3209D7E9A8F', X'020222E95385C04FB3D40000FCC2B6C7', X'020222E95385C04FB3D40000FCC2B6C7', 0, NULL, NULL, 2, NULL, 0, 'Cook cabbage in water until leaves can be separated. This makes them soft and pliable.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AAB71137158AE94489DCC454EBDC1EA6', X'020222E95385C04FB3D40000FCC2B6C7', X'020222E95385C04FB3D40000FCC2B6C7', 1, NULL, NULL, 2, NULL, 0, 'Grind back fat through 3 mm (1/8`) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8479B99B5596104C8B53A2241252CADA', X'020222E95385C04FB3D40000FCC2B6C7', X'020222E95385C04FB3D40000FCC2B6C7', 2, NULL, NULL, 2, NULL, 0, 'In a mixing bowl place fat, chopped onions, parsley, spices and flours. Mix all together and start adding slowly blood until a dough like texture is obtained.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AB90A9D686E9F348B916C9F8961D2457', X'020222E95385C04FB3D40000FCC2B6C7', X'020222E95385C04FB3D40000FCC2B6C7', 3, NULL, NULL, 2, NULL, 0, 'Shape the sausage mass into balls. Place the ball on a leaf, then wrapped the leaf around. Lace the stuffed sausage with twine all around.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BF5249C839390E459C3A33BA4CE6BE81', X'020222E95385C04FB3D40000FCC2B6C7', X'020222E95385C04FB3D40000FCC2B6C7', 4, NULL, NULL, 2, NULL, 0, 'Immerse sausages in a boiling water and cook for 90-120 minutes depending on a size. Drain and cool in air.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'68321A049DC8F348AB4C9640ED9A9973', X'020222E95385C04FB3D40000FCC2B6C7', X'020222E95385C04FB3D40000FCC2B6C7', 5, NULL, NULL, 2, NULL, 0, 'Refrigerate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8E03A20B74086B4FB0E283DB391A95E2', X'95463F201F8DA4439F8214F4801CA448', X'95463F201F8DA4439F8214F4801CA448', 0, NULL, NULL, 2, NULL, 0, 'Cut back fat into 8-10 mm (3/8`) pieces.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'00FE5FEB1FEC964FB6F9EAA1FF73F86C', X'95463F201F8DA4439F8214F4801CA448', X'95463F201F8DA4439F8214F4801CA448', 1, NULL, NULL, 2, NULL, 0, 'Mix fat, chopped onion, oregano and chopped parsley together. Add blood and flour and mix again.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'586810D65BCE6448A2E7534FB7B78583', X'95463F201F8DA4439F8214F4801CA448', X'95463F201F8DA4439F8214F4801CA448', 2, NULL, NULL, 2, NULL, 0, 'Stuff into 50 mm pork or 60 mm beef middles.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BE1F46778E42E54BA0761C9871EADBE4', X'95463F201F8DA4439F8214F4801CA448', X'95463F201F8DA4439F8214F4801CA448', 3, NULL, NULL, 2, NULL, 0, 'Cook in water at 80° C (176° F) for 90-120 minutes.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D17576276830FE4A826A9A8903BD6B7E', X'95463F201F8DA4439F8214F4801CA448', X'95463F201F8DA4439F8214F4801CA448', 4, NULL, NULL, 2, NULL, 0, 'To serve, cut into slices and fry until crispy.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'712CEB14726D2743BB8A896C3CCC7376', X'F10D40625CBD1549BE736DF61539C6F7', X'F10D40625CBD1549BE736DF61539C6F7', 0, NULL, NULL, 2, NULL, 0, 'Cover meats with water, add one bay leaf and a few black peppercorns. Bring to a boil, reduce heat and simmer until cooked. Save stock.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'272005D63A974346B5E7478C7303E22F', X'F10D40625CBD1549BE736DF61539C6F7', X'F10D40625CBD1549BE736DF61539C6F7', 1, NULL, NULL, 2, NULL, 0, 'Mix 30 g (1 oz) of rusk with 30 ml (1 oz fl) of meat stock.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'01ABF2200BA9AB4DB110892A7A33D41D', X'F10D40625CBD1549BE736DF61539C6F7', X'F10D40625CBD1549BE736DF61539C6F7', 2, NULL, NULL, 2, NULL, 0, 'Grind boiled meat with 1/4 (6 mm) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'81E50768F4EFFF489CAB547D8C28F71E', X'F10D40625CBD1549BE736DF61539C6F7', X'F10D40625CBD1549BE736DF61539C6F7', 3, NULL, NULL, 2, NULL, 0, 'Mix meat with soaked rusk and spices. Mix blood with dry rusk and add to the meat mixture. Mix all together.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F21B1E65D29E5E4BAC2E4F866A8859FF', X'F10D40625CBD1549BE736DF61539C6F7', X'F10D40625CBD1549BE736DF61539C6F7', 4, NULL, NULL, 2, NULL, 0, 'Stuff into sheep casings.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CDBB36C83B89BE4D96E0E7133ECF3891', X'F10D40625CBD1549BE736DF61539C6F7', X'F10D40625CBD1549BE736DF61539C6F7', 5, NULL, NULL, 2, NULL, 0, 'Cook in water at 80° C (176° F) for 30 minutes.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'256F6F036977E8429D5F7BA4729F80F4', X'F10D40625CBD1549BE736DF61539C6F7', X'F10D40625CBD1549BE736DF61539C6F7', 6, NULL, NULL, 2, NULL, 0, 'Cool in cold water, drain and let the moisture evaporate. Refrigerate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'52DD827799A01A4E9C83949EC3FCA4F1', X'F10D40625CBD1549BE736DF61539C6F7', X'F10D40625CBD1549BE736DF61539C6F7', 7, NULL, NULL, 2, NULL, 0, 'The sausage can be eaten cold, grilled or pan fried.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5C824674EC6CAC4DA4065DF37107A698', X'62B4C5CF3865954A82EADC919359D314', X'62B4C5CF3865954A82EADC919359D314', 0, NULL, NULL, 2, NULL, 0, 'Cook meats and skins in a small amount of water at 95° C (203° F) until soft.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6659F572294FA240A897692C59E0B151', X'62B4C5CF3865954A82EADC919359D314', X'62B4C5CF3865954A82EADC919359D314', 1, NULL, NULL, 2, NULL, 0, 'Chop onions finely and fry in fat until glassy.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'05FDF1E031E8C24FBB30174E30EEC92A', X'62B4C5CF3865954A82EADC919359D314', X'62B4C5CF3865954A82EADC919359D314', 2, NULL, NULL, 2, NULL, 0, 'Cut fat into 5-10 mm (1/4-3/8) cubes. Scald with hot water and drain.');
