INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BF83AC76835CF641A926AD2688D4C956', X'62B4C5CF3865954A82EADC919359D314', X'62B4C5CF3865954A82EADC919359D314', 4, NULL, NULL, 2, NULL, 0, 'Mix ground meats with spices and blood. Add fat cubes and mix again.');
