INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'231A5EBB1282D7428185CAD41DF45A74', X'7CE652724DF7BB4CA44F8BC262EBAC1F', X'7CE652724DF7BB4CA44F8BC262EBAC1F', 7, NULL, NULL, 2, NULL, 0, 'Boil in water at 80º C (176º F) for 60 minutes.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F831A5FB28B66540876173DCC7329BAA', X'7CE652724DF7BB4CA44F8BC262EBAC1F', X'7CE652724DF7BB4CA44F8BC262EBAC1F', 8, NULL, NULL, 2, NULL, 0, 'Immerse in col water for 10 minutes. Drain and finish cooling in air.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8EA8681135879F4B92401CFD3C608E97', X'7CE652724DF7BB4CA44F8BC262EBAC1F', X'7CE652724DF7BB4CA44F8BC262EBAC1F', 9, NULL, NULL, 2, NULL, 0, 'Refrigerate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'774BC74ECF2BC8458A0D862DFD013F34', X'1590B0260814B94FBBF20CF5F739D50A', X'1590B0260814B94FBBF20CF5F739D50A', 0, NULL, NULL, 2, NULL, 0, 'Cook meats in small amount of water: jowls at 80-85° C (176-185° F) until semi-soft; pork skins at 95° C (203° F) until soft. Spread meats apart on a flat surface to cool.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E5C603B506839345AF2F6A354F7F3E3F', X'1590B0260814B94FBBF20CF5F739D50A', X'1590B0260814B94FBBF20CF5F739D50A', 1, NULL, NULL, 2, NULL, 0, 'Cut jowls into 5-8 mm (2-3) cubes, grind cooked pork skins through 3 mm plate. Cut dry bread rolls, soak in blood for 20-30 min and grind through 8 mm plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'83DDCCC400133F4D9112EAB8A5FF8E7E', X'1590B0260814B94FBBF20CF5F739D50A', X'1590B0260814B94FBBF20CF5F739D50A', 2, NULL, NULL, 2, NULL, 0, 'Add salt and spices to ground meats and ground rolls (if used), add blood and stock fat and mix everything well together.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FBC9D347C0EC014DA3B03110BE236E12', X'1590B0260814B94FBBF20CF5F739D50A', X'1590B0260814B94FBBF20CF5F739D50A', 3, NULL, NULL, 2, NULL, 0, 'Stuffing materials: pork hog casings over 36 mm, beef middles up to 40 mm, fibrous casings, butchers twine. Stuff casings loosely and form rings. Tie the ends with twine.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'417D12D1A6C1394BAD6E0D8B4CAE6FB0', X'1590B0260814B94FBBF20CF5F739D50A', X'1590B0260814B94FBBF20CF5F739D50A', 4, NULL, NULL, 2, NULL, 0, 'Immerse sausages in boiling water and poach at 80-82° C (176-180° F) for 30-35 min until internal meat temperature reaches 68-70° C (154-158° F).');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'99611FB5619CBC43A2384377EA121934', X'1590B0260814B94FBBF20CF5F739D50A', X'1590B0260814B94FBBF20CF5F739D50A', 5, NULL, NULL, 2, NULL, 0, 'Immerse sausages for 5 min in cold running water, then hang them to cool down below 6°C (43°F). If no suitable cooling conditions are available, arrangements need to be made to bring the temperature to 12° C (53° F) or less.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AC0E43EE00D1AC45B37F61BCD1F87E30', X'C0143F3B5976AA4398715685F81526F4', X'C0143F3B5976AA4398715685F81526F4', 0, NULL, NULL, 2, NULL, 0, 'Cut fat into 5/8 (10 mm) pieces.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'20D91FAFDA5DC64D96C1BC5A8B66B49B', X'C0143F3B5976AA4398715685F81526F4', X'C0143F3B5976AA4398715685F81526F4', 1, NULL, NULL, 2, NULL, 0, 'Mix fat, blood and spices.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4C46712146E26D4DBE50741F8CE90466', X'C0143F3B5976AA4398715685F81526F4', X'C0143F3B5976AA4398715685F81526F4', 2, NULL, NULL, 2, NULL, 0, 'Stuff into 20-40 mm hog casings making 40 cm long horseshoe-shaped rings, with a cotton string tied or twisted around the ends.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A5BC89F925982A469FEE3081B92D3688', X'C0143F3B5976AA4398715685F81526F4', X'C0143F3B5976AA4398715685F81526F4', 3, NULL, NULL, 2, NULL, 0, 'Smoke for 10 days with oak wood at 18° C (64° F).');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2FC0B9CCCCEDB240BD3E48AB1EDDC405', X'C0143F3B5976AA4398715685F81526F4', X'C0143F3B5976AA4398715685F81526F4', 4, NULL, NULL, 2, NULL, 0, 'Store at a temperature of between 5° C and 15° C. It can be eaten as it is, roasted, fried or boiled. It is used as an ingredient in many of the traditional local dishes, the most famous of which is cozido à portuguesa. It is also often eaten as a starter or as an aperitif.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5F1E28E2817A1041B6A0EEB94E5DD2DB', X'DD9CA0D9423DD74EBABF9E60D4D1F5C4', X'DD9CA0D9423DD74EBABF9E60D4D1F5C4', 0, NULL, NULL, 2, NULL, 0, 'Boil rice, but do not overcook.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F6D4F8C543FF40448C24ED47F62578AE', X'DD9CA0D9423DD74EBABF9E60D4D1F5C4', X'DD9CA0D9423DD74EBABF9E60D4D1F5C4', 1, NULL, NULL, 2, NULL, 0, 'Boil meat and fat trimmings in a little water until soft. Cool and chop into 1/4 (6 mm) pieces.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'374B56DC7AEE3A489CD6D3658A9BAFC2', X'DD9CA0D9423DD74EBABF9E60D4D1F5C4', X'DD9CA0D9423DD74EBABF9E60D4D1F5C4', 2, NULL, NULL, 2, NULL, 0, 'Mix meat, rice, onions and all other ingredients with blood.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FA2DB362AF549E47B1609F54606BABE8', X'DD9CA0D9423DD74EBABF9E60D4D1F5C4', X'DD9CA0D9423DD74EBABF9E60D4D1F5C4', 3, NULL, NULL, 2, NULL, 0, 'Stuff into 36-40 mm hog casings. Tie off into rings.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B9AA26591364344E8A956CCF7ED3F6AF', X'DD9CA0D9423DD74EBABF9E60D4D1F5C4', X'DD9CA0D9423DD74EBABF9E60D4D1F5C4', 4, NULL, NULL, 2, NULL, 0, 'Cook in water at 80° C (176° F) for 35 minutes.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'04D9C8D037F2544484C1B6304E065110', X'DD9CA0D9423DD74EBABF9E60D4D1F5C4', X'DD9CA0D9423DD74EBABF9E60D4D1F5C4', 5, NULL, NULL, 2, NULL, 0, 'Cool and refrigerate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F3FC5D53F34898449C93670BA779FF7B', X'6569E27F1985D24BAD8FB98A54C1A225', X'6569E27F1985D24BAD8FB98A54C1A225', 0, NULL, NULL, 2, NULL, 0, 'Make marinade: wine, water, salt and spices.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B531CAAD29D6C34287238313D08F63E3', X'6569E27F1985D24BAD8FB98A54C1A225', X'6569E27F1985D24BAD8FB98A54C1A225', 1, NULL, NULL, 2, NULL, 0, 'Cut fat into 1/4 (6 mm) cubes. Mix fat with marinade and hold in refrigerator for 12 hours.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B30318BD4755ED44B7180687BBD3E5F1', X'6569E27F1985D24BAD8FB98A54C1A225', X'6569E27F1985D24BAD8FB98A54C1A225', 2, NULL, NULL, 2, NULL, 0, 'Mix fat, red pepper paste (see below) and blood together.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2C68E3543C89D943B759EF55F9FBFFD3', X'6569E27F1985D24BAD8FB98A54C1A225', X'6569E27F1985D24BAD8FB98A54C1A225', 3, NULL, NULL, 2, NULL, 0, 'Stuff into 36 mm hog casings. Tie the ends together to form a 20 (50 cm) long ring.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6223A880C972E14A8ED6142A245793AB', X'6569E27F1985D24BAD8FB98A54C1A225', X'6569E27F1985D24BAD8FB98A54C1A225', 4, NULL, NULL, 2, NULL, 0, 'Cook in water at 80° C (176° F) for 45 minutes.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E23EF955DE71F4468A44C2399ED1CD16', X'6569E27F1985D24BAD8FB98A54C1A225', X'6569E27F1985D24BAD8FB98A54C1A225', 5, NULL, NULL, 2, NULL, 0, 'Immerse sausages in cold water for 5 minutes.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2F21C73D1167004F853DFB3E2F19B3DE', X'6569E27F1985D24BAD8FB98A54C1A225', X'6569E27F1985D24BAD8FB98A54C1A225', 6, NULL, NULL, 2, NULL, 0, 'Cool in air and refrigerate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1B318F9B78AA6D4B9908DA306286B071', X'843E5FFCE12C644D81322A300F05C51A', X'843E5FFCE12C644D81322A300F05C51A', 0, NULL, NULL, 2, NULL, 0, 'Make marinade: wine, water, salt and spices.Cut all meats and fat into 5/8 (10 mm) pieces.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A6B071D3ECA28B409401741BC7888EE2', X'843E5FFCE12C644D81322A300F05C51A', X'843E5FFCE12C644D81322A300F05C51A', 1, NULL, NULL, 2, NULL, 0, 'Mix fat, blood and spices.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DA6DE328F8BD72439EC2E3764A6D2937', X'843E5FFCE12C644D81322A300F05C51A', X'843E5FFCE12C644D81322A300F05C51A', 2, NULL, NULL, 2, NULL, 0, 'Stuff into 20-40 mm hog casings making 40 cm long horseshoe-shaped rings, with a cotton string tied or twisted around the ends.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'70FFDD8BF8E20143A34B7807B8C7AC06', X'843E5FFCE12C644D81322A300F05C51A', X'843E5FFCE12C644D81322A300F05C51A', 3, NULL, NULL, 2, NULL, 0, 'Smoke for 14 days with oak wood at 18° C (64° F).');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'372B6289A676CD438F0BC1304FC4E271', X'843E5FFCE12C644D81322A300F05C51A', X'843E5FFCE12C644D81322A300F05C51A', 4, NULL, NULL, 2, NULL, 0, 'Store at 5-12° C (48-53° F).');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E9A2E4282FDB544098B289629DBF9D2D', X'1175EE025BA5374CAA4685CDDE61F745', X'1175EE025BA5374CAA4685CDDE61F745', 0, NULL, NULL, 2, NULL, 0, 'Peel off onions and chop them finely. Mix them with rice and leave overnight in a suitable container. The rice will absorb onion juice and will increase in volume.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B78A812C609B1D40B1E88959039F460E', X'1175EE025BA5374CAA4685CDDE61F745', X'1175EE025BA5374CAA4685CDDE61F745', 1, NULL, NULL, 2, NULL, 0, 'Dice fat into 1/2 cubes.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'405730F2A8D607478CE686C8B6BC0390', X'1175EE025BA5374CAA4685CDDE61F745', X'1175EE025BA5374CAA4685CDDE61F745', 2, NULL, NULL, 2, NULL, 0, 'Stuff loosely into 32-36 mm hog casings as the rice will still increase in volume during cooking. Make 12 rings.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'681ECD534291AD4895945798006F599A', X'1175EE025BA5374CAA4685CDDE61F745', X'1175EE025BA5374CAA4685CDDE61F745', 3, NULL, NULL, 2, NULL, 0, 'Cook in a hot water at 80° C (176° F) for about 60 minutes. Prick with a needle any sausage that comes to the top to remove air. Dont increase temperature as the casings may burst.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1FF585AAAA805142BDE8A7850E2F2C4E', X'1175EE025BA5374CAA4685CDDE61F745', X'1175EE025BA5374CAA4685CDDE61F745', 4, NULL, NULL, 2, NULL, 0, 'The color should be dark brown-red with white pieces of fat.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'92D46D6D7F0DB34198A7C7FC8B3B3A12', X'99C063B98D89154E8946E1E429F9F366', X'99C063B98D89154E8946E1E429F9F366', 0, NULL, NULL, 2, NULL, 0, 'Cook heart, lungs, stomach, skins in hot water (below boiling point) until soft. Grind through 5 mm (1/4`) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FEBEBAD186E3854A92B685CB98C0925A', X'99C063B98D89154E8946E1E429F9F366', X'99C063B98D89154E8946E1E429F9F366', 1, NULL, NULL, 2, NULL, 0, 'Grind meat and fat trimmings through 5 mm (1/4`) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'24511F5773C2184A90B7D8B8461DA0C8', X'99C063B98D89154E8946E1E429F9F366', X'99C063B98D89154E8946E1E429F9F366', 2, NULL, NULL, 2, NULL, 0, 'Dice hard fat (back fat) into 6 mm (1/4`) cubes.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DC29B35B77BDEC4CBB8BA1C4FD2398A1', X'99C063B98D89154E8946E1E429F9F366', X'99C063B98D89154E8946E1E429F9F366', 3, NULL, NULL, 2, NULL, 0, 'Mix meat, fat and all ingredients together.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D23496D9600D544B886BC11FB1BC80B4', X'99C063B98D89154E8946E1E429F9F366', X'99C063B98D89154E8946E1E429F9F366', 4, NULL, NULL, 2, NULL, 0, 'Stuff into 30-32 mm pork or sheep casings.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F4BB373FEF95D74882AD1DF04C8DD44E', X'99C063B98D89154E8946E1E429F9F366', X'99C063B98D89154E8946E1E429F9F366', 5, NULL, NULL, 2, NULL, 0, 'Dry in a low humidity, cool and well ventilated room, 12-15° C (53-59° F).');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2E78E1917ACDAA4B9499E1021BF88395', X'1CCBFD1FCBE1814A8F82195DD4312B12', X'1CCBFD1FCBE1814A8F82195DD4312B12', 0, NULL, NULL, 2, NULL, 0, 'Grind fat through 1/8 (3 mm) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D349C193E69DDD4BB89312F97934BA09', X'1CCBFD1FCBE1814A8F82195DD4312B12', X'1CCBFD1FCBE1814A8F82195DD4312B12', 1, NULL, NULL, 2, NULL, 0, 'Boil the onions until soft, drain and cut up into little cubes. Cool. Mix onions with blood.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F3085A8273126D47AD2DC018CADED7CE', X'1CCBFD1FCBE1814A8F82195DD4312B12', X'1CCBFD1FCBE1814A8F82195DD4312B12', 2, NULL, NULL, 2, NULL, 0, 'Place a frying pan on a low heat, add ground fat and melt it down, stirring often.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F0E1BB0EAB213C4199679018E8AD258F', X'1CCBFD1FCBE1814A8F82195DD4312B12', X'1CCBFD1FCBE1814A8F82195DD4312B12', 3, NULL, NULL, 2, NULL, 0, 'In a bowl mix onions, liquid fat and spices together.');
