INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0D1F53B004E89345950F062E3BBB9832', X'1CCBFD1FCBE1814A8F82195DD4312B12', X'1CCBFD1FCBE1814A8F82195DD4312B12', 5, NULL, NULL, 2, NULL, 0, 'Cook in water at 80° C (176° F) for 30 minutes.');
