INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'62D7536B9221A140A7365EF7C2BB1160', X'177D4F5714831845A8F03F2F8496D74D', X'177D4F5714831845A8F03F2F8496D74D', 4, NULL, NULL, 2, NULL, 0, 'In a skillet warm up some lard on low heat. Add the minced onions, half salt, minced garlic and one bay leaf. Cook uncovered on on very low heat until onions are glassy but not browned. Remove from the heat and place in a mixing bowl. Let them cool.');
