INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'15706186E57A224094029AC542088B7E', X'177D4F5714831845A8F03F2F8496D74D', X'177D4F5714831845A8F03F2F8496D74D', 9, NULL, NULL, 2, NULL, 0, 'Apply a thin cold smoke at 18° C (64° F) for 3 weeks. Cold smoking is not a continuous process so do not worry if there is no smoke overnight as long as the temperature stays below 18° C. Cold smoking is drying with smoke.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'413FCF7E029E844D89EF1089DAD841F6', X'75C1973E8C9D55429BB01B5086FD6A34', X'75C1973E8C9D55429BB01B5086FD6A34', 0, NULL, NULL, 2, NULL, 0, 'Dice fat into 6 mm (1/4`) cubes.');
