INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5BE05E7B982F0A47BD410713FCA26F7D', X'75C1973E8C9D55429BB01B5086FD6A34', X'75C1973E8C9D55429BB01B5086FD6A34', 3, NULL, NULL, 2, NULL, 0, 'Start smoking with oak wood at 28-30° C (82-86° F), high humidity. As the smoking progresses start lowering temperature and humidity. This process continues for 6-7 days.');
