INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ACB0994E47F7504180D2834DA6EDDE3F', X'7BD4607B8245D14B93FD873C5F9E34A1', X'7BD4607B8245D14B93FD873C5F9E34A1', 5, NULL, NULL, 2, NULL, 0, 'Dry in air to evaporate moisture. Dry at 12-15° C (53-59° F) for 2 weeks.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'13EEB30169BDA545A9254BE1416837BE', X'7BD4607B8245D14B93FD873C5F9E34A1', X'7BD4607B8245D14B93FD873C5F9E34A1', 6, NULL, NULL, 2, NULL, 0, 'Store at <12° C (53° F) or refrigerate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'40457F8014D7CE4C967E88F4C9F9235C', X'B1147781A7FFD04AACA002641693BEBF', X'B1147781A7FFD04AACA002641693BEBF', 0, NULL, NULL, 2, NULL, 0, 'Dice onions and cook in water for 45 minutes. Drain. Spread on the table and let the steam and the moisture to evaporate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FCC1CDBE85D6964CAC2F444405341CD8', X'B1147781A7FFD04AACA002641693BEBF', X'B1147781A7FFD04AACA002641693BEBF', 1, NULL, NULL, 2, NULL, 0, 'Cut pumpkin into smaller sections, remove seeds and fiber. Cook pumpkin for 25 minutes in water, 15 minutes in steam or bake for 30 minutes at 176° C (350° F). Remove the skin (it peels off easily) and cut the pieces into smaller parts. Place the pumpkin flesh in food processor and emulsify it into the puree. Dont add any water as pumpkin has plenty of it. Place pumpkin puree in a draining bag, place some weight on top and let it drain overnight.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1113E20729272340A1FC794F6FFBC4AA', X'B1147781A7FFD04AACA002641693BEBF', X'B1147781A7FFD04AACA002641693BEBF', 2, NULL, NULL, 2, NULL, 0, 'Cook the rice.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'020F95D58485D643BEC0A86776DE451E', X'B1147781A7FFD04AACA002641693BEBF', X'B1147781A7FFD04AACA002641693BEBF', 3, NULL, NULL, 2, NULL, 0, 'Boil potatoes and drain them.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1A8301873AE02E49A7303EC23B78B732', X'B1147781A7FFD04AACA002641693BEBF', X'B1147781A7FFD04AACA002641693BEBF', 4, NULL, NULL, 2, NULL, 0, 'Grind fat through 3 mm (1/8`) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3A518B03AA5C8344942A4EBFA8E5ACC7', X'B1147781A7FFD04AACA002641693BEBF', X'B1147781A7FFD04AACA002641693BEBF', 5, NULL, NULL, 2, NULL, 0, 'Grind the pumpkin, onions and potatoes through 3 mm (1/8`) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'79E9F0ADA12CBC4C818EE5AC9A87BFE9', X'B1147781A7FFD04AACA002641693BEBF', X'B1147781A7FFD04AACA002641693BEBF', 6, NULL, NULL, 2, NULL, 0, 'Except blood, mix everything together: fat, spices, pumpkin, potatoes and rice. Add finally the blood and mix all together.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'02E34CD8EE14074DA194268006445647', X'B1147781A7FFD04AACA002641693BEBF', X'B1147781A7FFD04AACA002641693BEBF', 7, NULL, NULL, 2, NULL, 0, 'Stuff into 36 mm pork casings forming U-shaped loops about 50 cm (20`) long. Tie the ends together with twine.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7F75BCBF56D70B41A96F690D83B1454B', X'B1147781A7FFD04AACA002641693BEBF', X'B1147781A7FFD04AACA002641693BEBF', 8, NULL, NULL, 2, NULL, 0, 'Dry at 12-15° C (53-59° F), 75-85% humidity for 30 days.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'90B3AE1ADEF10741BF75C6832A72728C', X'B1147781A7FFD04AACA002641693BEBF', X'B1147781A7FFD04AACA002641693BEBF', 9, NULL, NULL, 2, NULL, 0, 'Store at <12° C (53° F), <70% humidity or refrigerate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'04133CA504437144A2673F4D46728C98', X'327C5451F1EC3D4FB84E925769129E0D', X'327C5451F1EC3D4FB84E925769129E0D', 0, NULL, NULL, 2, NULL, 0, 'Dice the onion and cook in water for 30-45 minutes adding a little salt. Drain and cool.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6D89F067C800794DBE4743B8F6675FDF', X'327C5451F1EC3D4FB84E925769129E0D', X'327C5451F1EC3D4FB84E925769129E0D', 1, NULL, NULL, 2, NULL, 0, 'Dice fat (lard) into cubes.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C7ED65A7BABE9D47955088F03A1CDBA6', X'327C5451F1EC3D4FB84E925769129E0D', X'327C5451F1EC3D4FB84E925769129E0D', 2, NULL, NULL, 2, NULL, 0, 'Mix fat, onions, blood and spices.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6A558B1DB4BC044AA50D26F8D11FF60A', X'327C5451F1EC3D4FB84E925769129E0D', X'327C5451F1EC3D4FB84E925769129E0D', 3, NULL, NULL, 2, NULL, 0, 'Stuff into 36-40 mm pork or beef casings.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2824E783783F964B9F1A81B250089C45', X'327C5451F1EC3D4FB84E925769129E0D', X'327C5451F1EC3D4FB84E925769129E0D', 4, NULL, NULL, 2, NULL, 0, 'Immerse sausages into boiling water and cook at 80° C (176° F) for 45 minutes.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5F664702EA472448BD14C51E05CDAC08', X'327C5451F1EC3D4FB84E925769129E0D', X'327C5451F1EC3D4FB84E925769129E0D', 5, NULL, NULL, 2, NULL, 0, 'Immerse in cold water for 10 minutes, then cool in air and refrigerate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EAD0DEE69FF54042923F6EA6BCFBC00B', X'0587B05BE8B0BA46AC00C44768770D42', X'0587B05BE8B0BA46AC00C44768770D42', 0, NULL, NULL, 2, NULL, 0, 'Cut fat into 18 mm (3/4) pieces.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FD67F55515F1834B8C717EDEFE1A3069', X'0587B05BE8B0BA46AC00C44768770D42', X'0587B05BE8B0BA46AC00C44768770D42', 1, NULL, NULL, 2, NULL, 0, 'Dice onions into 18 mm (3/4) cubes. Place in a boiling water and cook for 80 minutes. Add rice, lower the heat and cook for 20 minutes more. Drain.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4098B794C9508843927B910414178767', X'0587B05BE8B0BA46AC00C44768770D42', X'0587B05BE8B0BA46AC00C44768770D42', 2, NULL, NULL, 2, NULL, 0, 'Mix fat, onions, spices and blood together.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B7271AD5B0C3494E8607D80B032A3CE5', X'0587B05BE8B0BA46AC00C44768770D42', X'0587B05BE8B0BA46AC00C44768770D42', 3, NULL, NULL, 2, NULL, 0, 'Stuff into 30-40 mm beef or pork casings. Form links 15-18 cm (6-7) long.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'18DA0872A12A264F89703B4806BA6113', X'0587B05BE8B0BA46AC00C44768770D42', X'0587B05BE8B0BA46AC00C44768770D42', 4, NULL, NULL, 2, NULL, 0, 'Cook in water at 85° C (185° F) for 30 minutes.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'74C0AEB933C3F141A2BFE00142F38736', X'0587B05BE8B0BA46AC00C44768770D42', X'0587B05BE8B0BA46AC00C44768770D42', 5, NULL, NULL, 2, NULL, 0, 'Drain and cool in air.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F151818202EEB2439037716E83196F32', X'B70FF9C2E8E3CC4CA2ABD22CBF82EBBA', X'B70FF9C2E8E3CC4CA2ABD22CBF82EBBA', 0, NULL, NULL, 2, NULL, 0, 'Peel onions, cut in half. Cook in water for 60 minutes, then drain and cool.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'770B2B4AA154C54F8B741A134AEBB985', X'B70FF9C2E8E3CC4CA2ABD22CBF82EBBA', X'B70FF9C2E8E3CC4CA2ABD22CBF82EBBA', 1, NULL, NULL, 2, NULL, 0, 'Grind back fat and onions through 3 mm (1/8) plate.*');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4E33250D0D8F3F4FB85C7E0B2C8CCA4C', X'B70FF9C2E8E3CC4CA2ABD22CBF82EBBA', X'B70FF9C2E8E3CC4CA2ABD22CBF82EBBA', 2, NULL, NULL, 2, NULL, 0, 'Mix onions, blood, salt, spices and fat together.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7556D2DF194AB748B73337B6461C38DA', X'B70FF9C2E8E3CC4CA2ABD22CBF82EBBA', X'B70FF9C2E8E3CC4CA2ABD22CBF82EBBA', 3, NULL, NULL, 2, NULL, 0, 'Stuff into 25 mm natural casings making 12 cm (5) long links.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'70332279D8EEB74FBD269526987DF220', X'B70FF9C2E8E3CC4CA2ABD22CBF82EBBA', X'B70FF9C2E8E3CC4CA2ABD22CBF82EBBA', 4, NULL, NULL, 2, NULL, 0, 'Cook in water at 80° C (176° F) for 30 minutes.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7116C1A37B1AED49AB5F4A5109FD6505', X'B70FF9C2E8E3CC4CA2ABD22CBF82EBBA', X'B70FF9C2E8E3CC4CA2ABD22CBF82EBBA', 5, NULL, NULL, 2, NULL, 0, 'Hang at room temperature (15° C/59° F) for 2-3 days. This is the optional drying/conditioning step which improves the flavor of the sausage and strengthens the casings so the sausage does not fall apart when fried.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C2AC584459182442B75382932A64A2D1', X'B70FF9C2E8E3CC4CA2ABD22CBF82EBBA', X'B70FF9C2E8E3CC4CA2ABD22CBF82EBBA', 6, NULL, NULL, 2, NULL, 0, 'Keep refrigerated.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2E1510360F047946B69B0CA4B63D3DE1', X'3FFB5BE198161C4B9F644E55E4BC054D', X'3FFB5BE198161C4B9F644E55E4BC054D', 0, NULL, NULL, 2, NULL, 0, 'Grind meat through 1/4 (6 mm) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'88FE8EF561272D408365E844E720269F', X'3FFB5BE198161C4B9F644E55E4BC054D', X'3FFB5BE198161C4B9F644E55E4BC054D', 1, NULL, NULL, 2, NULL, 0, 'Dice the back fat into 3/8 (10 mm) cubes. Except fat, combine blood with all other ingredients and mix for 3 minutes. Add diced fat and mix for one more minute.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'11DEB2F053D19C41B020B3BD40C35D75', X'3FFB5BE198161C4B9F644E55E4BC054D', X'3FFB5BE198161C4B9F644E55E4BC054D', 2, NULL, NULL, 2, NULL, 0, 'Laddle the mixture loosely through a funnel into 36 mm hog casings. Tie into 12 (30 cm) rings.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E1EE751EBB34CF408A6CE69A8E885445', X'3FFB5BE198161C4B9F644E55E4BC054D', X'3FFB5BE198161C4B9F644E55E4BC054D', 3, NULL, NULL, 2, NULL, 0, 'Place the sausages into a large pot with hot water. Cook at 80° C (176° F) for 35 minutes.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B57086A64556194089BC1941E16B3992', X'3FFB5BE198161C4B9F644E55E4BC054D', X'3FFB5BE198161C4B9F644E55E4BC054D', 4, NULL, NULL, 2, NULL, 0, 'Cool the sausages in cold water for 10 minutes.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5E12ACBDF8F91D4598CF4D08AF6906B3', X'3FFB5BE198161C4B9F644E55E4BC054D', X'3FFB5BE198161C4B9F644E55E4BC054D', 5, NULL, NULL, 2, NULL, 0, 'Remove from the water and place on the table so the moisture can evaporate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'64D0A2E483C0854A80562D84716491B7', X'3FFB5BE198161C4B9F644E55E4BC054D', X'3FFB5BE198161C4B9F644E55E4BC054D', 6, NULL, NULL, 2, NULL, 0, 'Refrigerate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D4C71A372873FA4F9BAEE027BA145531', X'BC9FBFA5F2893347B2C3302F81272096', X'BC9FBFA5F2893347B2C3302F81272096', 0, NULL, NULL, 2, NULL, 0, 'Cook liver, heart, lungs in water until soft. Drain and cool. Grind through 5 mm (1/4`) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4DF484B3E532D74897F7AF5EF837C8ED', X'BC9FBFA5F2893347B2C3302F81272096', X'BC9FBFA5F2893347B2C3302F81272096', 1, NULL, NULL, 2, NULL, 0, 'Chop the onion finely, place in a drain bag, place weight on top and let it drain for 6 hours.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CFFFE80AA961F845AEEFDD7919E47F65', X'BC9FBFA5F2893347B2C3302F81272096', X'BC9FBFA5F2893347B2C3302F81272096', 2, NULL, NULL, 2, NULL, 0, 'Cut dewlap and any fat into small cubes.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5FD244FCCDB8F64E83D3FAFD82507C87', X'BC9FBFA5F2893347B2C3302F81272096', X'BC9FBFA5F2893347B2C3302F81272096', 3, NULL, NULL, 2, NULL, 0, 'Mix all meats, fat, onions, spices and blood together.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'58D58BDE455305429BF3A596C0BAD28D', X'BC9FBFA5F2893347B2C3302F81272096', X'BC9FBFA5F2893347B2C3302F81272096', 4, NULL, NULL, 2, NULL, 0, 'Stuff into 5-65 mm beef middles.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'551F720612FBD64CB1494B11F60921A8', X'BC9FBFA5F2893347B2C3302F81272096', X'BC9FBFA5F2893347B2C3302F81272096', 5, NULL, NULL, 2, NULL, 0, 'Smoke first day with hot smoke, then smoke/dry with cold smoke until sausages develop black color.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'77CC7869F3F0FD478405DD5DAAD4A61F', X'62A0A84B1962B14B933A309EE7FB3DB5', X'62A0A84B1962B14B933A309EE7FB3DB5', 0, NULL, NULL, 2, NULL, 0, 'Cook offal meat in water (below the boiling point) until soft. Drain and cool.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E40EC99245DA2B45AF72DDDFCBE66D9F', X'62A0A84B1962B14B933A309EE7FB3DB5', X'62A0A84B1962B14B933A309EE7FB3DB5', 1, NULL, NULL, 2, NULL, 0, 'Grind meats and fat through 6 mm (1/4) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6D3CA2D268B6A24C9A69A6390CF6E781', X'62A0A84B1962B14B933A309EE7FB3DB5', X'62A0A84B1962B14B933A309EE7FB3DB5', 2, NULL, NULL, 2, NULL, 0, 'Mix meat and fat with salt and all spices. Hold for 12-24 hours in refrigerator.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'361296E0849CF34BB7BA9494A6005481', X'62A0A84B1962B14B933A309EE7FB3DB5', X'62A0A84B1962B14B933A309EE7FB3DB5', 3, NULL, NULL, 2, NULL, 0, 'Stuff into 24-26 mm sheep casings or 34-36 mm pork casings. Make rings about 25 cm (10) long.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CC5D977A5EAF4144974213EB2815EC64', X'62A0A84B1962B14B933A309EE7FB3DB5', X'62A0A84B1962B14B933A309EE7FB3DB5', 4, NULL, NULL, 2, NULL, 0, 'Dry 24-26 mm sausages for 15-20 days at 12-18° C (53-72° F), 80-85% humidity. Dry 34-36 mm sausages for 30-60 days at 12-18° C (53-72° F), 80-85% humidity.');
