INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F8849294C7D3204882A7684702FAAD71', X'2229B914D9DA354595E60D415FF7B637', X'2229B914D9DA354595E60D415FF7B637', 5, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 200° C (392° F) and bake the sausages for 25 minutes. Turn the sausages over and bake them for 25 minutes more. Baking the sausages twice makes them crispy.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7AD367586C6480419153214986CAB163', X'2229B914D9DA354595E60D415FF7B637', X'2229B914D9DA354595E60D415FF7B637', 6, NULL, NULL, 2, NULL, 0, 'Cool the sausages at room temperature (1-2 hours).');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1905237F59670749A644252B7A32B048', X'2229B914D9DA354595E60D415FF7B637', X'2229B914D9DA354595E60D415FF7B637', 7, NULL, NULL, 2, NULL, 0, 'Refrigerate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'976A908F43565F49B03DBFAF14CBB938', X'0C24C41FF361EC4D86F1800870DFA7AC', X'0C24C41FF361EC4D86F1800870DFA7AC', 0, NULL, NULL, 2, NULL, 0, 'Add salt to blood and stir it.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'792548AFE5DF564F9613E63AA0BE8AFF', X'0C24C41FF361EC4D86F1800870DFA7AC', X'0C24C41FF361EC4D86F1800870DFA7AC', 1, NULL, NULL, 2, NULL, 0, 'Mix blood with all ingredients.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2F5BF818497B2746BCD26436DFF6CA49', X'0C24C41FF361EC4D86F1800870DFA7AC', X'0C24C41FF361EC4D86F1800870DFA7AC', 2, NULL, NULL, 2, NULL, 0, 'Stuff into sheep stomachs or largest hog casings (38 - 42 mm)');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6D99E1716EA8CE42A51D8BE6350CDFA5', X'0C24C41FF361EC4D86F1800870DFA7AC', X'0C24C41FF361EC4D86F1800870DFA7AC', 3, NULL, NULL, 2, NULL, 0, 'Place sausages into boiling water and poach at 80º - 85º C (176º - 185º F) for about 60 - 90 minutes to reach internal sausage temperature of 68º - 70º C (154º - 158º F). Stomach will require longer cooking time.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'61CF8F8519C1714EB3E39DD0777D4C0C', X'0C24C41FF361EC4D86F1800870DFA7AC', X'0C24C41FF361EC4D86F1800870DFA7AC', 4, NULL, NULL, 2, NULL, 0, 'Cool and keep in refrigerator.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BD01DCB2C1CC0843837F7006C8258C6B', X'4EADBE353074874495145BB9FBF6486A', X'4EADBE353074874495145BB9FBF6486A', 0, NULL, NULL, 2, NULL, 0, 'Cook all meats below the boiling point of water until soft. Save meat stock.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EDD01A35A92B894FA473747E64A81952', X'4EADBE353074874495145BB9FBF6486A', X'4EADBE353074874495145BB9FBF6486A', 1, NULL, NULL, 2, NULL, 0, 'Chopped finely the onions and fry in fat until glassy and golden.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9FAAF13E4F5D1B41BACD53799E276F2E', X'4EADBE353074874495145BB9FBF6486A', X'4EADBE353074874495145BB9FBF6486A', 2, NULL, NULL, 2, NULL, 0, 'Dice fat into 6 mm (1/4`) cubes. Heat the diced fat is in the broth in which the meat was cooked until it is slightly translucent and springs away when taken in the hand. This ensures that the diced pig fat is not stained red by the blood, so that the white cubes of fat stand out against the dark sausage mixture and give the Oecher Puttes its characteristic cross-section appearance.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'89EA0504D6545A4F830088A7A04773B6', X'4EADBE353074874495145BB9FBF6486A', X'4EADBE353074874495145BB9FBF6486A', 3, NULL, NULL, 2, NULL, 0, 'Chop meats and skin or grind through 6 mm (1/4) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3D9DE8F5C8639449A86DE928D7F3C9B1', X'4EADBE353074874495145BB9FBF6486A', X'4EADBE353074874495145BB9FBF6486A', 4, NULL, NULL, 2, NULL, 0, 'Warm up the blood to about 45° C (113° F). This reduces the metallic taste of the blood and increases the ability of the sausage mixture to bind when the diced fat is added.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E336AF37ED8EF840B420F151DC97E91D', X'4EADBE353074874495145BB9FBF6486A', X'4EADBE353074874495145BB9FBF6486A', 5, NULL, NULL, 2, NULL, 0, 'Mix meats with onions, spices, blood, fat cubes and 30 ml (2 Tbsp) of meat stock.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CD138658DC60E14E9DE7D0A184F5F4E8', X'4EADBE353074874495145BB9FBF6486A', X'4EADBE353074874495145BB9FBF6486A', 6, NULL, NULL, 2, NULL, 0, 'Stuff into 36 mm hog casings.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'699FD673E4028446A24F433DE50984FA', X'4EADBE353074874495145BB9FBF6486A', X'4EADBE353074874495145BB9FBF6486A', 7, NULL, NULL, 2, NULL, 0, 'Boil in water at 78° C (174° F) for 40 minutes.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F72812E94CBD7042AE1425577A5ADF3C', X'4EADBE353074874495145BB9FBF6486A', X'4EADBE353074874495145BB9FBF6486A', 8, NULL, NULL, 2, NULL, 0, 'Cool and refrigerate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F8EECCB71525074DB7E0D19DCFE4311F', X'4EADBE353074874495145BB9FBF6486A', X'4EADBE353074874495145BB9FBF6486A', 9, NULL, NULL, 2, NULL, 0, 'Cook before serving.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8C7711FAB72DED42A65AD99A9ACAFD39', X'13C5B5277BDAD84CBA7ADBD6AEED2157', X'13C5B5277BDAD84CBA7ADBD6AEED2157', 0, NULL, NULL, 2, NULL, 0, 'Cook skins and meat in water until meat separates from bones. Separate meat from bones.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EB18319FB602B8489C97DE702FAB7F8A', X'13C5B5277BDAD84CBA7ADBD6AEED2157', X'13C5B5277BDAD84CBA7ADBD6AEED2157', 1, NULL, NULL, 2, NULL, 0, 'Grind meats through 10-12 mm (3/8-1/2`) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'21EAAF56774A52479935E43447F91AD7', X'13C5B5277BDAD84CBA7ADBD6AEED2157', X'13C5B5277BDAD84CBA7ADBD6AEED2157', 2, NULL, NULL, 2, NULL, 0, 'Grind skins through 3 mm (1/8`) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B9A576F8A534934F8E0CE7799D5EDC17', X'13C5B5277BDAD84CBA7ADBD6AEED2157', X'13C5B5277BDAD84CBA7ADBD6AEED2157', 3, NULL, NULL, 2, NULL, 0, 'Dice back fat into 6-10 mm (1/4-3/8`) cubes.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DC8ABC9F4773864D9877A349FD7EF840', X'13C5B5277BDAD84CBA7ADBD6AEED2157', X'13C5B5277BDAD84CBA7ADBD6AEED2157', 4, NULL, NULL, 2, NULL, 0, 'Mix meat, fat, skins with spices and blood.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0FEDF256A3B09C4598DEC3C1DA460E66', X'13C5B5277BDAD84CBA7ADBD6AEED2157', X'13C5B5277BDAD84CBA7ADBD6AEED2157', 5, NULL, NULL, 2, NULL, 0, 'Stuff into natural 36-42 mm (or larger) pork or beef casings forming straight (vela) or U-shaped (sarta) sections 25-35 cm (12-14`) long. Both ends of a loop tied with twine.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'264CA068D1D483419C698D8B7F1DB036', X'13C5B5277BDAD84CBA7ADBD6AEED2157', X'13C5B5277BDAD84CBA7ADBD6AEED2157', 6, NULL, NULL, 2, NULL, 0, 'Cook in water at 80° C (176° F) for 60 minutes.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2E68D2026A48D14393EDDB4FE0E4A7C2', X'13C5B5277BDAD84CBA7ADBD6AEED2157', X'13C5B5277BDAD84CBA7ADBD6AEED2157', 7, NULL, NULL, 2, NULL, 0, 'Dry at 12-15° C (53-59° F) for 3-4 days (depending on the diameter of the sausage).');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'87DEE51BA27F624381CF6ECEC2D79322', X'4E056E9A7887314EB533C0BE5EC302BE', X'4E056E9A7887314EB533C0BE5EC302BE', 0, NULL, NULL, 2, NULL, 0, 'Cook meats (except liver) in a small amount of water: dewlap/jowls at 80-85° C (176-185° F) until semi-soft; lungs at 85° C (185° F); skins, heads at 95° C (203° F) until soft.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AF1E1CC63AB2C543A5816E22FDECA0C9', X'4E056E9A7887314EB533C0BE5EC302BE', X'4E056E9A7887314EB533C0BE5EC302BE', 1, NULL, NULL, 2, NULL, 0, 'After cooking remove gristle from lungs and meat from heads. Spread meats apart on a flat surface to cool.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D53FCD65605CE5479B22055364D8F3F7', X'4E056E9A7887314EB533C0BE5EC302BE', X'4E056E9A7887314EB533C0BE5EC302BE', 2, NULL, NULL, 2, NULL, 0, 'Cut dewlap/jowls into 4-5 (10-12 mm) cubes. Grind all other meats through 1/8 (3 mm plate. Soak rolls or bread crumbs in left over stock, using 75% of stock in relation to bread crumbs.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'636A070B4D177C4198C4A153C15CDF02', X'4E056E9A7887314EB533C0BE5EC302BE', X'4E056E9A7887314EB533C0BE5EC302BE', 3, NULL, NULL, 2, NULL, 0, 'If semolina is used, emulsify it in a food processor with 75% of meat stock (in relation to semolina).');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9787C86CC8655B48A442DADCE9634AD8', X'4E056E9A7887314EB533C0BE5EC302BE', X'4E056E9A7887314EB533C0BE5EC302BE', 4, NULL, NULL, 2, NULL, 0, 'Add salt and spices to ground meats and bread crumbs (or semolina flour), add blood and mix everything well together.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DF893EF1B8FA6F40B1B54BF25A740282', X'4E056E9A7887314EB533C0BE5EC302BE', X'4E056E9A7887314EB533C0BE5EC302BE', 5, NULL, NULL, 2, NULL, 0, 'Stuff loosely into pork middles or beef rounds. Pork middles or beef rounds, 16 (40 cm) each. The ends tied with twine. Hanging loop 4-5 (10-12 cm) on one end.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'613A306F633DA4448FF75288FB20FC98', X'4E056E9A7887314EB533C0BE5EC302BE', X'4E056E9A7887314EB533C0BE5EC302BE', 6, NULL, NULL, 2, NULL, 0, 'Immerse sausages in boiling water and cook at 80-85° C (176-185° F) for about 45 min until sausages reach 68-70° C (154-158° F) internal temperature.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BFAE03C5EE77B049921EE3D48DA815C5', X'4E056E9A7887314EB533C0BE5EC302BE', X'4E056E9A7887314EB533C0BE5EC302BE', 7, NULL, NULL, 2, NULL, 0, 'Place the sausages in cold water for 15 minutes and then spread them on a flat surface to cool.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DE59144C6408C14D90019D73442DF968', X'4E056E9A7887314EB533C0BE5EC302BE', X'4E056E9A7887314EB533C0BE5EC302BE', 8, NULL, NULL, 2, NULL, 0, 'Refrigerate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FD14F79497448E43B09C3C7DEA6F029F', X'CD7C36C9B0659A47B85AD12CF53B9BC3', X'CD7C36C9B0659A47B85AD12CF53B9BC3', 0, NULL, NULL, 2, NULL, 0, 'Wash meats in cold water, soak any salted meats for 2-3 hrs in cold running water.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A45B43DFF779384E90A30B0C219E3A9D', X'CD7C36C9B0659A47B85AD12CF53B9BC3', X'CD7C36C9B0659A47B85AD12CF53B9BC3', 1, NULL, NULL, 2, NULL, 0, 'Poach meats (except liver, spleen and skwarki *) in small amount of water: lungs at 80-85° C (176-185° F) until soft; skins, beef trimmings, lips, udders, tripe and heads at 95° C (203° F); veal casings at 80-85° C (176-185° F) until semi-soft. After poaching remove gristle from lungs and meat from heads. Spread meats apart on a flat surface to cool.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EA63478F7C1CBA4B94EE8F778177C924', X'CD7C36C9B0659A47B85AD12CF53B9BC3', X'CD7C36C9B0659A47B85AD12CF53B9BC3', 2, NULL, NULL, 2, NULL, 0, 'Boil barley grouts (1 kg) in 2 liters of left over meat stock or water until semi-soft. Boil buckwheat grouts (1 kg) in 3.6-4.0 liters of left over meat stock or water. Slow boil grouts for ½ hr continuously stirring. After boiling leave for ½-1 hr covered.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'16192D323D278345B07E3352C54E0DD3', X'CD7C36C9B0659A47B85AD12CF53B9BC3', X'CD7C36C9B0659A47B85AD12CF53B9BC3', 3, NULL, NULL, 2, NULL, 0, 'Grind beef trimmings,pork skins, beef and veal lips, lungs, udders, veal casings, pork stomachs-all boiled through 3 mm plate. Grind boiled head meat through 3 mm plate. Grind beef and lamb liver, raw spleen and skwarki * through 3 mm plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D2A6E420FF470342ACF531CDB748F79D', X'CD7C36C9B0659A47B85AD12CF53B9BC3', X'CD7C36C9B0659A47B85AD12CF53B9BC3', 4, NULL, NULL, 2, NULL, 0, 'Add salt and spices to ground meats, mix barley or buckwheat grouts with blood, add stock fat and finely diced onions and then mix everything well together.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'77B0A59018C0DB43A4ED2A980BEBC812', X'CD7C36C9B0659A47B85AD12CF53B9BC3', X'CD7C36C9B0659A47B85AD12CF53B9BC3', 5, NULL, NULL, 2, NULL, 0, 'Stuffing materials: 50 mm cellulose or fibrous synthetic casings, butchers twine. Form a hanging loop.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F123F5D83168C643BD50C313F42AB4EC', X'CD7C36C9B0659A47B85AD12CF53B9BC3', X'CD7C36C9B0659A47B85AD12CF53B9BC3', 6, NULL, NULL, 2, NULL, 0, 'Stuff casings loosely, make straight links 40-45 cm (16-18) long. Tie the ends with twine. Make hanging loop 10-12 cm (4-5) at one end.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DDD4F754EB6ECC46B4AE9099C73BF0D6', X'CD7C36C9B0659A47B85AD12CF53B9BC3', X'CD7C36C9B0659A47B85AD12CF53B9BC3', 7, NULL, NULL, 2, NULL, 0, 'Immerse sausages in boiling water and poach at 75-78° C (167-172° F) for about 80-100 min until internal meat temperature reaches 68-70° C (154-158° F).');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8C15A163D6B60D4394B2858539B9A033', X'CD7C36C9B0659A47B85AD12CF53B9BC3', X'CD7C36C9B0659A47B85AD12CF53B9BC3', 8, NULL, NULL, 2, NULL, 0, 'Immerse sausages for 5 min in cold running water, then spread them on a flat surface to cool down below 6° C (43° F). If no suitable cooling conditions are available, arrangements need to be made to bring the temperature to 12°C (53°F) or less.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7C0B1020C2E1CD4087FF078649ED14EF', X'C3ACAEA8FBACCC4A9910F98134833214', X'C3ACAEA8FBACCC4A9910F98134833214', 0, NULL, NULL, 2, NULL, 0, 'Boil meat and skins at 90° C (194° F) until softer.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9CA8A665DF032B418C449CDBB82A2DB2', X'C3ACAEA8FBACCC4A9910F98134833214', X'C3ACAEA8FBACCC4A9910F98134833214', 1, NULL, NULL, 2, NULL, 0, 'Grind meat through 5 mm (1/4`) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'221B35964097B64E9DD3ED035DB2BB62', X'C3ACAEA8FBACCC4A9910F98134833214', X'C3ACAEA8FBACCC4A9910F98134833214', 2, NULL, NULL, 2, NULL, 0, 'Dice back fat into 8 mm (3/8`) cubes.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4E99FAE2199119429B8C1422CB103A20', X'C3ACAEA8FBACCC4A9910F98134833214', X'C3ACAEA8FBACCC4A9910F98134833214', 3, NULL, NULL, 2, NULL, 0, 'Grind skins and chopped raw onion through 3 mm (1/8`) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E2E7C4A246A3B747AB45CAC6F1656ECD', X'C3ACAEA8FBACCC4A9910F98134833214', X'C3ACAEA8FBACCC4A9910F98134833214', 4, NULL, NULL, 2, NULL, 0, 'Mix meat, fat, skins and blood with all ingredients together.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'88A35644D4DE3D46B8776C6C01A41A43', X'C3ACAEA8FBACCC4A9910F98134833214', X'C3ACAEA8FBACCC4A9910F98134833214', 5, NULL, NULL, 2, NULL, 0, 'Stuff into 90-100 synthetic waterproof casings.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'287984512A2D7B44BF02099F77690101', X'C3ACAEA8FBACCC4A9910F98134833214', X'C3ACAEA8FBACCC4A9910F98134833214', 6, NULL, NULL, 2, NULL, 0, 'Boil in water at 80° C (176° F) for 120 minutes.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'55C45424E78185409A377D7F7A9A7B0E', X'C3ACAEA8FBACCC4A9910F98134833214', X'C3ACAEA8FBACCC4A9910F98134833214', 7, NULL, NULL, 2, NULL, 0, 'Immerse in cold water for 10 minutes. Drain and cool in air.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8348C896E6520D41AF134C2D618D570B', X'C3ACAEA8FBACCC4A9910F98134833214', X'C3ACAEA8FBACCC4A9910F98134833214', 8, NULL, NULL, 2, NULL, 0, 'Refrigerate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C9EE4DDC013E8D4EA934F997021EF685', X'8BFA9712E2B3C2428899EC4E4BDE7AEE', X'8BFA9712E2B3C2428899EC4E4BDE7AEE', 0, NULL, NULL, 2, NULL, 0, 'The fatty cuts of meat are cut into small pieces.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'215B1ADA1322B64ABA9AFEA439348979', X'8BFA9712E2B3C2428899EC4E4BDE7AEE', X'8BFA9712E2B3C2428899EC4E4BDE7AEE', 1, NULL, NULL, 2, NULL, 0, 'The meat is seasoned with salt, cure #1, red or white wine and garlic. Hold for 2-3 days in refrigerator. Cut the wheat bread is cut small pieces. Soak bread in blood adding chopped onion, chopped parsley, olive oil and spices.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D7DDF4A0F649074E8F8F99F8B84E950F', X'8BFA9712E2B3C2428899EC4E4BDE7AEE', X'8BFA9712E2B3C2428899EC4E4BDE7AEE', 2, NULL, NULL, 2, NULL, 0, 'Mix the marinated meat with the bread and the blood.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2B0C867F2F0EF64681B8FA32B2F0EFED', X'8BFA9712E2B3C2428899EC4E4BDE7AEE', X'8BFA9712E2B3C2428899EC4E4BDE7AEE', 3, NULL, NULL, 2, NULL, 0, 'Stuff into 36 mm hog casings making one continuous coil. Twist the casing to the desired size links. You may use twine to make a knot on each end of the link and then cut the links. Tie the ends together to make a ring.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B6C8BC06116B8842A1B0B61FBCFE13B5', X'8BFA9712E2B3C2428899EC4E4BDE7AEE', X'8BFA9712E2B3C2428899EC4E4BDE7AEE', 4, NULL, NULL, 2, NULL, 0, 'Apply a thin smoke at 18° C (64° F) for 3-4 days. Smoking need not to be continuous, as long as the temperature inside of the smokehouse stays below 18° C. Think of the process as drying with smoke. Oak wood is used for smoking.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0F82C1C1D56BDB4F8C5C087FE2B7FAAF', X'E54B11D6F192CD40BD198B92DB045608', X'E54B11D6F192CD40BD198B92DB045608', 0, NULL, NULL, 2, NULL, 0, 'Boil the rice.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2094F1285925C942ACC8B80C1EE4D322', X'E54B11D6F192CD40BD198B92DB045608', X'E54B11D6F192CD40BD198B92DB045608', 1, NULL, NULL, 2, NULL, 0, 'Grind suet with 1/4 (6 mm) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EFCC404AF38B234299A21D5DF8443619', X'E54B11D6F192CD40BD198B92DB045608', X'E54B11D6F192CD40BD198B92DB045608', 2, NULL, NULL, 2, NULL, 0, 'Mix everything together.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D5D6BB9C23F7BA45BE95347785A9A38C', X'E54B11D6F192CD40BD198B92DB045608', X'E54B11D6F192CD40BD198B92DB045608', 3, NULL, NULL, 2, NULL, 0, 'Stuff loosely into 36 mm hog casings and tie off into 12 (25 cm) long rings.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E35C7AA215297E468CE32E685963CC5C', X'E54B11D6F192CD40BD198B92DB045608', X'E54B11D6F192CD40BD198B92DB045608', 4, NULL, NULL, 2, NULL, 0, 'Cook in water at 80° C (176° F) for about 40 minutes. Cool in cold water, dry in air and refrigerate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0968A3BEF82A9D4C9D45DD26AFD82EA4', X'E54B11D6F192CD40BD198B92DB045608', X'E54B11D6F192CD40BD198B92DB045608', 5, NULL, NULL, 2, NULL, 0, 'Cook before serving.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B50DB6D5D065F848AC547E3B6D71889E', X'A56F70CB86D6CD4C953566FE51F22054', X'A56F70CB86D6CD4C953566FE51F22054', 0, NULL, NULL, 2, NULL, 0, 'Wash meats in cold water, soak any salted meats for 2-3 hrs in cold running water.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'64E6FCA6F7ED2B43A93DC90435DB0E41', X'A56F70CB86D6CD4C953566FE51F22054', X'A56F70CB86D6CD4C953566FE51F22054', 1, NULL, NULL, 2, NULL, 0, 'Poach meats (except liver and skwarki *) in small amount of water: skins at 95° C (203° F); jowls at 80-85° C (176-185° F) until semi-soft; other meats at 80-85° C (176-185° F) until soft. Save the meat stock. After poaching remove gristle from lungs and meat from heads. Spread meats apart on a flat surface to cool.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'09D05862D6C9FA4295B67EC3637BF737', X'A56F70CB86D6CD4C953566FE51F22054', X'A56F70CB86D6CD4C953566FE51F22054', 2, NULL, NULL, 2, NULL, 0, 'Boil buckwheat grouts (1.5 kg) in 2.7-3.0 liters of left over meat stock or water. Boil grouts for ½ hr continuously stirring. After boiling leave for ½-1 hr covered.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'57D1786515646D44B91FA6DD825A4F76', X'A56F70CB86D6CD4C953566FE51F22054', X'A56F70CB86D6CD4C953566FE51F22054', 3, NULL, NULL, 2, NULL, 0, 'Soak skwarki * in stock using 25% of stock in relation to the weight of skwarki.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B344D2F8E9029A4AA6400B7EBB16A8EB', X'A56F70CB86D6CD4C953566FE51F22054', X'A56F70CB86D6CD4C953566FE51F22054', 4, NULL, NULL, 2, NULL, 0, 'Grind all meats, liver, skwarki * and onion through 3 mm plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'13CC606562A2014F88E24B668F0371ED', X'A56F70CB86D6CD4C953566FE51F22054', X'A56F70CB86D6CD4C953566FE51F22054', 5, NULL, NULL, 2, NULL, 0, 'Add salt and spices to ground meats, mix buckwheat grouts with blood, add stock fat and then mix everything well together.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F4C7945713EE51479A18468AD1DB94E1', X'A56F70CB86D6CD4C953566FE51F22054', X'A56F70CB86D6CD4C953566FE51F22054', 6, NULL, NULL, 2, NULL, 0, 'Stuffing materials: hog casings 32-36 mm, butchers twine. Stuff casings loosely into 18-20 cm (17-18) long links. Tie the ends with twine.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'403F73BDD7363D4AB111A72FFE85498B', X'A56F70CB86D6CD4C953566FE51F22054', X'A56F70CB86D6CD4C953566FE51F22054', 7, NULL, NULL, 2, NULL, 0, 'Immerse sausages in boiling water and poach at 85° C (185° F) for about 15-20 min.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E376398BBBA9DE4480B3798B8CC06DC6', X'A56F70CB86D6CD4C953566FE51F22054', X'A56F70CB86D6CD4C953566FE51F22054', 8, NULL, NULL, 2, NULL, 0, 'Immerse sausages for 15 min in cold water (about 5° C, 41° F) then divide them into pairs. Cool them down to below 6° C (43° F). If no suitable cooling conditions are available, arrangements need to be made to bring the temperature to 12° C (53° F) or less.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'58941169F8815B439CB103B9D3E65CC8', X'92F7CCC614DBAC4ABD79C6059846761D', X'92F7CCC614DBAC4ABD79C6059846761D', 0, NULL, NULL, 2, NULL, 0, 'Wash pork and veal lungs, groin and skins in cold running water.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FA8C073C5831C94B823A03E86B7EADC3', X'92F7CCC614DBAC4ABD79C6059846761D', X'92F7CCC614DBAC4ABD79C6059846761D', 1, NULL, NULL, 2, NULL, 0, 'Cook meats in small amount of water: lungs and groin at 80-85° C (176-185° F) until semi-soft; skins at 95° C (203° F) until soft. Save stock. After cooking remove gristle from lungs. Spread meats apart on a flat surface to cool.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AB915AF4BB62CE4DA53B8840ACCA1C8D', X'92F7CCC614DBAC4ABD79C6059846761D', X'92F7CCC614DBAC4ABD79C6059846761D', 2, NULL, NULL, 2, NULL, 0, 'Soak dry bread rolls (if used) with an equal amount of meat stock for about ½ hr. Soak skwarki * in stock using 25% of stock in relation to the weight of skwarki.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9764E9EFE7852845BD015C136FE8F7EB', X'92F7CCC614DBAC4ABD79C6059846761D', X'92F7CCC614DBAC4ABD79C6059846761D', 3, NULL, NULL, 2, NULL, 0, 'Grind soaked rolls through 2 mm plate. Grind all meats through 2 mm plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'68DF0CA309568640AC97D544112D61C1', X'92F7CCC614DBAC4ABD79C6059846761D', X'92F7CCC614DBAC4ABD79C6059846761D', 4, NULL, NULL, 2, NULL, 0, 'Mix all ground ingredients with blood, adding salt and spices.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C4D3AF0ABA08014B8DB6FE1D54BD1BFD', X'92F7CCC614DBAC4ABD79C6059846761D', X'92F7CCC614DBAC4ABD79C6059846761D', 5, NULL, NULL, 2, NULL, 0, 'Stuffing materials: Pork middles, beef rounds, fibrous casings, butchers twine. Stuff casings loosely, make 40 cm (16) links. Tie the ends with twine.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'200C7E13A851E74DAFB2B647FD6CA737', X'92F7CCC614DBAC4ABD79C6059846761D', X'92F7CCC614DBAC4ABD79C6059846761D', 6, NULL, NULL, 2, NULL, 0, 'Immerse sausages in boiling water and poach at 80-85 ° C (176-185° F) for about 50-80 min until internal meat temperature reaches 68-70° C (154-158° F).');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'36FE8937925A7F4D96E14DB62250E423', X'92F7CCC614DBAC4ABD79C6059846761D', X'92F7CCC614DBAC4ABD79C6059846761D', 7, NULL, NULL, 2, NULL, 0, 'Immerse sausages for 5 min in cold running water, then spread them on a flat surface to cool down below 6° C (43° F). If no suitable cooling conditions are available, arrangements need to be made to bring the temperature to 12° C (53 °F) or less.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2BC9F6004E4CC7439BA51D3D30EB7B43', X'FEEC7F80149CEF43A341789DB23450B2', X'FEEC7F80149CEF43A341789DB23450B2', 0, NULL, NULL, 2, NULL, 0, 'Cook rice.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A40F89D1E8DFF341B52E1E42EC568022', X'FEEC7F80149CEF43A341789DB23450B2', X'FEEC7F80149CEF43A341789DB23450B2', 1, NULL, NULL, 2, NULL, 0, 'Soak noodles in warm water until soft. Drain and chop with knife.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A2D0FD5FA9AA004C96ED5EB476AAF0B2', X'FEEC7F80149CEF43A341789DB23450B2', X'FEEC7F80149CEF43A341789DB23450B2', 2, NULL, NULL, 2, NULL, 0, 'Finely chop the scallion.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D14AF1489BCEE541BD189B090EF89B33', X'FEEC7F80149CEF43A341789DB23450B2', X'FEEC7F80149CEF43A341789DB23450B2', 3, NULL, NULL, 2, NULL, 0, 'Mix rice, noodles and all ingredients with blood.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AA5D95A34CED464888462584305868EA', X'FEEC7F80149CEF43A341789DB23450B2', X'FEEC7F80149CEF43A341789DB23450B2', 4, NULL, NULL, 2, NULL, 0, 'Stuff loosely into 36 mm hog casings. Tie the ends together to make a ring.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D3DBE12BCE6AB2429A7F1B7CBFF91672', X'FEEC7F80149CEF43A341789DB23450B2', X'FEEC7F80149CEF43A341789DB23450B2', 5, NULL, NULL, 2, NULL, 0, 'Cook in water at 80° C (176° F) for 35 minutes.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9D983A6B41FDA14FA13B44D11EBEE67B', X'FEEC7F80149CEF43A341789DB23450B2', X'FEEC7F80149CEF43A341789DB23450B2', 6, NULL, NULL, 2, NULL, 0, 'Keep refrigerated.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8339CA7925040040BE5566C416F4BC23', X'AD3BC6B59790CA4B9C9600A89E59A2EE', X'AD3BC6B59790CA4B9C9600A89E59A2EE', 0, NULL, NULL, 2, NULL, 0, 'Cure lean pork and jowls.*');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1E58D5FF406ADC498E47797BC3371143', X'AD3BC6B59790CA4B9C9600A89E59A2EE', X'AD3BC6B59790CA4B9C9600A89E59A2EE', 1, NULL, NULL, 2, NULL, 0, 'Boil cured meats at 80° C (176° F) until they are semi-soft. Shower with running water, then dice them into 12 mm (1/2`) cubes. Cook skins (below boiling point) until semi soft (a thumb should penetrate the skin). Cook liver (below boiling point) for 15 minutes.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7E02CE6D8132764ABB05E9C44A9025EA', X'AD3BC6B59790CA4B9C9600A89E59A2EE', X'AD3BC6B59790CA4B9C9600A89E59A2EE', 2, NULL, NULL, 2, NULL, 0, 'Grind cooked skins, onions and liver through 3 mm (1/8`) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'85C19988A48D0C429DEB266414098D3C', X'AD3BC6B59790CA4B9C9600A89E59A2EE', X'AD3BC6B59790CA4B9C9600A89E59A2EE', 3, NULL, NULL, 2, NULL, 0, 'Rinse cubed meat with hot water and place into a mixing bowl.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1F1646B7C899C441AC0B225CE18CBB2E', X'AD3BC6B59790CA4B9C9600A89E59A2EE', X'AD3BC6B59790CA4B9C9600A89E59A2EE', 4, NULL, NULL, 2, NULL, 0, 'Start mixing and add into the bowl: salt and all ingredients, ground skins, liver, onions, and blood.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'30E503BCE2E5F3439C3DCC4E0B7D2F0C', X'AD3BC6B59790CA4B9C9600A89E59A2EE', X'AD3BC6B59790CA4B9C9600A89E59A2EE', 5, NULL, NULL, 2, NULL, 0, 'Stuff loosely into large natural casings, pork blind caps (caecum), bladders, or synthetic casings.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A8F25882B1FD194287E942C9E269EBC9', X'AD3BC6B59790CA4B9C9600A89E59A2EE', X'AD3BC6B59790CA4B9C9600A89E59A2EE', 6, NULL, NULL, 2, NULL, 0, 'Cook in hot water at 85° C (185° F): pork caps for 80 minutes; bladders for 105 minutes; synthetic casings - allow 10 minutes boiling time for each 10 mm of casing''s diameter (10 mm = 1 cm) i.e. 40 mm sausage will be cooked for 40 minutes.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A34B173931CF4546888050D490E5E30A', X'AD3BC6B59790CA4B9C9600A89E59A2EE', X'AD3BC6B59790CA4B9C9600A89E59A2EE', 7, NULL, NULL, 2, NULL, 0, 'Immerse in cold water for 10 minutes, Finish cooling in air.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7677E46571A56A44BA33DF05F1CE4D96', X'AD3BC6B59790CA4B9C9600A89E59A2EE', X'AD3BC6B59790CA4B9C9600A89E59A2EE', 8, NULL, NULL, 2, NULL, 0, 'Refrigerate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CB83415BF3AD45438FF7CEA234FF12CB', X'0CC931D647E22943BC0A0711F206C312', X'0CC931D647E22943BC0A0711F206C312', 0, NULL, NULL, 2, NULL, 0, 'Soak cereal in the fresh pork blood the night befor preparation and keep in a cooler.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'72262581AD80B443B8D583B94BF642F2', X'0CC931D647E22943BC0A0711F206C312', X'0CC931D647E22943BC0A0711F206C312', 1, NULL, NULL, 2, NULL, 0, 'Grind meat with onions through 1/4 (6 mm) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D8F2B7928CAA264D897F5399B92D5597', X'0CC931D647E22943BC0A0711F206C312', X'0CC931D647E22943BC0A0711F206C312', 2, NULL, NULL, 2, NULL, 0, 'Mix meat with blood and spices.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'95817919842B91498BF680870B8BCD1B', X'0CC931D647E22943BC0A0711F206C312', X'0CC931D647E22943BC0A0711F206C312', 3, NULL, NULL, 2, NULL, 0, 'Stuff into 40 mm hog casings.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ED567C8A08266F489F05FDB9B90464E7', X'0CC931D647E22943BC0A0711F206C312', X'0CC931D647E22943BC0A0711F206C312', 4, NULL, NULL, 2, NULL, 0, 'Cook in water at 80° C (176° F) for 35 minutes.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E58C42065F207244A262273E0242C26C', X'0CC931D647E22943BC0A0711F206C312', X'0CC931D647E22943BC0A0711F206C312', 5, NULL, NULL, 2, NULL, 0, 'Cool and refrigerate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'89CDB92989779B419B2E66FF8AFDFD0B', X'DEC36B504FDA6D40A86AB5FA63D3CECF', X'DEC36B504FDA6D40A86AB5FA63D3CECF', 0, NULL, NULL, 2, NULL, 0, 'Cook all meats in water (below the boiling point) until soft. Spread on the table and cool.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FA3D5F3D0CB3BF4F86A1584135522511', X'DEC36B504FDA6D40A86AB5FA63D3CECF', X'DEC36B504FDA6D40A86AB5FA63D3CECF', 1, NULL, NULL, 2, NULL, 0, 'Grind the skins through 1/8 (3 mm) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'94F65DAA10B8044490A47E5B16A632FE', X'DEC36B504FDA6D40A86AB5FA63D3CECF', X'DEC36B504FDA6D40A86AB5FA63D3CECF', 2, NULL, NULL, 2, NULL, 0, 'Grind other meats and fat through 1/4 (6 mm) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C19A8839A41B204B8522330C4FE3AD46', X'DEC36B504FDA6D40A86AB5FA63D3CECF', X'DEC36B504FDA6D40A86AB5FA63D3CECF', 3, NULL, NULL, 2, NULL, 0, 'Boil the cabbage, drain and cool. Grind with onions through 1/8 (6 mm) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AFA6D3DE809B5C46B7368389D3B93729', X'DEC36B504FDA6D40A86AB5FA63D3CECF', X'DEC36B504FDA6D40A86AB5FA63D3CECF', 4, NULL, NULL, 2, NULL, 0, 'Stuff into 36 mm hog casings forming rings 12 (30 cm) long.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DCEBB2BB738F23489C71E60DC0E02E6E', X'DEC36B504FDA6D40A86AB5FA63D3CECF', X'DEC36B504FDA6D40A86AB5FA63D3CECF', 5, NULL, NULL, 2, NULL, 0, 'Cook in water at 80° C (176° F) for 30 minutes.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0B28D3A5CD179E448E23BA60D5448C28', X'DEC36B504FDA6D40A86AB5FA63D3CECF', X'DEC36B504FDA6D40A86AB5FA63D3CECF', 6, NULL, NULL, 2, NULL, 0, 'Place in cold water for 10 minutes.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F94064B8F68A6244B71BD435EABCA193', X'DEC36B504FDA6D40A86AB5FA63D3CECF', X'DEC36B504FDA6D40A86AB5FA63D3CECF', 7, NULL, NULL, 2, NULL, 0, 'Dry briefly and refrigerate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F35C740005D02D47B1D1103CA689012C', X'2F2B7C2CCBC79F42BECFEE89635D7971', X'2F2B7C2CCBC79F42BECFEE89635D7971', 0, NULL, NULL, 2, NULL, 0, 'Cook meats in a small amount of water: skins at 95° C (203° F) until soft; jowls at 85° C (185° F) until semi-soft; other meats at 80-85° C (176-185° F) until soft.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7DD43C4D549A6D4C9AFD12B615CA1D2C', X'2F2B7C2CCBC79F42BECFEE89635D7971', X'2F2B7C2CCBC79F42BECFEE89635D7971', 1, NULL, NULL, 2, NULL, 0, 'Save meat stock. Remove gristle from lungs and meat from heads when still warm. Spread meats apart on a flat surface to cool.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A4B9AD565B97D448978CD5C02C6AEF37', X'2F2B7C2CCBC79F42BECFEE89635D7971', X'2F2B7C2CCBC79F42BECFEE89635D7971', 2, NULL, NULL, 2, NULL, 0, 'Boil barley groats (200 g) in 400-440 ml of left over meat stock or water until semi-soft. Boil groats for 30 minutes stirring often. After boiling leave for 30-60 minutes covered.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4CEF2056326D294F9322970F120F8E34', X'2F2B7C2CCBC79F42BECFEE89635D7971', X'2F2B7C2CCBC79F42BECFEE89635D7971', 3, NULL, NULL, 2, NULL, 0, 'Grind head meat, hearts, snouts and ¼ (50 g) jowls through 3/8 (10 mm) plate. Grind lungs and remaining ¾ (150 g) jowls through 1/4 (5 mm plate). Grind boiled skins through 1/8 (3 mm) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E109F7B2696D994AA574BC9DBBD099FF', X'2F2B7C2CCBC79F42BECFEE89635D7971', X'2F2B7C2CCBC79F42BECFEE89635D7971', 4, NULL, NULL, 2, NULL, 0, 'Mix salt, spices and chopped onions with ground meats. Mix barley with blood and then mix everything together.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'561AA51A7623E140AC431480C9D75353', X'2F2B7C2CCBC79F42BECFEE89635D7971', X'2F2B7C2CCBC79F42BECFEE89635D7971', 5, NULL, NULL, 2, NULL, 0, 'Stuff loosely into pork middles or beef rounds forming straight links or rings 6-12 (15-30 cm) each.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'74C91416CFA03D44ACBFFD73A92331AA', X'2F2B7C2CCBC79F42BECFEE89635D7971', X'2F2B7C2CCBC79F42BECFEE89635D7971', 6, NULL, NULL, 2, NULL, 0, 'Immerse sausages in boiling water and cook at 80° C (176° F) for about 60-90 min until the sausages reach 68-70° C (154-158° F) internal temperature.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C6FF88C653C87A45805A0A4906BAF1E3', X'2F2B7C2CCBC79F42BECFEE89635D7971', X'2F2B7C2CCBC79F42BECFEE89635D7971', 7, NULL, NULL, 2, NULL, 0, 'Immerse sausages for 15 min in cold water, then hang in air to cool.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6D1433CD3F263D4A93B88E262FD60592', X'2F2B7C2CCBC79F42BECFEE89635D7971', X'2F2B7C2CCBC79F42BECFEE89635D7971', 8, NULL, NULL, 2, NULL, 0, 'Keep refrigerated');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4A1AFC5F29BF6545B14D6A3B0F51361C', X'54D9158AACEEBD46A4896B35453DCD48', X'54D9158AACEEBD46A4896B35453DCD48', 0, NULL, NULL, 2, NULL, 0, 'Boil skins and pork fat trimmings at 80° C (176° F) until soft. Grind when still warm together with raw chopped onion through 3 mm (1/8`) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F9783A783DA44F49A099B8489A168562', X'54D9158AACEEBD46A4896B35453DCD48', X'54D9158AACEEBD46A4896B35453DCD48', 1, NULL, NULL, 2, NULL, 0, 'Dice the cured tongues* into 25-30 mm (1`) cubes.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5E21EDB98453604A8777E35F52B23C9D', X'54D9158AACEEBD46A4896B35453DCD48', X'54D9158AACEEBD46A4896B35453DCD48', 2, NULL, NULL, 2, NULL, 0, 'Mix tongues, ground skins, blood and all ingredients together.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'15841DC3BBF657428A5FA7227D8DEA86', X'54D9158AACEEBD46A4896B35453DCD48', X'54D9158AACEEBD46A4896B35453DCD48', 3, NULL, NULL, 2, NULL, 0, 'Stuff into 120 mm synthetic waterproof casings making 50 cm (20`) sections.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4CBBF4CCC6BAFD4486D48182DEE29229', X'54D9158AACEEBD46A4896B35453DCD48', X'54D9158AACEEBD46A4896B35453DCD48', 4, NULL, NULL, 2, NULL, 0, 'Boil at 80° C (176° F) for 180 minutes.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3DAF7C660937654D8F11C80BB1B33FB7', X'54D9158AACEEBD46A4896B35453DCD48', X'54D9158AACEEBD46A4896B35453DCD48', 5, NULL, NULL, 2, NULL, 0, 'Immerse in tap water for 15 minutes. Place on a flat surface to cool. Roll over once in a while.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6E0C384EE9AF4F43B36EAC5A9BD9737A', X'54D9158AACEEBD46A4896B35453DCD48', X'54D9158AACEEBD46A4896B35453DCD48', 6, NULL, NULL, 2, NULL, 0, 'Refrigerate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3475528CDD8416438524A23072336C63', X'BAB883F711F8CC42A78E7DB9C1DE6F19', X'BAB883F711F8CC42A78E7DB9C1DE6F19', 0, NULL, NULL, 2, NULL, 0, 'Soak beans in water for 4 hours. Drain, then slow cook until tender. Use enough water to cover the beans.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'22D23E2983FC69449DCE85D44A0F44ED', X'BAB883F711F8CC42A78E7DB9C1DE6F19', X'BAB883F711F8CC42A78E7DB9C1DE6F19', 1, NULL, NULL, 2, NULL, 0, 'Add beans and 1/2 cup water into food processor and chop until a paste is obtained.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B7CFD800287CDD49A746930FF961675B', X'BAB883F711F8CC42A78E7DB9C1DE6F19', X'BAB883F711F8CC42A78E7DB9C1DE6F19', 2, NULL, NULL, 2, NULL, 0, 'Add all ingredients except wheat gluten and emulsify.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1C3A74D62EC757418C42CC64AA83DA3C', X'BAB883F711F8CC42A78E7DB9C1DE6F19', X'BAB883F711F8CC42A78E7DB9C1DE6F19', 3, NULL, NULL, 2, NULL, 0, 'Add vital wheat gluten and blend everything together. Add more water if necessary.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BEC639D095351F46B5EC4855FCD61E0B', X'BAB883F711F8CC42A78E7DB9C1DE6F19', X'BAB883F711F8CC42A78E7DB9C1DE6F19', 4, NULL, NULL, 2, NULL, 0, 'Stuff into desired casings (24-26 mm).');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AA553DA52EEC2D4AB75EF0FD48554857', X'BAB883F711F8CC42A78E7DB9C1DE6F19', X'BAB883F711F8CC42A78E7DB9C1DE6F19', 5, NULL, NULL, 2, NULL, 0, 'Cook sausages in water at 176 F (80 C), about 20 minutes.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E1CEC642BE33164CBC9BD4C4D88F87B7', X'BAB883F711F8CC42A78E7DB9C1DE6F19', X'BAB883F711F8CC42A78E7DB9C1DE6F19', 6, NULL, NULL, 2, NULL, 0, 'Cool in cold water.');
