INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2A4BAC82FADC9842A816B39B9EED5AF2', X'432C858A7DFCE544805E205FE956A2A5', X'432C858A7DFCE544805E205FE956A2A5', 6, NULL, NULL, 2, NULL, 0, 'Cool in cold water for 10 minutes, dry briefly and refrigerate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D64ABE37509207429782F30E84C6097E', X'BD1DCB9720E87A4D99875B9DEC62BECF', X'BD1DCB9720E87A4D99875B9DEC62BECF', 0, NULL, NULL, 2, NULL, 0, 'Wash sardines with water and drain.');
