INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'72624CC21644D84490BBE91180A7EDF9', X'BD1DCB9720E87A4D99875B9DEC62BECF', X'BD1DCB9720E87A4D99875B9DEC62BECF', 3, NULL, NULL, 2, NULL, 0, 'Stuff into 70 mm artificial waterproof casings forming 40 cm (16`) links.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1FEE9E3DDD035B4C8BE81BEDBA7AAD97', X'BD1DCB9720E87A4D99875B9DEC62BECF', X'BD1DCB9720E87A4D99875B9DEC62BECF', 4, NULL, NULL, 2, NULL, 0, 'Hold for 2 hours at room temperature.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FA9F82DA2BB66E4E8146F91F5CACC849', X'BD1DCB9720E87A4D99875B9DEC62BECF', X'BD1DCB9720E87A4D99875B9DEC62BECF', 5, NULL, NULL, 2, NULL, 0, 'Cook in water at 80° C (176° F) for 90 minutes.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E25172C17D819F438BB707EF123CE9CD', X'BD1DCB9720E87A4D99875B9DEC62BECF', X'BD1DCB9720E87A4D99875B9DEC62BECF', 6, NULL, NULL, 2, NULL, 0, 'Cool and refrigerate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F0B49C5640B9C64E981FBD44D1D1F689', X'E96F8AD18E5D2B4690F2A86629991320', X'E96F8AD18E5D2B4690F2A86629991320', 0, NULL, NULL, 2, NULL, 0, 'Grate the skin of the lemon, do not include the white underlayer known as pith which is bitter.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0C7CC8F72A8EDF4F87D99224F98A88ED', X'E96F8AD18E5D2B4690F2A86629991320', X'E96F8AD18E5D2B4690F2A86629991320', 1, NULL, NULL, 2, NULL, 0, 'Add 30 g of salt (almost 2 tablespoons) to 1 liter (quart) of water and bring to a boil. Choose best looking green cabbage leaves, cut into smaller pieces and blanch them for 5 minutes in a boiling water. Cool in cold water, drain and force the excess water out.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AE2DA31B97049C4E9CBB6DB31B1B53E0', X'E96F8AD18E5D2B4690F2A86629991320', X'E96F8AD18E5D2B4690F2A86629991320', 2, NULL, NULL, 2, NULL, 0, 'Chop onions finely and fry in lard in a skillet on low-heat until they become glassy and golden.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'71F195DC710EBC48B14A37013F1220CA', X'E96F8AD18E5D2B4690F2A86629991320', X'E96F8AD18E5D2B4690F2A86629991320', 3, NULL, NULL, 2, NULL, 0, 'Add cabbage leaves and fish stock, cover the skillet and simmer on low heat for 30-40 minutes. Drain resulting stew, and cool immediately in refrigerator.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0F83B7BAEF0C1B49B4C31B92931048AF', X'E96F8AD18E5D2B4690F2A86629991320', X'E96F8AD18E5D2B4690F2A86629991320', 4, NULL, NULL, 2, NULL, 0, 'Grind partially frozen fish flesh through 8-10 mm plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D36B049EAA8C114CB2EDA05220D1030A', X'E96F8AD18E5D2B4690F2A86629991320', X'E96F8AD18E5D2B4690F2A86629991320', 5, NULL, NULL, 2, NULL, 0, 'Mix ground fish, cabbage stew, eggs and spices together.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C58061B74F72E6439FEE2A21CC82C372', X'E96F8AD18E5D2B4690F2A86629991320', X'E96F8AD18E5D2B4690F2A86629991320', 6, NULL, NULL, 2, NULL, 0, 'Stuff the mixture into 30-32 mm pork casings, making 15 cm (6) links.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'55C5C15AFF629E4EB93C6486C6762B40', X'E96F8AD18E5D2B4690F2A86629991320', X'E96F8AD18E5D2B4690F2A86629991320', 7, NULL, NULL, 2, NULL, 0, 'Cook in salty water (20 g salt per 1 liter) at 80° C (176° F) for 25 minutes. Cool.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F28691F60211F6429EB6F9C0B85F55EC', X'E96F8AD18E5D2B4690F2A86629991320', X'E96F8AD18E5D2B4690F2A86629991320', 8, NULL, NULL, 2, NULL, 0, 'Keep refrigerated.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AF40B25B719977498DB3F1F5139AC0D8', X'3867F2B523776B478D79246B9CA4A206', X'3867F2B523776B478D79246B9CA4A206', 0, NULL, NULL, 2, NULL, 0, 'Clean the squids. Discard head/eyes/stomach but keep the tentacles. Rinse the tube well, inside and out, with cold water, then drain. Put 1 teaspoon of flour inside squid body and shake well to coat the inside. Shake any loose flour out of the body tubes. Set aside.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4CEAA32061A53443813C89EBBBDB0BD7', X'3867F2B523776B478D79246B9CA4A206', X'3867F2B523776B478D79246B9CA4A206', 1, NULL, NULL, 2, NULL, 0, 'Soak noodles in water for 30 minutes. Cook noodles in a boiling water for 3 minutes. Rinse in cold water and drain. Place noodles in a bowl and cut finely with scissors. Boil bean sprouts for 1 minute. Remove, rinse with cold water and drain.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6F096FF05E77FE4BAE24A6A75E57492C', X'3867F2B523776B478D79246B9CA4A206', X'3867F2B523776B478D79246B9CA4A206', 2, NULL, NULL, 2, NULL, 0, 'Chop tentacles into small pieces then mix into the seasoning paste. Seasoning Paste: Garlic, minced 7.0 g (2 cloves); Scallion, chopped 15 g (1 Tbsp); Red chili pepper 1; Green chili pepper 1; Sesame seeds, roasted 2 tsp; Sesame oil 5 ml (1 tsp); Salt 6.0 g (1 tsp); Black pepper 1.0 g (1/2 tsp). Finely mince the garlic, chopped scallions and peppers. Mix the ingredients together and let stand for 15 minutes.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'919C768A9208FE4FA75FFA03B8DBD16C', X'3867F2B523776B478D79246B9CA4A206', X'3867F2B523776B478D79246B9CA4A206', 3, NULL, NULL, 2, NULL, 0, 'Mash tofu and squeeze the water out. Place tofu in cheese cloth and squeeze out the remaining water.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1227DA4A2377E24BB9D4DA5B20F42305', X'3867F2B523776B478D79246B9CA4A206', X'3867F2B523776B478D79246B9CA4A206', 4, NULL, NULL, 2, NULL, 0, 'Place in a bowl: seasoning paste with tentacles, been sprouts, tofu, egg, soy sauce, chopped green onion and carrots and mix all together.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D51B9BAD66E5B34B9F6BCD7E5D0E5F5E', X'3867F2B523776B478D79246B9CA4A206', X'3867F2B523776B478D79246B9CA4A206', 5, NULL, NULL, 2, NULL, 0, 'Stuff squid and seal the open end with a toothpick or cooking thread.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9127DF06CCEF3143BF41656336890B8E', X'3867F2B523776B478D79246B9CA4A206', X'3867F2B523776B478D79246B9CA4A206', 6, NULL, NULL, 2, NULL, 0, 'Place in steamer basket and steam for 30 minutes. Remove from steamer and cool. Cut into 1/2 inch slices.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'79EF5F444482FE44AA457D18E2594C84', X'3867F2B523776B478D79246B9CA4A206', X'3867F2B523776B478D79246B9CA4A206', 7, NULL, NULL, 2, NULL, 0, 'Serve warm or at room temp with dipping sauce OR carefully insert each disc into a beaten egg and fry on pan on both sides in a little oil or butter on low heat until golden brown. Serve with dipping sauce. Dipping Sauce: Soy sauce 15 ml (1 Tbsp); Rice whine or rice vinegar 15 ml (1 Tbsp); Sugar 3.0 g (1/2 tsp); Sesame seeds, roasted 1 tsp.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'66C66F5F16D1B84FBFC0662A39D1E72B', X'CCA462B298F78F4091EAED54FE8D321D', X'CCA462B298F78F4091EAED54FE8D321D', 0, NULL, NULL, 2, NULL, 0, '*Bean paste. Soak dry beans overnight. Simmer in a little water for a few hours until soft. Rinse and drain. You may simplify the process by using canned beans.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FE6E8E3224174541968E59D601C5E2D7', X'CCA462B298F78F4091EAED54FE8D321D', X'CCA462B298F78F4091EAED54FE8D321D', 1, NULL, NULL, 2, NULL, 0, 'Grind beef through 3/16 (5 mm) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'644ACD02AF754646B253EC5932380850', X'CCA462B298F78F4091EAED54FE8D321D', X'CCA462B298F78F4091EAED54FE8D321D', 2, NULL, NULL, 2, NULL, 0, 'Mix everything together.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'15B3599C25F83245929608790B65580B', X'CCA462B298F78F4091EAED54FE8D321D', X'CCA462B298F78F4091EAED54FE8D321D', 3, NULL, NULL, 2, NULL, 0, 'Stuff into 36 mm casings.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1FBD89DA8384054BAE1BA409EAE45A67', X'CCA462B298F78F4091EAED54FE8D321D', X'CCA462B298F78F4091EAED54FE8D321D', 4, NULL, NULL, 2, NULL, 0, 'Cook sausage in water at 176° F (80° C), about 15-20 minutes.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0A3E6072F83D48429B6BB0280675EB52', X'CCA462B298F78F4091EAED54FE8D321D', X'CCA462B298F78F4091EAED54FE8D321D', 5, NULL, NULL, 2, NULL, 0, 'Cool in cold water.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A7F2E8E8F7D68E43A2CF247E46D3B814', X'CCA462B298F78F4091EAED54FE8D321D', X'CCA462B298F78F4091EAED54FE8D321D', 6, NULL, NULL, 2, NULL, 0, 'Store in refrigerator.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8CD5E36DC2751248811219E2097D1EA5', X'5962C7CF6153884FBEC1F2C0A74203FA', X'5962C7CF6153884FBEC1F2C0A74203FA', 0, NULL, NULL, 2, NULL, 0, 'Soak TVP in cold water for 30 minutes.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'121D2F9311CEE240911F2E9FF6499FAD', X'5962C7CF6153884FBEC1F2C0A74203FA', X'5962C7CF6153884FBEC1F2C0A74203FA', 1, NULL, NULL, 2, NULL, 0, 'Grind beef through a small plate (1/8, 3 mm).');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'744EFDDDF95ABD4985EAC978A88875BB', X'5962C7CF6153884FBEC1F2C0A74203FA', X'5962C7CF6153884FBEC1F2C0A74203FA', 2, NULL, NULL, 2, NULL, 0, 'In a food processor chop cold water, ground meat, salt, phosphate, cure #1, and sodium erythorbate. Add potato starch, non-fat dry milk, carrageenan and chop. Add spices and fat emulsion and chop/emulsify everything together.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'24644694F432134B86BD0C17654A0613', X'5962C7CF6153884FBEC1F2C0A74203FA', X'5962C7CF6153884FBEC1F2C0A74203FA', 3, NULL, NULL, 2, NULL, 0, 'Stuff into 22-26 mm sheep casings.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0938FF8809EEA042937108F3539119B2', X'5962C7CF6153884FBEC1F2C0A74203FA', X'5962C7CF6153884FBEC1F2C0A74203FA', 4, NULL, NULL, 2, NULL, 0, 'Hold at room temperature for 30 minutes.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7757476684FA1A4197877A6C4D13C4B9', X'5962C7CF6153884FBEC1F2C0A74203FA', X'5962C7CF6153884FBEC1F2C0A74203FA', 5, NULL, NULL, 2, NULL, 0, 'Smoke at 65° C (150° F) for 30-40 minutes.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A9E5ADEE43D5014BBEB102F39F46CE00', X'5962C7CF6153884FBEC1F2C0A74203FA', X'5962C7CF6153884FBEC1F2C0A74203FA', 6, NULL, NULL, 2, NULL, 0, 'Cook sausage in water at 176° F (80° C), about 15-20 minutes.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AAA99E82363D0847BFE075FE21C47BE8', X'5962C7CF6153884FBEC1F2C0A74203FA', X'5962C7CF6153884FBEC1F2C0A74203FA', 7, NULL, NULL, 2, NULL, 0, 'Cold shower or cool in cold water. Store in refrigerator.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D6239C90D27E234AA0EAF3D38AA55D36', X'1748DBA8F7EA3B49A4A460DC68DF6BDE', X'1748DBA8F7EA3B49A4A460DC68DF6BDE', 0, NULL, NULL, 2, NULL, 0, 'Soak TVP in cold water for 30 minutes.');
