INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'363B0415CC4661468EA968480A2CFE7F', X'40289882E6E56F4CA9D9A67DDEA70031', X'40289882E6E56F4CA9D9A67DDEA70031', 5, NULL, NULL, 2, NULL, 0, 'Cool down to less than 53º F (12º C).');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B46126B2E084AB4494FB16F942C36479', X'40289882E6E56F4CA9D9A67DDEA70031', X'40289882E6E56F4CA9D9A67DDEA70031', 6, NULL, NULL, 2, NULL, 0, 'Store in refrigerator.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'58C98B048DB4DD4D92B6B59ADABAEF97', X'0ED462D6BCC38544B1DF6C7155F823CA', X'0ED462D6BCC38544B1DF6C7155F823CA', 0, NULL, NULL, 2, NULL, 0, 'Grind all meats through 2 mm plate. Then (optional step), they may be briefly chopped (emulsified) in bowl cutter (food processor) without adding water. During emulsifying the temperature of the meat should not exceed 15° C (59° F).');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BBBC79ED18FC994B9E2E14A44360E415', X'0ED462D6BCC38544B1DF6C7155F823CA', X'0ED462D6BCC38544B1DF6C7155F823CA', 1, NULL, NULL, 2, NULL, 0, 'Mix ground meat with all ingredients (without adding water) until sticky.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5B25B7133C2CC54EAB336248AF8485CA', X'0ED462D6BCC38544B1DF6C7155F823CA', X'0ED462D6BCC38544B1DF6C7155F823CA', 2, NULL, NULL, 2, NULL, 0, 'Stuff into 50 mm synthetic cellulose or fibrous casings. Form straight 20-25 cm (8-10) long links.');
