INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'77D21AF9A7980845801BF8231C8CDC25', X'0ED462D6BCC38544B1DF6C7155F823CA', X'0ED462D6BCC38544B1DF6C7155F823CA', 4, NULL, NULL, 2, NULL, 0, 'Apply a thin cold smoke for 1-2 days until casings develop light brown color. Re-arrange smoke sticks or smoke carts during smoking.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2B5E90CEEF522D4892834A7AB9C4B0FF', X'0ED462D6BCC38544B1DF6C7155F823CA', X'0ED462D6BCC38544B1DF6C7155F823CA', 5, NULL, NULL, 2, NULL, 0, 'Cool in air to 12° C (53° F) or lower.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1DE7C5836DE9774B9FAAAFFE1382DEDD', X'B23EE6F9CBC53F45AC9F674A502F8BF5', X'B23EE6F9CBC53F45AC9F674A502F8BF5', 0, NULL, NULL, 2, NULL, 0, 'Grind beef and pork at least two times through a 1/8 (2 mm) or 3/16 (3 mm) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8930659DC5EFA148BC8FC0C6E86BD6E7', X'B23EE6F9CBC53F45AC9F674A502F8BF5', X'B23EE6F9CBC53F45AC9F674A502F8BF5', 1, NULL, NULL, 2, NULL, 0, 'Add to meat all remaining ingredients and mix everything well together until feels sticky. Do not add water.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6508C0B277F7BF4394F44F7998DE2FD5', X'B23EE6F9CBC53F45AC9F674A502F8BF5', X'B23EE6F9CBC53F45AC9F674A502F8BF5', 2, NULL, NULL, 2, NULL, 0, 'Stuff tightly into 36-40 mm hog casings, beef rounds, or synthetic fibrous casings.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AB3A6AD1154A94478C50A32C09A6A124', X'B23EE6F9CBC53F45AC9F674A502F8BF5', X'B23EE6F9CBC53F45AC9F674A502F8BF5', 3, NULL, NULL, 2, NULL, 0, 'Hang for 12 hours at 50º F (10º C) and 85-95 % humidity.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3E4E41E0593F2441A2CF4D562EEFA6D1', X'B23EE6F9CBC53F45AC9F674A502F8BF5', X'B23EE6F9CBC53F45AC9F674A502F8BF5', 4, NULL, NULL, 2, NULL, 0, 'Smoke with cold smoke (below 77º F, 25º C) until gold-brown color is obtained. That should take 10-12 hours.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3D663414E5160D4BB39202DBFC77B635', X'B23EE6F9CBC53F45AC9F674A502F8BF5', X'B23EE6F9CBC53F45AC9F674A502F8BF5', 5, NULL, NULL, 2, NULL, 0, 'Cool down to less than 53º F (12º C).');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E901B782EB9BB144B985DF2F40252AAE', X'B23EE6F9CBC53F45AC9F674A502F8BF5', X'B23EE6F9CBC53F45AC9F674A502F8BF5', 6, NULL, NULL, 2, NULL, 0, 'Store in refrigerator.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'51AFDD1216BE4C4380C6E115C0691ABB', X'445B9B0416FC28478F507797AC4C38E5', X'445B9B0416FC28478F507797AC4C38E5', 0, NULL, NULL, 2, NULL, 0, 'Grind all meats through 3 mm (1/8`) plate. Re-freeze and grind again. You may grind once and then emulsify in the food processor without adding water.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'623601E3D35F3840BCB7EBDD8A124B82', X'445B9B0416FC28478F507797AC4C38E5', X'445B9B0416FC28478F507797AC4C38E5', 1, NULL, NULL, 2, NULL, 0, 'Mix meats with all ingredients together.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4463A9A062A54C4EB610A6BEDA31D9E0', X'445B9B0416FC28478F507797AC4C38E5', X'445B9B0416FC28478F507797AC4C38E5', 2, NULL, NULL, 2, NULL, 0, 'Stuff firmly into 45 mm beef middles or fibrous casings. Form 40 cm (16`) links or shorter.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8F7F17CB0681E944B30810E57B48F6A7', X'445B9B0416FC28478F507797AC4C38E5', X'445B9B0416FC28478F507797AC4C38E5', 3, NULL, NULL, 2, NULL, 0, 'Ferment for 48 hours at 18º C (64º F), 75-80% humidity.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F1B71890888B184781B4DDF3262AD6A7', X'445B9B0416FC28478F507797AC4C38E5', X'445B9B0416FC28478F507797AC4C38E5', 4, NULL, NULL, 2, NULL, 0, 'Apply cold smoke for 12 hours at 18º C (64º F).');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BBF3CFF1AD66A04E81C89638DBF97165', X'445B9B0416FC28478F507797AC4C38E5', X'445B9B0416FC28478F507797AC4C38E5', 5, NULL, NULL, 2, NULL, 0, 'Refrigerate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C0E58E79B77FF84CA1D3E67404E10161', X'33C051079CD1E443876CA6CA28A804C9', X'33C051079CD1E443876CA6CA28A804C9', 0, NULL, NULL, 2, NULL, 0, 'Grind pork and beef through 3/16 plate (5 mm).');
