INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8ABEDAD0D80476429738130A42C1BEDE', X'33C051079CD1E443876CA6CA28A804C9', X'33C051079CD1E443876CA6CA28A804C9', 2, NULL, NULL, 2, NULL, 0, 'Stuff into beef middles or fibrous casings about 60 mm diameter, form 30 links.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ED64F61EC6E79C4DBEFE2E075F232206', X'33C051079CD1E443876CA6CA28A804C9', X'33C051079CD1E443876CA6CA28A804C9', 3, NULL, NULL, 2, NULL, 0, 'Ferment at 38º C (100º F) for 24 hours, 90-85% humidity.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AD01AC959A83BF488E240E888A348390', X'33C051079CD1E443876CA6CA28A804C9', X'33C051079CD1E443876CA6CA28A804C9', 4, NULL, NULL, 2, NULL, 0, 'Introduce warm smoke (43º C, 110º F), 70% humidity, for 12 hours. Gradually increase smoke temperature until internal meat temperature of 140º F (60º C) is obtained.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BF316B9C314AC64FBE1E61664DFED815', X'33C051079CD1E443876CA6CA28A804C9', X'33C051079CD1E443876CA6CA28A804C9', 5, NULL, NULL, 2, NULL, 0, 'For a drier sausage: dry for 2 days at 22-16º C (60-70º F), 65-75% humidity or until desired weight loss has occurred.');
