INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'19927BF95147C149A83B3A5D8E3F6A64', X'D170FAC479C30741A44BE28CCABA6852', X'D170FAC479C30741A44BE28CCABA6852', 0, NULL, NULL, 2, NULL, 0, 'Grind pork through plate 5 (1/4`) mm. Grind beef through 3 mm (1/8`) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F0170F3DA57E8C428E83F0B4450945AC', X'D170FAC479C30741A44BE28CCABA6852', X'D170FAC479C30741A44BE28CCABA6852', 1, NULL, NULL, 2, NULL, 0, 'Mix all ingredients with meat.');
