INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'42E1663295C65144BE330EE3D32A27B1', X'D170FAC479C30741A44BE28CCABA6852', X'D170FAC479C30741A44BE28CCABA6852', 4, NULL, NULL, 2, NULL, 0, 'Introduce warm smoke (43º C, 110º F), 70% humidity, for 6 hours. Gradually increase smoke temperature until internal meat temperature of 140º F (60º C) is obtained.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'07DAAAD2A5E2484095FF7BFA40CF98E9', X'D170FAC479C30741A44BE28CCABA6852', X'D170FAC479C30741A44BE28CCABA6852', 5, NULL, NULL, 2, NULL, 0, 'For a drier sausage: dry for 3 days at 22?16º C (70?60º F), 65-75% humidity or until desired weight loss has occurred.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1E385D46C8BB4A428461F7AF75723A59', X'D170FAC479C30741A44BE28CCABA6852', X'D170FAC479C30741A44BE28CCABA6852', 6, NULL, NULL, 2, NULL, 0, 'Store sausages at 10-15º C (50-59º F), <75% humidity..');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1B38A3C01521394A818CD6AD98DA7AB0', X'6C5B0214744B9849A552592EB39C4B71', X'6C5B0214744B9849A552592EB39C4B71', 0, NULL, NULL, 2, NULL, 0, 'Grind beef through 1/8 - 3/16 (3-5 mm) plate.');
