INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0697598F990EC44696D9D0E6FA12CFD6', X'6C5B0214744B9849A552592EB39C4B71', X'6C5B0214744B9849A552592EB39C4B71', 6, NULL, NULL, 2, NULL, 0, 'Store sausages at 10-15º C (50-59º F), <75% humidity.');
