INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'415CB0F3202EF1439493565466561610', X'EC035AD6B3665140A766AA032BEDCA11', X'EC035AD6B3665140A766AA032BEDCA11', 1, NULL, NULL, 2, NULL, 0, 'Grind collagen rich pork pork (with connective tissue) through 1/8` (2 mm) plate and then emulsify in food processor adding 20-30% of crushed ice or cold water. Add spices at this stage.Cut lean veal into 5-6 cm (2) pieces. Grind semi-fat veal through 1/2` (10 mm plate). Grind fat pieces through 3/8` (8 mm) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C96636D447162148816181DC01762FE4', X'EC035AD6B3665140A766AA032BEDCA11', X'EC035AD6B3665140A766AA032BEDCA11', 2, NULL, NULL, 2, NULL, 0, 'Mix veal class I with veal class II until gluey, then add emulsified mixture and mix everything together.');
