INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'855E611C959DE440A3FA0D19ED8D86BC', X'EC035AD6B3665140A766AA032BEDCA11', X'EC035AD6B3665140A766AA032BEDCA11', 4, NULL, NULL, 2, NULL, 0, 'Condition sausages for 12 hours at 35-43°F (2-6°C). Drying is also allowed at room temperature for 3-4 hours.');
