INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'89F4534C029BE448BCAF7577C10879CB', X'75F587623FEFC348A9C790ABFB20A07F', X'75F587623FEFC348A9C790ABFB20A07F', 6, NULL, NULL, 2, NULL, 0, 'Dry at 18º C (64º F), 90 ? 85% humidity for 2 days.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AA7CD7BCA8808547B5116347B308543A', X'75F587623FEFC348A9C790ABFB20A07F', X'75F587623FEFC348A9C790ABFB20A07F', 7, NULL, NULL, 2, NULL, 0, 'Cold smoke at 18º C (64º F) for 24 hours.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4D5736DB4C119C42A83DDB3F49B8E216', X'75F587623FEFC348A9C790ABFB20A07F', X'75F587623FEFC348A9C790ABFB20A07F', 8, NULL, NULL, 2, NULL, 0, 'Dry at 15 ? 12º C (59 ? 12º F), 85 ? 75% humidity for 4 weeks or until sausage looses 30% of its original weight.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5878300BF6CF5645B4154F1C81D0C5B4', X'75F587623FEFC348A9C790ABFB20A07F', X'75F587623FEFC348A9C790ABFB20A07F', 9, NULL, NULL, 2, NULL, 0, 'Store at 10-12º C (50-53º F), <75% humidity.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'78D9A59DF1293E4894638EF1A1A3CBA3', X'217A1ABB03D68944AD88EB933A721393', X'217A1ABB03D68944AD88EB933A721393', 0, NULL, NULL, 2, NULL, 0, 'Grind all meats through 3 mm (1/8`) plate. Re-freeze meats and grind again. You may grind once and then emulsify in the food processor without adding water.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D5E5DD4119F3AE4492CD2510C0D7B4B8', X'217A1ABB03D68944AD88EB933A721393', X'217A1ABB03D68944AD88EB933A721393', 1, NULL, NULL, 2, NULL, 0, 'Add all ingredients during this step.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6A1E0BAF211D7F42A9D9628AFB9F0B9C', X'217A1ABB03D68944AD88EB933A721393', X'217A1ABB03D68944AD88EB933A721393', 2, NULL, NULL, 2, NULL, 0, 'Stuff firmly into 40-60 mm beef middles or fibrous casings. Form 8-10 (20-25 cm) links.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'92FF7158D8D06D42951D5E05839140A2', X'217A1ABB03D68944AD88EB933A721393', X'217A1ABB03D68944AD88EB933A721393', 3, NULL, NULL, 2, NULL, 0, 'Ferment for 24 hours at 18º C (64º F), 75-80% humidity.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0A909AE67D151943A8FD56B9112506B5', X'217A1ABB03D68944AD88EB933A721393', X'217A1ABB03D68944AD88EB933A721393', 4, NULL, NULL, 2, NULL, 0, 'Cold smoke at 18º C (64º F) for 12 hours.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'05B332FA9DA61C47B7D78235DC02DF01', X'217A1ABB03D68944AD88EB933A721393', X'217A1ABB03D68944AD88EB933A721393', 5, NULL, NULL, 2, NULL, 0, 'Refrigerate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4BA81A6062891E4588C3208F8B21181A', X'CFB6555615D29A4EA33CF1BC19C54F6F', X'CFB6555615D29A4EA33CF1BC19C54F6F', 0, NULL, NULL, 2, NULL, 0, 'Curing meat. Cut meat into 1 inch (25 mm) pieces. Place beef, pork and back fat in separate food grade containers. Mix pork and beef with salt, Cure #1 and sugar according to the recipe (estimate amounts).');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6AB9CFB6E686014BA0F49A692D8D6921', X'CFB6555615D29A4EA33CF1BC19C54F6F', X'CFB6555615D29A4EA33CF1BC19C54F6F', 1, NULL, NULL, 2, NULL, 0, 'Back fat needs salt only. Pack tightly to eliminate air and cover with clean cloth. Place for 72 hours in refrigerator. Discard any liquid brine and submit meat to grinding.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0509D1411698044A87AA66AC7FB36780', X'CFB6555615D29A4EA33CF1BC19C54F6F', X'CFB6555615D29A4EA33CF1BC19C54F6F', 2, NULL, NULL, 2, NULL, 0, 'Grind beef through 1/8 (2 mm) plate. Grind pork through 1/8 (2 mm) plate. Dice back fat into 3/16 (4 mm) cubes.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FC1A1D1250A1714AB5616FFA69AE80E5', X'CFB6555615D29A4EA33CF1BC19C54F6F', X'CFB6555615D29A4EA33CF1BC19C54F6F', 3, NULL, NULL, 2, NULL, 0, 'Mix beef with salt, cure, sugar and spices. Add pork and mix. Add fat cubes and remix everything together. Dont add any water.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'49CC454DD6AAE3428EE289232AB9A4DB', X'CFB6555615D29A4EA33CF1BC19C54F6F', X'CFB6555615D29A4EA33CF1BC19C54F6F', 4, NULL, NULL, 2, NULL, 0, 'Stuff sausages firmly into 45-50 mm beef middles or fibrous casings. Sausage length up to 20 (50 cm).');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D1272B7D6F075E40BC990D82C584BB6C', X'CFB6555615D29A4EA33CF1BC19C54F6F', X'CFB6555615D29A4EA33CF1BC19C54F6F', 5, NULL, NULL, 2, NULL, 0, 'Hang sausages for 5-7 days at 2-4 C, 85-90% humidity.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'25052BAA09CCBD4894952C69108CF0B9', X'CFB6555615D29A4EA33CF1BC19C54F6F', X'CFB6555615D29A4EA33CF1BC19C54F6F', 6, NULL, NULL, 2, NULL, 0, 'Using dry wood, smoke sausages for 2-3 days with cold smoke, 18-22 C.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'311C33FF55322B42A0560AB6C311E5CC', X'CFB6555615D29A4EA33CF1BC19C54F6F', X'CFB6555615D29A4EA33CF1BC19C54F6F', 7, NULL, NULL, 2, NULL, 0, 'Dry sausages for 5-7 days (depending on sausage diameter) at 11-15 C, 80-84 humidity. Dry 20-23 days more at 10-12 C, 74-78% humidity.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FF9F4543935B8E4DB649A3E6782442E4', X'9A79D15E27A10C49B816F1B6B33164CF', X'9A79D15E27A10C49B816F1B6B33164CF', 0, NULL, NULL, 2, NULL, 0, 'Cut all lean pork, fat, tails, tongues, spare ribs and face meats into smaller (finger length) pieces.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'57AD8B9CC69D0143BF08F7CE91C2892C', X'9A79D15E27A10C49B816F1B6B33164CF', X'9A79D15E27A10C49B816F1B6B33164CF', 1, NULL, NULL, 2, NULL, 0, 'Mix meats with salt and spices. Hold for 24 hours in refrigerator.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A58C8F0F7B1C9040B3BAD10767F49311', X'9A79D15E27A10C49B816F1B6B33164CF', X'9A79D15E27A10C49B816F1B6B33164CF', 2, NULL, NULL, 2, NULL, 0, 'Stuff into pork stomachs.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9CCF647F53FCBE4BACE8B38A80CF1C56', X'9A79D15E27A10C49B816F1B6B33164CF', X'9A79D15E27A10C49B816F1B6B33164CF', 3, NULL, NULL, 2, NULL, 0, 'Dry at 20° C (68° F) for 20 days.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'76C7931BDB8BD741B823E46D67D2DECD', X'9A79D15E27A10C49B816F1B6B33164CF', X'9A79D15E27A10C49B816F1B6B33164CF', 4, NULL, NULL, 2, NULL, 0, 'Dry at 15-10° C (59-50° F) for 1-2 months depending on the size of a stomach.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9C092DCABD86D8418E19F5142847C710', X'EE0007A6E336B54F9A6C95A82CA6AFEF', X'EE0007A6E336B54F9A6C95A82CA6AFEF', 0, NULL, NULL, 2, NULL, 0, 'Meat is manually boned, which enables the thorough removal of sinews, gristle and connective tissue. Cut the meat into 3/16 (5 mm) pieces.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2CAAA7B3ED69DA42B3B246EFE697C136', X'EE0007A6E336B54F9A6C95A82CA6AFEF', X'EE0007A6E336B54F9A6C95A82CA6AFEF', 1, NULL, NULL, 2, NULL, 0, 'Cut fat into 1/8 (3 mm) pieces.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CD87AD480B08A947B75644885EBD1691', X'EE0007A6E336B54F9A6C95A82CA6AFEF', X'EE0007A6E336B54F9A6C95A82CA6AFEF', 2, NULL, NULL, 2, NULL, 0, 'Mix lean meat with all ingredients. Add fat and remix.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'986233F890FC164291334521AEC255F3', X'EE0007A6E336B54F9A6C95A82CA6AFEF', X'EE0007A6E336B54F9A6C95A82CA6AFEF', 3, NULL, NULL, 2, NULL, 0, 'Stuff into horse or 40-85 mm fibrous casings forming 13-20 (33-50 cm) long links.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'49CA9C24D6DFEC4DAA42CD94CC5463CB', X'EE0007A6E336B54F9A6C95A82CA6AFEF', X'EE0007A6E336B54F9A6C95A82CA6AFEF', 4, NULL, NULL, 2, NULL, 0, 'Apply a thin cold smoke at 16° C (60° F) for 12-20 days using beech wood. Cold smoking is not a continuous process, it can be interrupted often, however, the temperature of 16° C shoud be maintained. You are drying the meat with smoke.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'01F9068B467F0742A5BF8E147591D776', X'EE0007A6E336B54F9A6C95A82CA6AFEF', X'EE0007A6E336B54F9A6C95A82CA6AFEF', 5, NULL, NULL, 2, NULL, 0, 'Place sausages in a fermenting/drying room at 10-14° C (50-56° F), 86-90% humidity to permit the growth of mold.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B7BA7FD8C2C57041A1AF1969204F1A2A', X'EE0007A6E336B54F9A6C95A82CA6AFEF', X'EE0007A6E336B54F9A6C95A82CA6AFEF', 6, NULL, NULL, 2, NULL, 0, 'The fermentation/drying process continues for 2-3 months depending on the diameter (not the length) of the sausage. Once the mold appears the relative humidity can be lowered, however, it should not drop below 45% as the drying will be too fast.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'66D08098AD2DB9439C9713F2B0DC7F27', X'EE0007A6E336B54F9A6C95A82CA6AFEF', X'EE0007A6E336B54F9A6C95A82CA6AFEF', 7, NULL, NULL, 2, NULL, 0, 'The total production time from filling the sausages is at least 90 days.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'67DF2F20A66E3A4E84FD20CE038E204E', X'627619B5359759439549664DB56913AE', X'627619B5359759439549664DB56913AE', 0, NULL, NULL, 2, NULL, 0, 'Mix all ingredients together to create a marinade. Cut meat into small pieces.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5A7EF07D8C729A48A0BC776DB400E0F4', X'627619B5359759439549664DB56913AE', X'627619B5359759439549664DB56913AE', 1, NULL, NULL, 2, NULL, 0, 'Immerse meat in the marinade and hold for 3 days in refrigerator. Drain.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FC2AFB755569044DAB1C50B2EA59B03E', X'627619B5359759439549664DB56913AE', X'627619B5359759439549664DB56913AE', 2, NULL, NULL, 2, NULL, 0, 'Stuff into pigs stomach, bladder or a large diameter casing.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'24686067806D3A458FE9D4299DE0F0DC', X'627619B5359759439549664DB56913AE', X'627619B5359759439549664DB56913AE', 3, NULL, NULL, 2, NULL, 0, 'Apply a thin cold smoke at 18° C (64° F) for 15 days. Smoking need not to be continuous, for example the fire can be put out at night and restarted in the morning as long as the temperature inside of the smokehouse stays below 18° C. The sausage is smoked with oak, chestnut or a mixture of both.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D8CD0C58F1042949BC599080DDD23F4B', X'627619B5359759439549664DB56913AE', X'627619B5359759439549664DB56913AE', 4, NULL, NULL, 2, NULL, 0, 'Dry at 12-15° C (54-59° F), 75% humidity for 14 days.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2362B73449747C4D848EED9FFD755FC9', X'627619B5359759439549664DB56913AE', X'627619B5359759439549664DB56913AE', 5, NULL, NULL, 2, NULL, 0, 'Store at 12° C (54° F), 70% humidity.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'47A668287310234083AD81FA1F05B47A', X'D810845E0FB17A42A20617918EF4D5C9', X'D810845E0FB17A42A20617918EF4D5C9', 0, NULL, NULL, 2, NULL, 0, 'Grind meat and fat through 1/4 (6 mm) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C49C46663DA8C840B158B026A277CEB3', X'D810845E0FB17A42A20617918EF4D5C9', X'D810845E0FB17A42A20617918EF4D5C9', 1, NULL, NULL, 2, NULL, 0, 'Mix all ingredients with ground meat and fat. Hold for 24 hours in refrigerator.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'613579BF4C05804CA4EBC3C473052E42', X'D810845E0FB17A42A20617918EF4D5C9', X'D810845E0FB17A42A20617918EF4D5C9', 2, NULL, NULL, 2, NULL, 0, 'Stuff firmly into 32 mm hog casings. Make 24 (60 cm) long links.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4998EF57C36D6E4FA18FDB2366C286C8', X'D810845E0FB17A42A20617918EF4D5C9', X'D810845E0FB17A42A20617918EF4D5C9', 3, NULL, NULL, 2, NULL, 0, 'Ferment/dry at 25º C (77º F) for 24 hours, 90-85% humidity.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5B86A1E6E0EE3047A604961A6BC908F4', X'D810845E0FB17A42A20617918EF4D5C9', X'D810845E0FB17A42A20617918EF4D5C9', 4, NULL, NULL, 2, NULL, 0, 'Dry at 16-12º C (60-54º F), 85-80% humidity for 14 days. Store sausages at 10-12º C (50-54º F), <75% humidity or refrigerate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'62FEE32D4491DC44BAACDB19D7B5D885', X'BA8BDAF47D8B734F988F87F65469BB47', X'BA8BDAF47D8B734F988F87F65469BB47', 0, NULL, NULL, 2, NULL, 0, 'Grind meat through 13 mm (3/8`) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EA8774A161357C46BDF5D657008EE0A2', X'BA8BDAF47D8B734F988F87F65469BB47', X'BA8BDAF47D8B734F988F87F65469BB47', 1, NULL, NULL, 2, NULL, 0, 'Mix ground meat with all ingredients until sticky. Do not add water.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BE811D32525FB54FB6ED5584E5EE4047', X'BA8BDAF47D8B734F988F87F65469BB47', X'BA8BDAF47D8B734F988F87F65469BB47', 2, NULL, NULL, 2, NULL, 0, 'Stuff into 32-36 mm hog casings forming 15 cm (6) long links. Leave links in long coils.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C310AACB0BE4174DB6077BBFA212EDDA', X'BA8BDAF47D8B734F988F87F65469BB47', X'BA8BDAF47D8B734F988F87F65469BB47', 3, NULL, NULL, 2, NULL, 0, 'Condition (hang) sausage for 1-2 days at 2-6° C (35-42° F) and 85-90% humidity.');
