INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'05F3EDB4B4211C48885DC9A7E6CA083D', X'ED7CF93FFE18114288E01BCA6210ACDB', X'ED7CF93FFE18114288E01BCA6210ACDB', 0, NULL, NULL, 2, NULL, 0, 'Grind pork and fat through 8 mm (3/8`) plate.');
