INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'801FF2647F40324C994D0ADC5B5CA429', X'ED7CF93FFE18114288E01BCA6210ACDB', X'ED7CF93FFE18114288E01BCA6210ACDB', 2, NULL, NULL, 2, NULL, 0, 'Mix all ingredients with ground pork.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B2F9C0D75B559249B2482B1C9D48AC30', X'ED7CF93FFE18114288E01BCA6210ACDB', X'ED7CF93FFE18114288E01BCA6210ACDB', 3, NULL, NULL, 2, NULL, 0, 'Stuff firmly into 24 mm sheep casings. Make 24 (60 cm) long links.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D71BCF004DFA584E8BB529D8B3564A67', X'ED7CF93FFE18114288E01BCA6210ACDB', X'ED7CF93FFE18114288E01BCA6210ACDB', 4, NULL, NULL, 2, NULL, 0, 'Ferment at 20º C (68º F) for 72 hours, 90-85% humidity.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CADB65642F1C444799DEFFCCCBCC4747', X'ED7CF93FFE18114288E01BCA6210ACDB', X'ED7CF93FFE18114288E01BCA6210ACDB', 5, NULL, NULL, 2, NULL, 0, 'Dry at 15-12º C (59-53º F), 85-80% humidity for 3 weeks.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'469054E99C929A47AECF506D7BB68FF4', X'ED7CF93FFE18114288E01BCA6210ACDB', X'ED7CF93FFE18114288E01BCA6210ACDB', 6, NULL, NULL, 2, NULL, 0, 'Store sausages at 10-12º C (50-53º F), 65-70% humidity or refrigerate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'637080054A47D44C8091C237E9AB5106', X'5A660E2279125C4E8D799E8151FA239D', X'5A660E2279125C4E8D799E8151FA239D', 0, NULL, NULL, 2, NULL, 0, 'Cut meats into 25 mm (1) pieces. Mix with all ingredients.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C5C023FC20FEA0448AA68040F6F20620', X'5A660E2279125C4E8D799E8151FA239D', X'5A660E2279125C4E8D799E8151FA239D', 1, NULL, NULL, 2, NULL, 0, 'Hold in refrigerator for 24 hours.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E3A6575DF9A9E74B8CCCD8ACE0F52F70', X'5A660E2279125C4E8D799E8151FA239D', X'5A660E2279125C4E8D799E8151FA239D', 2, NULL, NULL, 2, NULL, 0, 'Grind meats through 8 mm (3/8) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'549ADCF040C7FC448F962169D4BE821D', X'5A660E2279125C4E8D799E8151FA239D', X'5A660E2279125C4E8D799E8151FA239D', 3, NULL, NULL, 2, NULL, 0, 'Stuff into 20-24 mm sheep casings, forming 15 cm (6) straight links.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'74101D27139B0E45AC9C71892B66A40D', X'5A660E2279125C4E8D799E8151FA239D', X'5A660E2279125C4E8D799E8151FA239D', 4, NULL, NULL, 2, NULL, 0, 'Ferment/dry at 22-24° C (72-75° F), 85-90% humidly for 48 hours.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B05FA5A29FA6D5438CE57FD588F3A5FA', X'5A660E2279125C4E8D799E8151FA239D', X'5A660E2279125C4E8D799E8151FA239D', 5, NULL, NULL, 2, NULL, 0, 'Dry at 15-12° C (59-53° F), 80-85% humidity for 6-25 days (depending on a size). Hold semi-dry sausage in refrigerator. Drying sausage for 25 days or more will produce ready to eat dry sausage.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'799BD0714C84CA4884CFE68DFC2C1F29', X'B40038EA6CB2EA458386493FB3740232', X'B40038EA6CB2EA458386493FB3740232', 0, NULL, NULL, 2, NULL, 0, 'Grind meat through 6 mm (1/4`) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'55EAC594D6C5AA459CD4950681341796', X'B40038EA6CB2EA458386493FB3740232', X'B40038EA6CB2EA458386493FB3740232', 1, NULL, NULL, 2, NULL, 0, 'Mix meat with all ingredients. Hold for 24 hours in refrigerator.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D57214121198A44C9CF24DFB73BC478C', X'B40038EA6CB2EA458386493FB3740232', X'B40038EA6CB2EA458386493FB3740232', 2, NULL, NULL, 2, NULL, 0, 'Stuff into 36 mm pork or beef casings.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'615474E70ED17A44B21BB2782A02FB6E', X'B40038EA6CB2EA458386493FB3740232', X'B40038EA6CB2EA458386493FB3740232', 3, NULL, NULL, 2, NULL, 0, 'Apply a thin smoke at 20-24º C (68-75º F) (smoking step is optional) or ferment/dry sausages at 20-24º C (68-75º F) for 24-48 hours.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6D88C0188788194286900593837C5D54', X'B40038EA6CB2EA458386493FB3740232', X'B40038EA6CB2EA458386493FB3740232', 4, NULL, NULL, 2, NULL, 0, 'Dry at 15-12º C (59-54º F), 75-80% humidity for 15 days.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FCB6A3CCD8BF134A9BE8A492C642DB6A', X'B40038EA6CB2EA458386493FB3740232', X'B40038EA6CB2EA458386493FB3740232', 5, NULL, NULL, 2, NULL, 0, 'Store sausages at 10-12º C (50-53º F), 65-70% humidity or refrigerate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'752A15AA008E8546AC5DC9504B0C81DE', X'B663D1DA4A4DAE45A8E21C21245E26B3', X'B663D1DA4A4DAE45A8E21C21245E26B3', 0, NULL, NULL, 2, NULL, 0, 'Grind pork and fat through 8 mm (3/8) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F608A3D7A6C4FE438E27575AFBF13D5E', X'B663D1DA4A4DAE45A8E21C21245E26B3', X'B663D1DA4A4DAE45A8E21C21245E26B3', 1, NULL, NULL, 2, NULL, 0, 'Mix all ingredients with ground meat. Hold for 24 hours in refrigerator.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C8597C65E1C3974ABD87200B333AF83B', X'B663D1DA4A4DAE45A8E21C21245E26B3', X'B663D1DA4A4DAE45A8E21C21245E26B3', 2, NULL, NULL, 2, NULL, 0, 'Stuff into 32-34 mm pork casings forming 30 cm (12`) loops OR into 50-60 mm pork bungs making straight sections.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7874A6C1910CAF4CABFE26FE8947BE2D', X'B663D1DA4A4DAE45A8E21C21245E26B3', X'B663D1DA4A4DAE45A8E21C21245E26B3', 3, NULL, NULL, 2, NULL, 0, 'Ferment/dry at 25-30º C (77-86º F) 2-3 days, 80-85% humidity. Apply a thin cold smoke at <25° C (77° F) during this step.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C1F643EA5389DF458A9384434F44EAF6', X'B663D1DA4A4DAE45A8E21C21245E26B3', X'B663D1DA4A4DAE45A8E21C21245E26B3', 4, NULL, NULL, 2, NULL, 0, 'Dry 32-34 mm sausages at 12-15º C (53-59º F), 70-75% humidity for 14 days OR pork bungs for 2 months.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1D2897C7B087A247B73A2000C1F2AFA3', X'B663D1DA4A4DAE45A8E21C21245E26B3', X'B663D1DA4A4DAE45A8E21C21245E26B3', 5, NULL, NULL, 2, NULL, 0, 'Store sausages at 10-12º C (50-53º F), 65-70% humidity.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5583D3FE47FCE8478DA0BBCB0F209A61', X'E30E82C1244B2543A9964465109CC2A6', X'E30E82C1244B2543A9964465109CC2A6', 0, NULL, NULL, 2, NULL, 0, 'Smash garlic and using mortar and pestle grind it with wine into the paste.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'35DE63D2D9404D449B8351CC7C811EB0', X'E30E82C1244B2543A9964465109CC2A6', X'E30E82C1244B2543A9964465109CC2A6', 1, NULL, NULL, 2, NULL, 0, 'Lean cuts from pork head, loin, shoulder, legs. Remove all membranes, sinews, gristles etc.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6A1A33BAB3F07042B84836D88B417B28', X'E30E82C1244B2543A9964465109CC2A6', X'E30E82C1244B2543A9964465109CC2A6', 2, NULL, NULL, 2, NULL, 0, 'Grind meat through 8 mm (3/8`) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'02DA8309370AA3408D13CDACD223F47C', X'E30E82C1244B2543A9964465109CC2A6', X'E30E82C1244B2543A9964465109CC2A6', 3, NULL, NULL, 2, NULL, 0, 'Mix ground meat with all ingredients adding as little water as needed for proper texture of the meat mass. Hold in refrigerator for 48 hours.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F71D08AD505C2043ADDD96A920C39595', X'E30E82C1244B2543A9964465109CC2A6', X'E30E82C1244B2543A9964465109CC2A6', 4, NULL, NULL, 2, NULL, 0, 'Stuff into 40-55 mm pork middles, tying off 8-10 cm (4`) links or into 50-90 mm pork bung. Prick each sausage with a needle, especially in visible air pockets. Ferment/dry at 22-24º C (72-75º F) for 48 hours, 90-85% humidity.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0FE70A5FE3598D41856ED5CECAA1E024', X'E30E82C1244B2543A9964465109CC2A6', X'E30E82C1244B2543A9964465109CC2A6', 5, NULL, NULL, 2, NULL, 0, 'Dry sausages for 30 days at 12-15° C (53-59° F), 75-80% humidity. Dry pork bungs longer, depending on diameter, until the sausage looses 33% of its original weight.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1868E3C22E41214AABC210F90FD9DBC2', X'E30E82C1244B2543A9964465109CC2A6', X'E30E82C1244B2543A9964465109CC2A6', 6, NULL, NULL, 2, NULL, 0, 'Store at 10° C (53° F) in a cool and ventilated area or refrigerate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CD5EE2A9C12AF2438167F1720D831F89', X'D42BD1D102D76F41ABCFF6B3183A4F8B', X'D42BD1D102D76F41ABCFF6B3183A4F8B', 0, NULL, NULL, 2, NULL, 0, 'Grind meat and belly through 10 mm (3/8) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CE00A67D54EE274C91CE8EE8F118D3E7', X'D42BD1D102D76F41ABCFF6B3183A4F8B', X'D42BD1D102D76F41ABCFF6B3183A4F8B', 1, NULL, NULL, 2, NULL, 0, 'Mix with all spices. Hold the mixture for 12 hours in refrigerator.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F703C9DD12940444979C817A97FE17BF', X'D42BD1D102D76F41ABCFF6B3183A4F8B', X'D42BD1D102D76F41ABCFF6B3183A4F8B', 2, NULL, NULL, 2, NULL, 0, 'Stuff into 32-36 mm hog casings forming links, Tie the ends together.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'59D36E511E196B4F9B27B9990CA93DAE', X'D42BD1D102D76F41ABCFF6B3183A4F8B', X'D42BD1D102D76F41ABCFF6B3183A4F8B', 3, NULL, NULL, 2, NULL, 0, 'Apply a thin cold smoke and dry at 18° C (64° F) for 2 days. Cold smoking does not have to be a continuous process.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A7AECAFE726B2F4AB8E209393F56DBA6', X'D42BD1D102D76F41ABCFF6B3183A4F8B', X'D42BD1D102D76F41ABCFF6B3183A4F8B', 4, NULL, NULL, 2, NULL, 0, 'Dry sausages for 14 days at 12-15° C (53-59° F), 75-80% humidity.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'15D3EC52B9913143AD36C0F5BDF6D4C8', X'3CAC8280111835498417356D4443CB41', X'3CAC8280111835498417356D4443CB41', 0, NULL, NULL, 2, NULL, 0, 'Grind meat through 8 mm (3/8) plate. For better particle definition manually cut partially frozen fat into 6-10 mm cubes.');
