INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'26568588A0DBBF4D8128806B531992E8', X'71DC5374C3F2BB4883E7515FCAFC928E', X'71DC5374C3F2BB4883E7515FCAFC928E', 6, NULL, NULL, 2, NULL, 0, 'Store at 10-12° C (50-52 F), 60-65% humidity or refrigerate.');
