INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0C90E210275D73448633EE8FDBB472AD', X'B837640631701541B068371B81984212', X'B837640631701541B068371B81984212', 2, NULL, NULL, 2, NULL, 0, 'Mix meat, pumpkin and all ingredients together. Hold for 24 hours in refrigerator.');
