INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5E3B4C33AEB7C84A982A6D0CD9883214', X'B837640631701541B068371B81984212', X'B837640631701541B068371B81984212', 6, NULL, NULL, 2, NULL, 0, 'Store sausages at 10-12º C (50-53º F), <75% humidity or refrigerate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0419BEE1DF5DB64199132D8ABC4B273A', X'FA0E191145DB6848B248237FFA5E414A', X'FA0E191145DB6848B248237FFA5E414A', 0, NULL, NULL, 2, NULL, 0, 'Grind pork through 10 mm (3/8`) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DE6053499E56F547A741389B9C05BC72', X'FA0E191145DB6848B248237FFA5E414A', X'FA0E191145DB6848B248237FFA5E414A', 1, NULL, NULL, 2, NULL, 0, 'Dice partially frozen fat into 6 mm (1/4`) cubes.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3DE6709AE65F584AB37DF799217B3CCE', X'FA0E191145DB6848B248237FFA5E414A', X'FA0E191145DB6848B248237FFA5E414A', 2, NULL, NULL, 2, NULL, 0, 'Mix salt, Cure #1, spices and meats together. Hold for 24 hours in refrigerator.');
