INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AB7F1760E1DD3147B935FDE450B76F10', X'FA0E191145DB6848B248237FFA5E414A', X'FA0E191145DB6848B248237FFA5E414A', 4, NULL, NULL, 2, NULL, 0, 'Ferment/dry at 25° C (77° F), 85-90% humidity for 30 hours.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'954235CFA04B3D469DE6271E91D64E6A', X'FA0E191145DB6848B248237FFA5E414A', X'FA0E191145DB6848B248237FFA5E414A', 5, NULL, NULL, 2, NULL, 0, 'Dry at 14-15° C (57-59° F), 70-80% humidity for 21 days.');
