INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'998C6C3D0E382A4583A904D61AE9E08E', X'A377C6EF15287F4E82526B339D158FB7', X'A377C6EF15287F4E82526B339D158FB7', 0, NULL, NULL, 2, NULL, 0, 'Remove tendons, sinews, hard connective tissue from offal meats, then cook the meats in water (below boiling point) until soft. Drain and cool.');
