INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4D1070F0305DA4469C099F61B4E18B11', X'0FCDF93C151A0648A601E74605948940', X'0FCDF93C151A0648A601E74605948940', 4, NULL, NULL, 2, NULL, 0, 'Store sausages at 10-12º C (50-53º F), <75% humidity or refrigerate.');
