INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F9BD792D3A287442BD9A62274E711BA5', X'B6398ACAD34E544B84002DB33CEFFACA', X'B6398ACAD34E544B84002DB33CEFFACA', 1, NULL, NULL, 2, NULL, 0, 'Stuff the mixture into 36 mm hog casing and tie the ends together forming a ring.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8578354555D1954AA60093AB6D8B0CDB', X'B6398ACAD34E544B84002DB33CEFFACA', X'B6398ACAD34E544B84002DB33CEFFACA', 2, NULL, NULL, 2, NULL, 0, 'Apply a thin cold smoke at 18° C (64° F) for 10-15 days. Smoking need not to be continuous, for example the fire can be put out at night and restarted in the morning as long as the temperature inside of the smokehouse stays below 18° C. The sausage is smoked with oak wood.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BD25E3A5F6936F449296337ACCDBA0B4', X'B6398ACAD34E544B84002DB33CEFFACA', X'B6398ACAD34E544B84002DB33CEFFACA', 3, NULL, NULL, 2, NULL, 0, 'Refrigerate.');
