INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3186953898439B44AC2A1B988E427A4E', X'9521E2C1F130204094CE2ED9E28E7DD4', X'9521E2C1F130204094CE2ED9E28E7DD4', 1, NULL, NULL, 2, NULL, 0, 'Grind fat through 5/16 (8 mm) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'70F8421CE313C048AFCB9207D3DC1414', X'9521E2C1F130204094CE2ED9E28E7DD4', X'9521E2C1F130204094CE2ED9E28E7DD4', 2, NULL, NULL, 2, NULL, 0, 'Mix meat, fat and all ingredients together. Place the mixture for a week in a refrigerator or a cool place where temperature will not exceed 11 ° C (52º F). Cover the container and stir the mixture twice a day.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A88627438F757D43890490D9BC5F9F6B', X'9521E2C1F130204094CE2ED9E28E7DD4', X'9521E2C1F130204094CE2ED9E28E7DD4', 3, NULL, NULL, 2, NULL, 0, 'Stuff firmly into small diameter (< 30 mm) hog casings forming horse-shoe shaped loops about 1 foot (30 cm) long.');
