INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4C1E22EDAF9B414DA4E00B5405BF6EA2', X'9521E2C1F130204094CE2ED9E28E7DD4', X'9521E2C1F130204094CE2ED9E28E7DD4', 5, NULL, NULL, 2, NULL, 0, 'Dry at 16 ? 12º C (60-54º F), 85 ? 80% humidity for 21 days. The sausage is dried until around 30% in weight is lost.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'12EE25FB7A4E134EB2184475C3BE9E04', X'9521E2C1F130204094CE2ED9E28E7DD4', X'9521E2C1F130204094CE2ED9E28E7DD4', 6, NULL, NULL, 2, NULL, 0, 'Store sausages at 10-15º C (50-59º F), < 75% humidity.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3041BDE0FE1CAA43A0F0F81CB9672A99', X'C9D8D1BC08FA254781728FF23F5008B9', X'C9D8D1BC08FA254781728FF23F5008B9', 0, NULL, NULL, 2, NULL, 0, 'Cut meat into 1 (25 mm) cubes.');
