INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'38B435C4336D2049A124EB3591AA8165', X'C9D8D1BC08FA254781728FF23F5008B9', X'C9D8D1BC08FA254781728FF23F5008B9', 2, NULL, NULL, 2, NULL, 0, 'Mix meat with marinade and place for 12 hours in refrigerator. Drain.');
